insert into room_unit_comments values (6049, 121301, 3, 7, 1, 0, 1, '因為要上台表演，\\n所以要小心\\n不能吃太多。\\n但是……真的好好吃啊♪', 0);
insert into room_unit_comments values (6050, 121301, 3, 8, 1, 0, 1, '這個故事好有趣……\\n似乎可以當作\\n編劇的參考呢……♪', 0);
insert into room_unit_comments values (6052, 107501, 1, 103, 12, 0, 1, '嘿嘿……♪\\n兩個人一起看的夕陽……\\n很漂亮呢……', 0);
insert into room_unit_comments values (6053, 107501, 2, 103, 12, 0, 1, '你接下來\\n要去練游泳嗎？\\n我可以陪你喔♪', 0);
insert into room_unit_comments values (6054, 107601, 1, 103, 12, 0, 1, '在主人的帶領下，\\n在下也可以抵達\\n未知的領域。', 0);
insert into room_unit_comments values (6055, 107601, 2, 103, 12, 0, 1, '只要跟著主人，\\n比海還深，\\n比天空還高的地方，\\n都可以做為目標前往呢。', 0);
insert into room_unit_comments values (6056, 107801, 1, 103, 12, 0, 1, '和那傢伙一起\\n在夏天的深海裡……討厭，\\n我真是的，\\n在想什麼啦！', 0);
insert into room_unit_comments values (6057, 107801, 2, 103, 12, 0, 1, '我之所以可以\\n過得如此開心，\\n都是因為有你喔。\\n謝、謝謝你。', 0);
