insert into room_unit_comments values (6068, 121401, 2, 4, 1, 0, 1, '物產展的東西\\n這樣就準備好了……\\n那麼，\\n你也來幫忙搬吧♪', 0);
insert into room_unit_comments values (6069, 121401, 2, 101, 4, 0, 1, '面對和平常不一樣的我，\\n你是不是看呆啦？\\n真拿你沒辦法耶～♪', 0);
insert into room_unit_comments values (6070, 121401, 2, 102, 8, 0, 1, '賞花給人\\n很風雅又成熟的感覺♪\\n但是……\\n也不能忘了糰子呢！', 0);
insert into room_unit_comments values (6071, 121401, 2, 1, 1, 1, 1, '幫大家\\n做了早餐，\\n他們會喜歡嗎？', 0);
insert into room_unit_comments values (6072, 121401, 2, 2, 1, 2, 1, '現在是\\n客人最多的時候呢！\\n打起精神上吧！', 0);
insert into room_unit_comments values (6073, 121401, 2, 3, 1, 3, 1, '差不多該睡了……\\n睡眠不足\\n可是肌膚的大敵呢。', 0);
insert into room_unit_comments values (6075, 121401, 3, 2, 1, 0, 1, '軟綿綿的\\n好舒服～♪\\n疲勞都消除了……', 0);
insert into room_unit_comments values (6076, 121401, 3, 3, 1, 0, 1, '要不要\\n也給那個孩子一件\\n跟我成對的服裝呢♪', 0);
insert into room_unit_comments values (6077, 121401, 3, 4, 1, 0, 1, '穿上漂亮的衣服，\\n讓香氣\\n包覆全身……呵呵，\\n這就是大人的愛好吧♪', 0);
insert into room_unit_comments values (6078, 121401, 3, 5, 1, 0, 1, '好溫暖～♪\\n在這裡睡午覺\\n好像會很舒服呢……', 0);
insert into room_unit_comments values (6080, 121401, 3, 7, 1, 0, 1, '努力工作後，\\n肚子就會比平常\\n還餓呢～！', 0);
insert into room_unit_comments values (6081, 121401, 3, 8, 1, 0, 1, '沒有趕上\\n最新的流行嗎？\\n得採用進\\n物產展的販售商品裡才行！', 0);
insert into room_unit_comments values (6083, 121501, 1, 1, 1, 0, 1, '怪盜的事情……\\n要是被爸爸媽媽\\n知道了，\\n我該怎麼解釋呢……', 0);
insert into room_unit_comments values (6084, 121501, 1, 2, 1, 0, 1, '就算是怪盜，\\n到別人家叨擾時，\\n也要注意自身儀容\\n才行……', 0);
insert into room_unit_comments values (6085, 121501, 1, 3, 1, 0, 1, '開鎖好難……\\n嗯～\\n我得多多練習才行。', 0);
insert into room_unit_comments values (6086, 121501, 1, 4, 1, 0, 1, '看不見真身的\\n怪盜傳聞……？\\n希、希望\\n不是在說我……！', 0);
insert into room_unit_comments values (6087, 121501, 1, 5, 1, 0, 1, '預告信……\\n郵票應該要\\n貼在哪裡啊……？', 0);
insert into room_unit_comments values (6088, 121501, 1, 101, 4, 0, 1, '我雖然已經習慣\\n怪盜活動了……\\n但前輩不在身邊\\n還是會很不安啊……', 0);
insert into room_unit_comments values (6089, 121501, 1, 102, 8, 0, 1, '總、總有一天，\\n我也能偷走前輩的心嗎……？\\n討討討、討厭啦，\\n我在說什麼呢……', 0);
insert into room_unit_comments values (6090, 121501, 2, 1, 1, 0, 1, '你在報紙上看到\\n怪盜很活躍的消息……？\\n會、會是誰呢……\\n啊哈哈。', 0);
insert into room_unit_comments values (6091, 121501, 2, 2, 1, 0, 1, '尾隨潛入……\\n沒有存在感這一點，\\n沒想到也有\\n派上用場的時候呢。', 0);
insert into room_unit_comments values (6092, 121501, 2, 3, 1, 0, 1, '前前前、前輩！\\n我硬是把帽子戴下去，\\n結果\\n就脫不下來了……！', 0);
insert into room_unit_comments values (6093, 121501, 2, 4, 1, 0, 1, '我、我才沒有\\n偷聽喔！\\n只是大家都沒有發現我，\\n就開始聊了起來……', 0);
insert into room_unit_comments values (6094, 121501, 2, 101, 4, 0, 1, '你想看祕密道具？\\n那、那可是機密耶！\\n畢竟是祕密道具啊……\\n但如果是前輩的話……', 0);
insert into room_unit_comments values (6095, 121501, 2, 102, 8, 0, 1, '嘿嘿，\\n被發現了。\\n前輩總是能\\n找到我呢……', 0);
insert into room_unit_comments values (6096, 121501, 2, 1, 1, 1, 1, '早哈……\\n我因為保養祕密道具\\n整晚沒睡……\\n還有點睏～', 0);
insert into room_unit_comments values (6097, 121501, 2, 2, 1, 2, 1, '怪盜在白天活動……\\n那樣說不定\\n比較會讓人\\n感到驚奇……？', 0);
insert into room_unit_comments values (6098, 121501, 2, 3, 1, 3, 1, '天黑了\\n……差不多該開始\\n怪盜活動了吧？', 0);
insert into room_unit_comments values (6100, 121501, 3, 2, 1, 0, 1, '在這裡放鬆\\n可以消除疲勞呢～\\n我今天也要\\n努力當個怪盜。', 0);
insert into room_unit_comments values (6101, 121501, 3, 3, 1, 0, 1, '哇，好可愛……！\\n心被偷走了，\\n就是這種情況吧……？', 0);
insert into room_unit_comments values (6102, 121501, 3, 4, 1, 0, 1, '好迷人的香氣……可是，\\n如果沾在衣服上，\\n我躲起來的時候\\n就會被發現了吧……', 0);
insert into room_unit_comments values (6103, 121501, 3, 5, 1, 0, 1, '暖呼呼的\\n好舒服……\\n走夜路前\\n先溫暖一下身子吧……', 0);
insert into room_unit_comments values (6105, 121501, 3, 7, 1, 0, 1, '準備進行怪盜活動，\\n得趁現在\\n好好吃一頓才行……！', 0);
insert into room_unit_comments values (6106, 121501, 3, 8, 1, 0, 1, '女孩子祕密愈多\\n就愈有魅力……？\\n原、原來如此……', 0);
insert into room_unit_comments values (6108, 104501, 1, 103, 12, 0, 1, '虐待狂先生一直都在\\n空花的妄想中……\\n嘻嘻，流口水～♪', 0);
insert into room_unit_comments values (6109, 104501, 2, 103, 12, 0, 1, '我不討厭你\\n溫柔的觸碰……\\n好……好害羞。', 0);
insert into room_unit_comments values (6110, 121601, 1, 1, 1, 0, 1, '沒有大怪盜大人\\n偷不走的東西！\\n大概吧！', 0);
insert into room_unit_comments values (6111, 121601, 1, 2, 1, 0, 1, '與我相襯的\\n大寶物\\n在哪裡呢～', 0);
insert into room_unit_comments values (6112, 121601, 1, 3, 1, 0, 1, '偷偷摸摸……\\n只要消除氣息，\\n點心就能吃到飽了……笑。', 0);
insert into room_unit_comments values (6113, 121601, 1, 4, 1, 0, 1, '我偷懶的事情\\n又被發現了……\\n老大是順風耳～！', 0);
insert into room_unit_comments values (6114, 121601, 1, 5, 1, 0, 1, '是幫派成員也是大怪盜……！\\n這麼多頭銜，\\n正是有能力的證明！', 0);
insert into room_unit_comments values (6115, 121601, 1, 101, 4, 0, 1, '如果是哥哥的話……\\n我可以跟你\\n對分寶物喔。', 0);
insert into room_unit_comments values (6116, 121601, 1, 102, 8, 0, 1, '哥哥是那個啦。\\n類似大怪盜祈梨大人的\\n搭檔之類的角色。', 0);
insert into room_unit_comments values (6117, 121601, 2, 1, 1, 0, 1, '謹慎面對任何事情\\n是很重要的。\\n我……我不是膽小喔！', 0);
insert into room_unit_comments values (6118, 121601, 2, 2, 1, 0, 1, '偷、偷聽？\\n我才沒有咧！\\n咻──咻──咻──', 0);
insert into room_unit_comments values (6119, 121601, 2, 3, 1, 0, 1, '想要情報\\n就用點心交換吧。\\n來，交換交換♪', 0);
insert into room_unit_comments values (6120, 121601, 2, 4, 1, 0, 1, '噓！\\n我正在跟監。\\n有話晚點說……', 0);
insert into room_unit_comments values (6121, 121601, 2, 101, 4, 0, 1, '一跟哥哥聊天\\n時間就過得特別快……？\\n你這個時間小偷！', 0);
insert into room_unit_comments values (6122, 121601, 2, 102, 8, 0, 1, '你來的正好！\\n我有一個好差事。\\n要不要參一腳呀？', 0);
insert into room_unit_comments values (6123, 121601, 2, 1, 1, 1, 1, '太陽\\n出來了呢……\\n不方便做壞事了。', 0);
insert into room_unit_comments values (6124, 121601, 2, 2, 1, 2, 1, '已經這個時候啦。\\n吃了飯還要睡午覺……\\n討厭～我好忙喔♪', 0);
insert into room_unit_comments values (6125, 121601, 2, 3, 1, 3, 1, '呵呵呵！\\n夜晚才是大怪盜的時間！\\n呼啊……', 0);
insert into room_unit_comments values (6127, 121601, 3, 2, 1, 0, 1, '嗯……\\n沒有警衛盯著\\n就很輕鬆呢。', 0);
insert into room_unit_comments values (6128, 121601, 3, 3, 1, 0, 1, '唔……！\\n這個太可愛了\\n我偷不下去……♪', 0);
insert into room_unit_comments values (6129, 121601, 3, 4, 1, 0, 1, '殘留在犯罪現場的\\n只有香水味……\\n呵呵，是時髦的大怪盜呢。', 0);
insert into room_unit_comments values (6130, 121601, 3, 5, 1, 0, 1, '這種地方意想不到地\\n會有暗門……\\n沒有嗎？', 0);
insert into room_unit_comments values (6132, 121601, 3, 7, 1, 0, 1, '嚼嚼……\\n這個美味……\\n真是了不起的陷阱呢。', 0);
insert into room_unit_comments values (6133, 121601, 3, 8, 1, 0, 1, '呣呣呣……\\n完全看不懂……\\n這都是暗號啦。', 0);
insert into room_unit_comments values (6135, 121701, 1, 1, 1, 0, 1, '哎呀，\\n咲戀\\n去哪裡了？', 0);
insert into room_unit_comments values (6136, 121701, 1, 2, 1, 0, 1, '哼哼哼♪\\n總覺得心情都\\n雀躍起來了♪', 0);
insert into room_unit_comments values (6137, 121701, 1, 3, 1, 0, 1, '那麼……\\n下一個事業要做什麼呢？\\n有什麼好玩的事情嗎……', 0);
insert into room_unit_comments values (6138, 121701, 1, 4, 1, 0, 1, '哦～呵呵呵\\n哎呀……我太開心了，\\n忍不住就笑了出來。', 0);
insert into room_unit_comments values (6139, 121701, 1, 5, 1, 0, 1, '這個房間很棒，\\n很有品味呢。\\n我也想要模仿看看。', 0);
insert into room_unit_comments values (6140, 121701, 1, 101, 4, 0, 1, '咲戀跟那個人\\n感情很好呢。\\n當、當然，\\n感情好是好事喔！', 0);
insert into room_unit_comments values (6141, 121701, 1, 102, 8, 0, 1, '無時無刻都要\\n堂堂正正！\\n這正是\\n我自豪的地方呢！', 0);
insert into room_unit_comments values (6142, 121701, 2, 1, 1, 0, 1, '呵呵，日安！\\n我狀態絕佳唷。', 0);
insert into room_unit_comments values (6143, 121701, 2, 2, 1, 0, 1, '發生什麼事了嗎？\\n如果有\\n任何困難，\\n我一定會幫得上忙的。', 0);
insert into room_unit_comments values (6144, 121701, 2, 3, 1, 0, 1, '偶爾像這樣\\n放鬆地\\n聊天\\n也很好呢。', 0);
insert into room_unit_comments values (6145, 121701, 2, 4, 1, 0, 1, '我有構想了\\n下一次的事業。\\n方便的話\\n可以跟你討論嗎？', 0);
insert into room_unit_comments values (6146, 121701, 2, 101, 4, 0, 1, '方便的話\\n要不要聊一聊？\\n我想聽你說\\n咲戀的事。', 0);
insert into room_unit_comments values (6147, 121701, 2, 102, 8, 0, 1, '呵呵……\\n我剛剛\\n才跟咲戀\\n聊到你呢。', 0);
insert into room_unit_comments values (6148, 121701, 2, 1, 1, 1, 1, '來吧！\\n今天也要\\n全力以赴！', 0);
insert into room_unit_comments values (6149, 121701, 2, 2, 1, 2, 1, '午餐打算怎麼辦呢？\\n要不要邀請\\n咲戀看看？', 0);
insert into room_unit_comments values (6150, 121701, 2, 3, 1, 3, 1, '我要再一下下……\\n要先做好\\n明天的準備才行……', 0);
insert into room_unit_comments values (6152, 121701, 3, 2, 1, 0, 1, '哈～真不錯呢。\\n嚇，我真是的，\\n太不成體統了。', 0);
insert into room_unit_comments values (6153, 121701, 3, 3, 1, 0, 1, '好可愛喔。\\n不論到幾歲，\\n都會忍不住\\n心花怒放呢。', 0);
insert into room_unit_comments values (6154, 121701, 3, 4, 1, 0, 1, '好沉穩。\\n不知道咲戀\\n喜歡什麼香氣呢？', 0);
insert into room_unit_comments values (6155, 121701, 3, 5, 1, 0, 1, '呵呵，\\n熱情的火炎熊熊燃燒……\\n真是不錯。', 0);
insert into room_unit_comments values (6157, 121701, 3, 7, 1, 0, 1, '因為這道美食\\n讓我的錢都花完了……\\n真是\\n出色的商品啊！', 0);
insert into room_unit_comments values (6158, 121701, 3, 8, 1, 0, 1, '這是……嗯嗯。\\n這似乎可以成為\\n新事業的靈感呢。', 0);
insert into room_unit_comments values (6161, 121801, 1, 1, 1, 0, 1, '接下來……\\n今天必須要\\n做什麼事情啊？', 0);
insert into room_unit_comments values (6162, 121801, 1, 2, 1, 0, 1, '雖然有成堆的\\n事情要做，\\n但我不討厭動手呢♪', 0);
insert into room_unit_comments values (6163, 121801, 1, 3, 1, 0, 1, '哎呀？\\n我好像聽見了\\n秋乃的聲音……', 0);
insert into room_unit_comments values (6164, 121801, 1, 4, 1, 0, 1, '只要跟秋乃在一起，\\n就會忍不住\\n笑開懷呢。', 0);
insert into room_unit_comments values (6165, 121801, 1, 5, 1, 0, 1, '我不能\\n讓秋乃聽見\\n我的喪氣話。\\n會被她笑的。', 0);
insert into room_unit_comments values (6166, 121801, 1, 101, 4, 0, 1, '回過神來，\\n我已被秋乃牽著鼻子走……\\n但神奇的是，\\n我不覺得討厭呢。', 0);
insert into room_unit_comments values (6167, 121801, 1, 102, 8, 0, 1, '秋乃\\n把我當作勁敵。\\n為了她，\\n我得振作才行。', 0);
insert into room_unit_comments values (6168, 121801, 2, 1, 1, 0, 1, '怎麼了？\\n秋乃\\n剛剛\\n沒在那裡嗎？', 0);
insert into room_unit_comments values (6169, 121801, 2, 2, 1, 0, 1, '我不要緊的。\\n因為大家\\n都會幫我打理\\n育幼院的事啊。', 0);
insert into room_unit_comments values (6170, 121801, 2, 3, 1, 0, 1, '秋乃\\n剛剛在找你喔。\\n你做了什麼嗎？', 0);
insert into room_unit_comments values (6171, 121801, 2, 4, 1, 0, 1, '別看我\\n那麼忙……\\n如果只有一下下，\\n我還是可以陪你啦。', 0);
insert into room_unit_comments values (6172, 121801, 2, 101, 4, 0, 1, '問你喔，\\n你跟秋乃……\\n不，\\n還是算了……', 0);
insert into room_unit_comments values (6173, 121801, 2, 102, 8, 0, 1, '要不要邀請秋乃\\n來喝茶呢？\\n我有好多話\\n想跟她說♪', 0);
insert into room_unit_comments values (6174, 121801, 2, 1, 1, 1, 1, '早起的鳥兒有蟲吃喔。\\n為了迎接美好的一天，\\n得趕緊起床了。', 0);
insert into room_unit_comments values (6175, 121801, 2, 2, 1, 2, 1, '今天的午餐……\\n預計跟秋乃\\n一起吃飯呢。\\n你也來吧？', 0);
insert into room_unit_comments values (6176, 121801, 2, 3, 1, 3, 1, '呼啊……\\n不行。\\n不能讓秋乃看見\\n我鬆懈的樣子。', 0);
insert into room_unit_comments values (6178, 121801, 3, 2, 1, 0, 1, '嗯～\\n好放鬆喔。\\n我會就這樣\\n睡著吧……', 0);
insert into room_unit_comments values (6179, 121801, 3, 3, 1, 0, 1, '育幼院的大家\\n也最喜歡\\n玩偶了。', 0);
insert into room_unit_comments values (6180, 121801, 3, 4, 1, 0, 1, '好香喔……\\n這是秋乃的\\n興趣嗎？', 0);
insert into room_unit_comments values (6181, 121801, 3, 5, 1, 0, 1, '明明燃燒得\\n如此劇烈，\\n卻又溫暖得讓人安心，\\n就好像秋乃一樣呢。', 0);
insert into room_unit_comments values (6183, 121801, 3, 7, 1, 0, 1, '接二連三地冒出來……\\n這個育幼院\\n也會很想要吧……', 0);
insert into room_unit_comments values (6184, 121801, 3, 8, 1, 0, 1, '嗯嗯……\\n秋乃之前\\n說的，\\n就是這件事嗎？', 0);
insert into room_unit_comments values (6187, 122001, 1, 1, 1, 0, 1, '這件衣服對我來說\\n有點太可愛了。\\n好害羞……', 0);
insert into room_unit_comments values (6188, 122001, 1, 2, 1, 0, 1, '有部分的人說我\\n比海盜更像海盜，\\n且還有個出名的父親。', 0);
insert into room_unit_comments values (6189, 122001, 1, 3, 1, 0, 1, '為了避免海風\\n讓武器生鏽，\\n我先來保養它們吧。', 0);
insert into room_unit_comments values (6190, 122001, 1, 4, 1, 0, 1, '需要人手找寶藏的話，\\n我可以幫得上忙。\\n但要看父親的幹勁就是了。', 0);
insert into room_unit_comments values (6191, 122001, 1, 5, 1, 0, 1, '除靈要特別小心。\\n因為做錯一步，\\n連父親都會成佛了。', 0);
insert into room_unit_comments values (6192, 122001, 1, 101, 4, 0, 1, '和那個人一起\\n欣賞的海景……\\n真想永存於心。', 0);
insert into room_unit_comments values (6193, 122001, 1, 102, 8, 0, 1, '就算變成幽靈，\\n只要能見上一面……\\n我一定會對那個人說……', 0);
insert into room_unit_comments values (6194, 122001, 2, 1, 1, 0, 1, '我划船的技巧\\n有稍微變厲害一點喔。\\n下次展示給你看吧。', 0);
insert into room_unit_comments values (6195, 122001, 2, 2, 1, 0, 1, '必須去採買\\n航海需要的東西呢。\\n你有空嗎？', 0);
insert into room_unit_comments values (6196, 122001, 2, 3, 1, 0, 1, '有很多受詛咒財寶的\\n解咒委託……\\n呼……下一個是……', 0);
insert into room_unit_comments values (6197, 122001, 2, 4, 1, 0, 1, '晚上航海時，\\n父親會\\n當我的明燈。', 0);
insert into room_unit_comments values (6198, 122001, 2, 101, 4, 0, 1, '船上的\\n食材有限，\\n所以剛好可以練習廚藝。', 0);
insert into room_unit_comments values (6199, 122001, 2, 102, 8, 0, 1, '今後，\\n我也想和你\\n一起創造出\\n許多回憶。', 0);
insert into room_unit_comments values (6200, 122001, 2, 1, 1, 1, 1, '那個人的睡臉……\\n好想\\n再看一下下……', 0);
insert into room_unit_comments values (6201, 122001, 2, 2, 1, 2, 1, '規律的作息\\n在船上\\n是很重要的。', 0);
insert into room_unit_comments values (6202, 122001, 2, 3, 1, 3, 1, '夜晚的海上\\n很容易分辨幽靈。\\n因為它們會散發出蒼白的光芒。', 0);
insert into room_unit_comments values (6204, 122001, 3, 2, 1, 0, 1, '這個舒適感\\n就好像在海上漂蕩一般呢。\\n真令人放鬆……', 0);
insert into room_unit_comments values (6205, 122001, 3, 3, 1, 0, 1, '你也要\\n一起去看海嗎？\\n開玩笑的啦……呵呵。', 0);
insert into room_unit_comments values (6206, 122001, 3, 4, 1, 0, 1, '好清爽的香氣喔……\\n我彷彿看見\\n一片藍色的海洋呢。', 0);
insert into room_unit_comments values (6207, 122001, 3, 5, 1, 0, 1, '好溫暖……\\n暖和了\\n我被海風吹冷的身體呢。', 0);
insert into room_unit_comments values (6209, 122001, 3, 7, 1, 0, 1, '啊，好好吃……\\n如果耐久放的話，\\n要不要帶著出海吃呢……', 0);
insert into room_unit_comments values (6210, 122001, 3, 8, 1, 0, 1, '杏奈\\n告訴我的小說是……\\n啊，是這本吧。', 0);
insert into room_unit_comments values (6212, 102601, 1, 103, 12, 0, 1, '我一直都在\\n袖手旁觀啊。\\n啊～好忙好忙。', 0);
insert into room_unit_comments values (6213, 102601, 2, 103, 12, 0, 1, '呼～只要跟你在一起，\\n不管身在何處，\\n都是那麼舒服地想發懶呢～', 0);
insert into room_unit_comments values (6214, 121901, 1, 1, 1, 0, 1, '我身為暗黑騎士，\\n也是傳說中的海盜，\\n字典裡沒有不可能三個字！', 0);
insert into room_unit_comments values (6215, 121901, 1, 2, 1, 0, 1, '海盜團的成員\\n隨時招募中……\\n這樣應該就可以\\n增加新的盟友吧……！', 0);
insert into room_unit_comments values (6216, 121901, 1, 3, 1, 0, 1, '好想要可以\\n放在肩上的寵物喔。\\n趁現在\\n去一趟寵物用品店吧……！', 0);
insert into room_unit_comments values (6217, 121901, 1, 4, 1, 0, 1, '哪裡有在賣\\n藏寶圖呢……\\n二手書店？骨董店？', 0);
insert into room_unit_comments values (6218, 121901, 1, 5, 1, 0, 1, '穿上\\n這套海盜服後，\\n就能喚醒\\n新的前世記憶吧！', 0);
insert into room_unit_comments values (6219, 121901, 1, 101, 4, 0, 1, '好想在不久的將來\\n跟吉格魯特一起\\n出海冒險喔……\\n一定會很有趣的！', 0);
insert into room_unit_comments values (6220, 121901, 1, 102, 8, 0, 1, '我恭候多時囉，盟友！\\n這是有你我才存在的\\n海盜團……所以……\\n你今天就陪著我吧。', 0);
insert into room_unit_comments values (6221, 121901, 2, 1, 1, 0, 1, '吉格魯特！\\n我在漁港\\n分到魚啦！\\n一起吃吧！', 0);
insert into room_unit_comments values (6222, 121901, 2, 2, 1, 0, 1, '吉格魯特，\\n你要不要試穿這件大衣？\\n呵呵，\\n一定會很適合的！', 0);
insert into room_unit_comments values (6223, 121901, 2, 3, 1, 0, 1, '我製作了\\n海盜船新的祕密武器喔！\\n吉格魯特，\\n你等一下要不要來看看？', 0);
insert into room_unit_comments values (6224, 121901, 2, 4, 1, 0, 1, '我是傳說中的海盜，\\n所以很擅長游泳！\\n但、但是我怕\\n深水區……', 0);
insert into room_unit_comments values (6225, 121901, 2, 101, 4, 0, 1, '呵呵，為了隨時可以\\n漂流到無人島上，\\n我都有準備乾糧喔。\\n也有吉格魯特的分呢！', 0);
insert into room_unit_comments values (6226, 121901, 2, 102, 8, 0, 1, '冥風戰記的新篇章\\n完成囉……！\\n吉格魯特，\\n我希望你可以第一個讀！', 0);
insert into room_unit_comments values (6227, 121901, 2, 1, 1, 1, 1, '呵，今天天氣晴朗，\\n是絕佳的航海日呢！\\n我們趕緊出海吧！', 0);
insert into room_unit_comments values (6228, 121901, 2, 2, 1, 2, 1, '我釣到\\n午餐要吃的魚了！\\n呵呵，\\n我確實很像海盜吧？', 0);
insert into room_unit_comments values (6229, 121901, 2, 3, 1, 3, 1, '夜晚的大海一片漆黑，\\n有點恐怖呢……\\n吉格魯特，\\n你不要離開我身邊喔……！', 0);
insert into room_unit_comments values (6231, 121901, 3, 2, 1, 0, 1, '這個東西真適合\\n不拘小節的大海盜呢……！\\n呃……這個\\n哪一間店買得到啊？', 0);
insert into room_unit_comments values (6232, 121901, 3, 3, 1, 0, 1, '哇哇，好可愛……！\\n好想放在\\n船艙的床上喔……\\n不能帶走嗎？', 0);
insert into room_unit_comments values (6233, 121901, 3, 4, 1, 0, 1, '喔，這個香氣是……！\\n專注力好像提升了。\\n這個一定要放在\\n海盜船的船長室裡！', 0);
insert into room_unit_comments values (6234, 121901, 3, 5, 1, 0, 1, '聽說這是\\n水手的傳統。\\n徘徊在海上的神秘火焰嗎！\\n好溫暖啊！', 0);
insert into room_unit_comments values (6236, 121901, 3, 7, 1, 0, 1, '肚子餓了\\n就無法當海盜！\\n也無法寫小說！', 0);
insert into room_unit_comments values (6237, 121901, 3, 8, 1, 0, 1, '女海盜跟騎士\\n後來墜入愛河……？\\n不是愛情故事嗎！？\\n我、我很有興趣。', 0);
insert into room_unit_comments values (6239, 122101, 1, 1, 1, 0, 1, '沒想到平時的\\n隱居山林\\n會掀起熱潮……\\n人生真是世事難料。', 0);
insert into room_unit_comments values (6240, 122101, 1, 2, 1, 0, 1, '露營道具……\\n不對，是露營器具！\\n這詞真帥……♪', 0);
insert into room_unit_comments values (6241, 122101, 1, 3, 1, 0, 1, "Let's BB營！\\n這個口呼\\n可能會流行……？", 0);
insert into room_unit_comments values (6242, 122101, 1, 4, 1, 0, 1, '來募集看看……\\n露營夥伴好了？\\n順便兼任朋友……', 0);
insert into room_unit_comments values (6243, 122101, 1, 5, 1, 0, 1, '優花梨小姐和\\n純小姐……不曉得\\n過得好嗎？真希望能再\\n和她們一起露營……', 0);
insert into room_unit_comments values (6244, 122101, 1, 101, 4, 0, 1, '刊載了我的\\n取材內容的書……\\n得把它送去給\\n【森林守衛】……♪', 0);
insert into room_unit_comments values (6245, 122101, 1, 102, 8, 0, 1, '如果那個人遇到了\\n感到沮喪的事……\\n到時候我希望\\n能幫上他的忙。', 0);
insert into room_unit_comments values (6246, 122101, 2, 1, 1, 0, 1, '野外求生技術是\\n也能夠活用\\n個人改造的\\n好朋友號的技術！', 0);
insert into room_unit_comments values (6247, 122101, 2, 2, 1, 0, 1, '人家會說……\\n聊天聊得很熱絡，\\n但我的話，\\n就會變成是自嗨呢。', 0);
insert into room_unit_comments values (6248, 122101, 2, 3, 1, 0, 1, '這衣服，其實是……\\n以前美里老師\\n幫我製作的\\n東西！', 0);
insert into room_unit_comments values (6249, 122101, 2, 4, 1, 0, 1, '我以前有用山中回音\\n來練習對話，\\n但幾乎都跟鸚鵡回話\\n一樣，很尷尬呢～', 0);
insert into room_unit_comments values (6250, 122101, 2, 101, 4, 0, 1, '大家好，我是大師。\\n……啊，不好意思。\\n為了回應大家的期待，\\n我正在練習……', 0);
insert into room_unit_comments values (6251, 122101, 2, 102, 8, 0, 1, '就算從邊緣人\\n畢業了……\\n我也一輩子都是BB團！', 0);
insert into room_unit_comments values (6252, 122101, 2, 1, 1, 1, 1, '聽著小鳥的\\n鳴叫聲醒來……\\n感覺真舒服呢。', 0);
insert into room_unit_comments values (6253, 122101, 2, 2, 1, 2, 1, '說到露營就是\\n好吃的餐點！\\n我要大顯身手囉！', 0);
insert into room_unit_comments values (6254, 122101, 2, 3, 1, 3, 1, '完全天黑了呢……\\n要不要在睡前\\n觀賞星星呢？', 0);
insert into room_unit_comments values (6256, 122101, 3, 2, 1, 0, 1, '在都市裡放鬆……\\n在自然裡放鬆……\\n放鬆的無限輪迴……', 0);
insert into room_unit_comments values (6257, 122101, 3, 3, 1, 0, 1, '只要好好觀察，\\n或許就能靠野外\\n求生技術重現……？', 0);
insert into room_unit_comments values (6258, 122101, 3, 4, 1, 0, 1, '呼……果然聞到\\n樹木的薰香\\n就令人感到放鬆呢。', 0);
insert into room_unit_comments values (6259, 122101, 3, 5, 1, 0, 1, '啊……整個人都\\n暖起來了。總覺得\\n心靈感到了寬裕呢……', 0);
insert into room_unit_comments values (6261, 122101, 3, 7, 1, 0, 1, '這美味的程度……！\\n下次把這個\\n帶去露營好了……', 0);
insert into room_unit_comments values (6262, 122101, 3, 8, 1, 0, 1, '嗯嗯……\\n有各種知識，\\n就能讓露營\\n更開心呢。', 0);
insert into room_unit_comments values (6264, 122201, 1, 1, 1, 0, 1, '房間漸漸被\\n露營用具佔滿了……\\n露營……是這麼\\n可怕的興趣嗎……？', 0);
insert into room_unit_comments values (6265, 122201, 1, 2, 1, 0, 1, '希望無論是工作\\n還是在戶外，\\n都能成為\\n可靠的姐姐……', 0);
insert into room_unit_comments values (6266, 122201, 1, 3, 1, 0, 1, '這服裝不僅可愛，\\n還真的很舒服呢～♪\\n能打扮和享受露營，\\n真是太棒了！', 0);
insert into room_unit_comments values (6267, 122201, 1, 4, 1, 0, 1, '嗯～接下來要做些\\n什麼露營餐呢？\\n他喜歡的東西是……', 0);
insert into room_unit_comments values (6268, 122201, 1, 5, 1, 0, 1, '週末能夠\\n快點到來嗎……\\n好想忘了工作，\\n盡情在露營喝酒……！', 0);
insert into room_unit_comments values (6269, 122201, 1, 101, 4, 0, 1, '我學會了新的\\n露營餐食譜！\\n他應該也會\\n喜歡……吧♪', 0);
insert into room_unit_comments values (6270, 122201, 1, 102, 8, 0, 1, '要不要也偷偷買齊\\n給他用的餐具呢……\\n這、這怎麼說\\n都還太早了吧……', 0);
insert into room_unit_comments values (6271, 122201, 2, 1, 1, 0, 1, '下次辦一場\\n財團主辦的\\n大規模露營\\n或許也不錯呢。', 0);
insert into room_unit_comments values (6272, 122201, 2, 2, 1, 0, 1, '單飛露營也不錯，\\n但我果然還是\\n想和你……\\n沒、沒什麼！', 0);
insert into room_unit_comments values (6273, 122201, 2, 3, 1, 0, 1, '露營的事就來問我，\\n我什麼都能教你！\\n……我可都會\\n先調查過喔！', 0);
insert into room_unit_comments values (6274, 122201, 2, 4, 1, 0, 1, '在露營場喝的\\n最棒的那一杯，\\n其他地方可品嚐不到！\\n來，乾杯！', 0);
insert into room_unit_comments values (6275, 122201, 2, 101, 4, 0, 1, '嗯，我決定了！\\n下次是禁酒露營！\\n因為我想和你\\n好好聊天呀♪', 0);
insert into room_unit_comments values (6276, 122201, 2, 102, 8, 0, 1, '託你的福，\\n我才能品嚐到\\n真正最棒的那一杯……\\n謝謝♪', 0);
insert into room_unit_comments values (6277, 122201, 2, 1, 1, 1, 1, '嗯～好舒服！\\n自從有了露營\\n這興趣後，連覺都\\n睡得很好呢♪', 0);
insert into room_unit_comments values (6278, 122201, 2, 2, 1, 2, 1, '中午我招待你\\n吃大餐。我要展現\\n我做露營餐\\n練出來的廚藝囉～！', 0);
insert into room_unit_comments values (6279, 122201, 2, 3, 1, 3, 1, '差不多該睡覺了。\\n這是為了明天\\n也能欣賞晨光。\\n當然還有附咖啡喔♪', 0);
insert into room_unit_comments values (6281, 122201, 3, 2, 1, 0, 1, '這最適合用來\\n好好放鬆了呢～\\n感覺也很適合\\n放在帳篷裡！', 0);
insert into room_unit_comments values (6282, 122201, 3, 3, 1, 0, 1, '呵呵，真可愛呢～\\n……下次帶去\\n露營場好了？', 0);
insert into room_unit_comments values (6283, 122201, 3, 4, 1, 0, 1, '嗯，好香……♪\\n是會讓人想起\\n露營場的自然薰香～', 0);
insert into room_unit_comments values (6284, 122201, 3, 5, 1, 0, 1, '呼，溫暖起來了～\\n真想在冬天\\n露營時用用看呢。', 0);
insert into room_unit_comments values (6286, 122201, 3, 7, 1, 0, 1, '好好吃～♪\\n非常適合\\n當露營喝酒的\\n下酒菜呢！', 0);
insert into room_unit_comments values (6287, 122201, 3, 8, 1, 0, 1, '喔……原來還有\\n這種露營餐？\\n他會喜歡嗎……', 0);
insert into room_unit_comments values (6289, 100701, 1, 103, 12, 0, 1, '要一輩子跟著你的\\n幽靈的一輩子是\\n很長～～～的！', 0);
insert into room_unit_comments values (6290, 100701, 2, 103, 12, 0, 1, '唔……你要是一直碰，\\n就要穿過你喔！\\n騙你的，呵呵♪', 0);
insert into room_unit_comments values (6291, 122301, 1, 1, 1, 0, 1, '嗯……\\n今天要和貝斯\\n玩些什麼呢～', 0);
insert into room_unit_comments values (6292, 122301, 1, 2, 1, 0, 1, '班比也想\\n和僕從的朋友\\n當好朋友♪', 0);
insert into room_unit_comments values (6293, 122301, 1, 3, 1, 0, 1, '班比要守護好\\n僕從的房間才行！', 0);
insert into room_unit_comments values (6294, 122301, 1, 4, 1, 0, 1, '如果要打掃，\\n班比也來幫忙吧～♪', 0);
insert into room_unit_comments values (6295, 122301, 1, 5, 1, 0, 1, '哼～哼～♪\\n僕從喜歡\\n這樣的房間啊～', 0);
insert into room_unit_comments values (6296, 122301, 1, 101, 4, 0, 1, '好無聊喔～\\n找僕從一起\\n去散步好了♪', 0);
insert into room_unit_comments values (6297, 122301, 1, 102, 8, 0, 1, '今天的湯\\n就加入僕從\\n最愛吃的食材吧♪', 0);
insert into room_unit_comments values (6298, 122301, 2, 1, 1, 0, 1, '僕從～！\\n和班比\\n一起出門吧♪', 0);
insert into room_unit_comments values (6299, 122301, 2, 2, 1, 0, 1, '好乖好乖！\\n僕從今天\\n也是乖孩子呢♪', 0);
insert into room_unit_comments values (6300, 122301, 2, 3, 1, 0, 1, '嗯嗯……\\n果然僕從\\n和貝斯很像？', 0);
insert into room_unit_comments values (6301, 122301, 2, 4, 1, 0, 1, '聽好囉？\\n你要乖乖聽\\n班比的話才行喔？', 0);
insert into room_unit_comments values (6302, 122301, 2, 101, 4, 0, 1, '因為你是\\n班比的僕從呀！\\n班比會珍惜你的！', 0);
insert into room_unit_comments values (6303, 122301, 2, 102, 8, 0, 1, '嘿嘿！\\n僕從果然有\\n很溫柔的味道♪', 0);
insert into room_unit_comments values (6304, 122301, 2, 1, 1, 1, 1, '早安……\\n班比要再\\n睡一下喔～', 0);
insert into room_unit_comments values (6305, 122301, 2, 2, 1, 2, 1, '班比最愛\\n曬太陽了！', 0);
insert into room_unit_comments values (6306, 122301, 2, 3, 1, 3, 1, '嗯～！晚上了♪\\n班比\\n狀態超好的喔！', 0);
insert into room_unit_comments values (6308, 122301, 3, 2, 1, 0, 1, '哇～！\\n鬆鬆軟軟的！\\n好舒服～♪', 0);
insert into room_unit_comments values (6309, 122301, 3, 3, 1, 0, 1, '好可愛～♪\\n抱起來的感覺\\n不輸給貝斯！', 0);
insert into room_unit_comments values (6310, 122301, 3, 4, 1, 0, 1, '這香味……\\n是什麼呢？\\n班比很喜歡♪', 0);
insert into room_unit_comments values (6311, 122301, 3, 5, 1, 0, 1, '好溫暖……\\n班比要一直\\n待在這裡……', 0);
insert into room_unit_comments values (6313, 122301, 3, 7, 1, 0, 1, '啊呣啊呣……\\n嗯～！\\n好吃～♪', 0);
insert into room_unit_comments values (6314, 122301, 3, 8, 1, 0, 1, '班比喜歡\\n這本書呢～♪', 0);
insert into room_unit_comments values (6316, 122501, 1, 1, 1, 0, 1, '說到夏天\\n就是刨冰吧。\\n附近的店……', 0);
insert into room_unit_comments values (6317, 122501, 1, 2, 1, 0, 1, '我也開始習慣\\n這身打扮了，\\n就算稍微鍛鍊……', 0);
insert into room_unit_comments values (6318, 122501, 1, 3, 1, 0, 1, '雖然方便活動……\\n但和平時不同的服裝，\\n有點靜不下來呢。', 0);
insert into room_unit_comments values (6319, 122501, 1, 4, 1, 0, 1, '接觸到夏天的\\n空氣，會不禁\\n令人放鬆呢。', 0);
insert into room_unit_comments values (6320, 122501, 1, 5, 1, 0, 1, '在沙灘上跑步\\n似乎正好能夠\\n鍛鍊體幹。', 0);
insert into room_unit_comments values (6321, 122501, 1, 101, 4, 0, 1, '雖然沒有不會\\n結束的夏天……\\n但總會不禁去祈願呢。', 0);
insert into room_unit_comments values (6322, 122501, 1, 102, 8, 0, 1, '為了能挺起胸膛\\n與他站在一起，\\n我也要變得更強……！', 0);
insert into room_unit_comments values (6323, 122501, 2, 1, 1, 0, 1, '怎麼了？\\n如果有想去的地方，\\n我陪你去吧？', 0);
insert into room_unit_comments values (6324, 122501, 2, 2, 1, 0, 1, '晴朗天氣\\n與徐徐微風……\\n這是絕佳的釣魚天。\\n走，出門去吧。', 0);
insert into room_unit_comments values (6325, 122501, 2, 3, 1, 0, 1, '我是有安排好\\n今天的行程了……\\n但我也想聽聽\\n你的希望。', 0);
insert into room_unit_comments values (6326, 122501, 2, 4, 1, 0, 1, '沙灘運動啊……\\n感覺能和你\\n有一場不錯的比賽。', 0);
insert into room_unit_comments values (6327, 122501, 2, 101, 4, 0, 1, '不需要對我們的\\n夏天有所顧慮，\\n盡情享受吧！', 0);
insert into room_unit_comments values (6328, 122501, 2, 102, 8, 0, 1, '有機會要再一起\\n去看煙火吧。\\n……我們約定好囉。', 0);
insert into room_unit_comments values (6329, 122501, 2, 1, 1, 1, 1, '聽著浪潮聲\\n醒來也很\\n不錯呢。', 0);
insert into room_unit_comments values (6330, 122501, 2, 2, 1, 2, 1, '我今天早上\\n有釣到魚，用這魚\\n來做午飯吧。', 0);
insert into room_unit_comments values (6331, 122501, 2, 3, 1, 3, 1, '晚上似乎\\n也有活動，\\n要一起去看嗎？', 0);
insert into room_unit_comments values (6333, 122501, 3, 2, 1, 0, 1, '心情覺得平靜……\\n感覺就會讓人……\\n不小心……睡著……', 0);
insert into room_unit_comments values (6334, 122501, 3, 3, 1, 0, 1, '打扮成這個樣子\\n就能很清楚\\n肌膚的觸感好壞。', 0);
insert into room_unit_comments values (6335, 122501, 3, 4, 1, 0, 1, '這香味會讓人\\n想起大海呢……\\n令人感到爽朗又平靜。', 0);
insert into room_unit_comments values (6336, 122501, 3, 5, 1, 0, 1, '身體濕濕的，\\n小心會感冒。\\n來，你也過來。', 0);
