insert into room_unit_comments values (6465, 122701, 2, 4, 1, 0, 1, '穿得比平時還清涼，\\n總覺得好難為情……\\n請、請你別看我。', 0);
insert into room_unit_comments values (6466, 122701, 2, 101, 4, 0, 1, '變態可疑分子～\\n咦？啊，這是在練習\\n在海邊大吶喊。', 0);
insert into room_unit_comments values (6467, 122701, 2, 102, 8, 0, 1, '居然要和你一起\\n去海邊……\\n感覺好成熟……嘿嘿。', 0);
insert into room_unit_comments values (6468, 122701, 2, 1, 1, 1, 1, '早安。\\n走，去海邊和我\\n一起運動吧。', 0);
insert into room_unit_comments values (6469, 122701, 2, 2, 1, 2, 1, '好熱……\\n啊！？不可以，\\n要抬頭挺胸才行！', 0);
insert into room_unit_comments values (6470, 122701, 2, 3, 1, 3, 1, '為了明天\\n也能去海邊玩，\\n要好好休息喔。', 0);
insert into room_unit_comments values (6472, 122701, 3, 2, 1, 0, 1, '在享受夏天時，\\n休息也很重要呢。\\n要好好計畫才行！', 0);
insert into room_unit_comments values (6473, 122701, 3, 3, 1, 0, 1, '好可愛～\\n不曉得海邊的伴手禮店\\n有在賣嗎？', 0);
insert into room_unit_comments values (6474, 122701, 3, 4, 1, 0, 1, '有成熟的香味，\\n我也要成為懂得\\n分辨的成熟女性。', 0);
insert into room_unit_comments values (6475, 122701, 3, 5, 1, 0, 1, '把濕掉的身體\\n擦乾吧。\\n毛巾、毛巾……', 0);
insert into room_unit_comments values (6477, 122701, 3, 7, 1, 0, 1, '游泳後要補充\\n營養……可是……\\n肚子會鼓起來。', 0);
insert into room_unit_comments values (6478, 122701, 3, 8, 1, 0, 1, '我沒有只顧著玩，\\n也會念書喔。\\n你要不要也一起？', 0);
insert into room_unit_comments values (6480, 122701, 3, 10, 1, 0, 1, '美美應該會喜歡……♪\\n我、我才沒有\\n很興奮……！', 0);
insert into room_unit_comments values (6481, 122701, 3, 11, 1, 0, 1, '感覺禊會用來\\n惡作劇……\\n我得好好試過才行！', 0);
insert into room_unit_comments values (6482, 123001, 1, 1, 1, 0, 1, '沒想到我竟然能\\n在這裡……自己都\\n覺得很不可思議呢。', 0);
insert into room_unit_comments values (6483, 123001, 1, 2, 1, 0, 1, '沒辦法像以前一樣\\n四處飛來飛去的，\\n有點不方便呢。', 0);
insert into room_unit_comments values (6484, 123001, 1, 3, 1, 0, 1, '我能像這樣跟大家\\n住在一起，也都是\\n託他跟大家的福呢。', 0);
insert into room_unit_comments values (6485, 123001, 1, 4, 1, 0, 1, '既然一起住在這裡，\\n我也應該幫忙做\\n些家事……', 0);
insert into room_unit_comments values (6486, 123001, 1, 5, 1, 0, 1, '……不行了！\\n我忍不住想\\n照顧人的衝動！', 0);
insert into room_unit_comments values (6487, 123001, 1, 101, 4, 0, 1, '他們也差不多\\n該回來了，該把這裡\\n打掃乾淨呢。', 0);
insert into room_unit_comments values (6488, 123001, 1, 102, 8, 0, 1, '難得我人在這裡，\\n應該多陪陪我啊。', 0);
insert into room_unit_comments values (6489, 123001, 2, 1, 1, 0, 1, '你知道有什麼\\n好吃的店嗎？\\n一起去吃吧。', 0);
insert into room_unit_comments values (6490, 123001, 2, 2, 1, 0, 1, '你很閒對吧？\\n過來幫我\\n做事吧。', 0);
insert into room_unit_comments values (6491, 123001, 2, 3, 1, 0, 1, '我要來找可可蘿\\n玩，麻煩你顧家\\n了唷♪', 0);
insert into room_unit_comments values (6492, 123001, 2, 4, 1, 0, 1, '哎呀歡迎回來……\\n怎麼遍體鱗傷的啊！\\n你們又跑去幫助人了嗎？', 0);
insert into room_unit_comments values (6493, 123001, 2, 101, 4, 0, 1, '我果然還是待在\\n你身旁最能感到\\n安心的樣子。', 0);
insert into room_unit_comments values (6494, 123001, 2, 102, 8, 0, 1, '不管到哪裡我都\\n會一直陪著你的唷。', 0);
insert into room_unit_comments values (6495, 123001, 2, 1, 1, 1, 1, '哈啊……早安。\\n不知道是身體變大的\\n影響嗎？感覺早上特別睏。', 0);
insert into room_unit_comments values (6496, 123001, 2, 2, 1, 2, 1, '你想吃午餐？\\n真拿你沒辦法呢……\\n我做點什麼給你吧。', 0);
insert into room_unit_comments values (6497, 123001, 2, 3, 1, 3, 1, '你身體還在成長，\\n不早點睡的話會長\\n不大唷？', 0);
insert into room_unit_comments values (6499, 123001, 3, 2, 1, 0, 1, '我偶爾也需要好好\\n休息，不然身體會\\n撐不住呀。', 0);
insert into room_unit_comments values (6500, 123001, 3, 3, 1, 0, 1, '還挺可愛的嘛～♪\\n我想把她當禮物\\n送給可可蘿呢。', 0);
insert into room_unit_comments values (6501, 123001, 3, 4, 1, 0, 1, '不錯的香味呢，\\n塔裡也放些\\n這樣的東西吧。', 0);
insert into room_unit_comments values (6502, 123001, 3, 5, 1, 0, 1, '火焰嗎……\\n之前感覺會燒到\\n翅膀，還滿怕的呢。', 0);
insert into room_unit_comments values (6504, 123001, 3, 7, 1, 0, 1, '嚼嚼……\\n妖精也有體重的\\n設定嗎……？', 0);
insert into room_unit_comments values (6505, 123001, 3, 8, 1, 0, 1, '跟記憶資料不同，\\n故事這種東西很棒呢，\\n完全猜不到結尾。', 0);
insert into room_unit_comments values (6507, 123101, 1, 1, 1, 0, 1, '得帶些冒險的\\n禮物給大家呢。', 0);
insert into room_unit_comments values (6508, 123101, 1, 2, 1, 0, 1, '這世上有很多\\n比童話還要\\n不可思議的事情呢。', 0);
insert into room_unit_comments values (6509, 123101, 1, 3, 1, 0, 1, '要去冒險的話，\\n應該穿方便活動的\\n衣服呢。', 0);
insert into room_unit_comments values (6510, 123101, 1, 4, 1, 0, 1, '地圖跟水壺……\\n應該沒其他\\n忘了帶的東西吧？', 0);
insert into room_unit_comments values (6511, 123101, 1, 5, 1, 0, 1, '人家在寫筆記，\\n正在整理至今為止\\n冒險的記錄。', 0);
insert into room_unit_comments values (6512, 123101, 1, 101, 4, 0, 1, '跟王子先生的每項冒\\n險都是真步真步王國\\n重要的歷史呢♪', 0);
insert into room_unit_comments values (6513, 123101, 1, 102, 8, 0, 1, '不管要去哪裡冒險，\\n人家跟王子先生都會\\n在一起的♪', 0);
insert into room_unit_comments values (6514, 123101, 2, 1, 1, 0, 1, '人家在練習用魔法\\n點火啦～雖然\\n常常會爆炸……', 0);
insert into room_unit_comments values (6515, 123101, 2, 2, 1, 0, 1, '跟王子先生用一樣\\n的冒險道具也挺不\\n錯的呢♪', 0);
insert into room_unit_comments values (6516, 123101, 2, 3, 1, 0, 1, '人家搭帳篷的能力也\\n進步不少了呢，這也\\n是託王子先生的福呢。', 0);
insert into room_unit_comments values (6517, 123101, 2, 4, 1, 0, 1, '人家想到給蘭斯洛特\\n他們新的冒險服裝的\\n點子了呢。', 0);
insert into room_unit_comments values (6518, 123101, 2, 101, 4, 0, 1, '就算迷路了，只要\\n跟王子先生在一起\\n的話，一定沒問題的呢。', 0);
insert into room_unit_comments values (6519, 123101, 2, 102, 8, 0, 1, '人家會好好保護好\\n王子先生的安全\\n的唷。', 0);
insert into room_unit_comments values (6520, 123101, 2, 1, 1, 1, 1, '貪睡過頭的話，\\n會搶走棉被來\\n叫醒唷。', 0);
insert into room_unit_comments values (6521, 123101, 2, 2, 1, 2, 1, '準備好的話，\\n就開始進行\\n今天的冒險吧。', 0);
insert into room_unit_comments values (6522, 123101, 2, 3, 1, 3, 1, '還想再聊一下天？\\n只能在一下下唷♪', 0);
insert into room_unit_comments values (6524, 123101, 3, 2, 1, 0, 1, '對冒險後疲倦的\\n身體來說這治癒力\\n真棒……呼嚕……', 0);
insert into room_unit_comments values (6525, 123101, 3, 3, 1, 0, 1, '呵呵，\\n您也要和人家\\n一起冒險嗎？', 0);
insert into room_unit_comments values (6526, 123101, 3, 4, 1, 0, 1, '防蚊香是冒險的\\n必備品，千萬\\n不能忘記帶。', 0);
insert into room_unit_comments values (6527, 123101, 3, 5, 1, 0, 1, '人家也該練習一下\\n不使用魔法也能點火\\n的方法。', 0);
insert into room_unit_comments values (6529, 123101, 3, 7, 1, 0, 1, '這道料理……\\n在冒險的時候\\n做做看吧。', 0);
insert into room_unit_comments values (6530, 123101, 3, 8, 1, 0, 1, '和王子先生的愛與\\n冒險的童話……呼……\\n真是太棒了♪', 0);
insert into room_unit_comments values (6532, 103101, 1, 103, 12, 0, 1, '為了與那個人的\\n美好未來，需要更加\\n精進占卜與靈能力。', 0);
insert into room_unit_comments values (6533, 103101, 2, 103, 12, 0, 1, '我想更加加深\\n與你之間，\\n羈絆以及靈魂的連繫。', 0);
insert into room_unit_comments values (6534, 104401, 1, 103, 12, 0, 1, '羈絆契約嗎……\\n若是他所希望的話，\\n倒也不是不能考慮一下。', 0);
insert into room_unit_comments values (6535, 104401, 2, 103, 12, 0, 1, '怎麼啦？\\n為妾身的姿態\\n著迷了嗎？', 0);
insert into room_unit_comments values (6536, 123201, 1, 1, 1, 0, 1, '旅行的行李還真多\\n啊……欸，噗吉～\\n你也幫忙拿一些嘛～', 0);
insert into room_unit_comments values (6537, 123201, 1, 2, 1, 0, 1, '傘不用帶也沒關係\\n吧，反正有噗吉在♪\\n「喂，什麼意思……？」', 0);
insert into room_unit_comments values (6538, 123201, 1, 3, 1, 0, 1, '旅途上可不能怕生\\n呢，首先從打招呼\\n開始吧……！', 0);
insert into room_unit_comments values (6539, 123201, 1, 4, 1, 0, 1, '土產果然還是要選\\n當地的玩偶對吧♪\\n好興奮呢……', 0);
insert into room_unit_comments values (6540, 123201, 1, 5, 1, 0, 1, '陌生的地方嗎……\\n唔……光是想像就有\\n點想家了……', 0);
insert into room_unit_comments values (6541, 123201, 1, 101, 4, 0, 1, '竟然要野營地話……\\n這說不定是能睡在哥哥\\n披風上的機會……！', 0);
insert into room_unit_comments values (6542, 123201, 1, 102, 8, 0, 1, '不論多麼危險的冒險，\\n只要跟哥哥在一起就沒\\n問題！……大概吧？', 0);
insert into room_unit_comments values (6543, 123201, 2, 1, 1, 0, 1, '噗吉的眼罩跟肚圍\\n都要帶去！這是旅行\\n的必需品～！', 0);
insert into room_unit_comments values (6544, 123201, 2, 2, 1, 0, 1, '這些全都要背起來\\n還挺困難的，\\n搞錯順序會很慘。', 0);
insert into room_unit_comments values (6545, 123201, 2, 3, 1, 0, 1, '欸嘿嘿～摸摸我的頭？\\n隔著帽子摸也行唷♪', 0);
insert into room_unit_comments values (6546, 123201, 2, 4, 1, 0, 1, '「千萬不可觸碰OK繃\\n的部分，棉花會破\\n出來。」', 0);
insert into room_unit_comments values (6547, 123201, 2, 101, 4, 0, 1, '我想跟哥哥一起看\\n更多更多各式各樣\\n的景色！', 0);
insert into room_unit_comments values (6548, 123201, 2, 102, 8, 0, 1, '旅程結束時一定很\\n令人感到寂寞呢……\\n歸途上能跟哥哥牽\\n著手嗎？', 0);
insert into room_unit_comments values (6549, 123201, 2, 1, 1, 1, 1, '出發日太令人期待，\\n完全沒睡好……\\n「旅行前日常有的事呢。」', 0);
insert into room_unit_comments values (6550, 123201, 2, 2, 1, 2, 1, '哇啊，天氣真好！\\n旅程中一直都是\\n晴天就好了呢。', 0);
insert into room_unit_comments values (6551, 123201, 2, 3, 1, 3, 1, '在陌生的場所睡覺\\n還真恐怖……在睡\\n覺時回家睡嗎……', 0);
insert into room_unit_comments values (6553, 123201, 3, 2, 1, 0, 1, '旅程中沒有坐墊，\\n也沒有沙發呢……\\n「不是還有我在嗎？」', 0);
insert into room_unit_comments values (6554, 123201, 3, 3, 1, 0, 1, '玩偶的大家呀，\\n祈求我們旅程平\\n安吧……！', 0);
insert into room_unit_comments values (6555, 123201, 3, 4, 1, 0, 1, '旅途中的空氣也能\\n保存在回憶中帶回\\n去有多好的啊～', 0);
insert into room_unit_comments values (6556, 123201, 3, 5, 1, 0, 1, '或許有旅行用攜帶式\\n暖爐會派上用場……！\\n「還要增加行李嗎？」', 0);
insert into room_unit_comments values (6558, 123201, 3, 7, 1, 0, 1, '「出發時間到囉？」\\n咦～等一下嘛！\\n還沒吃飽飯呢……', 0);
insert into room_unit_comments values (6559, 123201, 3, 8, 1, 0, 1, '原來世界是如此廣闊\\n的啊……！\\n真想快點親眼見識呢～', 0);
insert into room_unit_comments values (6561, 123301, 1, 1, 1, 0, 1, '真是超閒的～\\n沒什麼有～趣\\n的事情嗎～', 0);
insert into room_unit_comments values (6562, 123301, 1, 2, 1, 0, 1, '這裡是個好地方呢～\\n下次也帶小卡里莎\\n過來吧～', 0);
insert into room_unit_comments values (6563, 123301, 1, 3, 1, 0, 1, '好～想看口愛\\n的正太呀♪\\n想摸～～～一摸♪', 0);
insert into room_unit_comments values (6564, 123301, 1, 4, 1, 0, 1, '感覺衣服有點緊呀～\\n變胖了嗎～？\\n嗯～\\n算了，錯～覺吧～♪', 0);
insert into room_unit_comments values (6565, 123301, 1, 5, 1, 0, 1, '啊～糟糕，\\n忘記給姊姊\\n傳訊息了～\\n算了吧～', 0);
insert into room_unit_comments values (6566, 123301, 1, 101, 4, 0, 1, '好想吃鬆餅～\\n來邀小騎士去吧～♪', 0);
insert into room_unit_comments values (6567, 123301, 1, 102, 8, 0, 1, '今～天也～\\n是被喜歡包圍的一天～\\n真、幸、福♪', 0);
insert into room_unit_comments values (6568, 123301, 2, 1, 1, 0, 1, '要不要跟我玩玩呀～？\\n這樣好像在搭訕呢～\\n超好笑～', 0);
insert into room_unit_comments values (6569, 123301, 2, 2, 1, 0, 1, '嗯～？\\n現在沒那心情，Pass～\\n之後再說吧～', 0);
insert into room_unit_comments values (6570, 123301, 2, 3, 1, 0, 1, '你有沒有看到\\n小卡里莎～？\\n敢從姐姐這逃走，\\n需要懲罰一下呢～', 0);
insert into room_unit_comments values (6571, 123301, 2, 4, 1, 0, 1, '我想擦指甲油，\\n但沒辦法～\\n史萊姆整個\\n鑽進指甲縫裡了～', 0);
insert into room_unit_comments values (6572, 123301, 2, 101, 4, 0, 1, '想要姐姐\\n陪～陪你嗎？\\n真口～愛♪', 0);
insert into room_unit_comments values (6573, 123301, 2, 102, 8, 0, 1, '怎～麼啦小騎士♪\\n想在這地方～\\n卿、卿、我、我～？', 0);
insert into room_unit_comments values (6574, 123301, 2, 1, 1, 1, 1, '睏啊～\\n但從外面\\n傳來正太的聲音～\\n自然地就醒了～', 0);
insert into room_unit_comments values (6575, 123301, 2, 2, 1, 2, 1, '午餐後呀果然\\n非吃甜食不可呢～\\n甜食有甜食的胃～♪', 0);
insert into room_unit_comments values (6576, 123301, 2, 3, 1, 3, 1, '這麼晚了\\n還醒著的壞孩子～\\n會被我吃掉唷～♪', 0);
insert into room_unit_comments values (6578, 123301, 3, 2, 1, 0, 1, '啊～舒湖～\\n一～輩子在這\\n休息都沒問題呀～', 0);
insert into room_unit_comments values (6579, 123301, 3, 3, 1, 0, 1, '這什麼呀～\\n挺可愛的嘛～\\n僅次於正太～', 0);
insert into room_unit_comments values (6580, 123301, 3, 4, 1, 0, 1, '不錯的味道呢～\\n是什麼味道～\\n我不太清楚就是了～', 0);
insert into room_unit_comments values (6581, 123301, 3, 5, 1, 0, 1, '好溫暖～♪\\n感覺會融化～\\n變回史萊姆～', 0);
insert into room_unit_comments values (6583, 123301, 3, 7, 1, 0, 1, '嗯、嗯、嗯～♪\\n好～吃～\\n繼～續吃更多～♪', 0);
insert into room_unit_comments values (6584, 123301, 3, 8, 1, 0, 1, '哇～看起來好複雜～\\n看不懂～\\n超好笑～', 0);
insert into room_unit_comments values (6586, 123501, 1, 1, 1, 0, 1, '紅豆麵包\\n算零食嗎？\\n不知道萬聖節\\n有沒機會收到？', 0);
insert into room_unit_comments values (6587, 123501, 1, 2, 1, 0, 1, '要是不給我糖果，\\n我就要偷懶喔～！\\n萬聖節過了以後也！', 0);
insert into room_unit_comments values (6588, 123501, 1, 3, 1, 0, 1, '變成幽靈的話\\n能不工作\\n整天無所事事\\n的嗎？', 0);
insert into room_unit_comments values (6589, 123501, 1, 4, 1, 0, 1, '哦呀……這變裝\\n袖子很長呢\\n要小心一點\\n不要去勾到～', 0);
insert into room_unit_comments values (6590, 123501, 1, 5, 1, 0, 1, '要搗蛋也是\\n需要用到體力的呢～\\n沒有什麼\\n輕鬆的方法嗎？', 0);
insert into room_unit_comments values (6591, 123501, 1, 101, 4, 0, 1, '那傢伙\\n會玩變裝的嗎？\\n怎樣的鬼怪\\n會比較適合呢？', 0);
insert into room_unit_comments values (6592, 123501, 1, 102, 8, 0, 1, '一直都是萬聖節的話\\n就能一直跟\\n那傢伙玩的說～', 0);
insert into room_unit_comments values (6593, 123501, 2, 1, 1, 0, 1, '今天的街上\\n充滿萬聖節的氣氛呢～\\n這人群密度真糟糕。', 0);
insert into room_unit_comments values (6594, 123501, 2, 2, 1, 0, 1, '這變裝如何？\\n你喜歡的話，明年\\n也變裝給你看喔～', 0);
insert into room_unit_comments values (6595, 123501, 2, 3, 1, 0, 1, '其實這長袖子裡\\n被橡子給\\n塞得滿滿的呢……', 0);
insert into room_unit_comments values (6596, 123501, 2, 4, 1, 0, 1, '竟然有南瓜燈\\n的話\\n有橡子燈也\\n挺不錯的吧～♪', 0);
