insert into room_unit_comments values (6732, 106901, 2, 101, 4, 0, 1, '連你都要我\\n告訴你天氣嗎……？\\n欠揍啊？', 0);
insert into room_unit_comments values (6733, 106901, 2, 102, 8, 0, 1, '有空就陪我一下吧，\\n玩你喜歡的遊戲就好，\\n反正我會贏。', 0);
insert into room_unit_comments values (6734, 106901, 2, 1, 1, 1, 1, '人生苦短……\\n活出沒有遺憾的\\n每一天吧。', 0);
insert into room_unit_comments values (6735, 106901, 2, 2, 1, 2, 1, '午睡很棒喔……\\n呼哈……要思考就\\n不能沒有睡眠……', 0);
insert into room_unit_comments values (6736, 106901, 2, 3, 1, 3, 1, '不要夙興夜寐，\\n休息很重要的。', 0);
insert into room_unit_comments values (6738, 106901, 3, 2, 1, 0, 1, '竟然能滿足我，\\n真是了不起……\\n我會讓晶大量生產的。', 0);
insert into room_unit_comments values (6739, 106901, 3, 3, 1, 0, 1, '我想要有\\n凱留之外\\n可以搗毀的東西……♪', 0);
insert into room_unit_comments values (6740, 106901, 3, 4, 1, 0, 1, '好香啊，\\n沒見過的調香耶，\\n是誰準備的？', 0);
insert into room_unit_comments values (6741, 106901, 3, 5, 1, 0, 1, '力道不夠啊，\\n用力點，\\n讓我更加放鬆吧。', 0);
insert into room_unit_comments values (6743, 106901, 3, 7, 1, 0, 1, '先不提營養價值，\\n味道的比例還不錯。', 0);
insert into room_unit_comments values (6744, 106901, 3, 8, 1, 0, 1, '無聊的書我會重寫，\\n如何呀？', 0);
insert into room_unit_comments values (6746, 124001, 1, 1, 1, 0, 1, '今年茜里和\\n姊姊一起，聖誕\\n老人會有兩個……\\n代表幸福也是兩倍吧♪', 0);
insert into room_unit_comments values (6747, 124001, 1, 2, 1, 0, 1, '我準備在聖誕節表演\\n薩克斯風，\\n所以要先練習……和哥哥\\n一起進行瘋狂特訓！', 0);
insert into room_unit_comments values (6748, 124001, 1, 3, 1, 0, 1, '聖誕樹的裝飾很完美♪\\n哥哥、姊姊\\n會不會喜歡呢？', 0);
insert into room_unit_comments values (6749, 124001, 1, 4, 1, 0, 1, '哇，外面一片白！\\n好像擠滿了\\n鮮奶油的蛋糕喔♪', 0);
insert into room_unit_comments values (6750, 124001, 1, 5, 1, 0, 1, '走煙囪會\\n弄髒衣服的……\\n不能走玄關嗎？', 0);
insert into room_unit_comments values (6751, 124001, 1, 101, 4, 0, 1, '等哥哥來了，\\n我要讓他盛讚\\n我的聖誕裝♪', 0);
insert into room_unit_comments values (6752, 124001, 1, 102, 8, 0, 1, '如果聖誕夜\\n能和哥哥度過……\\n茜里或許也會\\n變成熟一點吧♪', 0);
insert into room_unit_comments values (6753, 124001, 2, 1, 1, 0, 1, '現在的茜里是\\n聖誕老人♪\\n有什麼願望\\n儘管告訴我喔。', 0);
insert into room_unit_comments values (6754, 124001, 2, 2, 1, 0, 1, '外面很冷，\\n和茜里緊緊靠著\\n再出去吧♪', 0);
insert into room_unit_comments values (6755, 124001, 2, 3, 1, 0, 1, '哥哥是\\n茜里的馴鹿，\\n所以要一直\\n陪伴我喔♪', 0);
insert into room_unit_comments values (6756, 124001, 2, 4, 1, 0, 1, '哥哥，幫我！\\n茜里用自己的\\n身體練習包裝，\\n結果纏在一起了……', 0);
insert into room_unit_comments values (6757, 124001, 2, 101, 4, 0, 1, '準備了好多禮物喔♪\\n哥哥的份……\\n還要保密──♪', 0);
insert into room_unit_comments values (6758, 124001, 2, 102, 8, 0, 1, '茜里想要\\n成熟一點的禮物，\\n因為我想早點\\n追上哥哥♪', 0);
insert into room_unit_comments values (6759, 124001, 2, 1, 1, 1, 1, '早安♪\\n呵呵，聖誕老人茜里\\n是乖孩子的榜樣，\\n所以我會早睡早起♪', 0);
insert into room_unit_comments values (6760, 124001, 2, 2, 1, 2, 1, '咦？\\n已經中午了？\\n聖誕歌練一練\\n就已經這種時間了？', 0);
insert into room_unit_comments values (6761, 124001, 2, 3, 1, 3, 1, '冬天夜空的\\n星星好美喔～\\n哥哥，要不要\\n一起去看？', 0);
insert into room_unit_comments values (6763, 124001, 3, 2, 1, 0, 1, '呼，太舒服了，\\n差點融化了……\\n這種禮物\\n也不錯呢。', 0);
insert into room_unit_comments values (6764, 124001, 3, 3, 1, 0, 1, '哇，好可愛♪\\n嘿嘿，\\n哥哥想要\\n什麼樣的禮物呢～？', 0);
insert into room_unit_comments values (6765, 124001, 3, 4, 1, 0, 1, '哇哇，大人的香味……\\n感覺可以度過\\n一天浪漫的聖誕節♪', 0);
insert into room_unit_comments values (6766, 124001, 3, 5, 1, 0, 1, '用這個讓\\n身體暖起來，\\n茜里想把體溫……\\n分享給哥哥♪', 0);
insert into room_unit_comments values (6768, 124001, 3, 7, 1, 0, 1, '肚子好撐喔……\\n不過這樣我或許\\n又更像聖誕\\n老人了……？', 0);
insert into room_unit_comments values (6769, 124001, 3, 8, 1, 0, 1, '「聖誕夜要怎麼\\n與情人度過」……？\\n哇啊啊，這對茜里來說好像\\n還太早了……！', 0);
insert into room_unit_comments values (6771, 124001, 3, 14, 1, 0, 1, '哇，好厲害啊！\\n真是神奇的繪本♪\\n我用這本書對哥哥\\n惡作劇吧♪', 0);
insert into room_unit_comments values (6772, 124001, 3, 15, 1, 0, 1, '茜里變\\n暖和了～♪\\n可以緊緊抱住我嗎？', 0);
insert into room_unit_comments values (6773, 124101, 1, 1, 1, 0, 1, '我以為有可能\\n又會變成天使……\\n卻沒想到會新增\\n聖誕老人的元素。', 0);
insert into room_unit_comments values (6774, 124101, 1, 2, 1, 0, 1, '拉菲他們不在的時候，\\n我要好好振作，\\n我好歹……\\n也是天使。', 0);
insert into room_unit_comments values (6775, 124101, 1, 3, 1, 0, 1, '或許可以……\\n裝飾房間？\\n呵呵，我會不會太興奮了？', 0);
insert into room_unit_comments values (6776, 124101, 1, 4, 1, 0, 1, '如果是派對的話，\\n或許大家可以玩\\n適合多人數的桌遊……\\n太好了。', 0);
insert into room_unit_comments values (6777, 124101, 1, 5, 1, 0, 1, '這件衣服毛茸茸的……\\n一回神就摸下去了……\\n真高招啊，拉菲。', 0);
insert into room_unit_comments values (6778, 124101, 1, 101, 4, 0, 1, '和那傢伙在一起，\\n會有種不同於怕生時的\\n緊張感……\\n是說你都聽見了？', 0);
insert into room_unit_comments values (6779, 124101, 1, 102, 8, 0, 1, '我沒有……\\n欺負玩偶喔。\\n我有沒有珍惜嗎？\\n那就是……唔。', 0);
insert into room_unit_comments values (6780, 124101, 2, 1, 1, 0, 1, '要怎麼分辨\\n乖孩子和壞孩子呢？\\n……難道是看坦率程度嗎？\\n那我就慘了！', 0);
insert into room_unit_comments values (6781, 124101, 2, 2, 1, 0, 1, '講到禮物\\n就會想到遊戲！\\n年底的商戰最棒了──！', 0);
insert into room_unit_comments values (6782, 124101, 2, 3, 1, 0, 1, '聖誕老人\\n工作時不能被\\n看到吧？\\n……是天職？', 0);
insert into room_unit_comments values (6783, 124101, 2, 4, 1, 0, 1, '這件衣服已經做好\\n十足的防污準備，\\n可以走煙囪……\\n不對，我不行啊！？', 0);
insert into room_unit_comments values (6784, 124101, 2, 101, 4, 0, 1, '現在……\\n我可以幫你\\n實現一個願望喔，\\n你看，我現在穿聖誕裝嘛。', 0);
insert into room_unit_comments values (6785, 124101, 2, 102, 8, 0, 1, '我們不知道\\n為什麼和玩偶\\n很有緣呢……\\n呵呵。', 0);
insert into room_unit_comments values (6786, 124101, 2, 1, 1, 1, 1, '早、早安！\\n……呼，\\n幸好來得及\\n整理睡醒的頭髮。', 0);
insert into room_unit_comments values (6787, 124101, 2, 2, 1, 2, 1, '咦……一起吃飯？\\n是、是喔……嗯，\\nOK，反正機會難得。', 0);
insert into room_unit_comments values (6788, 124101, 2, 3, 1, 3, 1, '這個時間\\n玩遊戲莫名開心耶，\\n所以，\\n要不要玩一下？', 0);
insert into room_unit_comments values (6790, 124101, 3, 2, 1, 0, 1, '啊啊～不行了……\\n我想要一直\\n在這裡玩遊戲～', 0);
insert into room_unit_comments values (6791, 124101, 3, 3, 1, 0, 1, '我需要研究一下\\n什麼禮物比較好，嗯。\\n……啊，好可愛～♪', 0);
insert into room_unit_comments values (6792, 124101, 3, 4, 1, 0, 1, '也有那種\\n集中注意力的香味吧……\\n在一決勝負的時候\\n要不要帶在身上呢？', 0);
insert into room_unit_comments values (6793, 124101, 3, 5, 1, 0, 1, '感覺好悠哉喔……\\n時間都\\n融化了……', 0);
insert into room_unit_comments values (6795, 124101, 3, 7, 1, 0, 1, '能夠吃到肚子撐\\n也是一種幸福。', 0);
insert into room_unit_comments values (6796, 124101, 3, 8, 1, 0, 1, '小時候我和茜里一起\\n讀過繪本呢……\\n好懷念喔。', 0);
insert into room_unit_comments values (6798, 124101, 3, 14, 1, 0, 1, '立體繪本……\\n如果用這個\\n做遊戲書感覺會\\n很有趣……', 0);
insert into room_unit_comments values (6799, 124101, 3, 15, 1, 0, 1, '好暖和……\\n毛茸茸……軟綿綿……\\n我可以稍微……\\n休息一下吧……？', 0);
insert into room_unit_comments values (6800, 105101, 1, 103, 12, 0, 1, '唉呀，怎麼啦？\\n難道是想跟我\\n一起進行新的實驗嗎？', 0);
insert into room_unit_comments values (6801, 105101, 2, 103, 12, 0, 1, '不舒服的時候\\n就來診所吧，\\n我會特別為你\\n仔細地看診♪', 0);
insert into room_unit_comments values (6802, 124201, 1, 1, 1, 0, 1, '就算不特別，\\n能夠悠閒度過\\n聖誕節也很好。', 0);
insert into room_unit_comments values (6803, 124201, 1, 2, 1, 0, 1, '針織帽、圍巾和\\n耳罩……\\n真實面貌使用的防寒裝\\n都要準備好。', 0);
insert into room_unit_comments values (6804, 124201, 1, 3, 1, 0, 1, '我找不到襪子，\\n枕頭邊就改放\\n鎧甲吧。', 0);
insert into room_unit_comments values (6805, 124201, 1, 4, 1, 0, 1, '我沒有了不起到\\n足以被說是聖女，\\n但我每天都在努力，\\n希望內心是聖潔的。', 0);
insert into room_unit_comments values (6806, 124201, 1, 5, 1, 0, 1, '現在沒有頭盔了，\\n聖誕燈飾\\n顯得比平常\\n更刺眼了。', 0);
insert into room_unit_comments values (6807, 124201, 1, 101, 4, 0, 1, '我來裝飾\\n聖誕花圈，\\n願大家都幸福。', 0);
insert into room_unit_comments values (6808, 124201, 1, 102, 8, 0, 1, '這服裝對我來說\\n太可愛了……\\n不過我就豁出去了，\\n好好享受聖誕氣氛吧。', 0);
insert into room_unit_comments values (6809, 124201, 2, 1, 1, 0, 1, '可以幫我\\n裝飾聖誕樹嗎？\\n裝飾風格就交給\\n少年你作主了。', 0);
insert into room_unit_comments values (6810, 124201, 2, 2, 1, 0, 1, '準備派對的時候\\n也很開心，\\n少年啊，你懂我的心情嗎？', 0);
insert into room_unit_comments values (6811, 124201, 2, 3, 1, 0, 1, '你問為什麼馴鹿的\\n鼻子是紅的嗎？\\n因為沒有開車燈的馴鹿\\n會違規被取締啊。', 0);
insert into room_unit_comments values (6812, 124201, 2, 4, 1, 0, 1, '小智和茉莉\\n都是乖孩子，\\n一定能見到\\n聖誕老人的。', 0);
insert into room_unit_comments values (6813, 124201, 2, 101, 4, 0, 1, '其實我一直\\n很在意柊樹花的\\n髮飾，希望\\n少年能稱讚我。', 0);
insert into room_unit_comments values (6814, 124201, 2, 102, 8, 0, 1, '少年啊……\\n你也該看習慣了吧。\\n你這樣一直盯著我看\\n很害羞耶。', 0);
insert into room_unit_comments values (6815, 124201, 2, 1, 1, 1, 1, '在晨光的照耀下，\\n街道閃閃發光\\n如白銀一般。', 0);
insert into room_unit_comments values (6816, 124201, 2, 2, 1, 2, 1, '去吃午餐\\n散步一下吧，\\n外面慶典很熱鬧，\\n街上非常美喔。', 0);
insert into room_unit_comments values (6817, 124201, 2, 3, 1, 3, 1, '聖誕節的晚上\\n果然才是重頭戲吧？\\n點根蠟燭\\n莊重地度過吧。', 0);
insert into room_unit_comments values (6819, 124201, 3, 2, 1, 0, 1, '穿鎧甲裝時無法體會，\\n但是這好柔軟，\\n好舒服喔……\\n軟綿綿……軟綿綿……', 0);
insert into room_unit_comments values (6820, 124201, 3, 3, 1, 0, 1, '呵呵，真可愛，\\n緊緊抱住時\\n讓我想到那個下雪天\\n出生的嬰兒。', 0);
insert into room_unit_comments values (6821, 124201, 3, 4, 1, 0, 1, '脫下頭盔後\\n才能遇到\\n這個香味……\\n聖誕節的魔法真不賴。', 0);
insert into room_unit_comments values (6822, 124201, 3, 5, 1, 0, 1, '從指尖到身體深處\\n都會很暖和，\\n這樣下雪天\\n也可以放心了。', 0);
insert into room_unit_comments values (6824, 124201, 3, 7, 1, 0, 1, '嚼嚼、嚼嚼……\\n不要一直看我\\n吃飯的樣子啦。', 0);
insert into room_unit_comments values (6825, 124201, 3, 8, 1, 0, 1, '上面刊登了\\n推薦餐廳的特輯，\\n如果聖誕夜能訂到位，一定會\\n超級幸福吧。', 0);
insert into room_unit_comments values (6827, 124201, 3, 14, 1, 0, 1, '哇！\\n………不，\\n我沒有被嚇到。', 0);
insert into room_unit_comments values (6828, 124201, 3, 15, 1, 0, 1, '呵呵……臉頰可以感受到\\n鬆鬆軟軟的，\\n好溫暖，好舒服喔。', 0);
insert into room_unit_comments values (6829, 124301, 1, 1, 1, 0, 1, '彈鋼琴和讀書……\\n要和古蕾婭一起\\n做些什麼呢～♪', 0);
insert into room_unit_comments values (6830, 124301, 1, 2, 1, 0, 1, '我先和古蕾婭一起唸書\\n好了，以免回到\\n原本的世界時跟不上\\n課程進度。', 0);
insert into room_unit_comments values (6831, 124301, 1, 3, 1, 0, 1, '街上有魔物餐廳啊！\\n我要和古蕾婭去吃♪', 0);
insert into room_unit_comments values (6832, 124301, 1, 4, 1, 0, 1, '好不容易\\n學會新的魔法，\\n真想讓古蕾婭看到！', 0);
insert into room_unit_comments values (6833, 124301, 1, 5, 1, 0, 1, '我想約古蕾婭\\n繼續深入探索\\n蘭德索爾！', 0);
insert into room_unit_comments values (6834, 124301, 1, 101, 4, 0, 1, '我們也向\\n那個人看齊，\\n去打工吧！', 0);
insert into room_unit_comments values (6835, 124301, 1, 102, 8, 0, 1, '真希望有一天能\\n招待那個人\\n來我們的學院！', 0);
insert into room_unit_comments values (6836, 124301, 2, 1, 1, 0, 1, '聽我說！\\n我待會要和古蕾婭\\n去魔法同好會！', 0);
insert into room_unit_comments values (6837, 124301, 2, 2, 1, 0, 1, '你待會要做什麼？\\n我們也可以\\n一起跟去嗎！？', 0);
insert into room_unit_comments values (6838, 124301, 2, 3, 1, 0, 1, '你要不要\\n跟我和古蕾婭一起\\n練習魔法？', 0);
insert into room_unit_comments values (6839, 124301, 2, 4, 1, 0, 1, '我常跟古蕾婭聊到，\\n希望能讓你穿上\\n馬納歷亞的制服。', 0);
insert into room_unit_comments values (6840, 124301, 2, 101, 4, 0, 1, '在古蕾婭身邊\\n就是溫暖啊～！\\n暖呼呼的～♪', 0);
insert into room_unit_comments values (6841, 124301, 2, 102, 8, 0, 1, '你是\\n古蕾婭和我的朋友！\\n以後也都是！', 0);
insert into room_unit_comments values (6842, 124301, 2, 1, 1, 1, 1, '早安！\\n我今天早上和古蕾婭\\n去外面散步了！', 0);
insert into room_unit_comments values (6843, 124301, 2, 2, 1, 2, 1, '肚子餓了……\\n好，和古蕾婭一起做\\n魔物料理吧！', 0);
insert into room_unit_comments values (6844, 124301, 2, 3, 1, 3, 1, '已經這麼晚了，\\n得為了明天做準備，\\n趕快睡覺才行！', 0);
insert into room_unit_comments values (6846, 124301, 3, 2, 1, 0, 1, '呼～這個觸感……\\n很像是古蕾婭的尾巴，\\n讓人放鬆啊～', 0);
insert into room_unit_comments values (6847, 124301, 3, 3, 1, 0, 1, '玩偶真是不錯……\\n我也常常\\n抱住古蕾婭喔。', 0);
insert into room_unit_comments values (6848, 124301, 3, 4, 1, 0, 1, '好香喔～\\n古蕾婭也喜歡\\n這種味道喔！', 0);
insert into room_unit_comments values (6849, 124301, 3, 5, 1, 0, 1, '好溫暖～\\n讓我想起\\n黏著古蕾婭的時候了～', 0);
insert into room_unit_comments values (6851, 124301, 3, 7, 1, 0, 1, '這個世界\\n有很多別處沒有的\\n美食呢！', 0);
insert into room_unit_comments values (6852, 124301, 3, 8, 1, 0, 1, '連結世界的魔法……\\n書上有沒有\\n相關的線索啊……', 0);
insert into room_unit_comments values (6855, 124401, 1, 1, 1, 0, 1, '彈鋼琴和讀書……\\n今天的安\\n要做什麼呢……', 0);
insert into room_unit_comments values (6856, 124401, 1, 2, 1, 0, 1, '我不像安\\n那麼會唸書，\\n所以要好好自習。', 0);
insert into room_unit_comments values (6857, 124401, 1, 3, 1, 0, 1, '一旦起了頭\\n就會堅持到底，\\n安就是這樣啊……', 0);
insert into room_unit_comments values (6858, 124401, 1, 4, 1, 0, 1, '呵呵……光是欣賞\\n埋頭苦幹的安，\\n一天轉眼就過了……', 0);
insert into room_unit_comments values (6859, 124401, 1, 5, 1, 0, 1, '蘭德索爾好大啊……\\n我想和安一起去\\n很多地方。', 0);
insert into room_unit_comments values (6860, 124401, 1, 101, 4, 0, 1, '看到安和\\n那個人的笑容\\n我就會非常放心……', 0);
insert into room_unit_comments values (6861, 124401, 1, 102, 8, 0, 1, '如果那個人\\n也進入我們的學院……\\n呵呵，感覺很有趣呢。', 0);
insert into room_unit_comments values (6862, 124401, 2, 1, 1, 0, 1, '安和你都好厲害啊……\\n你們一下子就能和大家\\n打成一片了。', 0);
insert into room_unit_comments values (6863, 124401, 2, 2, 1, 0, 1, '你待會要做些什麼？\\n那個……我和安\\n可以跟著你嗎？', 0);
insert into room_unit_comments values (6864, 124401, 2, 3, 1, 0, 1, '安約你練習魔法？\\n其實我也是……\\n要不要一起？', 0);
insert into room_unit_comments values (6865, 124401, 2, 4, 1, 0, 1, '我常和安聊到，\\n如果你也進入\\n馬納歷亞就讀，\\n應該會很有趣吧……', 0);
insert into room_unit_comments values (6866, 124401, 2, 101, 4, 0, 1, '這個世界\\n也有像安一樣\\n接納我的人們啊……', 0);
insert into room_unit_comments values (6867, 124401, 2, 102, 8, 0, 1, '如果有一天\\n要回到原本的世界，\\n「下次見」會比「永別\\n了」更適合我們吧。', 0);
insert into room_unit_comments values (6868, 124401, 2, 1, 1, 1, 1, '早安，\\n我剛剛和安去看了\\n蘭德索爾的晨曦。', 0);
insert into room_unit_comments values (6869, 124401, 2, 2, 1, 2, 1, '我們待會\\n才要去吃飯，\\n你午餐吃了什麼？', 0);
insert into room_unit_comments values (6870, 124401, 2, 3, 1, 3, 1, '差不多是\\n安要睡覺的時間了……\\n你也晚安囉。', 0);
insert into room_unit_comments values (6872, 124401, 3, 2, 1, 0, 1, '呼，消除疲勞了……\\n這條尾巴……\\n有點重呢。', 0);
insert into room_unit_comments values (6873, 124401, 3, 3, 1, 0, 1, '這個……\\n安看到了\\n會喜歡嗎……', 0);
insert into room_unit_comments values (6874, 124401, 3, 4, 1, 0, 1, '好香喔……\\n也想聽聽看\\n安的想法。', 0);
insert into room_unit_comments values (6875, 124401, 3, 5, 1, 0, 1, '好溫暖……\\n如果安在這裡，\\n應該會看著書\\n睡著吧。', 0);
insert into room_unit_comments values (6877, 124401, 3, 7, 1, 0, 1, '這麼好吃的食物\\n我可以吃很多，\\n可是尾巴又會……\\n變粗吧。', 0);
insert into room_unit_comments values (6878, 124401, 3, 8, 1, 0, 1, '安推薦我\\n看的書\\n是這本嗎……？', 0);
insert into room_unit_comments values (6881, 124701, 1, 1, 1, 0, 1, '凶、凶、凶……大凶？\\n這什麼籤啊？\\n就算是自家用的，\\n出籤率也太隨便了吧……', 0);
insert into room_unit_comments values (6882, 124701, 1, 2, 1, 0, 1, '今年一定要\\n做出最好的新藥。\\n希望能以近乎偶然的\\n最少步驟做出來。', 0);
insert into room_unit_comments values (6883, 124701, 1, 3, 1, 0, 1, '在堵住喉嚨前\\n融化消失的麻糬……\\n這是第一優先的研究主題。', 0);
insert into room_unit_comments values (6884, 124701, 1, 4, 1, 0, 1, '很好，完成了\\n深月特製……\\n「年菜新詮」，\\n滿滿都是醫學根據喔。', 0);
insert into room_unit_comments values (6885, 124701, 1, 5, 1, 0, 1, '應該給他壓歲錢嗎……？\\n各種意義上\\n都很奇怪呢……', 0);
insert into room_unit_comments values (6886, 124701, 1, 101, 4, 0, 1, '呵呵，今年又可以\\n記錄他的生命跡象\\n一整年了♪', 0);
insert into room_unit_comments values (6887, 124701, 1, 102, 8, 0, 1, '今年一定要用\\n我的藥瘋狂改造他……\\n不對，是把他改造為熟男！', 0);
insert into room_unit_comments values (6888, 124701, 2, 1, 1, 0, 1, '轉陀螺時看著\\n陀螺看到暈了？\\n沒錯……不需要照顧……', 0);
insert into room_unit_comments values (6889, 124701, 2, 2, 1, 0, 1, '你用我的臉\\n製作笑福面？\\n唉呀……連獨眼都有，\\n重現度真高啊♪', 0);
insert into room_unit_comments values (6890, 124701, 2, 3, 1, 0, 1, '門松好了，注連繩好了，\\n即便是這樣的公會，\\n姑且還是要討個吉利。', 0);
insert into room_unit_comments values (6891, 124701, 2, 4, 1, 0, 1, '這是用真正的藥草\\n熬煮的真心七草粥，\\n粥的顏色就別在意了。', 0);
insert into room_unit_comments values (6892, 124701, 2, 101, 4, 0, 1, '祝福你今年身體健康、\\n平安無事，\\n繼續擔任……我的實驗品。', 0);
insert into room_unit_comments values (6893, 124701, 2, 102, 8, 0, 1, '無論迎接多少次新年，\\n都不會同流合污的你……\\n你就是我\\n最好的解藥。', 0);
insert into room_unit_comments values (6894, 124701, 2, 1, 1, 1, 1, '新年來了，\\n不要一直睡，\\n來吧，開始做實驗♪', 0);
insert into room_unit_comments values (6895, 124701, 2, 2, 1, 2, 1, '歌牌和雙六棋都玩過了，\\n剩下能做的\\n就只有實驗了呢。', 0);
insert into room_unit_comments values (6896, 124701, 2, 3, 1, 3, 1, '平常做的都是些\\n意味深長的夢，不知道初夢\\n會不會是別的……', 0);
insert into room_unit_comments values (6898, 124701, 3, 2, 1, 0, 1, '我倒是會猶豫\\n要不要穿成這樣\\n來睡掉年假。', 0);
insert into room_unit_comments values (6899, 124701, 3, 3, 1, 0, 1, '身、身為一個密醫，\\n我不能被這麼可愛的東西\\n吸引了……', 0);
insert into room_unit_comments values (6900, 124701, 3, 4, 1, 0, 1, '年糕湯的香氣……\\n真是食指大動啊，\\n香味也是很好的藥喔。', 0);
insert into room_unit_comments values (6901, 124701, 3, 5, 1, 0, 1, '我不會遮住胸口喔，\\n不過偶爾會在這裡\\n靜靜取暖……', 0);
insert into room_unit_comments values (6903, 124701, 3, 7, 1, 0, 1, '無中生有，\\n才剛迎接新年\\n就忽視了物理法則啊……', 0);
insert into room_unit_comments values (6904, 124701, 3, 8, 1, 0, 1, '每本書我都讀過了，\\n找對象的新書\\n還沒進貨嗎……', 0);
insert into room_unit_comments values (6906, 124601, 1, 1, 1, 0, 1, '這是他全心全意\\n寫給我的賀年卡，\\n誠意滿滿，\\n超級開心的♪', 0);
insert into room_unit_comments values (6907, 124601, 1, 2, 1, 0, 1, '已經很仔細\\n大掃除過了，\\n可以用非常舒適的心情\\n迎接新年了♪', 0);
insert into room_unit_comments values (6908, 124601, 1, 3, 1, 0, 1, '他的和服裝\\n一定很正氣凜然，\\n光是想像就\\n露出笑容了～♪', 0);
insert into room_unit_comments values (6909, 124601, 1, 4, 1, 0, 1, '每個地區的年糕湯\\n都各不相同，\\n不過各地的秘方\\n應該都一樣，是真誠的心吧♪', 0);
insert into room_unit_comments values (6910, 124601, 1, 5, 1, 0, 1, '雖然想和他\\n放風箏玩耍，\\n但是在森林裡會卡住，\\n或許玩不起來……？', 0);
insert into room_unit_comments values (6911, 124601, 1, 101, 4, 0, 1, '希望他今年也成長為\\n一個坦率的人，\\n我要對他說很多次\\n「乖孩子」……♪', 0);
insert into room_unit_comments values (6912, 124601, 1, 102, 8, 0, 1, '希望一如和他\\n在繪馬上寫的，\\n大家今年都能\\n常保笑容。', 0);
insert into room_unit_comments values (6913, 124601, 2, 1, 1, 0, 1, '跟我一起去\\n新年參拜吧～♪\\n要牽著手免得\\n走散了喔。', 0);
insert into room_unit_comments values (6914, 124601, 2, 2, 1, 0, 1, '新年特賣太棒了，\\n但是要是不注意，\\n可能這個要買、\\n那個也買，結果買太多了……', 0);
insert into room_unit_comments values (6915, 124601, 2, 3, 1, 0, 1, '不要感冒了，\\n要好好保暖喔～\\n咦？想要老師的溫暖？\\n呵呵，真愛撒嬌。', 0);
insert into room_unit_comments values (6916, 124601, 2, 4, 1, 0, 1, '我為你\\n準備了比較\\n豪華的年菜喔，\\n合你的胃口嗎？', 0);
insert into room_unit_comments values (6917, 124601, 2, 101, 4, 0, 1, '你也想穿和服？\\n呵呵，你穿起來\\n可能會很挺拔，\\n讓我心跳加速呢……♪', 0);
insert into room_unit_comments values (6918, 124601, 2, 102, 8, 0, 1, '看到你成長\\n我非常開心，\\n不過今年還是\\n想當你的老師啊……♪', 0);
insert into room_unit_comments values (6919, 124601, 2, 1, 1, 1, 1, '唉呀，你醒了？\\n年糕湯快好了，\\n先去好好洗把臉吧。', 0);
insert into room_unit_comments values (6920, 124601, 2, 2, 1, 2, 1, '午餐好好吃一頓，\\n然後大家一起\\n舉辦歌牌大會吧～♪', 0);
insert into room_unit_comments values (6921, 124601, 2, 3, 1, 3, 1, '好了～乖孩子\\n該睡覺囉。\\n祝你能做一個\\n美麗的初夢♪', 0);
insert into room_unit_comments values (6923, 124601, 3, 2, 1, 0, 1, '真想把這個蓬鬆的\\n玩偶拿去曬……\\n不過在曬之前\\n先休息一下好了。', 0);
insert into room_unit_comments values (6924, 124601, 3, 3, 1, 0, 1, '呵呵呵\\n，好可愛～\\n初音和小碧\\n一定也會喜歡♪', 0);
insert into room_unit_comments values (6925, 124601, 3, 4, 1, 0, 1, '新年的空氣好清澈，\\n所以這個香氣\\n聞起來也比以前都更\\n美妙呢。', 0);
insert into room_unit_comments values (6926, 124601, 3, 5, 1, 0, 1, '在溫暖的火焰\\n包圍下，\\n睡意不可思議地……\\n呼啊啊……', 0);
insert into room_unit_comments values (6928, 124601, 3, 7, 1, 0, 1, '充滿愛心的料理\\n不但美味，\\n還能讓心靈滿足♪', 0);
insert into room_unit_comments values (6929, 124601, 3, 8, 1, 0, 1, '新年有好多儀式啊～\\n我每天都在研究～', 0);
insert into room_unit_comments values (6931, 104701, 1, 103, 12, 0, 1, '對少年的感謝，\\n該用什麼形式表達呢……\\n比起語言，\\n送東西是不是比較好？', 0);
insert into room_unit_comments values (6932, 104701, 2, 103, 12, 0, 1, '你願意陪我\\n聊聊嗎？\\n跟少年在一起\\n真的很快樂。', 0);
insert into room_unit_comments values (6933, 124501, 1, 1, 1, 0, 1, '就算是新年，\\n幫派老大\\n也不能鬆懈喔～', 0);
insert into room_unit_comments values (6934, 124501, 1, 2, 1, 0, 1, '祈梨說想要壓歲錢♪\\n她是壞孩子，\\n給她10盧幣\\n就夠了吧☆', 0);
insert into room_unit_comments values (6935, 124501, 1, 3, 1, 0, 1, '因為是新年\\n所以我煮了年糕湯，\\n年糕湯深受\\n嘉夜的好評呢♪', 0);
insert into room_unit_comments values (6936, 124501, 1, 4, 1, 0, 1, '拉格想吃\\n麻糬……\\n但是給牠吃\\n我會有點擔心～', 0);
insert into room_unit_comments values (6937, 124501, 1, 5, 1, 0, 1, '新年盛裝打扮的人\\n果然很多啊～\\n下次我也推出\\n我的漫畫好了。', 0);
insert into room_unit_comments values (6938, 124501, 1, 101, 4, 0, 1, '我想……\\n單獨和你去神殿\\n新年參拜～♪', 0);
insert into room_unit_comments values (6939, 124501, 1, 102, 8, 0, 1, '和你過新年，\\n感覺麻煩的事\\n通通都要\\n忘掉了……', 0);
insert into room_unit_comments values (6940, 124501, 2, 1, 1, 0, 1, '你覺得這件和服\\n怎麼樣？希望\\n穿起來好看～♪', 0);
insert into room_unit_comments values (6941, 124501, 2, 2, 1, 0, 1, '不要拉袖子喔，\\n衣服會亂掉♪', 0);
insert into room_unit_comments values (6942, 124501, 2, 3, 1, 0, 1, '你有什麼\\n漫畫的新年題材嗎？\\n我想參考看看。', 0);
insert into room_unit_comments values (6943, 124501, 2, 4, 1, 0, 1, '以前都會\\n放風箏或打陀螺，\\n長大之後玩的機會\\n就變少了呢……', 0);
insert into room_unit_comments values (6944, 124501, 2, 101, 4, 0, 1, '我也給你\\n壓歲錢好了？\\n想要多少都可以說☆', 0);
insert into room_unit_comments values (6945, 124501, 2, 102, 8, 0, 1, '明年、後年……\\n能像這樣\\n和大家一起慶祝新年\\n真的很棒呢～♪', 0);
insert into room_unit_comments values (6946, 124501, 2, 1, 1, 1, 1, '早安～♪\\n早餐要吃\\n烤麻糬\\n之類的嗎？', 0);
insert into room_unit_comments values (6947, 124501, 2, 2, 1, 2, 1, '中午了～\\n我準備了年菜，\\n大家一起吃吧。', 0);
insert into room_unit_comments values (6948, 124501, 2, 3, 1, 3, 1, '已經這麼晚了～\\n和服穿了一整天，\\n肩膀好痠啊。', 0);
insert into room_unit_comments values (6950, 124501, 3, 2, 1, 0, 1, '穿著睡覺會皺嗎？\\n換衣服比較好？', 0);
insert into room_unit_comments values (6951, 124501, 3, 3, 1, 0, 1, '這種東西很適合\\n當作拉格的\\n玩具呢♪', 0);
insert into room_unit_comments values (6952, 124501, 3, 4, 1, 0, 1, '雖然味道很香，\\n但是要小心\\n別讓和服染上了。', 0);
insert into room_unit_comments values (6953, 124501, 3, 5, 1, 0, 1, '火勢很剛好呢♪\\n好溫暖啊～♪', 0);
insert into room_unit_comments values (6955, 124501, 3, 7, 1, 0, 1, '穿和服時要\\n適可而止……\\n要是太貪心，\\n肚子會很難受～', 0);
insert into room_unit_comments values (6956, 124501, 3, 8, 1, 0, 1, '嗯──\\n祈梨最近在讀\\n這種漫畫啊～♪', 0);
insert into room_unit_comments values (6958, 124801, 1, 1, 1, 0, 1, '沒有……樂園的氣息！\\n大家都向前邁進。\\n這樣才對嘛！', 0);
insert into room_unit_comments values (6959, 124801, 1, 2, 1, 0, 1, '訓練的成果\\n如果像這樣\\n顯而易見，\\n就很好懂了。', 0);
insert into room_unit_comments values (6960, 124801, 1, 3, 1, 0, 1, '下首歌曲若充滿野性，\\n感覺壞壞的也不錯……\\n就像嘉夜與矛依未\\n給人的印象那樣！', 0);
insert into room_unit_comments values (6961, 124801, 1, 4, 1, 0, 1, '我……有穿著衣服吧？\\n嗯嗯，有穿著！\\n身體太過輕盈，\\n就容易突然感到焦慮～', 0);
insert into room_unit_comments values (6962, 124801, 1, 5, 1, 0, 1, '最近粉絲的年齡層\\n似乎越來越寬廣。\\n這麼說，我離……\\n國民偶像又近一步了？', 0);
insert into room_unit_comments values (6963, 124801, 1, 101, 4, 0, 1, '與烘焙坊合作\\n是什麼樣的感覺啊\\n……像是望望馬卡龍♪\\n之類？……或許行得通。', 0);
insert into room_unit_comments values (6964, 124801, 1, 102, 8, 0, 1, '一旦和他在一起……\\n無論是快樂或痛苦，\\n兩邊都不想放棄……\\n這很像是偶像呢。', 0);
insert into room_unit_comments values (6965, 124801, 2, 1, 1, 0, 1, '我覺得這身裝扮\\n也很適合\\n千歌和紡希～\\n你也這麼認為吧？', 0);
insert into room_unit_comments values (6966, 124801, 2, 2, 1, 0, 1, '如果走到街上去，\\n就會被當成是演唱會\\n或寫真集的\\n活動宣傳吧！', 0);
insert into room_unit_comments values (6967, 124801, 2, 3, 1, 0, 1, '我持有的劍上\\n所綁的蝴蝶結……\\n你有注意到什麼嗎？\\n提示是……顏色喔♪', 0);
insert into room_unit_comments values (6968, 124801, 2, 4, 1, 0, 1, '你的氣息\\n很容易察覺到呢。\\n你只要一接近，\\n我馬上就知道♪', 0);
insert into room_unit_comments values (6969, 124801, 2, 101, 4, 0, 1, '啊，那是……我送你的\\n第一張音響盤！\\n那個，我們一起來聽吧。', 0);
insert into room_unit_comments values (6970, 124801, 2, 102, 8, 0, 1, '你要是發起呆來，\\n我就要對你惡作劇囉♪\\n你要再多多\\n看我幾眼才行！', 0);
insert into room_unit_comments values (6971, 124801, 2, 1, 1, 1, 1, '早晨是一天的開始！\\n讓今天過得\\n比昨天還要更好吧♪', 0);
insert into room_unit_comments values (6972, 124801, 2, 2, 1, 2, 1, '我課上得很認真，\\n現在還是中午……\\n就算午餐吃得很豐盛，\\n也不成問題！', 0);
insert into room_unit_comments values (6973, 124801, 2, 3, 1, 3, 1, '睡前得先確認好\\n明天的預定事項！\\n呃，集合地點是在……', 0);
insert into room_unit_comments values (6975, 124801, 3, 2, 1, 0, 1, '原本只想稍微休息一下，\\n結果卻變成休息很久～\\n這也是常有的事～', 0);
insert into room_unit_comments values (6976, 124801, 3, 3, 1, 0, 1, '望望的熱情擁抱♪\\n……如果只在這裡聽，\\n或許會成為獨家新聞呢？\\n開玩笑的啦！', 0);
insert into room_unit_comments values (6977, 124801, 3, 4, 1, 0, 1, '香味似乎會\\n留在記憶裡呢\\n……只有我會這樣嗎？\\n大家應該都一樣吧？', 0);
insert into room_unit_comments values (6978, 124801, 3, 5, 1, 0, 1, '不知為何，\\n我想起了演唱會的\\n抗奮之情……！\\n再來上課吧！', 0);
insert into room_unit_comments values (6980, 124801, 3, 7, 1, 0, 1, '如果餓肚子，\\n就沒有辦法\\n有好的演出！\\n……嗯～真好吃～♪', 0);
insert into room_unit_comments values (6981, 124801, 3, 8, 1, 0, 1, '要寫出好的歌詞，\\n必須要先知道\\n什麼是最適當的話語！', 0);
insert into room_unit_comments values (6983, 124901, 1, 1, 1, 0, 1, '如果有\\n煩惱不已的傢伙，\\n把他帶來我這裡，\\n我會推他一把的。', 0);
insert into room_unit_comments values (6984, 124901, 1, 2, 1, 0, 1, '最近總覺得\\n家族的夥伴們好像\\n顯得有點焦躁不安……\\n為什麼會這樣？', 0);
insert into room_unit_comments values (6985, 124901, 1, 3, 1, 0, 1, '祈梨那傢伙，\\n老是喜歡\\n搔癢別人的腰……\\n真的很癢呢。', 0);
insert into room_unit_comments values (6986, 124901, 1, 4, 1, 0, 1, '一身裝扮變輕盈\\n是很好啦……可惡\\n，到處都覺得\\n涼颼颼的……', 0);
