insert into room_unit_comments values (7112, 120001, 1, 1, 1, 0, 1, '愛與希望的魔法少女！\\n弟弟的和平\\n就由姐姐來守護♪', 0);
insert into room_unit_comments values (7113, 120001, 1, 2, 1, 0, 1, '給你瞧瞧\\n魔法少女的戰鬥方式！\\n姐姐手刀♪', 0);
insert into room_unit_comments values (7114, 120001, 1, 3, 1, 0, 1, '黑暗力量愈發高漲……\\n得趕緊擁～抱一下\\n弟弟哪！', 0);
insert into room_unit_comments values (7115, 120001, 1, 4, 1, 0, 1, '召喚迷你弟弟\\n到房間裡來，\\n布置成弟弟之家～♪', 0);
insert into room_unit_comments values (7116, 120001, 1, 5, 1, 0, 1, '來練習一下\\n招牌姿勢吧……\\n好，擺姿勢♪', 0);
insert into room_unit_comments values (7117, 120001, 1, 101, 4, 0, 1, '真想以魔法少女之力，\\n瞬間移動到\\n弟弟身邊去。', 0);
insert into room_unit_comments values (7118, 120001, 1, 102, 8, 0, 1, '禁忌的必殺技，\\n其名乃為──\\n黑暗姐姐頭錘。', 0);
insert into room_unit_comments values (7119, 120001, 2, 1, 1, 0, 1, '你對魔法少女般的姐姐\\n很感興趣吧？\\n姐姐會把所有一切\\n都說給你聽的♪', 0);
insert into room_unit_comments values (7120, 120001, 2, 2, 1, 0, 1, '以黑暗姐姐身分讓世界\\n成為弟弟的囊中物……\\n沒事～這只是玩笑話♪', 0);
insert into room_unit_comments values (7121, 120001, 2, 3, 1, 0, 1, '弟弟很想成為\\n魔法少女吧？……\\n嗯，姐姐會聲援你的！', 0);
insert into room_unit_comments values (7122, 120001, 2, 4, 1, 0, 1, '你想要簽名？\\n呵呵，交給我吧！\\n致親愛的弟弟，這樣♪', 0);
insert into room_unit_comments values (7123, 120001, 2, 101, 4, 0, 1, '哼嗯哼嗯～♪\\n魔法少女姐姐的\\n主題曲完成囉♪', 0);
insert into room_unit_comments values (7124, 120001, 2, 102, 8, 0, 1, '嘿嘿，姐姐感受到來自\\n弟弟的愛與希望……\\n還會再變身下去喔♪', 0);
insert into room_unit_comments values (7125, 120001, 2, 1, 1, 1, 1, '以魔法守護\\n弟弟的早晨！\\n一個充滿希望的早晨♪', 0);
insert into room_unit_comments values (7126, 120001, 2, 2, 1, 2, 1, '以魔法守護\\n弟弟的中午！\\n請享用愛的便當吧♪', 0);
insert into room_unit_comments values (7127, 120001, 2, 3, 1, 3, 1, '以魔法守護\\n弟弟的夜晚！\\n接下來就是黑暗時間♪', 0);
insert into room_unit_comments values (7129, 120001, 3, 2, 1, 0, 1, '魔法少女的休息時光♪\\n愛與希望……還有\\n黑暗力量逐漸恢復～♪', 0);
insert into room_unit_comments values (7130, 120001, 3, 3, 1, 0, 1, '好可愛好可愛♪\\n用姐姐的魔法\\n多增加一點吧……♪', 0);
insert into room_unit_comments values (7131, 120001, 3, 4, 1, 0, 1, '這香味……\\n與弟弟一樣……\\n嗅嗅、嗅嗅……', 0);
insert into room_unit_comments values (7132, 120001, 3, 5, 1, 0, 1, '先把身體弄暖……\\n再將這份溫暖全～部\\n傳給弟弟……！', 0);
insert into room_unit_comments values (7134, 120001, 3, 7, 1, 0, 1, '如果要召喚迷你弟弟，\\n就得多吃一點……因為\\n使用魔法很容易肚子餓。', 0);
insert into room_unit_comments values (7135, 120001, 3, 8, 1, 0, 1, '守護弟弟的和平……\\n所以說，\\n要好好檢查一下……♪', 0);
insert into room_unit_comments values (7137, 125301, 1, 1, 1, 0, 1, '這件衣服\\n一開始還穿不太習慣，\\n如今已經可以\\n活動自如了。', 0);
insert into room_unit_comments values (7138, 125301, 1, 2, 1, 0, 1, '為了守護大自然，\\n必須好好學習，\\n每天進行鍛鍊。', 0);
insert into room_unit_comments values (7139, 125301, 1, 3, 1, 0, 1, '在下先來調配\\n剛剛採集到的藥草吧。\\n不時之需難以預測呢。', 0);
insert into room_unit_comments values (7140, 125301, 1, 4, 1, 0, 1, '集訓雖然辛苦，\\n但也多虧有參加集訓，\\n總覺得自己的力氣\\n稍微變大了……♪', 0);
insert into room_unit_comments values (7141, 125301, 1, 5, 1, 0, 1, '餵食汪汪主人的\\n時間到了……', 0);
insert into room_unit_comments values (7142, 125301, 1, 101, 4, 0, 1, '在下要多加\\n訓練和學習，\\n期望能幫上\\n主人的忙……', 0);
insert into room_unit_comments values (7143, 125301, 1, 102, 8, 0, 1, '啊，\\n差不多該帶\\n汪汪主人\\n去散步了。', 0);
insert into room_unit_comments values (7144, 125301, 2, 1, 1, 0, 1, '主人如果方便的話，\\n要不要去散步呢？\\n今天的森林很平靜，\\n很適合去走走。', 0);
insert into room_unit_comments values (7145, 125301, 2, 2, 1, 0, 1, '汪汪主人\\n如今也成了\\n【美食殿堂】的\\n重要夥伴。', 0);
insert into room_unit_comments values (7146, 125301, 2, 3, 1, 0, 1, '森林裡\\n處處充滿危險，\\n請務必小心，\\n別受傷了。', 0);
insert into room_unit_comments values (7147, 125301, 2, 4, 1, 0, 1, '如果對森林或動物們\\n有任何疑問，\\n歡迎隨時提出。', 0);
insert into room_unit_comments values (7148, 125301, 2, 101, 4, 0, 1, '進入森林前，\\n務必要好好檢查裝備。\\n主人，\\n請遵守跟在下約定喔。', 0);
insert into room_unit_comments values (7149, 125301, 2, 102, 8, 0, 1, '在森林中\\n與主人們一起\\n悠閒度過的時光，\\n對在下而言很寶貴。', 0);
insert into room_unit_comments values (7150, 125301, 2, 1, 1, 1, 1, '早安。\\n好好吃頓早餐後，\\n就準備執行勤務了。', 0);
insert into room_unit_comments values (7151, 125301, 2, 2, 1, 2, 1, '現在是\\n汪汪主人的點心時間。\\n來，請吃。', 0);
insert into room_unit_comments values (7152, 125301, 2, 3, 1, 3, 1, '就算已適應了\\n露營的生活，\\n在下還是比較想\\n躺在床上睡覺。', 0);
insert into room_unit_comments values (7154, 125301, 3, 2, 1, 0, 1, '這張座椅\\n汪汪主人\\n似乎也很喜歡呢。', 0);
insert into room_unit_comments values (7155, 125301, 3, 3, 1, 0, 1, '這類玩具\\n是否可以給\\n汪汪主人玩呢？', 0);
insert into room_unit_comments values (7156, 125301, 3, 4, 1, 0, 1, '這香味聞起來很舒服。\\n下次也來試試\\n天然的香料吧。', 0);
insert into room_unit_comments values (7157, 125301, 3, 5, 1, 0, 1, '野營的時候，\\n保暖是不容忽視的。', 0);
insert into room_unit_comments values (7159, 125301, 3, 7, 1, 0, 1, '能吃的時候盡量吃，\\n在大自然中\\n也是很重要的事。', 0);
insert into room_unit_comments values (7160, 125301, 3, 8, 1, 0, 1, '累積更多的知識，\\n讓自己懂得活用\\n大自然的力量吧。', 0);
insert into room_unit_comments values (7162, 100901, 1, 103, 12, 0, 1, '這次的《冥風戰記》\\n也寫得很好。\\n呵呵，吉格魯特也會\\n為我感到開心吧？', 0);
insert into room_unit_comments values (7163, 100901, 2, 103, 12, 0, 1, '若是為了你，\\n我可以隨時解放\\n我真正的魔力喔！', 0);
insert into room_unit_comments values (7164, 125401, 1, 1, 1, 0, 1, '要不要幫\\n喵特刷牙呢。', 0);
insert into room_unit_comments values (7165, 125401, 1, 2, 1, 0, 1, '呵呵……\\n這就是遊俠的\\n制服嗎？', 0);
insert into room_unit_comments values (7166, 125401, 1, 3, 1, 0, 1, '差不多到\\n巡邏的時間了……', 0);
insert into room_unit_comments values (7167, 125401, 1, 4, 1, 0, 1, '我們去找找\\n可以對魔物\\n有更多認識的書吧。', 0);
insert into room_unit_comments values (7168, 125401, 1, 5, 1, 0, 1, '成為遊俠後，\\n我發現自己的\\n生病次數減少了……', 0);
insert into room_unit_comments values (7169, 125401, 1, 101, 4, 0, 1, '我想要知道\\n更多從書中學不到的\\n這個世界上的事……', 0);
insert into room_unit_comments values (7170, 125401, 1, 102, 8, 0, 1, '我相信\\n就算是這樣的我……\\n也有能做到的事。', 0);
insert into room_unit_comments values (7171, 125401, 2, 1, 1, 0, 1, '那個……\\n如果可以的話，\\n要不要一起去巡邏？', 0);
insert into room_unit_comments values (7172, 125401, 2, 2, 1, 0, 1, '如果可以再和你\\n一起做遊俠的工作，\\n我會很高興。', 0);
insert into room_unit_comments values (7173, 125401, 2, 3, 1, 0, 1, '咳咳。\\n……我、我沒事。\\n只是偶爾\\n會咳一下而已。', 0);
insert into room_unit_comments values (7174, 125401, 2, 4, 1, 0, 1, '喵特\\n對你也\\n越來越親近了呢。', 0);
insert into room_unit_comments values (7175, 125401, 2, 101, 4, 0, 1, '要和喵特\\n一起去散步嗎？\\n……也去\\n邀那個人一起吧。', 0);
insert into room_unit_comments values (7176, 125401, 2, 102, 8, 0, 1, '我高興可以\\n和你並肩從事\\n遊俠的工作。', 0);
insert into room_unit_comments values (7177, 125401, 2, 1, 1, 1, 1, '早安。\\n遊俠‧栞\\n精神飽滿地出門工作。', 0);
insert into room_unit_comments values (7178, 125401, 2, 2, 1, 2, 1, '也要為喵特\\n準備午飯才行。', 0);
insert into room_unit_comments values (7179, 125401, 2, 3, 1, 3, 1, '因為明天有工作，\\n必須早起。\\n晚安囉。', 0);
insert into room_unit_comments values (7181, 125401, 3, 2, 1, 0, 1, '呼……為了工作\\n四處奔走，\\n稍微休息一下吧。', 0);
insert into room_unit_comments values (7182, 125401, 3, 3, 1, 0, 1, '工作結束後，\\n像這樣撫摸著，\\n真的很療癒……', 0);
insert into room_unit_comments values (7183, 125401, 3, 4, 1, 0, 1, '有森林的芳香……\\n看來今晚\\n會很好睡了。', 0);
insert into room_unit_comments values (7184, 125401, 3, 5, 1, 0, 1, '和喵特\\n一樣暖和……', 0);
insert into room_unit_comments values (7186, 125401, 3, 7, 1, 0, 1, '做完遊俠的工作，\\n肚子就餓了。', 0);
insert into room_unit_comments values (7187, 125401, 3, 8, 1, 0, 1, '無論有多忙，\\n唯有讀書\\n無法放棄。', 0);
insert into room_unit_comments values (7189, 125801, 1, 1, 1, 0, 1, '兄長大人不在時，\\n我會守護好\\n兄長大人的家！', 0);
insert into room_unit_comments values (7190, 125801, 1, 2, 1, 0, 1, '兄、兄、兄長大人\\n的家……\\n既然受邀前來\\n就不能有失禮數……', 0);
insert into room_unit_comments values (7191, 125801, 1, 3, 1, 0, 1, '呵呵……\\n兄長大人出色的品行，\\n從家中散發出來……♪', 0);
insert into room_unit_comments values (7192, 125801, 1, 4, 1, 0, 1, '我還不習慣\\n穿著鞋子走進屋內……\\n啊！若用飛的\\n就沒關係……！', 0);
insert into room_unit_comments values (7193, 125801, 1, 5, 1, 0, 1, '這裡若插幾束花，\\n似乎就會變得很華麗。\\n兄長大人……\\n喜歡花嗎……？', 0);
insert into room_unit_comments values (7194, 125801, 1, 101, 4, 0, 1, '如果我說……\\n想一直待在這裡，\\n想必會讓兄長大人\\n覺得困擾吧……', 0);
insert into room_unit_comments values (7195, 125801, 1, 102, 8, 0, 1, '做些料理迎接兄長大人\\n感覺也挺不錯的……\\n我也要再多加磨練\\n自己的手藝……！', 0);
insert into room_unit_comments values (7196, 125801, 2, 1, 1, 0, 1, '很抱歉\\n讓您看到這樣的我……！\\n因、因為有老鼠，\\n不小心認真起來……！', 0);
insert into room_unit_comments values (7197, 125801, 2, 2, 1, 0, 1, '歡迎回來。\\n請好好休息，\\n消除身體忙碌一天的疲勞吧。', 0);
insert into room_unit_comments values (7198, 125801, 2, 3, 1, 0, 1, '陪兄長大人聊天…！？\\n該、該怎麼辦呢…\\n我根本不會聊些什麼\\n有趣的話題…啊嗚…！', 0);
insert into room_unit_comments values (7199, 125801, 2, 4, 1, 0, 1, '我好像具有\\n受動物們喜愛的體質…\\n這有助於\\n尋找失蹤貓咪的打工嗎？', 0);
insert into room_unit_comments values (7200, 125801, 2, 101, 4, 0, 1, '璃亞說……\\n如果到兄長大人家裡，\\n「陪睡」是種禮貌，\\n這是……真的嗎？', 0);
insert into room_unit_comments values (7201, 125801, 2, 102, 8, 0, 1, '我可以……\\n待在這裡嗎？\\n作為各位的\\n「夥伴」……', 0);
insert into room_unit_comments values (7202, 125801, 2, 1, 1, 1, 1, '我很擅長早起。\\n兄長大人如果\\n很難清醒過來，\\n我會叫您起床的。', 0);
insert into room_unit_comments values (7203, 125801, 2, 2, 1, 2, 1, '兄長大人……\\n我試著做午餐，\\n結果失敗了……', 0);
insert into room_unit_comments values (7204, 125801, 2, 3, 1, 3, 1, '呼啊……\\n啊！呃……\\n讓您看到令人\\n難為情的模樣……！', 0);
insert into room_unit_comments values (7206, 125801, 3, 2, 1, 0, 1, '這、這是……！\\n明明在他人家裡，\\n卻不禁……想……\\n懶散起來……！', 0);
insert into room_unit_comments values (7207, 125801, 3, 3, 1, 0, 1, '不、不會\\n飛撲過來……\\n是吧……？', 0);
insert into room_unit_comments values (7208, 125801, 3, 4, 1, 0, 1, '吸……吐……\\n這芬芳的氣味，\\n心靈被洗滌淨化了……', 0);
insert into room_unit_comments values (7209, 125801, 3, 5, 1, 0, 1, '怎麼會這麼暖和……\\n就像兄長大人溫暖的\\n寬大胸襟一樣……♪', 0);
insert into room_unit_comments values (7211, 125801, 3, 7, 1, 0, 1, '吃成這個樣子，\\n是不是……\\n太不成體統了？', 0);
insert into room_unit_comments values (7212, 125801, 3, 8, 1, 0, 1, '兄長大人的書架\\n就像是知識的寶庫。\\n讓我切身體會到\\n自己的懵懂無知。', 0);
insert into room_unit_comments values (7214, 125501, 1, 1, 1, 0, 1, '為了下次的冒險，\\n來做騎空士便當吧。\\n首先就從飯糰開始！', 0);
insert into room_unit_comments values (7215, 125501, 1, 2, 1, 0, 1, '魔法修行勢在必行，\\n因為我想成為\\n更加值得信賴的團長！', 0);
insert into room_unit_comments values (7216, 125501, 1, 3, 1, 0, 1, '我想邀碧卡拉\\n一起去玩……\\n不知道她喜歡去\\n什麼樣的地方呢？', 0);
insert into room_unit_comments values (7217, 125501, 1, 4, 1, 0, 1, '認真確實地\\n把工作做好，\\n是登上騎空艇的\\n最佳捷徑對吧。', 0);
insert into room_unit_comments values (7218, 125501, 1, 5, 1, 0, 1, '在場的這些人，\\n有沒有人對騎空團\\n有興趣呢……？', 0);
insert into room_unit_comments values (7219, 125501, 1, 101, 4, 0, 1, '雖說至今已經累積了\\n許多回憶……\\n但我接下來還想\\n再創造出更多的回憶！', 0);
insert into room_unit_comments values (7220, 125501, 1, 102, 8, 0, 1, '因為身邊有夥伴，\\n騎空士才能超越\\n自己的極限呢。', 0);
insert into room_unit_comments values (7221, 125501, 2, 1, 1, 0, 1, '這套服裝感覺\\n很像魔法師對吧。\\n為了配得上它，\\n我得好好努力才行！', 0);
insert into room_unit_comments values (7222, 125501, 2, 2, 1, 0, 1, '如果是和你在一起，\\n無論遇到什麼對手，\\n我都不覺得害怕喔！', 0);
insert into room_unit_comments values (7223, 125501, 2, 3, 1, 0, 1, '花凜小姐給了我們\\n新的委託！\\n我們一起加油吧！', 0);
insert into room_unit_comments values (7224, 125501, 2, 4, 1, 0, 1, '要成為操舵士，\\n似乎很不容易……\\n但願能早日找到\\n值得依賴的夥伴呢。', 0);
insert into room_unit_comments values (7225, 125501, 2, 101, 4, 0, 1, '你想要比試一下嗎？\\n好喔，偶爾也讓你\\n瞧瞧團長應有的風範！', 0);
insert into room_unit_comments values (7226, 125501, 2, 102, 8, 0, 1, '雖然目前成員\\n只有兩個人……\\n但這個騎空團\\n是我的寶物喔！', 0);
insert into room_unit_comments values (7227, 125501, 2, 1, 1, 1, 1, '早安～\\n我昨天研究魔法\\n到很晚……\\n呼啊……好睏喔……', 0);
insert into room_unit_comments values (7228, 125501, 2, 2, 1, 2, 1, '好啦，\\n現在是午休時間！\\n我要去沖壺紅茶，\\n我們一起喝吧！', 0);
insert into room_unit_comments values (7229, 125501, 2, 3, 1, 3, 1, '忙到這麼晚，辛苦了！\\n不要太勉強自己，\\n我們一起加油吧！', 0);
insert into room_unit_comments values (7231, 125501, 3, 2, 1, 0, 1, '暫停一下\\n魔法的研究！\\n得讓頭腦好好休息。', 0);
insert into room_unit_comments values (7232, 125501, 3, 3, 1, 0, 1, '就像變魔法\\n一樣可愛！\\n要加入騎空團嗎？\\n開玩笑的！', 0);
insert into room_unit_comments values (7233, 125501, 3, 4, 1, 0, 1, '很棒的香氣……\\n而且總覺得\\n專注力也提升了不少。', 0);
insert into room_unit_comments values (7234, 125501, 3, 5, 1, 0, 1, '不只是身體，\\n連內心也變得\\n暖烘烘的……\\n感覺就像魔法一樣。', 0);
insert into room_unit_comments values (7236, 125501, 3, 7, 1, 0, 1, '動腦思考過後，\\n肚子都餓了！\\n我要開動囉──！', 0);
insert into room_unit_comments values (7237, 125501, 3, 8, 1, 0, 1, '「若要抵達\\n法則的另一端……」\\n……這本魔導書\\n可以學到很多呢。', 0);
insert into room_unit_comments values (7239, 125701, 1, 1, 1, 0, 1, '一穿上這件衣服，\\n就變得很想\\n出去冒險呢。', 0);
insert into room_unit_comments values (7240, 125701, 1, 2, 1, 0, 1, '冒險者心得之一，\\n準備工作要盡可能地\\n做到最完善。\\n你覺得如何？', 0);
insert into room_unit_comments values (7241, 125701, 1, 3, 1, 0, 1, '調配所需的素材\\n有那個、這個和這個…\\n一有時間就會\\n忍不住去想這些事呢。', 0);
insert into room_unit_comments values (7242, 125701, 1, 4, 1, 0, 1, '機會難得，\\n我想要自己去\\n收集更好的素材。', 0);
insert into room_unit_comments values (7243, 125701, 1, 5, 1, 0, 1, '這份藥水\\n若可以派上用場，\\n我會很開心的。', 0);
insert into room_unit_comments values (7244, 125701, 1, 101, 4, 0, 1, '冒險者心得之二，\\n情況危急時，\\n要先逃走！\\n這很重要呢。', 0);
insert into room_unit_comments values (7245, 125701, 1, 102, 8, 0, 1, '那個人的冒險看起來\\n似乎一直都很開心，\\n讓我也能振奮起來。', 0);
insert into room_unit_comments values (7246, 125701, 2, 1, 1, 0, 1, '我有一份\\n想推薦給你的委託，\\n請你過來這裡♪', 0);
insert into room_unit_comments values (7247, 125701, 2, 2, 1, 0, 1, '你如果有想要什麼藥，\\n我可以用鍊金術\\n製藥給你喔。', 0);
insert into room_unit_comments values (7248, 125701, 2, 3, 1, 0, 1, '對委託若有任何疑問，\\n都可以向我確認。', 0);
insert into room_unit_comments values (7249, 125701, 2, 4, 1, 0, 1, '我想在這棟公會小屋裡\\n設置鍊金術的\\n實驗設備……\\n咦？不行嗎？', 0);
insert into room_unit_comments values (7250, 125701, 2, 101, 4, 0, 1, '這瓶藥水送你。\\n沒問題的，\\n我已經確認過\\n其效果了♪', 0);
insert into room_unit_comments values (7251, 125701, 2, 102, 8, 0, 1, '下次要去進行\\n什麼樣的冒險呢？\\n我都會陪你一起去喔。', 0);
insert into room_unit_comments values (7252, 125701, 2, 1, 1, 1, 1, '今天似乎\\n也會是個好日子。\\n應該可說是\\n最適合冒險的日子吧。', 0);
insert into room_unit_comments values (7253, 125701, 2, 2, 1, 2, 1, '時間正好，\\n要吃午餐嗎？\\n順便也可以開個有關\\n下次冒險的作戰會議♪', 0);
insert into room_unit_comments values (7254, 125701, 2, 3, 1, 3, 1, '夜已深了，\\n為了明天的冒險，\\n今天早點睡吧。', 0);
insert into room_unit_comments values (7256, 125701, 3, 2, 1, 0, 1, '呼……冒險果然\\n和事務工作不一樣。\\n稍微休息一下吧……', 0);
insert into room_unit_comments values (7257, 125701, 3, 3, 1, 0, 1, '冒險的緊張接連不斷，\\n用可愛的東西\\n來療癒一下心靈吧。', 0);
insert into room_unit_comments values (7258, 125701, 3, 4, 1, 0, 1, '嗯，這香氣……\\n是以絕佳的比例\\n調配而成的呢。', 0);
insert into room_unit_comments values (7259, 125701, 3, 5, 1, 0, 1, '這般火力……\\n似乎可用於\\n鍊金術的調配。', 0);
insert into room_unit_comments values (7261, 125701, 3, 7, 1, 0, 1, '在冒險之際，\\n吃得下就要盡量吃！\\n……貪吃佩可小姐\\n也曾這樣說過呢。', 0);
insert into room_unit_comments values (7262, 125701, 3, 8, 1, 0, 1, '嗯嗯……原來如此。\\n原來也有\\n這種調配方式……\\n真的很深奧呢。', 0);
insert into room_unit_comments values (7264, 106101, 1, 103, 12, 0, 1, '那傢伙去哪了……？\\n如果要戰鬥，\\n就也該帶我去啊──！', 0);
insert into room_unit_comments values (7265, 106101, 2, 103, 12, 0, 1, '今天也要在擊退壞蛋…\\n在這之前\\n先填飽了肚子！\\n我們去吃點什麼吧！', 0);
insert into room_unit_comments values (7266, 125601, 1, 1, 1, 0, 1, '各位～！\\n在公會小屋\\n和我握手吧！', 0);
insert into room_unit_comments values (7267, 125601, 1, 2, 1, 0, 1, '儘管種族、年齡\\n各不相同，但大家\\n看起來都很愉快呢！\\n這果然是我的烏～托邦！', 0);
insert into room_unit_comments values (7268, 125601, 1, 3, 1, 0, 1, '哈哈！這裡簡直\\n就像是派對會場！\\n看來很值得\\n大玩特玩呢！', 0);
insert into room_unit_comments values (7269, 125601, 1, 4, 1, 0, 1, '嗨，各位！\\n要來副充滿吉利的\\n老鼠耳朵嗎？\\n一副三盧幣喔！', 0);
insert into room_unit_comments values (7270, 125601, 1, 5, 1, 0, 1, '待在這裡，\\n就不愁缺少玩伴了呢。\\n神明保佑神明保佑～！', 0);
insert into room_unit_comments values (7271, 125601, 1, 101, 4, 0, 1, '要不要去\\n哪裡玩呢♪\\n對了，也邀那個人\\n一起去吧！', 0);
insert into room_unit_comments values (7272, 125601, 1, 102, 8, 0, 1, '和那個人在一起，\\n連我也會變得開心！\\n每天都是\\n想遊行的心情！', 0);
insert into room_unit_comments values (7273, 125601, 2, 1, 1, 0, 1, '老鼠耳朵已經\\n沒庫存了！\\n你願意\\n協助我製作嗎？', 0);
insert into room_unit_comments values (7274, 125601, 2, 2, 1, 0, 1, '嗯？你要握手？\\n當然沒問題！\\n隨時隨地要握幾次，\\n我都很樂意喔！', 0);
insert into room_unit_comments values (7275, 125601, 2, 3, 1, 0, 1, '你對這件衣服\\n有興趣嗎？\\n你還挺有眼光的嘛！', 0);
insert into room_unit_comments values (7276, 125601, 2, 4, 1, 0, 1, '我打算在這裡\\n打擾一段時間喔。\\n待會你可以為我\\n介紹城鎮嗎？', 0);
insert into room_unit_comments values (7277, 125601, 2, 101, 4, 0, 1, '這珍藏的起司，\\n我們一人一半！\\n要對大家保密喔？', 0);
insert into room_unit_comments values (7278, 125601, 2, 102, 8, 0, 1, '在我需要你的時候，\\n你總是在我身邊呢。\\n真是神明保佑！', 0);
insert into room_unit_comments values (7279, 125601, 2, 1, 1, 1, 1, '嗨，早安！\\n真是個舒服的早晨呢。\\n今天也要開心地\\n努力販賣老鼠耳朵喔！', 0);
insert into room_unit_comments values (7280, 125601, 2, 2, 1, 2, 1, '已經中午囉！\\n我帶來好吃的起司，\\n我們一人一半吧！', 0);
insert into room_unit_comments values (7281, 125601, 2, 3, 1, 3, 1, '今天一整天\\n都很開心呢！\\n肯定可以\\n做個好夢啊！', 0);
insert into room_unit_comments values (7283, 125601, 3, 2, 1, 0, 1, '我也需要休息啊！\\n放鬆一下～\\n放鬆一下～♪', 0);
insert into room_unit_comments values (7284, 125601, 3, 3, 1, 0, 1, '我要不要也來\\n製作布偶販賣呢……\\n說不定能一口氣湊齊\\n足夠的修繕費用呀！', 0);
insert into room_unit_comments values (7285, 125601, 3, 4, 1, 0, 1, '好香的味道呀！\\n這和東國的薰香\\n十分相似呢。', 0);
insert into room_unit_comments values (7286, 125601, 3, 5, 1, 0, 1, '呼……整個人\\n都暖和起來了……\\n對了，要不要試著\\n用這火來烤起司呢？', 0);
insert into room_unit_comments values (7288, 125601, 3, 7, 1, 0, 1, '嗯～這味道……\\n會讓我想起東國呢。\\n我看看，\\n再讓我品嚐一個吧！', 0);
insert into room_unit_comments values (7289, 125601, 3, 8, 1, 0, 1, '這是蘭德索爾的\\n導覽書呢！\\n請借給我\\n做個參考吧！', 0);
insert into room_unit_comments values (7291, 126201, 1, 1, 1, 0, 1, '想不到，妾身竟然會有\\n穿這種服裝的一天……\\n真令人感慨。', 0);
insert into room_unit_comments values (7292, 126201, 1, 2, 1, 0, 1, '妾身的美貌，\\n讓這棟公會小屋\\n顯得更加華麗了哪。', 0);
insert into room_unit_comments values (7293, 126201, 1, 3, 1, 0, 1, '別讓無趣的戰爭\\n弄髒了盛裝。\\n今天一整天\\n就悠閒地度過吧。', 0);
insert into room_unit_comments values (7294, 126201, 1, 4, 1, 0, 1, '今天妾身\\n要準備餐點，\\n等待那傢伙回來。\\n菜單要如何設計呢？', 0);
insert into room_unit_comments values (7295, 126201, 1, 5, 1, 0, 1, '那傢伙是叫雪嗎……\\n雖然難以捉摸，\\n但妾身很認同\\n那傢伙的毅力。', 0);
insert into room_unit_comments values (7296, 126201, 1, 101, 4, 0, 1, '像這樣，與夥伴\\n悠閒度過的時光\\n也不賴嘛♪', 0);
insert into room_unit_comments values (7297, 126201, 1, 102, 8, 0, 1, '妾身很期待\\n邀約那傢伙\\n去賞月的夜晚♪', 0);
insert into room_unit_comments values (7298, 126201, 2, 1, 1, 0, 1, '無論什麼樣的服裝，\\n妾身都能\\n完美地穿搭♪', 0);
insert into room_unit_comments values (7299, 126201, 2, 2, 1, 0, 1, '怎麼了？\\n對妾身的姿態\\n深深著迷了嗎？\\n你、你很坦率，很好。', 0);
insert into room_unit_comments values (7300, 126201, 2, 3, 1, 0, 1, '這服裝很適合\\n身為吸血鬼的妾身。\\n之後希望能再配上\\n鮮紅的血液……', 0);
insert into room_unit_comments values (7301, 126201, 2, 4, 1, 0, 1, '你累了嗎？\\n妾身來療癒你吧。\\n來，再靠近一點。', 0);
insert into room_unit_comments values (7302, 126201, 2, 101, 4, 0, 1, '只要有你在，\\n公會小屋就會\\n變得很熱鬧。\\n這也算是一種才能呢♪', 0);
insert into room_unit_comments values (7303, 126201, 2, 102, 8, 0, 1, '眷屬啊，你要洗澡？\\n還是吃飯？\\n不管哪項，都讓妾身…\\n不，快點忘掉吧。', 0);
insert into room_unit_comments values (7304, 126201, 2, 1, 1, 1, 1, '一大清早\\n就這麼想見妾身哪……\\n眷屬啊，\\n你很值得讚賞♪', 0);
insert into room_unit_comments values (7305, 126201, 2, 2, 1, 2, 1, '眷屬啊，\\n妾身已準備了餐點。\\n要好好努力，\\n別輸給可恨的太陽。', 0);
insert into room_unit_comments values (7306, 126201, 2, 3, 1, 3, 1, '夜晚拿來睡覺太可惜了。\\n眷屬啊，\\n你就在妾身身旁\\n多待一會兒吧。', 0);
insert into room_unit_comments values (7308, 126201, 3, 2, 1, 0, 1, '鬆鬆軟軟的♪\\n下次也讓那傢伙\\n試著坐坐看。', 0);
insert into room_unit_comments values (7309, 126201, 3, 3, 1, 0, 1, '可愛的傢伙，\\n要不要陪妾身\\n稍微玩一下呢？', 0);
insert into room_unit_comments values (7310, 126201, 3, 4, 1, 0, 1, '很有品味的香氣。\\n妾身的古城裡\\n也想擺一個。', 0);
insert into room_unit_comments values (7311, 126201, 3, 5, 1, 0, 1, '很棒的溫度……\\n完全不同於\\n可恨的太陽。', 0);
insert into room_unit_comments values (7313, 126201, 3, 7, 1, 0, 1, '做為調味的參考，\\n再吃一個就好……\\n好吃得讓人停不了……', 0);
insert into room_unit_comments values (7314, 126201, 3, 8, 1, 0, 1, '上頭記載了\\n吸血鬼的缺點嗎……\\n嗯，\\n來看一看提神吧。', 0);
insert into room_unit_comments values (7316, 100801, 1, 103, 12, 0, 1, '即使我\\n就這樣繼續變美，\\n相信他也一定會願意\\n陪在我身旁的！', 0);
insert into room_unit_comments values (7317, 100801, 2, 103, 12, 0, 1, '你再靠近一點啊。\\n我的身邊可是\\n專為你而留的\\n特等席。', 0);
insert into room_unit_comments values (7318, 126301, 1, 1, 1, 0, 1, '或許是受服裝影響……\\n感覺自己比之前更喜歡\\n黑色和紅色了。', 0);
insert into room_unit_comments values (7319, 126301, 1, 2, 1, 0, 1, '只是有我在，\\n這裡就瞬間\\n變華麗了呢♪', 0);
insert into room_unit_comments values (7320, 126301, 1, 3, 1, 0, 1, '我下次帶\\n我的畫像來給\\n這裡裝飾一下好了。', 0);
insert into room_unit_comments values (7321, 126301, 1, 4, 1, 0, 1, '我真是被自己\\n迷到神魂顛倒♪\\n這正是魔性的存在啊。', 0);
insert into room_unit_comments values (7322, 126301, 1, 5, 1, 0, 1, '有人想聽\\n美的建議嗎？\\n我可以讓你\\n更加發光發熱喔♪', 0);
insert into room_unit_comments values (7323, 126301, 1, 101, 4, 0, 1, '那個人下次會\\n怎麼讚美我呢？\\n即使他用盡詞彙了，\\n我也原諒他吧♪', 0);
insert into room_unit_comments values (7324, 126301, 1, 102, 8, 0, 1, '來幫那個人想想\\n適合他的穿搭吧，\\n不過他說不定穿什麼\\n都會很有型呢♪', 0);
insert into room_unit_comments values (7325, 126301, 2, 1, 1, 0, 1, '我還是不要\\n變成什麼吸血鬼了！\\n因為還有其他比\\n永恆之美更重要的事。', 0);
insert into room_unit_comments values (7326, 126301, 2, 2, 1, 0, 1, '有益肌膚的東西，\\n我大部分都喜歡。\\n比如像是番茄汁。', 0);
insert into room_unit_comments values (7327, 126301, 2, 3, 1, 0, 1, '銀飾品\\n是我的最愛，\\n我等你隨時\\n送我當禮物喔♪', 0);
insert into room_unit_comments values (7328, 126301, 2, 4, 1, 0, 1, '我也想像伊莉亞一樣\\n住在城堡裡看看♪\\n這很適合\\n統御美麗之人對吧？', 0);
insert into room_unit_comments values (7329, 126301, 2, 101, 4, 0, 1, '我現在只要碰到杯子，\\n杯子就會壞掉。\\n所以你來\\n餵我喝紅茶吧。', 0);
insert into room_unit_comments values (7330, 126301, 2, 102, 8, 0, 1, '只要你也提升\\n自己的美，一定能\\n更發光發熱的！\\n我來幫你梳妝打扮吧？', 0);
insert into room_unit_comments values (7331, 126301, 2, 1, 1, 1, 1, '嗯嗯，還是好睏……\\n為了美容，\\n我要再多睡一下……', 0);
insert into room_unit_comments values (7332, 126301, 2, 2, 1, 2, 1, '我買了\\n和我非常相襯的陽傘♪\\n我們去散步吧！', 0);
insert into room_unit_comments values (7333, 126301, 2, 3, 1, 3, 1, '夜晚就是我的時間！\\n好了，來讓所有的人\\n認識我的美貌吧！', 0);
insert into room_unit_comments values (7335, 126301, 3, 2, 1, 0, 1, '放鬆是很重要的。\\n保持精神上的從容，\\n才是美麗的祕訣呀♪', 0);
insert into room_unit_comments values (7336, 126301, 3, 3, 1, 0, 1, '我也讓你變成\\n我美貌的俘虜♪\\n你很開心對吧？', 0);
insert into room_unit_comments values (7337, 126301, 3, 4, 1, 0, 1, '足以魅惑人們的\\n魔性香氣……\\n很適合我呢♪', 0);
insert into room_unit_comments values (7338, 126301, 3, 5, 1, 0, 1, '寒冷是很傷美貌的。\\n如果想變得\\n像我這麼美，\\n就應該注意保暖。', 0);
insert into room_unit_comments values (7340, 126301, 3, 7, 1, 0, 1, '我用餐時\\n所展現出的優美……♪\\n想必連吸血鬼都會\\n感到怦然心動吧。', 0);
insert into room_unit_comments values (7341, 126301, 3, 8, 1, 0, 1, '吸血鬼是\\n美麗的存在呢……\\n但還比不上我就是了。', 0);
insert into room_unit_comments values (7343, 126401, 1, 1, 1, 0, 1, '這裡就是騎士先生的\\n家呀～！和你給人的\\n氛圍一模一樣！', 0);
insert into room_unit_comments values (7344, 126401, 1, 2, 1, 0, 1, '門和牆壁\\n都不是點心對吧？\\n好不可思議喔～♪', 0);
insert into room_unit_comments values (7345, 126401, 1, 3, 1, 0, 1, '我要小心\\n不能失禮！\\n因為我和萊拉耶爾殿下\\n約定好了！', 0);
insert into room_unit_comments values (7346, 126401, 1, 4, 1, 0, 1, '希望等我回去那裡時，\\n能帶很多有趣的經歷\\n回去說給大家聽～♪', 0);
insert into room_unit_comments values (7347, 126401, 1, 5, 1, 0, 1, '除了戰鬥用的手臂，\\n真希望也可以另外有\\n觀光用的手臂呢～', 0);
insert into room_unit_comments values (7348, 126401, 1, 101, 4, 0, 1, '騎士先生的充電\\n和萊拉耶爾殿下的\\n充電不一樣，\\n但我兩邊都非常喜歡♪', 0);
insert into room_unit_comments values (7349, 126401, 1, 102, 8, 0, 1, '……如果騎士先生\\n能成為我的Master……\\n嘿嘿……沒事！', 0);
insert into room_unit_comments values (7350, 126401, 2, 1, 1, 0, 1, '如果需要幫忙，\\n請交給我吧！\\n像是你的家，\\n需要我幫忙搬移嗎？', 0);
insert into room_unit_comments values (7351, 126401, 2, 2, 1, 0, 1, '我來這裡前，\\n波波爺爺\\n有充分幫我\\n做好維護喔♪', 0);
insert into room_unit_comments values (7352, 126401, 2, 3, 1, 0, 1, '雖然待在家裡也不錯，\\n可是外面的世界\\n也令人很好奇～！\\n我們出去看看吧♪', 0);
insert into room_unit_comments values (7353, 126401, 2, 4, 1, 0, 1, '我發現了一隻\\n和沃爾加諾\\n長得好像的小貓咪！\\n是他的親戚嗎？', 0);
insert into room_unit_comments values (7354, 126401, 2, 101, 4, 0, 1, '我偷偷跟你說……\\n其實我的線\\n如果被人碰到，\\n我會覺得有點癢喔！', 0);
insert into room_unit_comments values (7355, 126401, 2, 102, 8, 0, 1, '這個家的屋主\\n是騎士先生對吧？\\n那你在這裡\\n就是主人呢！', 0);
insert into room_unit_comments values (7356, 126401, 2, 1, 1, 1, 1, '早安！\\n今天也一起\\n全力加油吧♪', 0);
insert into room_unit_comments values (7357, 126401, 2, 2, 1, 2, 1, '午餐時間到了！\\n麻煩幫我進行\\n午餐的充電～♪', 0);
insert into room_unit_comments values (7358, 126401, 2, 3, 1, 3, 1, '你還不睡覺\\n不要緊嗎？\\n如果要熬夜，\\n我可以陪你喔♪', 0);
insert into room_unit_comments values (7360, 126401, 3, 2, 1, 0, 1, '盡可能降低\\n能量的消耗\\n也是很重要的呢～', 0);
insert into room_unit_comments values (7361, 126401, 3, 3, 1, 0, 1, '就算被我碰到\\n也沒事……！\\n可愛又堅固！\\n我喜歡！', 0);
insert into room_unit_comments values (7362, 126401, 3, 4, 1, 0, 1, '根據感應器的判斷……\\n這個氣味似乎\\n歸類為香氣！\\n原來如此～！', 0);
insert into room_unit_comments values (7363, 126401, 3, 5, 1, 0, 1, '溫暖是\\n很棒的事對吧！\\n而且大家也都\\n很開心的樣子♪', 0);
insert into room_unit_comments values (7365, 126401, 3, 7, 1, 0, 1, '這就是\\n這個世界的食物呀！\\n感覺很可口！', 0);
insert into room_unit_comments values (7366, 126401, 3, 8, 1, 0, 1, '輕輕地輕輕地……\\n小心別把書弄破……\\n這比要理解書上的\\n內容還要難～！', 0);
insert into room_unit_comments values (7368, 126701, 1, 1, 1, 0, 1, '我也帶你去\\n紫藤家的\\n私人海灘吧♪', 0);
insert into room_unit_comments values (7369, 126701, 1, 2, 1, 0, 1, '還可以為你準備\\n環遊世界度假村的\\n郵輪之旅喔。', 0);
insert into room_unit_comments values (7370, 126701, 1, 3, 1, 0, 1, '我們家在避暑勝地\\n有專門的別墅，\\n下次招待你來玩吧。', 0);
insert into room_unit_comments values (7371, 126701, 1, 4, 1, 0, 1, '曬傷是少女的天敵呢，\\n得充分\\n做好防曬才行……', 0);
insert into room_unit_comments values (7372, 126701, 1, 5, 1, 0, 1, '只有仙女棒\\n感覺不夠過癮呢，\\n要再更有爆炸性的……', 0);
insert into room_unit_comments values (7373, 126701, 1, 101, 4, 0, 1, '無論是公會活動還是\\n度假都會全力以赴！\\n這就是我！', 0);
insert into room_unit_comments values (7374, 126701, 1, 102, 8, 0, 1, '這是以我的品味\\n所挑選的訂製泳裝，\\n穿得好看是當然的♪', 0);
insert into room_unit_comments values (7375, 126701, 2, 1, 1, 0, 1, '如果你希望，\\n我能立刻招待你參加\\n夏季飛空艇遊覽之旅♪', 0);
insert into room_unit_comments values (7376, 126701, 2, 2, 1, 0, 1, '我喜歡夏天熱鬧的\\n氣氛……但炎熱的天氣\\n會讓人受不了呢。', 0);
insert into room_unit_comments values (7377, 126701, 2, 3, 1, 0, 1, '曬越多日光浴越是\\n閃耀動人的夏季之我！\\n今天也是絕佳狀態♪', 0);
insert into room_unit_comments values (7378, 126701, 2, 4, 1, 0, 1, '若要協助舉辦夏日\\n祭典，無論是資金還是\\n人才都必須準備呢。', 0);
insert into room_unit_comments values (7379, 126701, 2, 101, 4, 0, 1, '大人……那個……\\n那樣盯著我看\\n會讓人很難為情……', 0);
insert into room_unit_comments values (7380, 126701, 2, 102, 8, 0, 1, '我也想去\\n珠希打工的\\n海之家看看♪', 0);
insert into room_unit_comments values (7381, 126701, 2, 1, 1, 1, 1, '現在是港口市場\\n正熱鬧的時候呢！\\n我們也去看看吧。', 0);
insert into room_unit_comments values (7382, 126701, 2, 2, 1, 2, 1, '要不要一起喝個\\n冰涼的飲料呢？\\n我有請家裡的\\n主廚準備喔。', 0);
insert into room_unit_comments values (7383, 126701, 2, 3, 1, 3, 1, '再多和你……\\n一起度過這個\\n夏天的夜晚……', 0);
insert into room_unit_comments values (7385, 126701, 3, 2, 1, 0, 1, '如果不介意，\\n請你也到這邊來呀♪', 0);
insert into room_unit_comments values (7386, 126701, 3, 3, 1, 0, 1, '配合夏季\\n換上適合的服裝，\\n似乎也滿好玩的呢♪', 0);
insert into room_unit_comments values (7387, 126701, 3, 4, 1, 0, 1, '這個有夏季感的清爽\\n香氣……真想以期間\\n限定來銷售出去。', 0);
insert into room_unit_comments values (7388, 126701, 3, 5, 1, 0, 1, '唔……我要忍耐……\\n要撐過這個炎熱\\n才能夠提升美貌……', 0);
insert into room_unit_comments values (7390, 126701, 3, 7, 1, 0, 1, '大家在這樣炎熱的\\n季節工作，也必須為\\n她們準備慰勞品呢。', 0);
insert into room_unit_comments values (7391, 126701, 3, 8, 1, 0, 1, '事先掌握\\n夏季的流行\\n也很重要呢。', 0);
insert into room_unit_comments values (7393, 126701, 3, 10, 1, 0, 1, '我感受到\\n這商品的潛力了，\\n如果順利的話，\\n很有可能會熱賣……！', 0);
insert into room_unit_comments values (7394, 126701, 3, 11, 1, 0, 1, '這極致的觸感……\\n隨便你開價，\\n到底願意多少錢賣我？', 0);
insert into room_unit_comments values (7395, 126601, 1, 1, 1, 0, 1, '難得來度假……\\n來盡情地\\n自由放鬆吧。', 0);
insert into room_unit_comments values (7396, 126601, 1, 2, 1, 0, 1, '不管你\\n想隱藏什麼祕密，\\n都瞞不過我的喔。', 0);
insert into room_unit_comments values (7397, 126601, 1, 3, 1, 0, 1, '我對騙小孩的\\n鬼故事沒興趣……\\n但幽靈是我感興趣的\\n觀察對象。', 0);
insert into room_unit_comments values (7398, 126601, 1, 4, 1, 0, 1, '他的圖畫日記……\\n我之後會\\n好好閱讀一番的。', 0);
insert into room_unit_comments values (7399, 126601, 1, 5, 1, 0, 1, '蟬鳴聲、積雨雲\\n以及冰淇淋汽水，\\n這就是夏天呢。', 0);
insert into room_unit_comments values (7400, 126601, 1, 101, 4, 0, 1, '乾脆來準備\\n暑假作業的題目吧。\\n要給予稍微超過\\n他極限的負荷……', 0);
insert into room_unit_comments values (7401, 126601, 1, 102, 8, 0, 1, '將與他一起度過的夏天\\n變成特別的回憶……\\n這就是我的願望。', 0);
insert into room_unit_comments values (7402, 126601, 2, 1, 1, 0, 1, '就算是夏天\\n也不能玩得太瘋，\\n適度地好好玩樂吧？', 0);
insert into room_unit_comments values (7403, 126601, 2, 2, 1, 0, 1, '別看真軌那個樣子，\\n他可是我的公主騎士。\\n若是太小看他\\n我會很傷腦筋喔？', 0);
insert into room_unit_comments values (7404, 126601, 2, 3, 1, 0, 1, '我的祕密嗎？\\n我好怕豆沙包……\\n這樣的你覺得如何？', 0);
insert into room_unit_comments values (7405, 126601, 2, 4, 1, 0, 1, '這裡是最適合\\n偷聽別人祕密的\\n地方呢。\\n……我開玩笑的。', 0);
insert into room_unit_comments values (7406, 126601, 2, 101, 4, 0, 1, '要找我一起悠閒地\\n聆聽風鈴聲……？\\n……如果是和你一起，\\n我是無所謂。', 0);
insert into room_unit_comments values (7407, 126601, 2, 102, 8, 0, 1, '我向你保證，\\n會給你一個\\n難以忘懷的夏天。', 0);
insert into room_unit_comments values (7408, 126601, 2, 1, 1, 1, 1, '居然會早起，\\n很不錯嘛。\\n那一起來做體操吧？', 0);
insert into room_unit_comments values (7409, 126601, 2, 2, 1, 2, 1, '中午要吃流水涼麵？\\n這很沒效率呢。\\n……我可沒說\\n我不吃。', 0);
insert into room_unit_comments values (7410, 126601, 2, 3, 1, 3, 1, '……如果你希望的話，\\n要我說多少鬼故事\\n給你聽都可以……\\n你覺得呢？', 0);
insert into room_unit_comments values (7412, 126601, 3, 2, 1, 0, 1, '七冠也是\\n需要休息的，\\n我要暫時\\n在這裡歇會。', 0);
insert into room_unit_comments values (7413, 126601, 3, 3, 1, 0, 1, '其實我還滿喜歡的……\\n不過這是祕密。', 0);
insert into room_unit_comments values (7414, 126601, 3, 4, 1, 0, 1, '這清爽的香氣令人聯想\\n到初夏吹拂的風……\\n品味還不錯呢。', 0);
insert into room_unit_comments values (7415, 126601, 3, 5, 1, 0, 1, '正因為是夏天，\\n做好對策避免著涼\\n才顯得更重要。\\n……不過還真熱呢。', 0);
insert into room_unit_comments values (7417, 126601, 3, 7, 1, 0, 1, '我喜歡\\n什麼樣的口味……\\n這當然是祕密。', 0);
insert into room_unit_comments values (7418, 126601, 3, 8, 1, 0, 1, '以夏天為舞台的\\n小說有不少，\\n來找個幾本\\n他應該會喜歡的書吧。', 0);
insert into room_unit_comments values (7420, 126601, 3, 10, 1, 0, 1, '無論是外表還是觸感，\\n我都完美記下來了，\\n隨時可以模仿。', 0);
insert into room_unit_comments values (7421, 126601, 3, 11, 1, 0, 1, '嗯……這坐墊\\n很有夏天的風格呢。\\n感覺能襯托\\n夜間泳池。', 0);
insert into room_unit_comments values (7422, 105401, 1, 103, 12, 0, 1, '怜大人很帥氣，\\n但騎士先生也……\\n啊，你沒聽到\\n我剛才說什麼吧？', 0);
insert into room_unit_comments values (7423, 105401, 2, 103, 12, 0, 1, '騎士先生感覺很適合\\n簡約的服裝！\\n下次一起\\n去看衣服吧♪', 0);
insert into room_unit_comments values (7424, 126801, 1, 1, 1, 0, 1, '本來一開始\\n還不曉得會怎樣……\\n但穿泳裝也意外地\\n沒什麼問題呢……', 0);
insert into room_unit_comments values (7425, 126801, 1, 2, 1, 0, 1, '聽說穿泳裝喝太多\\n會讓肚子著涼……\\n但如果是喝麥酒\\n就沒問題了吧！', 0);
insert into room_unit_comments values (7426, 126801, 1, 3, 1, 0, 1, '天氣越熱，\\n之後的麥酒\\n會越好喝喔……', 0);
insert into room_unit_comments values (7427, 126801, 1, 4, 1, 0, 1, '認真做完工作後，\\n再痛快地乾一杯麥酒！\\n果然就是這個好！', 0);
insert into room_unit_comments values (7428, 126801, 1, 5, 1, 0, 1, '呵呵，無論是設計還是\\n舒適度都太棒了，\\n有花錢準備這套泳裝\\n真是太對了♪', 0);
insert into room_unit_comments values (7429, 126801, 1, 101, 4, 0, 1, '如果有機會\\n再喝到傳說的麥酒，\\n真想再喝一次呢……', 0);
insert into room_unit_comments values (7430, 126801, 1, 102, 8, 0, 1, '身為聖騎士不辱此名，\\n我……乾了！', 0);
insert into room_unit_comments values (7431, 126801, 2, 1, 1, 0, 1, '這個打扮即使被水\\n弄濕也不用擔心！\\n雖是這樣，但還是\\n不能弄濕文件呢。', 0);
insert into room_unit_comments values (7432, 126801, 2, 2, 1, 0, 1, '被你這樣\\n盯著看……感覺有點\\n害羞……', 0);
insert into room_unit_comments values (7433, 126801, 2, 3, 1, 0, 1, '今天也是個\\n好天氣呢。\\n呵呵，之後一起去\\n沙灘玩吧♪', 0);
insert into room_unit_comments values (7434, 126801, 2, 4, 1, 0, 1, '泳裝穿習慣後，\\n其實也滿舒服的呢。\\n對了，你要不要\\n也換上泳裝？', 0);
insert into room_unit_comments values (7435, 126801, 2, 101, 4, 0, 1, '即使是穿上泳裝，\\n我也不會忘了\\n身為聖騎士的榮耀！', 0);
insert into room_unit_comments values (7436, 126801, 2, 102, 8, 0, 1, '只要是和你在一起，\\n即使沒有麥酒，\\n我覺得也能體驗到\\n最棒的夏日度假♪', 0);
insert into room_unit_comments values (7437, 126801, 2, 1, 1, 1, 1, '為了工作\\n結束後的麥酒，\\n今天一整天\\n也要加油囉♪', 0);
insert into room_unit_comments values (7438, 126801, 2, 2, 1, 2, 1, '今天的中午\\n要吃什麼呢……\\n我、我怎麼說也不會\\n從中午就開始喝喔。', 0);
insert into room_unit_comments values (7439, 126801, 2, 3, 1, 3, 1, '好，為了洗滌\\n一整天的疲勞……\\n來小～飲一下囉！', 0);
insert into room_unit_comments values (7441, 126801, 3, 2, 1, 0, 1, '像這樣直接摸，\\n就能清楚感受到\\n觸感有多棒♪', 0);
insert into room_unit_comments values (7442, 126801, 3, 3, 1, 0, 1, '今天也很可愛呢～\\n如何？下次\\n姐姐我自己喝酒時，\\n要不要來陪我？', 0);
insert into room_unit_comments values (7443, 126801, 3, 4, 1, 0, 1, '好香喔……啊！\\n如果有麥酒香氣的\\n蒸氣精油，\\n不覺得很好嗎？', 0);
insert into room_unit_comments values (7444, 126801, 3, 5, 1, 0, 1, '穿成這個打扮碰到火，\\n會讓人有點明白\\n被火烤的魷魚乾\\n是什麼心情……', 0);
insert into room_unit_comments values (7446, 126801, 3, 7, 1, 0, 1, '嚼嚼……嗯，\\n這感覺也滿適合\\n當下酒菜的呢～♪', 0);
insert into room_unit_comments values (7447, 126801, 3, 8, 1, 0, 1, '參加考試看的參考書，\\n偶爾也要複習一下\\n才行呢。', 0);
insert into room_unit_comments values (7449, 126801, 3, 10, 1, 0, 1, '呵呵♪\\n好棒的觸感～', 0);
insert into room_unit_comments values (7450, 126801, 3, 11, 1, 0, 1, '……啊呼……\\n懶懶散散的～\\n最棒了～', 0);
insert into room_unit_comments values (7451, 126901, 1, 1, 1, 0, 1, '嗯……這套泳裝\\n果然……\\n太暴露了吧……？', 0);
insert into room_unit_comments values (7452, 126901, 1, 2, 1, 0, 1, '在海邊也要照顧你，\\n我有辦法嗎……？\\n像是要代替\\n游泳圈之類的……？', 0);
insert into room_unit_comments values (7453, 126901, 1, 3, 1, 0, 1, '～♪\\n呵呵……去海邊的\\n行前準備感覺……\\n也很開心。', 0);
insert into room_unit_comments values (7454, 126901, 1, 4, 1, 0, 1, '啊唔唔……穿著泳裝\\n走路……泳裝……\\n很容易卡到臀部……', 0);
insert into room_unit_comments values (7455, 126901, 1, 5, 1, 0, 1, '在海邊還是表現得\\n興奮點比較好對吧……\\n哇、哇……♪\\n……是像這樣嗎？', 0);
insert into room_unit_comments values (7456, 126901, 1, 101, 4, 0, 1, '只要是和你一起……\\n感覺就能製造夏天的\\n……快樂回憶……', 0);
insert into room_unit_comments values (7457, 126901, 1, 102, 8, 0, 1, '希望明年……\\n還有後年……\\n都能再和你\\n一起來海邊……', 0);
insert into room_unit_comments values (7458, 126901, 2, 1, 1, 0, 1, '我在練習\\n怎麼穿泳裝……\\n背後、會很奇怪嗎……？', 0);
insert into room_unit_comments values (7459, 126901, 2, 2, 1, 0, 1, '去海邊的準備……\\n進行得還順利嗎……？\\n要我幫忙嗎……？', 0);
insert into room_unit_comments values (7460, 126901, 2, 3, 1, 0, 1, '防、防曬準備\\n做得很充分喔……\\n如果一直待在太陽\\n底下……會融化……', 0);
insert into room_unit_comments values (7461, 126901, 2, 4, 1, 0, 1, '在這裡……\\n練、練習游泳……\\n會不會太奇怪？', 0);
insert into room_unit_comments values (7462, 126901, 2, 101, 4, 0, 1, '要走進人潮中\\n還是會讓我不安……\\n但是有你在我身邊，\\n一定……沒問題……', 0);
insert into room_unit_comments values (7463, 126901, 2, 102, 8, 0, 1, '託你的福，\\n我才知道了許多\\n在海邊開心的事情……\\n謝謝……♪', 0);
insert into room_unit_comments values (7464, 126901, 2, 1, 1, 1, 1, '要出發的當天要\\n很早起對吧……？\\n所以我一直醒著\\n沒有睡……', 0);
insert into room_unit_comments values (7465, 126901, 2, 2, 1, 2, 1, '海邊現在應該是\\n陽光正強的時候……\\n光、光是想像……\\n就令人頭暈了……', 0);
insert into room_unit_comments values (7466, 126901, 2, 3, 1, 3, 1, '我聽說在海邊晚上\\n就是要放煙火……\\n有不會「碰」的……\\n煙火嗎……？', 0);
insert into room_unit_comments values (7468, 126901, 3, 2, 1, 0, 1, '肌膚直接碰到\\n軟綿綿的……\\n好舒服……♪', 0);
insert into room_unit_comments values (7469, 126901, 3, 3, 1, 0, 1, '呵呵……要一起去\\n……海邊嗎？', 0);
insert into room_unit_comments values (7470, 126901, 3, 4, 1, 0, 1, '呼……♪\\n連心情……\\n都感到好爽朗……', 0);
insert into room_unit_comments values (7471, 126901, 3, 5, 1, 0, 1, '會有種又熱……\\n又冷的……\\n奇妙感覺……呢？', 0);
insert into room_unit_comments values (7473, 126901, 3, 7, 1, 0, 1, '游泳之後\\n要多吃一點，似乎\\n對身體比較好呢……', 0);
insert into room_unit_comments values (7474, 126901, 3, 8, 1, 0, 1, '在海邊的陰涼處\\n讀書……感覺\\n也會很舒服……♪', 0);
insert into room_unit_comments values (7476, 126901, 3, 10, 1, 0, 1, '這樣有對嗎……？我不曉得\\n該怎麼玩……', 0);
insert into room_unit_comments values (7477, 126901, 3, 11, 1, 0, 1, '輕飄飄……♪\\n就像在海底一樣……', 0);
insert into room_unit_comments values (7478, 127001, 1, 1, 1, 0, 1, '穿著害羞的打扮在室內\\n走路的這種快感，\\n感覺快讓人上癮了……', 0);
insert into room_unit_comments values (7479, 127001, 1, 2, 1, 0, 1, '穿著這泳裝……\\n不知道為什麼就\\n讓人想擺姿勢呢……', 0);
insert into room_unit_comments values (7480, 127001, 1, 3, 1, 0, 1, '海裡會有\\n什麼樣的生物呢～\\n好期待好興奮～♪', 0);
insert into room_unit_comments values (7481, 127001, 1, 4, 1, 0, 1, '夏天很熱呢……空花的\\n身體很熱也是夏天的\\n關係……嘻嘻嘻……♪', 0);
insert into room_unit_comments values (7482, 127001, 1, 5, 1, 0, 1, '和虐待狂先生\\n被留在無人島上……\\n這樣的妄想很棒呢。', 0);
insert into room_unit_comments values (7483, 127001, 1, 101, 4, 0, 1, '虐待狂先生那個\\n充滿慾望的眼神……\\n由空花來承受。', 0);
insert into room_unit_comments values (7484, 127001, 1, 102, 8, 0, 1, '和虐待狂先生一起去\\n海邊……好期待喔……\\n嘻嘻嘻♪', 0);
insert into room_unit_comments values (7485, 127001, 2, 1, 1, 0, 1, '居然說「穿更暴露的\\n泳裝給我看啊」，\\n不可以，\\n虐待狂先生……', 0);
insert into room_unit_comments values (7486, 127001, 2, 2, 1, 0, 1, '穿著泳裝跳舞就行了\\n對吧……既然虐待狂\\n先生希望的話！', 0);
insert into room_unit_comments values (7487, 127001, 2, 3, 1, 0, 1, '感受到了虐待狂先生的\\n熱烈視線……\\n流口水……\\n請繼續看著空花……', 0);
insert into room_unit_comments values (7488, 127001, 2, 4, 1, 0, 1, '居然將穿著這身打扮的\\n空花放置不管……\\n真不愧是虐待狂先生！', 0);
insert into room_unit_comments values (7489, 127001, 2, 101, 4, 0, 1, '說到在海邊穿泳裝，\\n感覺就有事會發生！\\n好期待虐待狂先生的\\n新虐待玩法喔……！', 0);
insert into room_unit_comments values (7490, 127001, 2, 102, 8, 0, 1, '雖然很害羞，\\n但空花還是努力了。\\n虐待狂先生……\\n喜歡這種泳裝嗎？', 0);
insert into room_unit_comments values (7491, 127001, 2, 1, 1, 1, 1, '這麼一大早就讓\\n空花換上泳裝……\\n虐待狂先生\\n真是忠實慾望呢！', 0);
insert into room_unit_comments values (7492, 127001, 2, 2, 1, 2, 1, '虐待狂先生在這種\\n光天化日下把濕黏的\\n液體塗在空花身上～！\\n咦……這是防曬乳？', 0);
insert into room_unit_comments values (7493, 127001, 2, 3, 1, 3, 1, '虐待狂先生……\\n要現在和空花去看\\n晚上的大海嗎？\\n一定會很美喔……♪', 0);
insert into room_unit_comments values (7495, 127001, 3, 2, 1, 0, 1, '在這裡空花\\n就能專心妄想了～♪\\n流口水～♪', 0);
insert into room_unit_comments values (7496, 127001, 3, 3, 1, 0, 1, '它這麼可愛，\\n讓它在妄想中登場，\\n感覺不太好意思……', 0);
insert into room_unit_comments values (7497, 127001, 3, 4, 1, 0, 1, '這個使空花\\n興奮的香氣……！啊！\\n是虐待狂先生的陷阱！', 0);
insert into room_unit_comments values (7498, 127001, 3, 5, 1, 0, 1, '是想讓空花身體\\n發熱脫掉泳裝！？\\n真是太高招了……！', 0);
insert into room_unit_comments values (7500, 127001, 3, 7, 1, 0, 1, '空花的嘴裡\\n塞滿滿的……♪\\n停不下來……！', 0);
insert into room_unit_comments values (7501, 127001, 3, 8, 1, 0, 1, '讀書會讓心\\n平靜下來呢……！\\n嘻嘻嘻……\\n平靜下來……', 0);
insert into room_unit_comments values (7503, 127001, 3, 10, 1, 0, 1, '坐起來好舒服……♪\\n啊……普通地\\n享受起來了……', 0);
insert into room_unit_comments values (7504, 127001, 3, 11, 1, 0, 1, '這是……\\n能刺激妄想的\\n觸感……！', 0);
insert into room_unit_comments values (7505, 100501, 1, 103, 12, 0, 1, '我要配合\\n成長的自己，\\n研究更帥氣的\\n姿勢！', 0);
insert into room_unit_comments values (7506, 100501, 2, 103, 12, 0, 1, '今後也要一起\\n以最棒的英雄\\n為目標喔♪', 0);
insert into room_unit_comments values (7507, 127101, 1, 1, 1, 0, 1, '爸爸，\\n看到女兒穿泳裝\\n有這麼開心嗎？', 0);
insert into room_unit_comments values (7508, 127101, 1, 2, 1, 0, 1, '我說鬼故事給\\n小朋友們聽，結果\\n把他們弄哭了……\\n程度真難拿捏……', 0);
insert into room_unit_comments values (7509, 127101, 1, 3, 1, 0, 1, '去海邊時爸爸都一直\\n追著穿泳裝的女性跑，\\n很令人傷腦筋……', 0);
insert into room_unit_comments values (7510, 127101, 1, 4, 1, 0, 1, '來撿貝殼\\n開始嘗試\\n新的占卜好了。', 0);
insert into room_unit_comments values (7511, 127101, 1, 5, 1, 0, 1, '將貝殼放在耳邊能聽到\\n海浪的音色……\\n啊，這個不可以，\\n有不好的呼聲。', 0);
insert into room_unit_comments values (7512, 127101, 1, 101, 4, 0, 1, '這個飲料會瞬間\\n讓人感到一陣沁涼……\\n我也想把這件事\\n告訴那個人。', 0);
insert into room_unit_comments values (7513, 127101, 1, 102, 8, 0, 1, '我不太喜歡大太陽\\n和吵鬧的海邊，\\n可是和那個人一起，\\n卻會奇妙地感到開心。', 0);
insert into room_unit_comments values (7514, 127101, 2, 1, 1, 0, 1, '要不要試試看\\n使用水和冰的\\n夏季限定特別占卜呢？', 0);
insert into room_unit_comments values (7515, 127101, 2, 2, 1, 0, 1, '炎熱的日子\\n就是要說鬼故事呢，\\n我來說精選的\\n鬼故事全集給你聽。', 0);
insert into room_unit_comments values (7516, 127101, 2, 3, 1, 0, 1, '感到有寒意？\\n那不是因為幽靈，\\n是你冰吃太多了。', 0);
insert into room_unit_comments values (7517, 127101, 2, 4, 1, 0, 1, '今天的占卜，\\n你的幸運對象是……\\n穿白色泳裝的女性。', 0);
insert into room_unit_comments values (7518, 127101, 2, 101, 4, 0, 1, '那個，如果方便，\\n可以請你幫我\\n塗防曬乳嗎？\\n我塗不到背後……', 0);
insert into room_unit_comments values (7519, 127101, 2, 102, 8, 0, 1, '要不要去海邊呢？\\n我想和你單獨在\\n寧靜的沙灘上散步。', 0);
insert into room_unit_comments values (7520, 127101, 2, 1, 1, 1, 1, '早晨只有海浪聲的大海\\n能令內心感到平靜，\\n要現在一起去\\n散步看看嗎？', 0);
insert into room_unit_comments values (7521, 127101, 2, 2, 1, 2, 1, '好不可思議。\\n即使只是一般的餐點，\\n為什麼在海邊吃\\n就覺得特別好吃呢？', 0);
insert into room_unit_comments values (7522, 127101, 2, 3, 1, 3, 1, '要去晚上的海邊嗎？\\n現在或許能\\n看到夜光蟲喔。', 0);
insert into room_unit_comments values (7524, 127101, 3, 2, 1, 0, 1, '夏天會不知不覺間\\n消耗掉體力，\\n我們適度地\\n休息吧。', 0);
insert into room_unit_comments values (7525, 127101, 3, 3, 1, 0, 1, '這個玩偶……\\n不用擔心，\\n沒有被任何東西\\n附身喔。', 0);
insert into room_unit_comments values (7526, 127101, 3, 4, 1, 0, 1, '有類似線香的\\n淡淡香氣……\\n總覺得\\n令人感到平靜。', 0);
insert into room_unit_comments values (7527, 127101, 3, 5, 1, 0, 1, '人類靈魂、鬼火\\n與聖艾爾摩之火……\\n看到火焰就會令人\\n聯想到許多東西。', 0);
insert into room_unit_comments values (7529, 127101, 3, 7, 1, 0, 1, '要注意有些東西\\n不能一起吃，\\n否則會吃壞肚子。', 0);
insert into room_unit_comments values (7530, 127101, 3, 8, 1, 0, 1, '這是本很棒的\\n鬼故事大全呢……\\n不小心\\n就看得入迷了。', 0);
insert into room_unit_comments values (7532, 127101, 3, 10, 1, 0, 1, '呵呵，\\n坐上這樣的東西，\\n有種像是回到\\n小時候的感覺呢。', 0);
insert into room_unit_comments values (7533, 127101, 3, 11, 1, 0, 1, '這、這個……\\n有點太童話風了，\\n好害羞……', 0);
insert into room_unit_comments values (7534, 126501, 1, 1, 1, 0, 1, '真是好棒的家……♪\\n感覺從氛圍中能感受到\\n騎士你們的為人……\\n令人莫名地平靜。', 0);
insert into room_unit_comments values (7535, 126501, 1, 2, 1, 0, 1, '這個世界……\\n沒有將日用品順手\\n當點心的習慣呢……\\n得注意才行。', 0);
insert into room_unit_comments values (7536, 126501, 1, 3, 1, 0, 1, '伴手禮的點心\\n應該放哪裡才好？', 0);
insert into room_unit_comments values (7537, 126501, 1, 4, 1, 0, 1, '簡直就像是進到了\\n童話世界中一樣……\\n說是不同世界，\\n這形容真貼切。', 0);
insert into room_unit_comments values (7538, 126501, 1, 5, 1, 0, 1, '微服來訪……呵呵♪\\n真是令人興奮的詞彙，\\n給人一種淘氣……\\n又帶有祕密的印象。', 0);
insert into room_unit_comments values (7539, 126501, 1, 101, 4, 0, 1, '呼啊……啊，\\n不小心打哈欠了……\\n太過舒適也\\n很困擾呢……呵呵♪', 0);
insert into room_unit_comments values (7540, 126501, 1, 102, 8, 0, 1, '騎士不只有\\n不可思議的力量，\\n似乎還有來自\\n某人的加護……', 0);
insert into room_unit_comments values (7541, 126501, 2, 1, 1, 0, 1, '這件「皇家裝備」\\n有點難處理，\\n若被我之外的人碰到，\\n那個……就會放電。', 0);
insert into room_unit_comments values (7542, 126501, 2, 2, 1, 0, 1, '我跟小朋友們\\n學了新的遊戲♪\\n來玩大眼瞪小眼吧。\\n啊、噗……噗～！', 0);
insert into room_unit_comments values (7543, 126501, 2, 3, 1, 0, 1, '其實我一直很憧憬，\\n能夠去朋友家\\n這樣的經驗……呵呵。', 0);
insert into room_unit_comments values (7544, 126501, 2, 4, 1, 0, 1, '公會小屋……\\n我有在管理協會？那裡\\n聽說這是公會\\n活動的據點。', 0);
insert into room_unit_comments values (7545, 126501, 2, 101, 4, 0, 1, '你相信命運嗎？\\n我……相信。\\n不過只相信\\n好的命運♪', 0);
insert into room_unit_comments values (7546, 126501, 2, 102, 8, 0, 1, '如果我真的\\n是天使的話……\\n你會怎麼辦呢？\\n……呵呵，開玩笑的♪', 0);
insert into room_unit_comments values (7547, 126501, 2, 1, 1, 1, 1, '早上能在\\n鬆餅的香氣中醒來\\n非常棒喔，\\n請務必試試看♪', 0);
insert into room_unit_comments values (7548, 126501, 2, 2, 1, 2, 1, '現在小憩片刻的時間。\\n吃飯和睡午覺……\\n去做該做的事，\\n好好養精蓄銳吧。', 0);
insert into room_unit_comments values (7549, 126501, 2, 3, 1, 3, 1, '差不多該休息了吧？\\n若睡不著，我能說睡前\\n故事或唱搖籃曲。\\n呵呵，不用客氣。', 0);
insert into room_unit_comments values (7551, 126501, 3, 2, 1, 0, 1, '呼…………啊！\\n我沒睡著喔。\\n就算是很舒適，\\n我也……呼啊～', 0);
insert into room_unit_comments values (7552, 126501, 3, 3, 1, 0, 1, '哎呀，好可愛……♪\\n請務必讓我邀請你\\n來我的寢室。', 0);
insert into room_unit_comments values (7553, 126501, 3, 4, 1, 0, 1, '好香的氣味……\\n與烤箱中飄出來的\\n糕點香氣\\n又是不同的魅力……', 0);
insert into room_unit_comments values (7554, 126501, 3, 5, 1, 0, 1, '大家一起邊取暖\\n邊談天說笑……\\n好令人放鬆的時光。', 0);
insert into room_unit_comments values (7556, 126501, 3, 7, 1, 0, 1, '食物可以使身心\\n得到放鬆。\\n……若是吃辣的食物？\\n那就，那個……', 0);
insert into room_unit_comments values (7557, 126501, 3, 8, 1, 0, 1, '我必須多學習\\n這個世界的知識。\\n餐桌禮儀的內容\\n是在哪一頁……', 0);
insert into room_unit_comments values (7559, 127201, 1, 1, 1, 0, 1, '買了新色的螢光筆呢。\\n這樣一來，讀書就會\\n變得更有趣了～♪', 0);
insert into room_unit_comments values (7560, 127201, 1, 2, 1, 0, 1, '學校的營養午餐\\n還滿好吃的呢～\\n下次跟貪吃佩可\\n說一聲吧？', 0);
insert into room_unit_comments values (7561, 127201, 1, 3, 1, 0, 1, '呵呵呵……使用板擦\\n在門上設陷阱……\\n那傢伙會中招嗎？', 0);
insert into room_unit_comments values (7562, 127201, 1, 4, 1, 0, 1, '如果要參加社團，\\n運動性社團也很不錯～\\n朝著夕陽\\n全力奔跑吧！', 0);
insert into room_unit_comments values (7563, 127201, 1, 5, 1, 0, 1, '每天都穿一成不變的\\n制服，學生也真可憐～\\n嗯，不過是很可愛啦。', 0);
insert into room_unit_comments values (7564, 127201, 1, 101, 4, 0, 1, '我借給那傢伙的筆記\\n沒有被亂畫\\n奇怪的東西吧……？\\n……像是愛情傘之類的……', 0);
insert into room_unit_comments values (7565, 127201, 1, 102, 8, 0, 1, '昨天家政課\\n做的蛋糕……\\n該怎麼拿給\\n那傢伙呢……？', 0);
insert into room_unit_comments values (7566, 127201, 2, 1, 1, 0, 1, '你說要我教你寫作業？\\n真拿你沒辦法～\\n那麼，我們\\n一起來寫吧。', 0);
insert into room_unit_comments values (7567, 127201, 2, 2, 1, 0, 1, '啊，明天是我們\\n當值日生吧？\\n煩人的工作\\n就交給你囉～', 0);
insert into room_unit_comments values (7568, 127201, 2, 3, 1, 0, 1, '住宿生活雖然有趣，\\n但就寢時間太早了。\\n冷不防地熄燈，\\n總讓我嚇一跳。', 0);
insert into room_unit_comments values (7569, 127201, 2, 4, 1, 0, 1, '雖說校規禁止這樣做，\\n不過，共乘掃帚上學，\\n不是很青春洋溢嗎？', 0);
insert into room_unit_comments values (7570, 127201, 2, 101, 4, 0, 1, '穿上制服，可讓自己\\n自然地與喜歡的人配成對，\\n感覺很棒……唔，我不是\\n要跟你配成對喔……', 0);
insert into room_unit_comments values (7571, 127201, 2, 102, 8, 0, 1, '你的領帶\\n歪掉囉～？\\n來，我幫你調整好，\\n請轉向我這邊吧。', 0);
insert into room_unit_comments values (7572, 127201, 2, 1, 1, 1, 1, '早安～！今天也要\\n精神抖擻地去上學！\\n東西都帶齊了吧！', 0);
insert into room_unit_comments values (7573, 127201, 2, 2, 1, 2, 1, '上午的課累死人了～\\n好好吃頓午餐後，\\n下午繼續努力上課！', 0);
insert into room_unit_comments values (7574, 127201, 2, 3, 1, 3, 1, '唔……突然想起了\\n學校流傳的鬼故事……\\n……雖然我是\\n不覺得可怕啦！', 0);
insert into room_unit_comments values (7576, 127201, 3, 2, 1, 0, 1, '軟綿綿的～\\n舒服到連制服\\n換都沒換\\n就快睡著了……', 0);
insert into room_unit_comments values (7577, 127201, 3, 3, 1, 0, 1, '這很可愛又療癒人心，\\n把它帶進宿舍\\n應該不成問題吧……？', 0);
insert into room_unit_comments values (7578, 127201, 3, 4, 1, 0, 1, '善用可提高集中力的香氣\\n超級提升讀書效率，\\n下次的考試\\n肯定能考個好成績～！', 0);
insert into room_unit_comments values (7579, 127201, 3, 5, 1, 0, 1, '唔，感覺稍微多了\\n幾分涼意……\\n裙子裡要多套件\\n運動褲嗎？', 0);
insert into room_unit_comments values (7581, 127201, 3, 7, 1, 0, 1, '嚼嚼……嚼嚼……\\n若不先吃點東西，\\n還沒下課肚子就餓了～', 0);
insert into room_unit_comments values (7582, 127201, 3, 8, 1, 0, 1, '從圖書室借來的書\\n明天就要歸還了……\\n今天之內把它讀完吧！', 0);
insert into room_unit_comments values (7584, 104301, 1, 103, 12, 0, 1, '把自己的背後交給那傢伙\\n很讓人放心。將來有一天，\\n或許就可以把自己全都……', 0);
insert into room_unit_comments values (7585, 104301, 2, 103, 12, 0, 1, '什、什麼啊？\\n不要一直盯著我看。\\n尾巴會搖起來啦。', 0);
insert into room_unit_comments values (7586, 127301, 1, 1, 1, 0, 1, '練習魔法！\\n嗯嗯嗯～\\n看招……！', 0);
insert into room_unit_comments values (7587, 127301, 1, 2, 1, 0, 1, '對了！要穿著這個\\n去月學嗎？\\n好好秀一下我們的制服♪', 0);
insert into room_unit_comments values (7588, 127301, 1, 3, 1, 0, 1, '如果有能迴避不及格的\\n魔法，那不是很棒嗎？\\n誰可以教教人家～', 0);
insert into room_unit_comments values (7589, 127301, 1, 4, 1, 0, 1, '去哪裡拍照能讓制服\\n看起來最上相呢？\\n教室？還是遊樂園？', 0);
insert into room_unit_comments values (7590, 127301, 1, 5, 1, 0, 1, '讀書好累人喲……\\n下次要請凱留寶\\n來幫幫人家。', 0);
insert into room_unit_comments values (7591, 127301, 1, 101, 4, 0, 1, '社團活動嗎……\\n如果跟秀才一起參加，\\n好像會很有趣呢～♪', 0);
insert into room_unit_comments values (7592, 127301, 1, 102, 8, 0, 1, '男生制服\\n超可愛的……\\n不如下次跟秀才\\n借來試穿看看吧？', 0);
insert into room_unit_comments values (7593, 127301, 2, 1, 1, 0, 1, '哈嚕～\\n用人家最強的魔法\\n讓你嗨起來～', 0);
insert into room_unit_comments values (7594, 127301, 2, 2, 1, 0, 1, '九九乘法很像是咒語吧？\\n你看，好比說七四……\\n七四，接著是什麼啊？', 0);
insert into room_unit_comments values (7595, 127301, 2, 3, 1, 0, 1, '秀才老師，\\n請教人家讀書！', 0);
insert into room_unit_comments values (7596, 127301, 2, 4, 1, 0, 1, '學生優惠真的幫大忙～\\n秀才，我們一起\\n卯起來用吧♪', 0);
insert into room_unit_comments values (7597, 127301, 2, 101, 4, 0, 1, '人家下次會幫秀才\\n做一份便當，\\n一起在頂樓享用吧♪', 0);
insert into room_unit_comments values (7598, 127301, 2, 102, 8, 0, 1, '告訴人家可以讓秀才\\n振奮精神的話語吧。\\n當你累慘的時候，\\n人家會努力說給你聽喔。', 0);
insert into room_unit_comments values (7599, 127301, 2, 1, 1, 1, 1, '哈嚕哈嚕～\\n今天也是一早就\\n活力滿點♪', 0);
insert into room_unit_comments values (7600, 127301, 2, 2, 1, 2, 1, '中午要吃什麼好呢？\\n偶爾點個\\n大份量的餐點也不錯？', 0);
insert into room_unit_comments values (7601, 127301, 2, 3, 1, 3, 1, '糟了，作業\\n還沒寫完……\\n秀才救救人家～', 0);
insert into room_unit_comments values (7603, 127301, 3, 2, 1, 0, 1, '充電中～\\n……學校的椅子\\n也換成這個吧～', 0);
insert into room_unit_comments values (7604, 127301, 3, 3, 1, 0, 1, '既可愛\\n又療癒人心，\\n人家要被萌死了……！', 0);
insert into room_unit_comments values (7605, 127301, 3, 4, 1, 0, 1, '找到可以讓人\\n放鬆的魔法！\\n好香的氣味～♪', 0);
insert into room_unit_comments values (7606, 127301, 3, 5, 1, 0, 1, '暖烘烘的好舒服♪\\n真想今天一整天\\n都待在這裡～', 0);
insert into room_unit_comments values (7608, 127301, 3, 7, 1, 0, 1, '超好吃的～♪\\n學校營養午餐若有這道菜，\\n人家可以天天吃～', 0);
insert into room_unit_comments values (7609, 127301, 3, 8, 1, 0, 1, '嗯嗯……\\n人家超睏的……\\n下次再繼續吧～', 0);
insert into room_unit_comments values (7611, 127501, 1, 1, 1, 0, 1, '下次我也想\\n帶孩子們去\\n薩拉薩利亞呢。', 0);
insert into room_unit_comments values (7612, 127501, 1, 2, 1, 0, 1, '孩子們說\\n我很像公主呢♪', 0);
insert into room_unit_comments values (7613, 127501, 1, 3, 1, 0, 1, '這把劍……用來點火\\n很方便喔。\\n也很容易控制火勢。', 0);
insert into room_unit_comments values (7614, 127501, 1, 4, 1, 0, 1, '薩拉薩利亞的\\n乾燥炎熱，\\n有點令人懷念呢……', 0);
insert into room_unit_comments values (7615, 127501, 1, 5, 1, 0, 1, '買回來的伴手禮，\\n這就發給大家吧，\\n感謝他們平日的關照♪', 0);
insert into room_unit_comments values (7616, 127501, 1, 101, 4, 0, 1, '只要是和你在一起，\\n無論身處哪個國度，\\n都有辦法生活下去♪', 0);
insert into room_unit_comments values (7617, 127501, 1, 102, 8, 0, 1, '拓展現在的事業，\\n和你一起去\\n環遊世界……\\n你不覺得這樣很棒嗎？', 0);
insert into room_unit_comments values (7618, 127501, 2, 1, 1, 0, 1, '很懷念在薩拉薩利亞\\n吃到的料理……？\\n真拿你沒辦法……\\n我下次做給你吃吧。', 0);
insert into room_unit_comments values (7619, 127501, 2, 2, 1, 0, 1, '下次的旅行，\\n你想去哪呢？\\n如果有想去的地方，\\n要跟我說喔。', 0);
insert into room_unit_comments values (7620, 127501, 2, 3, 1, 0, 1, '薩拉薩利亞的辛香料\\n似乎在蘭德索爾\\n也會大受歡迎呢。\\n我有預感會是好商機！', 0);
insert into room_unit_comments values (7621, 127501, 2, 4, 1, 0, 1, '被你這麼直盯著看，\\n我會很害羞的……', 0);
insert into room_unit_comments values (7622, 127501, 2, 101, 4, 0, 1, '雖說我很嚮往出嫁，\\n但招贅似乎也不錯……\\n你覺得呢？', 0);
insert into room_unit_comments values (7623, 127501, 2, 102, 8, 0, 1, '我果然還是\\n很希望你可以一直\\n待在我身邊……', 0);
insert into room_unit_comments values (7624, 127501, 2, 1, 1, 1, 1, '趁陽光還沒有變強，\\n快點處理好\\n瑣碎雜事吧。', 0);
insert into room_unit_comments values (7625, 127501, 2, 2, 1, 2, 1, '我試著重現了在\\n薩拉薩利亞吃的料理，\\n你要嚐嚐看嗎？', 0);
insert into room_unit_comments values (7626, 127501, 2, 3, 1, 3, 1, '睡眠要充足，\\n才耐得酷熱。', 0);
insert into room_unit_comments values (7628, 127501, 3, 2, 1, 0, 1, '無論在這裡還是那裡，\\n忙碌的程度都一樣……\\n稍微休息一下吧～', 0);
insert into room_unit_comments values (7629, 127501, 3, 3, 1, 0, 1, '在薩拉薩利亞\\n看到的玩偶\\n也很可愛呢。', 0);
insert into room_unit_comments values (7630, 127501, 3, 4, 1, 0, 1, '每個地方的香氣\\n都各有其特色。\\n這股香氣……\\n感覺有點神祕？', 0);
insert into room_unit_comments values (7631, 127501, 3, 5, 1, 0, 1, '沙漠地區晚上會變冷，\\n事先準備好保暖措施，\\n會讓人比較放心……♪', 0);
insert into room_unit_comments values (7633, 127501, 3, 7, 1, 0, 1, '吃慣的口味，\\n為何可以讓人\\n如此放鬆下來呢……', 0);
insert into room_unit_comments values (7634, 127501, 3, 8, 1, 0, 1, '由於該國很可能成為\\n今後的交易對象，\\n他們的文化\\n也得事先學習……', 0);
insert into room_unit_comments values (7636, 101401, 1, 103, 12, 0, 1, '犯人就在這群人當中！\\n這是我當上偵探，\\n最想說出口的台詞。', 0);
insert into room_unit_comments values (7637, 101401, 2, 103, 12, 0, 1, '必須事先取得\\n助手先生的資訊……\\n因為我希望他所看到的，\\n一直都是理想中的我。', 0);
insert into room_unit_comments values (7638, 127601, 1, 1, 1, 0, 1, '在久違的長途旅行中，\\n思鄉之情油然而生。\\n不如就在蘭德索爾\\n悠閒地待上一陣子吧。', 0);
insert into room_unit_comments values (7639, 127601, 1, 2, 1, 0, 1, '偶爾穿搭不同的服裝，\\n使用不同的武器，\\n感覺也挺好的。', 0);
insert into room_unit_comments values (7640, 127601, 1, 3, 1, 0, 1, '待在薩拉薩利亞時，\\n原本還很擔心自己\\n能否適應滿身都是沙，\\n現在反倒覺得懷念。', 0);
insert into room_unit_comments values (7641, 127601, 1, 4, 1, 0, 1, '魔毯真的很好用，\\n速度快又方便……\\n真想帶一塊當紀念品。', 0);
insert into room_unit_comments values (7642, 127601, 1, 5, 1, 0, 1, '在沙地戰鬥，\\n是很好的鍛鍊……\\n下次就去那裡\\n進行修練吧。', 0);
insert into room_unit_comments values (7643, 127601, 1, 101, 4, 0, 1, '薩拉薩利亞的人們\\n也都是些投緣的人，\\n不曉得他們\\n最近過得好不好。', 0);
insert into room_unit_comments values (7644, 127601, 1, 102, 8, 0, 1, '雖說出門在外不怕丟臉，\\n但這倒也不是一場\\n有多糟的旅行。\\n下次要和他去哪呢？', 0);
insert into room_unit_comments values (7645, 127601, 2, 1, 1, 0, 1, '你是來揶揄我\\n這一身打扮的嗎？\\n遺憾的是，\\n我已經穿得很習慣了。', 0);
insert into room_unit_comments values (7646, 127601, 2, 2, 1, 0, 1, '我正好要把\\n伴手禮帶去給\\n公會的夥伴們，\\n你陪我一起去吧。', 0);
insert into room_unit_comments values (7647, 127601, 2, 3, 1, 0, 1, '已經很久沒去\\n水邊釣魚了……\\n所以說，你也會\\n和我一起去吧？', 0);
insert into room_unit_comments values (7648, 127601, 2, 4, 1, 0, 1, '啊，\\n薩拉薩利亞的那些人\\n寄信來了。\\n那麼，要一起讀信嗎？', 0);
insert into room_unit_comments values (7649, 127601, 2, 101, 4, 0, 1, '我最近在練習\\n薩拉薩利亞的舞蹈，\\n之後再請你觀賞\\n我自己的編舞。', 0);
insert into room_unit_comments values (7650, 127601, 2, 102, 8, 0, 1, '小哥，你說什麼？\\n……你說我\\n有點像少夫人？\\n嗯，這個嘛，呃……', 0);
insert into room_unit_comments values (7651, 127601, 2, 1, 1, 1, 1, '這時間正好，\\n趁太陽完全升起之前，\\n得趕緊把該做的事\\n全都做完。', 0);
insert into room_unit_comments values (7652, 127601, 2, 2, 1, 2, 1, '中午想吃什麼呢？\\n要來煮一頓\\n薩拉薩利亞風味嗎？', 0);
insert into room_unit_comments values (7653, 127601, 2, 3, 1, 3, 1, '好啦，早點睡吧。\\n因為沙漠的夜晚會很冷\\n……唔，我還沉浸在\\n旅行的氛圍當中啊。', 0);
insert into room_unit_comments values (7655, 127601, 3, 2, 1, 0, 1, '坐起來很舒服。\\n不禁讓我想起\\n薩拉薩利亞宮殿的\\n大廳……', 0);
insert into room_unit_comments values (7656, 127601, 3, 3, 1, 0, 1, '若送禮給神燈精靈，\\n祂會很開心吧？\\n在下次見面之前，\\n好好挑選個禮物吧。', 0);
insert into room_unit_comments values (7657, 127601, 3, 4, 1, 0, 1, '這股香氣……\\n是薩拉薩利亞原產的\\n香木嗎？', 0);
insert into room_unit_comments values (7658, 127601, 3, 5, 1, 0, 1, '讓人想起\\n旅途中的營火呢。\\n整個人不禁放鬆下來。', 0);
insert into room_unit_comments values (7660, 127601, 3, 7, 1, 0, 1, '呵呵，雖說熟悉的口味\\n也很棒，但我\\n已經開始想念\\n那邊的調味了。', 0);
insert into room_unit_comments values (7661, 127601, 3, 8, 1, 0, 1, '嗯嗯……\\n一千零一夜的故事嗎？\\n真的很有趣呢。', 0);
insert into room_unit_comments values (7663, 126001, 1, 1, 1, 0, 1, '氣派的公會小屋♪\\n有個好歸處，\\n外出冒險似乎也會\\n變得更加有趣♪', 0);
insert into room_unit_comments values (7664, 126001, 1, 2, 1, 0, 1, '我原本想來\\n幫忙打掃的……\\n真不愧是哥哥的住所，\\n整理得很乾淨呢♪', 0);
insert into room_unit_comments values (7665, 126001, 1, 3, 1, 0, 1, '希望我們有一天\\n也能成為\\n擁有氣派公會小屋的\\n冒險者……♪', 0);
insert into room_unit_comments values (7666, 126001, 1, 4, 1, 0, 1, '雖說我是個冒險者，\\n不過在家守著\\n等候哥哥回來，\\n這感覺也……挺好的？', 0);
insert into room_unit_comments values (7667, 126001, 1, 5, 1, 0, 1, '要來做些點心嗎……♪\\n不如多做一點，\\n帶去給\\n哥哥吃吧♪', 0);
insert into room_unit_comments values (7668, 126001, 1, 101, 4, 0, 1, '我非常喜歡這個家♪\\n所以，呃……\\n可以讓我再\\n多待一會兒嗎……？', 0);
insert into room_unit_comments values (7669, 126001, 1, 102, 8, 0, 1, '我來寫封\\n給哥哥的感謝信，\\n偷偷地藏在\\n枕頭底下吧……♪', 0);
insert into room_unit_comments values (7670, 126001, 2, 1, 1, 0, 1, '……哥哥\\n有空嗎？\\n那麼，我們一起\\n外出冒險吧♪', 0);
insert into room_unit_comments values (7671, 126001, 2, 2, 1, 0, 1, '有什麼食材是\\n哥哥不敢吃的嗎？\\n那個，若不嫌棄的話，\\n我現在幫你做飯……♪', 0);
insert into room_unit_comments values (7672, 126001, 2, 3, 1, 0, 1, '待在家裡安穩度日\\n感覺也不錯。\\n因為對冒險者來說，\\n稍作休息也很重要♪', 0);
insert into room_unit_comments values (7673, 126001, 2, 4, 1, 0, 1, '我會先習慣\\n這身打扮的，\\n這樣我才能\\n隨時保護哥哥……！', 0);
insert into room_unit_comments values (7674, 126001, 2, 101, 4, 0, 1, '哥哥看起來很睏……\\n那就來睡個午覺吧♪\\n來，我的大腿\\n讓你躺著當枕頭……♪', 0);
insert into room_unit_comments values (7675, 126001, 2, 102, 8, 0, 1, '哥哥受傷了……！？\\n我來幫你治療……\\n呼──呼──', 0);
insert into room_unit_comments values (7676, 126001, 2, 1, 1, 1, 1, '早安♪\\n據說早上朝氣蓬勃地\\n打聲招呼，一整天就會\\n過得很幸福喔♪', 0);
insert into room_unit_comments values (7677, 126001, 2, 2, 1, 2, 1, '午餐時間到了♪\\n……忙到空不出手嗎？\\n我知道了，那麼，\\n我來餵你吃吧♪啊～', 0);
insert into room_unit_comments values (7678, 126001, 2, 3, 1, 3, 1, '呼啊……我覺得好睏……\\n哥哥……\\n我可以留下來\\n住一晚嗎……？', 0);
insert into room_unit_comments values (7680, 126001, 3, 2, 1, 0, 1, '呼～～♪\\n這模樣可不能\\n讓其他人看到哪……♪\\n毛茸茸的～～♪', 0);
insert into room_unit_comments values (7681, 126001, 3, 3, 1, 0, 1, '哥哥喜歡這樣嗎……\\n呵呵，我也是呢♪\\n抱～～～♪', 0);
insert into room_unit_comments values (7682, 126001, 3, 4, 1, 0, 1, '精油的療癒效果……\\n如果調配得宜，\\n或許可以用來\\n為大家治療～？', 0);
insert into room_unit_comments values (7683, 126001, 3, 5, 1, 0, 1, '呼～真舒服～♪\\n身體變暖和，\\n心中也會暖烘烘的♪', 0);
insert into room_unit_comments values (7685, 126001, 3, 7, 1, 0, 1, '唔，真好吃♪\\n如果是普蕾西亞，\\n說不定會一直\\n吃個不停呢……♪', 0);
insert into room_unit_comments values (7686, 126001, 3, 8, 1, 0, 1, '書本會帶領我\\n前往未知的世界……\\n簡直就像\\n哥哥一樣♪', 0);
insert into room_unit_comments values (7688, 103801, 1, 103, 12, 0, 1, '現在熱門的新書……\\n不曉得他也有看嗎？\\n下次見面時，想問看看\\n他的感想呢……♪', 0);
insert into room_unit_comments values (7689, 103801, 2, 103, 12, 0, 1, '唔呀……啊，\\n你在量我的體溫嗎？\\n謝謝你，\\n我完全康復囉♪', 0);
insert into room_unit_comments values (7690, 126101, 1, 1, 1, 0, 1, '哥的氣息……\\n嗯，找到了。\\n抱。', 0);
insert into room_unit_comments values (7691, 126101, 1, 2, 1, 0, 1, '東張西望，\\n這裡也要，\\n巡邏喔。', 0);
insert into room_unit_comments values (7692, 126101, 1, 3, 1, 0, 1, '家裡，很熱鬧，\\n我，不寂寞。', 0);
insert into room_unit_comments values (7693, 126101, 1, 4, 1, 0, 1, '給我，飯糰。\\n給我，肉包。\\n給我……哥？', 0);
insert into room_unit_comments values (7694, 126101, 1, 5, 1, 0, 1, '小樂，在哪裡？\\n哥，你吃掉了？', 0);
insert into room_unit_comments values (7695, 126101, 1, 101, 4, 0, 1, '嗅嗅……這個房間，\\n有哥的味道，\\n可以，住下來嗎？', 0);
insert into room_unit_comments values (7696, 126101, 1, 102, 8, 0, 1, '我不擅長，打掃，\\n可是，我會練習，\\n擦擦。', 0);
insert into room_unit_comments values (7697, 126101, 2, 1, 1, 0, 1, '想摸，我的耳朵？\\n嗯……不行……\\n會癢癢。', 0);
insert into room_unit_comments values (7698, 126101, 2, 2, 1, 0, 1, '黑普蕾西亞，\\n帥氣嗎？\\n嗯……謝謝。', 0);
insert into room_unit_comments values (7699, 126101, 2, 3, 1, 0, 1, '哥的房間，\\n讓人，好好奇，\\n我要探險喔。', 0);
insert into room_unit_comments values (7700, 126101, 2, 4, 1, 0, 1, '被摸摸頭……\\n會想……睡覺……\\n呼……呼……呼……', 0);
insert into room_unit_comments values (7701, 126101, 2, 101, 4, 0, 1, '頭髮，幫我，\\n綁上蝴蝶結，\\n拜託你。', 0);
insert into room_unit_comments values (7702, 126101, 2, 102, 8, 0, 1, '我要住在，\\n這裡喔，\\n每天，都能在一起。', 0);
insert into room_unit_comments values (7703, 126101, 2, 1, 1, 1, 1, '早安。\\n早餐、\\n早餐。', 0);
insert into room_unit_comments values (7704, 126101, 2, 2, 1, 2, 1, '哥，\\n辛苦了。\\n我們，休息。', 0);
insert into room_unit_comments values (7705, 126101, 2, 3, 1, 3, 1, '我可以，自己睡，\\n不過，\\n就算，不是自己睡，\\n也可以喔……？', 0);
insert into room_unit_comments values (7707, 126101, 3, 2, 1, 0, 1, '鬆鬆軟軟，\\n就像爸爸的\\n肚子一樣。', 0);
insert into room_unit_comments values (7708, 126101, 3, 3, 1, 0, 1, '你好，\\n我是普蕾西亞。\\n我們當朋友吧？', 0);
insert into room_unit_comments values (7709, 126101, 3, 4, 1, 0, 1, '好香。\\n可是，\\n哥的味道更香。', 0);
insert into room_unit_comments values (7710, 126101, 3, 5, 1, 0, 1, '總之，\\n來烤肉吧。', 0);
insert into room_unit_comments values (7712, 126101, 3, 7, 1, 0, 1, '嚼嚼……\\n也希望有，\\n無限的肉包。', 0);
insert into room_unit_comments values (7713, 126101, 3, 8, 1, 0, 1, '全部，看完了。\\n可以給我，\\n更厚的書喔？', 0);
insert into room_unit_comments values (7715, 127901, 1, 1, 1, 0, 1, '為了過個\\n幸福的聖誕節，\\n得將公會小屋\\n打掃乾淨才行♪', 0);
insert into room_unit_comments values (7716, 127901, 1, 2, 1, 0, 1, '嘿嘿♪\\n要送給他的禮物……\\n先悄悄地\\n放在他的枕頭邊吧☆', 0);
insert into room_unit_comments values (7717, 127901, 1, 3, 1, 0, 1, '說到聖誕節，\\n就是整隻烤火雞……☆\\n把口水擦乾淨，\\n來開始準備吧♪', 0);
insert into room_unit_comments values (7718, 127901, 1, 4, 1, 0, 1, '準備了\\n要送給他的禮物……\\n結果太大了，\\n裝不進襪子裡……', 0);
insert into room_unit_comments values (7719, 127901, 1, 5, 1, 0, 1, '禮物無論是收禮的人\\n還是送禮的人，\\n雙方都會\\n覺得幸福呢……☆', 0);
insert into room_unit_comments values (7720, 127901, 1, 101, 4, 0, 1, '在能看到夜景的餐廳\\n和那個人度過\\n聖誕節的約會……\\n真令人憧憬呢☆', 0);
insert into room_unit_comments values (7721, 127901, 1, 102, 8, 0, 1, '禮物是我……\\n雖然很難為情，\\n但希望他能喜歡\\n這份禮物……☆', 0);
insert into room_unit_comments values (7722, 127901, 2, 1, 1, 0, 1, '我準備了\\n很多的裝飾物，\\n兩人一起來\\n裝飾聖誕樹吧☆', 0);
insert into room_unit_comments values (7723, 127901, 2, 2, 1, 0, 1, '啊哈哈，\\n肚子餓了嗎？\\n請你要忍耐到\\n派對開始為止喔♪', 0);
insert into room_unit_comments values (7724, 127901, 2, 3, 1, 0, 1, '叮叮噹～♪\\n你也一起來\\n唱聖誕歌吧♪', 0);
insert into room_unit_comments values (7725, 127901, 2, 4, 1, 0, 1, '準備驚喜禮物……\\n呃！？\\n你、你沒有聽到\\n我剛才說什麼吧……？', 0);
insert into room_unit_comments values (7726, 127901, 2, 101, 4, 0, 1, '聖誕節晚上\\n來辦派對吧，\\n如果可以……希望……\\n就兩人單獨……☆', 0);
insert into room_unit_comments values (7727, 127901, 2, 102, 8, 0, 1, '和你一起度過了\\n最棒的聖誕節……\\n我會將這份回憶永遠\\n永遠珍藏在內心深處。', 0);
insert into room_unit_comments values (7728, 127901, 2, 1, 1, 1, 1, '早安！\\n我們來將這天\\n變成實現大家願望的\\n美好一天吧☆', 0);
insert into room_unit_comments values (7729, 127901, 2, 2, 1, 2, 1, '中午了呢！\\n等買完晚餐\\n要用的東西後，\\n稍微睡個午覺吧☆', 0);
insert into room_unit_comments values (7730, 127901, 2, 3, 1, 3, 1, '差不多是\\n就寢的時間了呢～\\n別忘記要準備好\\n襪子放在枕頭邊喔～☆', 0);
insert into room_unit_comments values (7732, 127901, 3, 2, 1, 0, 1, '呼～好舒服……☆\\n在家裡悠閒地\\n過聖誕節\\n也不錯呢……♪', 0);
insert into room_unit_comments values (7733, 127901, 3, 3, 1, 0, 1, '好可愛～♪\\n這是聖誕老人\\n送的禮物嗎～？', 0);
insert into room_unit_comments values (7734, 127901, 3, 4, 1, 0, 1, '吸～呼～\\n好棒的香氣……\\n和剛煮好的飯\\n難分軒輊呢……♪', 0);
insert into room_unit_comments values (7735, 127901, 3, 5, 1, 0, 1, '雖然很溫暖……\\n但想起那天在\\n山中小屋的事，\\n就很難為情呢……☆', 0);
insert into room_unit_comments values (7737, 127901, 3, 7, 1, 0, 1, '嚼嚼嚼嚼嚼嚼嚼嚼，\\n我會不停下來的，\\n因為是聖誕節啊。\\n嚼嚼嚼嚼嚼嚼嚼嚼。', 0);
insert into room_unit_comments values (7738, 127901, 3, 8, 1, 0, 1, '我也想\\n像故事裡出現的\\n聖誕老人一樣\\n分送幸福給大家……☆', 0);
insert into room_unit_comments values (7740, 127901, 3, 14, 1, 0, 1, '哇！\\n這……這是很棒的\\n驚喜禮物呢……☆', 0);
insert into room_unit_comments values (7741, 127901, 3, 15, 1, 0, 1, '穿上這套服裝以後，\\n我也能成為出色的\\n聖誕老人嗎……？', 0);
