insert into room_unit_comments values (7743, 106601, 2, 103, 12, 0, 1, '我現在開始\\n會做為代理老大\\n叱吒風雲的，\\n跟上我吧！', 0);
insert into room_unit_comments values (7744, 128001, 1, 1, 1, 0, 1, '陛下～♪\\n您的克蕾琪塔\\n精心打扮一番後\\n趕來這裡了～♪', 0);
insert into room_unit_comments values (7745, 128001, 1, 2, 1, 0, 1, '派對是展現財力\\n狀況好壞的地方……\\n根本不該節省\\n什麼經費！', 0);
insert into room_unit_comments values (7746, 128001, 1, 3, 1, 0, 1, '看這天花板的高度，\\n應該搬得進來呢。\\n給陛下的特製蛋糕♪', 0);
insert into room_unit_comments values (7747, 128001, 1, 4, 1, 0, 1, '……哈啾！\\n呼……是我背後\\n露太多了嗎……？', 0);
insert into room_unit_comments values (7748, 128001, 1, 5, 1, 0, 1, '這裡不會\\n被秘書注意到，\\n也不需要擔心會\\n被打小報告了吧～♪', 0);
insert into room_unit_comments values (7749, 128001, 1, 101, 4, 0, 1, '你可以儘管\\n回頭看我喔，\\n直到你打從心底接受\\n這真的是我為止。', 0);
insert into room_unit_comments values (7750, 128001, 1, 102, 8, 0, 1, '沒想到賄賂\\n之外的禮物……\\n也挺不錯的呢。', 0);
insert into room_unit_comments values (7751, 128001, 2, 1, 1, 0, 1, '別看我這樣，\\n我可是有學過社交舞，\\n可以當\\n你的舞伴喔。', 0);
insert into room_unit_comments values (7752, 128001, 2, 2, 1, 0, 1, '這身禮服當然\\n也是特別訂製的♪\\n你該不會以為是\\n租借的服裝吧？', 0);
insert into room_unit_comments values (7753, 128001, 2, 3, 1, 0, 1, '包包裡裝的東西？\\n有工作需要的東西……\\n還有幾顆用來\\n滋潤我心靈的寶石♪', 0);
insert into room_unit_comments values (7754, 128001, 2, 4, 1, 0, 1, '幹勁是工作\\n不可或缺的條件，\\n這可以說是類似\\n著裝守則吧。', 0);
insert into room_unit_comments values (7755, 128001, 2, 101, 4, 0, 1, '以我自己的經驗，\\n所有的工作都會\\n轉化成自己的血肉，\\n與能換成金錢的東西。', 0);
insert into room_unit_comments values (7756, 128001, 2, 102, 8, 0, 1, '我會記取\\n今年的教訓，\\n明年會給你更加\\n美好的聖誕節！', 0);
insert into room_unit_comments values (7757, 128001, 2, 1, 1, 1, 1, '早安。\\n今天這個賺錢的\\n時間開始囉！', 0);
insert into room_unit_comments values (7758, 128001, 2, 2, 1, 2, 1, '快速、便宜又好吃，\\n這就是中餐\\n不可或缺的營養成分！', 0);
insert into room_unit_comments values (7759, 128001, 2, 3, 1, 3, 1, '通宵工作會\\n妨礙安眠！\\n如果想要安穩的睡眠，\\n現在正是決勝時刻！', 0);
insert into room_unit_comments values (7761, 128001, 3, 2, 1, 0, 1, '偶爾像這樣\\n休息也……\\n……啊！？\\n禮服皺掉了～！', 0);
insert into room_unit_comments values (7762, 128001, 3, 3, 1, 0, 1, '……我的直覺\\n告訴我，\\n這麼可愛！\\n肯、定會爆賣！', 0);
insert into room_unit_comments values (7763, 128001, 3, 4, 1, 0, 1, '好香的氣味……\\n但比不上金錢的\\n氣味就是了♪', 0);
insert into room_unit_comments values (7764, 128001, 3, 5, 1, 0, 1, '……啊！？\\n我整個人\\n徹底放空了……！\\n溫暖……真是可怕！', 0);
insert into room_unit_comments values (7766, 128001, 3, 7, 1, 0, 1, '我沒有想……\\n我才沒有想\\n什麼卡路里的事！', 0);
insert into room_unit_comments values (7767, 128001, 3, 8, 1, 0, 1, '偶爾讀讀\\n商業書刊之外的書\\n也不錯呢……', 0);
insert into room_unit_comments values (7769, 128001, 3, 14, 1, 0, 1, '這本繪本做得真好！\\n我以為是給小孩看的，\\n就小看它了……！', 0);
insert into room_unit_comments values (7770, 128001, 3, 15, 1, 0, 1, '這狹窄的視野……\\n會令人回想起\\n打工發面紙的\\n過往呢……', 0);
insert into room_unit_comments values (7771, 128101, 1, 1, 1, 0, 1, '呵呵呵……\\n正在偷吃甜點的璃乃\\n被我發現囉～♪', 0);
insert into room_unit_comments values (7772, 128101, 1, 2, 1, 0, 1, '姐姐的突擊房間檢查♪\\n掉落的蛋糕碎屑，\\n這是璃乃吃的吧～', 0);
insert into room_unit_comments values (7773, 128101, 1, 3, 1, 0, 1, '這套衣服很顯眼，\\n不管我在哪裡，\\n都能讓弟弟注意到呢！', 0);
insert into room_unit_comments values (7774, 128101, 1, 4, 1, 0, 1, '弟弟，快來幫忙！\\n我正在向璃乃施加壓力，\\n要她乖乖讀書呢……♪', 0);
insert into room_unit_comments values (7775, 128101, 1, 5, 1, 0, 1, '感覺到羈絆之力了，\\n這樣一來似乎\\n就可以更周全地\\n照顧好弟弟了呢！', 0);
insert into room_unit_comments values (7776, 128101, 1, 101, 4, 0, 1, '姐姐是不是偶爾\\n也要像璃乃一樣\\n撒嬌看看呢？', 0);
insert into room_unit_comments values (7777, 128101, 1, 102, 8, 0, 1, '弟弟和璃乃\\n兩人都是我\\n重要的家人喔！', 0);
insert into room_unit_comments values (7778, 128101, 2, 1, 1, 0, 1, '我知道的♪弟弟想\\n躺在姐姐的大腿上，\\n對嗎？咦？不是？\\n不可能不是啊？', 0);
insert into room_unit_comments values (7779, 128101, 2, 2, 1, 0, 1, '璃乃她啊，正在練習\\n妹妹空手接白刃，\\n以擋住姐姐手刀呢～', 0);
insert into room_unit_comments values (7780, 128101, 2, 3, 1, 0, 1, '得封印姐姐頭錘\\n才行了呢……因為，\\n如果現在施展頭槌，\\n頭飾會讓威力……嗯。', 0);
insert into room_unit_comments values (7781, 128101, 2, 4, 1, 0, 1, '呵呵♪\\n就算閉上眼睛\\n還是看得到弟弟唷。\\n……在那裡對吧？', 0);
insert into room_unit_comments values (7782, 128101, 2, 101, 4, 0, 1, '姐姐也要\\n像璃乃一樣，\\n一舉一動都\\n充滿了可愛喔♪', 0);
insert into room_unit_comments values (7783, 128101, 2, 102, 8, 0, 1, '弟弟♪\\n要一直待在能聽到\\n姐姐聲音的範圍內呦。\\n……一定要喔！', 0);
insert into room_unit_comments values (7784, 128101, 2, 1, 1, 1, 1, '弟弟，早安！\\n璃乃幫我你叫起床啦♪\\n……被搶先一步了。', 0);
insert into room_unit_comments values (7785, 128101, 2, 2, 1, 2, 1, '姐姐的午餐時間到囉♪\\n來，姐姐餵你吃，\\n嘴巴張開……♪', 0);
insert into room_unit_comments values (7786, 128101, 2, 3, 1, 3, 1, '睡覺的時間到囉……\\n弟弟睡在中間，\\n我們姐妹睡在你左右，\\n所以可以安心囉♪', 0);
insert into room_unit_comments values (7788, 128101, 3, 2, 1, 0, 1, '就算姐姐在全力放鬆，\\n只要弟弟說一聲，\\n立刻就會展開行動喔♪', 0);
insert into room_unit_comments values (7789, 128101, 3, 3, 1, 0, 1, '璃乃感覺會\\n喜歡這種的呢，\\n下次要不要送這個\\n當禮物試試看呢？', 0);
insert into room_unit_comments values (7790, 128101, 3, 4, 1, 0, 1, '有好香的味道呢～\\n咦？\\n璃乃的蛋糕甜味\\n也混進來了。', 0);
insert into room_unit_comments values (7791, 128101, 3, 5, 1, 0, 1, '姐姐對弟弟的愛\\n也猛烈地\\n燃燒起來呢……！', 0);
insert into room_unit_comments values (7793, 128101, 3, 7, 1, 0, 1, '多吃一點，\\n補充營養，\\n全部轉換成姐姐之力！', 0);
