insert into room_unit_comments values (7841, 128301, 3, 3, 1, 0, 1, '呵呵，\\n可愛程度僅次於\\n穿著和服的我呢。', 0);
insert into room_unit_comments values (7842, 128301, 3, 4, 1, 0, 1, '有股很香的味道……？\\n雖然我不太明白，\\n但和服上也傳來了\\n一樣的味道呢……', 0);
insert into room_unit_comments values (7843, 128301, 3, 5, 1, 0, 1, '呼～好溫暖……\\n畢竟新年唯一的缺點\\n就是寒冷啊……', 0);
