insert into room_unit_comments values (7866, 128401, 3, 3, 1, 0, 1, '真可愛～\\n好療癒，\\n感覺可以睡得很好呢～', 0);
insert into room_unit_comments values (7867, 128401, 3, 4, 1, 0, 1, '好香～\\n一放鬆下來\\n就變得想睡了啊……', 0);
insert into room_unit_comments values (7868, 128401, 3, 5, 1, 0, 1, '好溫暖好舒服～\\n不禁讓人想起\\n新年日出的溫暖啊～♪', 0);
insert into room_unit_comments values (7870, 128401, 3, 7, 1, 0, 1, '嚼嚼，好好吃～\\n但吃了這麼多之後，\\n就變得想睡了……', 0);
insert into room_unit_comments values (7871, 128401, 3, 8, 1, 0, 1, '嗯嗯，\\n新年料理也有很多\\n不同類型呢～\\n下次做看看好了。', 0);
insert into room_unit_comments values (7873, 106501, 1, 103, 12, 0, 1, '最近不知道為什麼，\\n變得看到他\\n就覺得好像很好吃……', 0);
insert into room_unit_comments values (7874, 106501, 2, 103, 12, 0, 1, '你可以握住我的手嗎？\\n不然我……\\n無法完全抑制力量……', 0);
insert into room_unit_comments values (7875, 128501, 1, 1, 1, 0, 1, '一邊享受短暫的和平，\\n一邊舒舒服服地\\n沉浸在閱讀中……\\n呵呵，太棒了呢。', 0);
insert into room_unit_comments values (7876, 128501, 1, 2, 1, 0, 1, '如果布置了新年裝飾，\\n看起來會不會\\n很熱鬧喜慶呢……？', 0);
insert into room_unit_comments values (7877, 128501, 1, 3, 1, 0, 1, '糟、糟了……\\n一個人玩太久，\\n都要慢慢掌握所有\\n新年遊戲的玩法了……', 0);
insert into room_unit_comments values (7878, 128501, 1, 4, 1, 0, 1, '懸疑事件並不會\\n選擇時間和場所，\\n也就是說……即便新年\\n偵探也是不休息的喔！', 0);
insert into room_unit_comments values (7879, 128501, 1, 5, 1, 0, 1, '寫個賀年卡\\n送給關照過我的人吧。\\n助手先生會不會\\n高興呢……嘿嘿！', 0);
insert into room_unit_comments values (7880, 128501, 1, 101, 4, 0, 1, '在繪馬上寫下願望吧！\\n希望可以和助手先生\\n待在一起……♪\\n……好、好害羞啊。', 0);
insert into room_unit_comments values (7881, 128501, 1, 102, 8, 0, 1, '助手先生是不是喜歡\\n更華麗的和服呢……？\\n好、好在意……', 0);
insert into room_unit_comments values (7882, 128501, 2, 1, 1, 0, 1, '嗨，\\n助手先生也不知道如何\\n消磨新年休假時光嗎？\\n呵呵，我也是呢。', 0);
insert into room_unit_comments values (7883, 128501, 2, 2, 1, 0, 1, '助手先生、助手先生！\\n我們去放風箏吧！\\n……這、這是\\n搜查的一環。', 0);
insert into room_unit_comments values (7884, 128501, 2, 3, 1, 0, 1, '穿著和服感覺很容易\\n跌倒，很恐怖啊……\\n不、不要推我喔！\\n我是認真的喔！？', 0);
insert into room_unit_comments values (7885, 128501, 2, 4, 1, 0, 1, '要、要看會在意\\n旁人眼光的書，就趁\\n所有人都回鄉的現在！\\n哇！助手先生！？', 0);
insert into room_unit_comments values (7886, 128501, 2, 101, 4, 0, 1, '欸、欸，助手先生……\\n要一起去新年參拜嗎？\\n啊、不是的！\\n我沒有特別的用意！', 0);
insert into room_unit_comments values (7887, 128501, 2, 102, 8, 0, 1, '啊哈哈……和助手先生\\n待在一起，就算只是\\n在家裡無所事事\\n也很開心呢……♪', 0);
insert into room_unit_comments values (7888, 128501, 2, 1, 1, 1, 1, '呼啊～\\n都是新年連假的關係，\\n我日夜顛倒了啦……', 0);
insert into room_unit_comments values (7889, 128501, 2, 2, 1, 2, 1, '新年也是委託不斷啊，\\n好了，去鎮上吧！\\n你當然也要一起囉。', 0);
insert into room_unit_comments values (7890, 128501, 2, 3, 1, 3, 1, '再、再靠近一點\\n也沒關係喔！\\n因為冬天晚上……\\n很冷嘛。', 0);
insert into room_unit_comments values (7892, 128501, 3, 2, 1, 0, 1, '嗚嗚嗚～！\\n再這樣下去\\n就要睡掉年假了……！', 0);
insert into room_unit_comments values (7893, 128501, 3, 3, 1, 0, 1, '就算大家都回鄉，\\n只要有這個就\\n不寂寞了……吧？', 0);
insert into room_unit_comments values (7894, 128501, 3, 4, 1, 0, 1, '嗯～♪\\n正好適合新年假期\\n提神醒腦呢♪', 0);
insert into room_unit_comments values (7895, 128501, 3, 5, 1, 0, 1, '呼……好溫暖啊。\\n冬天很冷，\\n真是幫了大忙了……', 0);
insert into room_unit_comments values (7897, 128501, 3, 7, 1, 0, 1, '嗯～好好吃♪\\n剛好適合\\n看書時享用。', 0);
insert into room_unit_comments values (7898, 128501, 3, 8, 1, 0, 1, '如果時間充裕，\\n就來看書吧。\\n目標，看完整個書架♪', 0);
insert into room_unit_comments values (7900, 128801, 1, 1, 1, 0, 1, '這股新的力量……\\n若是想要更熟練地\\n加以運用……', 0);
insert into room_unit_comments values (7901, 128801, 1, 2, 1, 0, 1, '沉睡在我體內的\\n屬性之力……\\n除了風之力外\\n還有什麼呢？', 0);
insert into room_unit_comments values (7902, 128801, 1, 3, 1, 0, 1, '現在的我\\n能做到什麼程度呢……\\n我會盡我所能試試。', 0);
insert into room_unit_comments values (7903, 128801, 1, 4, 1, 0, 1, '如何控制風之力……\\n我漸漸抓到訣竅了呢。', 0);
insert into room_unit_comments values (7904, 128801, 1, 5, 1, 0, 1, '風之力啊……\\n感覺應用層面很廣。\\n來一一嘗試\\n看能做些什麼吧。', 0);
insert into room_unit_comments values (7905, 128801, 1, 101, 4, 0, 1, '下次我和你要不要\\n來一場料理對決？\\n我可不會輕易敗北喔！', 0);
insert into room_unit_comments values (7906, 128801, 1, 102, 8, 0, 1, '從我內心滿溢而出的\\n溫暖感受……\\n一定是對他的……', 0);
insert into room_unit_comments values (7907, 128801, 2, 1, 1, 0, 1, '感覺劍技\\n又更上一層樓了……\\n不……不可疏忽大意。', 0);
insert into room_unit_comments values (7908, 128801, 2, 2, 1, 0, 1, '你說很帥？\\n謝謝……\\n這麼直接獲得稱讚\\n真令人害羞……', 0);
insert into room_unit_comments values (7909, 128801, 2, 3, 1, 0, 1, '無論受到\\n多麼厚重的牆壁阻隔，\\n我也會將其打破的。', 0);
insert into room_unit_comments values (7910, 128801, 2, 4, 1, 0, 1, '如果沒有事情做，\\n要不要稍微陪我\\n練習一下劍技呢？', 0);
insert into room_unit_comments values (7911, 128801, 2, 101, 4, 0, 1, '通過測驗……\\n我清楚明白\\n在我內心的東西了喔。', 0);
insert into room_unit_comments values (7912, 128801, 2, 102, 8, 0, 1, '從你那裡流淌過來的\\n力量中的暖意……\\n就是我們之間的羈絆。', 0);
insert into room_unit_comments values (7913, 128801, 2, 1, 1, 1, 1, '早上了，\\n差不多要起床囉，\\n還是你希望我\\n叫醒你呢？', 0);
insert into room_unit_comments values (7914, 128801, 2, 2, 1, 2, 1, '午餐由我來做喔，\\n你有什麼想吃的嗎？\\n我會盡我所能完成的。', 0);
insert into room_unit_comments values (7915, 128801, 2, 3, 1, 3, 1, '晚安，\\n明天也一起冒險吧，\\n約好囉！', 0);
insert into room_unit_comments values (7917, 128801, 3, 2, 1, 0, 1, '能休息時就休息……\\n因為這是繼續戰鬥的\\n必要條件呢。', 0);
insert into room_unit_comments values (7918, 128801, 3, 3, 1, 0, 1, '內心安穩的重要性……\\n我能知道這點\\n都是多虧了你們呢。', 0);
insert into room_unit_comments values (7919, 128801, 3, 4, 1, 0, 1, '心情很平靜……\\n這股香味，能不能\\n用風之力留在身邊呢？', 0);
insert into room_unit_comments values (7920, 128801, 3, 5, 1, 0, 1, '溫暖的空氣在流動……\\n我變得比以前\\n更能察覺風的變化了。', 0);
insert into room_unit_comments values (7922, 128801, 3, 7, 1, 0, 1, '這個調味……\\n下次試試看吧？\\n試味道就交給你囉。', 0);
insert into room_unit_comments values (7923, 128801, 3, 8, 1, 0, 1, '若要鍛鍊心、技、體，\\n鍛鍊一切，\\n那閱讀學習\\n也是必要的呢。', 0);
insert into room_unit_comments values (7925, 128901, 1, 1, 1, 0, 1, '有適合冥想的地方嗎？\\n能夠專注……\\n也能夠忍耐\\n午後睡意的地方。', 0);
insert into room_unit_comments values (7926, 128901, 1, 2, 1, 0, 1, '這裡流動著溫暖的\\n空氣呢……\\n多虧了大家\\n願意接納我。', 0);
insert into room_unit_comments values (7927, 128901, 1, 3, 1, 0, 1, '簡直像是\\n心裡充滿了光……\\n這種感受，\\n真想分享給所有人啊♪', 0);
insert into room_unit_comments values (7928, 128901, 1, 4, 1, 0, 1, '這套服裝\\n容易反映出屬性之力呢。\\n我也能理解\\n它為什麼閃閃發光了。', 0);
insert into room_unit_comments values (7929, 128901, 1, 5, 1, 0, 1, '精靈族之村的人\\n都還好嗎……\\n好想再去見見大家啊。', 0);
insert into room_unit_comments values (7930, 128901, 1, 101, 4, 0, 1, '還能順利變身……\\n我與騎士先生的\\n修行有了成果呢……\\n呵呵♪', 0);
insert into room_unit_comments values (7931, 128901, 1, 102, 8, 0, 1, '如果脫掉這件衣服\\n會怎麼樣呢……\\n雖、雖然我沒有勇氣\\n嘗試……', 0);
insert into room_unit_comments values (7932, 128901, 2, 1, 1, 0, 1, '呼……啊、騎士先生，\\n我剛剛正好在冥想，\\n要……一起嗎？', 0);
insert into room_unit_comments values (7933, 128901, 2, 2, 1, 0, 1, '雖然很不可思議，\\n但不知不覺間\\n背上的輪環……\\n會一下出現一下消失！', 0);
insert into room_unit_comments values (7934, 128901, 2, 3, 1, 0, 1, '這套衣服的觸感\\n非常舒服喔！\\n要、要摸摸看嗎？\\n很滑順喔？', 0);
insert into room_unit_comments values (7935, 128901, 2, 4, 1, 0, 1, '星素的力量……\\n可以持續非常久耶，\\n修行有了成果喔。', 0);
insert into room_unit_comments values (7936, 128901, 2, 101, 4, 0, 1, '只要有這股力量，\\n就算是在暗處掉了東西\\n或許也很好找到……？', 0);
insert into room_unit_comments values (7937, 128901, 2, 102, 8, 0, 1, '已經準備好要冒險囉！\\n隨時都可以來找我喔。\\n……我很期待！', 0);
insert into room_unit_comments values (7938, 128901, 2, 1, 1, 1, 1, '騎士先生，早安。\\n要不要……\\n一起去散步呢？', 0);
insert into room_unit_comments values (7939, 128901, 2, 2, 1, 2, 1, '吃完午餐後\\n要冥想很困難呢，\\n會變得很想睡……', 0);
insert into room_unit_comments values (7940, 128901, 2, 3, 1, 3, 1, '熬夜也要適可而止啊，\\n得要讓\\n努力了一天的自己，\\n好好休息才行。', 0);
insert into room_unit_comments values (7942, 128901, 3, 2, 1, 0, 1, '一悠閒下來，\\n就變得好想\\n吃蛋糕啊……\\n但是會胖……對吧？', 0);
insert into room_unit_comments values (7943, 128901, 3, 3, 1, 0, 1, '哇啊，好可愛……！\\n光是用看的\\n就讓人露出笑容……♪', 0);
insert into room_unit_comments values (7944, 128901, 3, 4, 1, 0, 1, '這是……花香嗎？\\n可能是我\\n知道的花……？\\n來猜猜看吧。', 0);
insert into room_unit_comments values (7945, 128901, 3, 5, 1, 0, 1, '溫暖為什麼會\\n讓人這麼放鬆呢……', 0);
insert into room_unit_comments values (7947, 128901, 3, 7, 1, 0, 1, '我明明知道要是吃太多\\n肚子可能會\\n變得很明顯……！\\n但是停不下來啊～！', 0);
insert into room_unit_comments values (7948, 128901, 3, 8, 1, 0, 1, '戀、戀愛的精靈\\n是存在的嗎……？\\n哇！不行不行！\\n得要靠實力努力。', 0);
insert into room_unit_comments values (7950, 128701, 1, 1, 1, 0, 1, '在房間裡也要\\n修行、修行♪\\n不過得要注意\\n不要燒起來才行呢。', 0);
insert into room_unit_comments values (7951, 128701, 1, 2, 1, 0, 1, '嗯～\\n這件衣服算是保暖\\n還是清涼呢……？', 0);
insert into room_unit_comments values (7952, 128701, 1, 3, 1, 0, 1, '沒有平常的肉球，\\n很方便打掃呢♪\\n來把騎士先生的房間\\n掃得清潔溜溜吧～！', 0);
insert into room_unit_comments values (7953, 128701, 1, 4, 1, 0, 1, '放假想和騎士先生\\n一起去烤肉啊～\\n如果和我一起去\\n就不需要煩惱升火囉。', 0);
insert into room_unit_comments values (7954, 128701, 1, 5, 1, 0, 1, '這個樣子可以\\n幫助更多人……！\\n欸嘿嘿！\\n熱血起來了──！', 0);
insert into room_unit_comments values (7955, 128701, 1, 101, 4, 0, 1, '等騎士先生回來，\\n就露出像埃拉那樣的\\n笑容，出去迎接\\n說你辛苦了……♪', 0);
insert into room_unit_comments values (7956, 128701, 1, 102, 8, 0, 1, '這個模樣時，\\n胸口會熾熱地燃燒，\\n是因為一直想著\\n騎士先生的事嗎……？', 0);
insert into room_unit_comments values (7957, 128701, 2, 1, 1, 0, 1, '騎士先生騎士先生！\\n輕易觸摸我\\n小心燙傷喔……？\\n開玩笑的啦～♪', 0);
insert into room_unit_comments values (7958, 128701, 2, 2, 1, 0, 1, '哦，騎士先生，\\n想去修行嗎？\\n嘿嘿，真好呢～♪\\n兩個人一起燃燒吧～！', 0);
insert into room_unit_comments values (7959, 128701, 2, 3, 1, 0, 1, '對了，騎士先生，\\n來猜拳吧！\\n沒有戴拳套，\\n所以我也能出剪刀哦♪', 0);
insert into room_unit_comments values (7960, 128701, 2, 4, 1, 0, 1, '可以維持這個樣子的\\n時間增長了好多……！\\n嘿嘿！\\n我們修行有了成果呢！', 0);
insert into room_unit_comments values (7961, 128701, 2, 101, 4, 0, 1, '不要一直盯著看啦，\\n臉都紅了……糟糕！\\n感覺臉燙得真的要\\n噴出火來了……！', 0);
insert into room_unit_comments values (7962, 128701, 2, 102, 8, 0, 1, '就算抗火能力變強，\\n被騎士先生一碰，\\n我的臉頰還是會\\n不禁變得很燙……', 0);
insert into room_unit_comments values (7963, 128701, 2, 1, 1, 1, 1, '騎士先生，早安啊～！\\n今天一天也\\n猛烈燃燒吧～！\\n點火──！！', 0);
insert into room_unit_comments values (7964, 128701, 2, 2, 1, 2, 1, '騎士先生，中午了！\\n好好吃頓飯，\\n撐過中午之後的\\n修行吧～！', 0);
insert into room_unit_comments values (7965, 128701, 2, 3, 1, 3, 1, '今天也辛苦了！\\n對了，在睡前\\n圍個營火吧♪', 0);
insert into room_unit_comments values (7967, 128701, 3, 2, 1, 0, 1, '呼哼～嗯！\\n嘿嘿，真舒服……\\n修行前\\n稍微休息一下──♪', 0);
insert into room_unit_comments values (7968, 128701, 3, 3, 1, 0, 1, '真可愛啊～♪\\n我摸我摸我摸我摸我摸\\n我摸我摸我摸我摸！\\n啊哈哈，摩擦生熱！', 0);
insert into room_unit_comments values (7969, 128701, 3, 4, 1, 0, 1, '嗯～好香的味道♪\\n稍微再熱一點\\n香味會更明顯……啊！\\n不小心蒸發了！！！', 0);
insert into room_unit_comments values (7970, 128701, 3, 5, 1, 0, 1, '雖然在外面也可以\\n靠自己的火來取暖，\\n但在房間暖呼呼的\\n是最棒的呢♪', 0);
insert into room_unit_comments values (7972, 128701, 3, 7, 1, 0, 1, '嚼嚼嚼，\\n餓著肚子\\n是無法幫助人的！\\n嚼嚼嚼！', 0);
insert into room_unit_comments values (7973, 128701, 3, 8, 1, 0, 1, '嗯嗯……\\n原來如此原來如此……\\n這就是\\n優秀的烤肉方法啊……', 0);
insert into room_unit_comments values (7975, 129001, 1, 1, 1, 0, 1, '……牆壁、門、柱子，\\n全部都跟在紀錄上看到得一樣。\\n我真的……來到這裡了呢。', 0);
insert into room_unit_comments values (7976, 129001, 1, 2, 1, 0, 1, '……喧鬧的場所。\\n竟敢因騎士先生寬容\\n就任意吵鬧。', 0);
insert into room_unit_comments values (7977, 129001, 1, 3, 1, 0, 1, '這裡就是……騎士先生\\n想守護的地方。\\n充滿了騎士先生的\\n回憶……呢。', 0);
insert into room_unit_comments values (7978, 129001, 1, 4, 1, 0, 1, '我感受到一股視線……\\n像是在探查\\n這邊的狀況……\\n雖不足以影響心情。', 0);
insert into room_unit_comments values (7979, 129001, 1, 5, 1, 0, 1, '……到處都是……\\n會我回想起\\n他和我不想提起的人\\n一起度過的紀錄……', 0);
insert into room_unit_comments values (7980, 129001, 1, 101, 4, 0, 1, '雖然人很多……\\n但比在深山大雪中\\n容易生活……吧。', 0);
insert into room_unit_comments values (7981, 129001, 1, 102, 8, 0, 1, '雖然沒什麼自覺，\\n但我或許是那種……\\n極端不懂得放棄的\\n女人吧。', 0);
insert into room_unit_comments values (7982, 129001, 2, 1, 1, 0, 1, '謝謝你邀請我。\\n所以……\\n我在這裡應該要\\n做點什麼……嗎？', 0);
insert into room_unit_comments values (7983, 129001, 2, 2, 1, 0, 1, '這裡……好明亮呢。\\n和我的城堡相比\\n甚至有些亮得刺眼。', 0);
insert into room_unit_comments values (7984, 129001, 2, 3, 1, 0, 1, '已經不需要面具了喔。\\n因為……\\n那樣騎士先生\\n就看不見我的臉了。', 0);
insert into room_unit_comments values (7985, 129001, 2, 4, 1, 0, 1, '只要騎士先生願意，\\n我的城堡隨時\\n會為你準備\\n專門的房間喔。', 0);
insert into room_unit_comments values (7986, 129001, 2, 101, 4, 0, 1, '……希望騎士先生\\n能再借我你的披風。\\n我會保持乾淨的。\\n……不可以嗎？', 0);
insert into room_unit_comments values (7987, 129001, 2, 102, 8, 0, 1, '兩人的願望，總有一天\\n會互相重合……\\n我如此深信著哦。\\n……無論經過多久……', 0);
insert into room_unit_comments values (7988, 129001, 2, 1, 1, 1, 1, '早安，騎士先生。\\n……昨天在夢裡\\n不小心聊太久了，\\n真是抱歉。呵呵。', 0);
insert into room_unit_comments values (7989, 129001, 2, 2, 1, 2, 1, '你肚子……餓不餓？\\n我這裡雖然沒有吃的，\\n但可以為你施加\\n忘記飢餓的魔法喔。', 0);
insert into room_unit_comments values (7990, 129001, 2, 3, 1, 3, 1, '騎士先生……\\n你又睡不著了嗎？\\n……這樣啊。那麼……\\n我也一起醒著吧。', 0);
insert into room_unit_comments values (7992, 129001, 3, 2, 1, 0, 1, '想放輕鬆……還需要\\n時間適應。但要是……\\n能和騎士先生獨處\\n肯定會有所不同。', 0);
insert into room_unit_comments values (7993, 129001, 3, 3, 1, 0, 1, '怪奇怪靈……\\n沒錯，那個確實\\n就是這種感覺……', 0);
insert into room_unit_comments values (7994, 129001, 3, 4, 1, 0, 1, '我喜歡……花香。\\n因為可以讓我的心緒\\n獲得片刻的寧靜。', 0);
insert into room_unit_comments values (7995, 129001, 3, 5, 1, 0, 1, '好溫暖……\\n但是跟騎士先生的\\n溫柔相比，還差得遠。', 0);
insert into room_unit_comments values (7997, 129001, 3, 7, 1, 0, 1, '好、吃……？\\n這股感覺，就是好吃？\\n不知為何……\\n我可以接受。', 0);
insert into room_unit_comments values (7998, 129001, 3, 8, 1, 0, 1, '想獲得情報\\n居然必須要\\n特地翻開書本……\\n身為玩家真不方便呢。', 0);
insert into room_unit_comments values (8000, 129301, 1, 1, 1, 0, 1, '墨丘利度假村啊……\\n沒有好的回憶呢。\\n嗯，忘了吧。', 0);
insert into room_unit_comments values (8001, 129301, 1, 2, 1, 0, 1, '知性戰士優妮！\\n那令人畏懼的腕力！\\n呼喔喔！唔喵──！', 0);
insert into room_unit_comments values (8002, 129301, 1, 3, 1, 0, 1, '砰～！\\n請給我石頭用的泳裝！\\n性感羅塞塔\\n要霸占海灘的視線！', 0);
insert into room_unit_comments values (8003, 129301, 1, 4, 1, 0, 1, '我漸漸覺得與其\\n爭奪學院的獎學金，\\n不如詐騙某財團\\n還比較快呢……', 0);
insert into room_unit_comments values (8004, 129301, 1, 5, 1, 0, 1, 'One two one two……\\n呀啊，腳抽筋了──！\\n可惡的慈樂之音，\\n不可原諒！', 0);
insert into room_unit_comments values (8005, 129301, 1, 101, 4, 0, 1, '話說回來……\\n這裡明明不是泳池，\\n為何我還穿著泳裝……\\n可惡的世界！', 0);
insert into room_unit_comments values (8006, 129301, 1, 102, 8, 0, 1, '和他一起拍的影片。\\n唔，無法直盯著看……\\n這股刺刺癢癢的感覺\\n是怎麼回事？', 0);
insert into room_unit_comments values (8007, 129301, 2, 1, 1, 0, 1, '什麼？\\n你問我穿泳裝不冷嗎？\\n有很多女孩平時就\\n穿得更單薄吧。', 0);
insert into room_unit_comments values (8008, 129301, 2, 2, 1, 0, 1, '雖然已經把墨利村的\\n邀請函寄給忍了，\\n但還是很擔心\\n她父親會暴走啊……', 0);
insert into room_unit_comments values (8009, 129301, 2, 3, 1, 0, 1, '我有把墨利村的邀請函\\n寄給美美她們喔。\\n必須準備迎接她們……\\n你有帶奶嘴嗎？', 0);
insert into room_unit_comments values (8010, 129301, 2, 4, 1, 0, 1, '本質的賢者……？\\n啊不，他過得很好。\\n但你為什麼會認識\\n我的親人啊……？', 0);
insert into room_unit_comments values (8011, 129301, 2, 101, 4, 0, 1, '泳裝很適合我？你、\\n你突然講這幹嘛……\\n別說了啦嗚嘻嘻……', 0);
insert into room_unit_comments values (8012, 129301, 2, 102, 8, 0, 1, '下次我想要在夏天\\n去墨利村。\\n當然，\\n你也要一起去喔。', 0);
insert into room_unit_comments values (8013, 129301, 2, 1, 1, 1, 1, '唔啊啊，太陽……！\\n太陽升起來了……！\\n夜貓子優妮要死了。', 0);
insert into room_unit_comments values (8014, 129301, 2, 2, 1, 2, 1, '唉……已經中午啦……\\n差不多該睡了。\\n不維持規律作息\\n不健康呢……', 0);
insert into room_unit_comments values (8015, 129301, 2, 3, 1, 3, 1, '呀哈──晚上了──！\\n又到了我們的時間啦！\\n呼喔喔，嗨起來～！', 0);
insert into room_unit_comments values (8017, 129301, 3, 2, 1, 0, 1, '這是……\\n要我睡在這裡嗎？\\n我雖是不介意……', 0);
insert into room_unit_comments values (8018, 129301, 3, 3, 1, 0, 1, '呀、呀！\\n這個好可愛～！\\n……很好，今天也能\\n完美假扮成小孩子呢。', 0);
insert into room_unit_comments values (8019, 129301, 3, 4, 1, 0, 1, '嗯，真香。\\n但也只是如此嗎……\\n沒有提振精神的\\n效果啊……嘖──', 0);
insert into room_unit_comments values (8020, 129301, 3, 5, 1, 0, 1, '看著火光……\\n就會想起魔導罐呢……\\n不知道巨蟹座過得好不好……', 0);
insert into room_unit_comments values (8022, 129301, 3, 7, 1, 0, 1, '別說了，\\n我不需要固體食物！\\n把藥水交出來！', 0);
insert into room_unit_comments values (8023, 129301, 3, 8, 1, 0, 1, '書本果然只屬於文科。\\n多麼舒心的文筆啊……\\n呃啊突然出現理科文！\\n咿，眼睛要變理科了！', 0);
insert into room_unit_comments values (8025, 129301, 3, 10, 1, 0, 1, '呼啊啊～！\\n知性的海上騎士優妮！\\n這是新的可能性！', 0);
insert into room_unit_comments values (8026, 129301, 3, 11, 1, 0, 1, '這是……什麼？\\n哪裡有趣了？\\n還真難啊……', 0);
insert into room_unit_comments values (8027, 108901, 1, 103, 12, 0, 1, '和重要的人\\n一起過新年的回憶嗎……\\n這是以前的我\\n無法想像的。', 0);
insert into room_unit_comments values (8028, 108901, 2, 103, 12, 0, 1, '我有很多\\n想實現的願望喔。\\n如果要選一個最想實現的，\\n果然還是跟你……', 0);
insert into room_unit_comments values (8029, 108801, 1, 103, 12, 0, 1, '神殿的\\n戀愛詩籤……\\n要不要抽看看呢……', 0);
insert into room_unit_comments values (8030, 108801, 2, 103, 12, 0, 1, '騎士先生，\\n明年也要一起綁詩籤喔。\\n約好囉。', 0);
insert into room_unit_comments values (8031, 108701, 1, 103, 12, 0, 1, '下次我要把\\n跟騎士先生\\n一起拍的照片\\n送給婆婆♪', 0);
insert into room_unit_comments values (8032, 108701, 2, 103, 12, 0, 1, '啊，騎士先生！\\n今年也……不對，\\n今後也請\\n多多指教♪', 0);
insert into room_unit_comments values (8033, 129401, 1, 1, 1, 0, 1, '舊校舍的地下牢房……\\n寒冬的盛夏計畫……\\n率真自然的ＰＲ……\\n唔，我的頭……！', 0);
insert into room_unit_comments values (8034, 129401, 1, 2, 1, 0, 1, '閒閒沒事就來照鏡子吧。\\n喔，腹肌練得不錯……\\n啊？你看什麼看啊？', 0);
insert into room_unit_comments values (8035, 129401, 1, 3, 1, 0, 1, '我是金色之疾風！\\n唔唔……好像不太對。\\n吾名為金色之……\\n啊？你看什麼看啊？', 0);
insert into room_unit_comments values (8036, 129401, 1, 4, 1, 0, 1, '莫妮卡跟優妮學姊\\n雖然語氣跟身高很相似，\\n但被當作小孩子時\\n反應完全不同呢……', 0);
insert into room_unit_comments values (8037, 129401, 1, 5, 1, 0, 1, '不知道小栞\\n過得好不好……\\n以前一直不停摸她，\\n好像被她討厭了呢……', 0);
insert into room_unit_comments values (8038, 129401, 1, 101, 4, 0, 1, '我是覺得沒差啦……\\n不過你認識的人裡面，\\n平常就一副類似泳裝\\n打扮的女生還真多啊？', 0);
insert into room_unit_comments values (8039, 129401, 1, 102, 8, 0, 1, '對了，\\n我弟弟們想見你呢。\\n你要是有空，\\n隨時都可以來我家喔。', 0);
insert into room_unit_comments values (8040, 129401, 2, 1, 1, 0, 1, '雖然特別講座\\n發生很多事……\\n不過墨利村本身\\n還不賴呢。', 0);
insert into room_unit_comments values (8041, 129401, 2, 2, 1, 0, 1, '美衣子前輩說，\\n從跟泳裝搭配的小東西\\n就能看出品味。\\n我超贊成。', 0);
insert into room_unit_comments values (8042, 129401, 2, 3, 1, 0, 1, '穿泳裝的美冬姐\\n果然有練過。那就是\\n屠龍者的身材嗎……\\n超酷的啦……', 0);
insert into room_unit_comments values (8043, 129401, 2, 4, 1, 0, 1, '哈呦嗯！？\\n不對，我就說了……\\n不要從背後\\n跟我搭話啦……', 0);
insert into room_unit_comments values (8044, 129401, 2, 101, 4, 0, 1, '多虧有幹練的攝影師，\\n墨利村的特別講座\\n才能順利結束。\\n謝啦。', 0);
insert into room_unit_comments values (8045, 129401, 2, 102, 8, 0, 1, '啊？這件泳裝怎麼了？\\n很適……咦，什麼？\\n抱歉，我沒聽見。\\n你再說一次。', 0);
insert into room_unit_comments values (8046, 129401, 2, 1, 1, 1, 1, '好扯，\\n居然已經早上了。\\n我昨天陪兩個人唱了\\n一整晚的卡拉ＯＫ啊。', 0);
insert into room_unit_comments values (8047, 129401, 2, 2, 1, 2, 1, '唉，得去上課才行……\\n日正當中就要念書，\\n實在是太痛苦了……', 0);
insert into room_unit_comments values (8048, 129401, 2, 3, 1, 3, 1, '今天待會要上晚班……\\n不是打工，\\n我是說志工啦？\\n違反校規不好。', 0);
insert into room_unit_comments values (8050, 129401, 3, 2, 1, 0, 1, '哇啊，好蓬鬆喔……\\n軟得跟見鬼一樣……\\n糟糕，要優妮化了……', 0);
insert into room_unit_comments values (8051, 129401, 3, 3, 1, 0, 1, '我要是有個妹妹，\\n買個這種也不錯……\\n算了，自己用也行啦。', 0);
insert into room_unit_comments values (8052, 129401, 3, 4, 1, 0, 1, '嗯……\\n這股香味不錯呢。\\n聞起來特別出色。', 0);
insert into room_unit_comments values (8053, 129401, 3, 5, 1, 0, 1, '我家祖先的森林\\n好像因為\\n大火燒光了……雖然\\n那是我出生前的事了。', 0);
insert into room_unit_comments values (8055, 129401, 3, 7, 1, 0, 1, '這是什麼超感謝──\\n食物堆滿山笑死人。\\n白米真是太寶貴了……', 0);
insert into room_unit_comments values (8056, 129401, 3, 8, 1, 0, 1, '是沒差啦，\\n但為什麼我要\\n穿著泳裝看書？\\n這是什麼懲罰遊戲啊。', 0);
insert into room_unit_comments values (8058, 129401, 3, 10, 1, 0, 1, '金色的海上騎士克蘿依\\n不請自來登場！煞！\\n啊？你看什麼看啊？', 0);
insert into room_unit_comments values (8059, 129401, 3, 11, 1, 0, 1, '這是什麼？貝殼椅子？\\n這要怎麼玩……\\n坐下就結束了嗎……\\n嗯，也是啦……', 0);
insert into room_unit_comments values (8060, 129801, 1, 1, 1, 0, 1, '接著……\\n要來思考\\n明天的訓練計畫了。', 0);
insert into room_unit_comments values (8061, 129801, 1, 2, 1, 0, 1, '又獲得新人女孩\\n親手做的便當了……\\n雖然我還是會\\n心懷感激地吃完啦。', 0);
insert into room_unit_comments values (8062, 129801, 1, 3, 1, 0, 1, '無關種族，\\n大家齊心協力\\n守護蘭德索爾……\\n時代變好了呢。', 0);
insert into room_unit_comments values (8063, 129801, 1, 4, 1, 0, 1, '每個人都會有長處。\\n幫新人找到優勢，\\n也是教官的工作。', 0);
insert into room_unit_comments values (8064, 129801, 1, 5, 1, 0, 1, '不能太過嚴苛，\\n也不能太過放水……\\n教官很難當呢。', 0);
insert into room_unit_comments values (8065, 129801, 1, 101, 4, 0, 1, '如何協助\\n不擅長戰鬥的新人……\\n稍後跟那傢伙\\n討論看看吧。', 0);
insert into room_unit_comments values (8066, 129801, 1, 102, 8, 0, 1, '那傢伙太過溫柔，\\n不適合戰鬥……\\n不過這種天真\\n我不討厭呢。', 0);
insert into room_unit_comments values (8067, 129801, 2, 1, 1, 0, 1, '要變強沒有捷徑，\\n唯有踏實的鍛鍊。\\n來，開始訓練了！', 0);
insert into room_unit_comments values (8068, 129801, 2, 2, 1, 0, 1, '這句話我也常對\\n新人說……\\n你要用心保養裝備，\\n知道嗎？', 0);
insert into room_unit_comments values (8069, 129801, 2, 3, 1, 0, 1, '你也想參加講座嗎？\\n很有心嘛！\\n我什麼都會教你喔……\\n除了魔法以外。', 0);
insert into room_unit_comments values (8070, 129801, 2, 4, 1, 0, 1, '新人們很常聊\\n戀愛話題呢……\\n我、我問你……\\n你有在意的人嗎？', 0);
insert into room_unit_comments values (8071, 129801, 2, 101, 4, 0, 1, '喂，\\n你可以幫我伸展嗎？\\n訓練前必須確實\\n讓肌肉放鬆才行……', 0);
insert into room_unit_comments values (8072, 129801, 2, 102, 8, 0, 1, '你很努力鍛鍊呢。\\n很好！\\n做為獎勵，\\n我做點心給你吃吧♪', 0);
insert into room_unit_comments values (8073, 129801, 2, 1, 1, 1, 1, '真是適合訓練的日子！\\n那麼……首先，\\n來練跑吧！', 0);
insert into room_unit_comments values (8074, 129801, 2, 2, 1, 2, 1, '要好好吃午餐啊。\\n不然待會的訓練\\n會撐不住喔？', 0);
insert into room_unit_comments values (8075, 129801, 2, 3, 1, 3, 1, '已經是熄燈時間了。\\n趕快睡吧。\\n明天還要早起呢。', 0);
insert into room_unit_comments values (8077, 129801, 3, 2, 1, 0, 1, '休息也是訓練的一部分。\\n現在就讓我\\n放鬆一下吧。', 0);
insert into room_unit_comments values (8078, 129801, 3, 3, 1, 0, 1, '你的表情很不錯。\\n我很中意你喔！\\n要不要加入我這班啊？\\n開玩笑的啦♪', 0);
insert into room_unit_comments values (8079, 129801, 3, 4, 1, 0, 1, '……有股療癒的香氣呢。\\n要嘗試也推薦給\\n新人們嗎……', 0);
insert into room_unit_comments values (8080, 129801, 3, 5, 1, 0, 1, '正因為很溫暖，\\n所以不能睡著，\\n否則會有損\\n教官的威嚴……', 0);
insert into room_unit_comments values (8082, 129801, 3, 7, 1, 0, 1, '吃飯也是訓練的一環。\\n話雖如此，\\n要小心不要吃太多……', 0);
insert into room_unit_comments values (8083, 129801, 3, 8, 1, 0, 1, '東國的劍術……嗎。\\n或許能\\n活用在訓練上。', 0);
insert into room_unit_comments values (8085, 129901, 1, 1, 1, 0, 1, '對接近那位大人的\\n無禮之徒，我就用特別\\n訓練來讓他見識\\n什麼叫地獄吧。', 0);
insert into room_unit_comments values (8086, 129901, 1, 2, 1, 0, 1, '這麼寬廣的話，\\n動作大一些\\n應該也沒關係吧。', 0);
insert into room_unit_comments values (8087, 129901, 1, 3, 1, 0, 1, '……剛剛有誰\\n叫我「魔鬼」教官了嗎？', 0);
insert into room_unit_comments values (8088, 129901, 1, 4, 1, 0, 1, '有沒有人能來試試看，\\n這剛開發出來的藥物？', 0);
insert into room_unit_comments values (8089, 129901, 1, 5, 1, 0, 1, '太嚴苛會半途而廢，\\n太溫柔又會偷懶，\\n人類究竟有多麼\\n無可救藥呢。', 0);
insert into room_unit_comments values (8090, 129901, 1, 101, 4, 0, 1, '開始吧，\\n為了射中\\n那位大人的心\\n的模擬演習。', 0);
insert into room_unit_comments values (8091, 129901, 1, 102, 8, 0, 1, '為了從各式困難和\\n可恨的女人們手中\\n保護那位大人，\\n我也得鍛鍊自己才行。', 0);
insert into room_unit_comments values (8092, 129901, 2, 1, 1, 0, 1, '您這樣一直盯著我\\n……咦？\\n您說惠理子很帥氣？\\n是的，那是當然。', 0);
insert into room_unit_comments values (8093, 129901, 2, 2, 1, 0, 1, '身體不適的話\\n請立刻向我報告，\\n畢竟顧好身體健康\\n也是教官重要的職責。', 0);
insert into room_unit_comments values (8094, 129901, 2, 3, 1, 0, 1, '新的訓練道具\\n用起來如何……咦？\\n您說太重了？我已經\\n減輕重量了呢……', 0);
insert into room_unit_comments values (8095, 129901, 2, 4, 1, 0, 1, '運動後請喝這個\\n我特製的能量飲料。\\n嘻嘻，裡面\\n什麼都沒加喔。', 0);
insert into room_unit_comments values (8096, 129901, 2, 101, 4, 0, 1, '您辛苦了。\\n啊啊……\\n疲倦的表情也\\n充滿魅力。', 0);
insert into room_unit_comments values (8097, 129901, 2, 102, 8, 0, 1, '大人，出發前往\\n只有我們兩人的\\n遠征訓練吧。\\n我已經做好萬全準備了。', 0);
insert into room_unit_comments values (8098, 129901, 2, 1, 1, 1, 1, '還想睡嗎？\\n那麼拉個筋\\n痛醒……我是說\\n放鬆身體吧。', 0);
insert into room_unit_comments values (8099, 129901, 2, 2, 1, 2, 1, '午餐我準備了便當。\\n份量與能量都加倍，\\n請全都吃完不要剩下。', 0);
insert into room_unit_comments values (8100, 129901, 2, 3, 1, 3, 1, '良好的睡眠\\n對健康是不可或缺的。\\n所以就讓我來守夜吧。', 0);
insert into room_unit_comments values (8102, 129901, 3, 2, 1, 0, 1, '本來是打算\\n休息時用的，\\n但坐著太舒適\\n都要站不起來了……', 0);
insert into room_unit_comments values (8103, 129901, 3, 3, 1, 0, 1, '在裡面塞重物的話，\\n就能夠一邊把玩\\n一邊運動了。', 0);
