insert into unit_data values (100101, '日和', '日和', 100101, 0, 0, 1, 1, 1, 450, 200, 1, 2.295, 100101, 0, 100101, 0, 7, 0, '【物理】位於前衛，壓制敵方前線，精神百倍並帶著笑容的女孩。\\n擁有能大幅度攻擊前衛的必殺技，以及能加強自身攻擊力的技能的攻擊者。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100201, '優衣', '優衣', 100201, 0, 0, 1, 7, 7, 450, 800, 2, 2.27, 100201, 0, 100201, 0, 7, 0, '【魔法】位於最後列，恢復及輔助己方的魔法師。\\n打擊力量微弱，但擁有高度魔法攻擊力，當 Rank 上升，其攻擊力及輔助能力將會取得平衡。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100301, '怜', '怜', 100301, 0, 0, 1, 4, 4, 450, 250, 1, 1.965, 100301, 0, 100301, 0, 7, 0, '【物理】位於前衛，能使出單點突破來討伐敵方，是一名孤高的劍士。\\n必殺技與一般技能，皆為了打倒眼前敵人而特化，\\n並驅使攻擊技能和反擊技能來壓制前線。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100401, '禊', '禊', 100401, 0, 0, 1, 1, 1, 450, 205, 1, 2.17, 100401, 0, 100401, 0, 13, 0, '【物理】在最前線干擾敵人物理攻擊的淘氣少女。\\n擁有能使眼前的敵人陷入黑暗狀態、命中率大幅下降之外，\\n還能使一名敵人的物理攻擊力下降的技能。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100501, '茉莉', '茉莉', 100501, 0, 0, 2, 2, 2, 450, 185, 1, 2.125, 100501, 0, 100501, 0, 2, 0, '【物理】以機敏的行動在敵陣穿梭的先鋒見習騎士。\\n以必殺技衝進對手陣地中央，\\n再攻擊自己周圍的技能來進攻對手。', 0, '2019/01/22 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100601, '茜里', '茜里', 100601, 0, 0, 2, 6, 6, 450, 570, 2, 2.19, 100601, 0, 100601, 0, 14, 0, '【魔法】位於中衛，擔任魔法隊中強化與恢復的角色，是雙胞胎中的妹妹。\\n擁有能大幅降低敵人魔法防禦力的技能，以及可提升魔法攻擊力及擁有恢復效果的必殺技。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100701, '宮子', '宮子', 100701, 0, 0, 2, 3, 3, 450, 125, 1, 1.7, 100701, 0, 100701, 0, 14, 0, '【物理】在最前線專門閃避敵人攻擊的幽靈少女。\\n以技能變身成為幽靈來躲避敵人攻擊，\\n必殺技則是把敵人變成布丁吃掉。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100801, '雪', '雪', 100801, 0, 0, 2, 21, 7, 450, 805, 2, 2.07, 100801, 0, 100801, 0, 12, 0, '【魔法】位於後衛，能輔助物理隊伍，是個活生生的藝術品。\\n除了能提早發動己方必殺技的技能，還擁有能使敵人陷入黑暗狀態，藉此封鎖敵人物理攻擊的技能。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (100901, '杏奈', '杏奈', 100901, 0, 0, 3, 5, 5, 450, 440, 2, 2.25, 100901, 0, 100901, 0, 11, 0, '【魔法】可從中衛發動壓倒性的魔力來攻擊敵人，人稱之疾風之冥姬。\\n施展必殺技能雖能給予敵軍全體大規模的傷害，\\n然而卻因這份魔力過於強大，對自己也有相當的風險。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101001, '真步', '真步', 101001, 0, 0, 3, 7, 7, 450, 795, 2, 2.27, 101001, 0, 101001, 0, 4, 0, '【魔法】位於後衛，負責支援己方的童話少女。\\n擁有可加強己方全體，恢復ＴＰ之必殺技，\\n以及可恢復ＨＰ，使敵方陷入黑暗的技能，能使戰況好轉。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101101, '璃乃', '璃乃', 101101, 0, 0, 3, 8, 8, 450, 700, 1, 1.97, 101101, 0, 101101, 0, 6, 0, '【物理】能從後衛降下箭雨，是一名妹妹屬性的弓箭手。\\n擁有能提高自己的暴擊率的一般技能，\\n還能使傷害次數較高的必殺技，擁有更顯著的效果。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101201, '初音', '初音', 101201, 0, 0, 3, 7, 7, 450, 755, 2, 2.07, 101201, 0, 101201, 0, 9, 0, '【魔法】位於後衛，擅長對付物理攻擊型敵軍，\\n是一位超能力少女。\\n因為能對物理攻擊型的敵軍發揮真正的價值，\\n因此可將物理攻擊型的敵軍給一網打盡。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101301, '七七香', '七七香', 101301, 0, 0, 2, 7, 7, 450, 740, 2, 2.07, 101301, 0, 101301, 0, 9, 0, '【魔法】從後衛位置施放多采多姿的魔法，是一名魔法宅少女。\\n針對體力最多的對象所使用的必殺技或技能，\\n是會依敵人的 HP 狀況改變攻擊目標的高威力魔法攻擊。', 0, '2019/03/27 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101401, '霞', '霞', 101401, 0, 0, 3, 21, 7, 450, 730, 2, 2.27, 101401, 0, 101401, 0, 9, 0, '【魔法】位於後衛，特別強化於妨礙對手行動的名偵探。\\n能以造成束縛與混亂的技能來使對手的行動遭受妨礙，\\n並展開降低魔法防禦力的領域來解決對手。', 0, '2019/07/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101501, '美里', '美里', 101501, 0, 0, 2, 9, 7, 450, 735, 2, 2.27, 101501, 0, 101501, 0, 9, 0, '【魔法】位於後衛支援使用魔法的角色，是大家的老師。\\n負責必殺技的 HP 恢復及提升魔法防禦，持續恢復前方角色 HP，大幅提升己方的持久度。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101601, '鈴奈', '鈴奈', 101601, 0, 0, 2, 8, 8, 450, 705, 1, 1.97, 101601, 0, 101601, 0, 15, 0, '【物理】會從後衛進行暴擊攻擊的超級名模。\\n暴擊的傷害不僅能使傷害增倍，\\n再加上還能靠技能提升攻擊力，使破壞力更甚。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101701, '香織', '香織', 101701, 0, 0, 2, 1, 1, 450, 145, 1, 2.17, 101701, 0, 101701, 0, 4, 0, '【物理】在最前線利用累積技能賺傷害的南國少女。\\n一旦發動「精神統一」技能，香織的攻擊會隨次數\\n對敵人累積效果，後續的攻擊會逐漸增加。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (101801, '伊緒', '伊緒', 101801, 0, 0, 3, 5, 5, 450, 715, 2, 2.6, 101801, 0, 101801, 0, 15, 0, '【魔法】在後方誘惑全體敵人的性感老師。\\n以奪取敵人ＴＰ技能使其必殺技延遲發動，\\n或施放誘惑的必殺技，使戰場陷入大混亂。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102001, '美美', '美美', 102001, 0, 0, 2, 5, 5, 450, 360, 1, 2.125, 102001, 0, 102001, 0, 13, 0, '【物理】位於中衛，崩毀敵方前衛，是一隻小兔兔女孩。\\n必殺技為，在給予離自己最近的敵人大幅傷害後，\\n能順便攻擊到該對象旁邊的敵軍，能有效早期攻略敵人的前衛。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102101, '胡桃', '胡桃', 102101, 0, 0, 1, 3, 3, 450, 240, 1, 1.675, 102101, 0, 102101, 0, 3, 0, '【物理】位於前衛，能守護己方，是一名極度害羞的女孩子。\\n技能能提高自己周圍的物理、魔法防禦力，\\n並且能使接近的敵方暈眩難以靠近。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102201, '依里', '依里', 102201, 0, 0, 1, 6, 6, 450, 575, 2, 2.19, 102201, 0, 102201, 0, 14, 0, '【魔法】位於中衛，發散強力的魔法，是雙胞胎中的姊姊。\\n可以犧牲自身的 HP，來增強魔法攻擊力，\\n能使得必殺技與技能的威力更上一層樓。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102301, '綾音', '綾音', 102301, 0, 0, 2, 3, 3, 450, 210, 1, 1.425, 102301, 0, 102301, 0, 3, 0, '【物理】位於前衛，能使戰場混亂，是一個熊孩子。\\n在以必殺技將對手吹飛的同時，以暈眩及給予大幅度的傷害使敵方陣形大亂。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102501, '鈴莓', '鈴莓', 102501, 0, 0, 1, 7, 7, 450, 720, 2, 2.27, 102501, 0, 102501, 0, 3, 0, '【魔法】位於後衛，同時擅長恢復擊攻擊的冒失女僕。\\n可一邊恢復己方 HP 一邊提高物理攻擊力的技能，適合與負責攻擊的中衛、後衛搭配。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102601, '鈴', '鈴', 102601, 0, 0, 2, 6, 6, 450, 550, 1, 2.34, 102601, 0, 102601, 0, 3, 0, '【物理】中衛，能加強物理攻擊的怕麻煩的少女。\\n能同時提升己方的物理攻擊力及魔法防禦，能同時支援隊伍的攻守兩方。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102701, '惠理子', '惠理子', 102701, 0, 0, 2, 3, 3, 450, 230, 1, 1.425, 102701, 0, 102701, 0, 11, 0, '【物理】位於前衛，以毒技能粉碎敵人，通稱破壞者。\\n擁有最頂級的攻擊力，用必殺技給予敵人致命一擊時，\\n攻擊力會一次次地向上提升。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102801, '咲戀', '咲戀', 102801, 0, 0, 3, 4, 4, 450, 430, 1, 2.09, 102801, 0, 102801, 0, 3, 0, '【物理】位於中衛，能瞬間翻轉局勢，大家的咲戀媽媽。\\n有著ＨＰ越低，傷害力就越高的必殺技，\\n和可以恢復己方的技能，一口氣逆轉局勢。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (102901, '望', '望', 102901, 0, 0, 3, 4, 4, 450, 160, 1, 1.965, 102901, 0, 102901, 0, 10, 0, '【物理】位於前衛，成為大家的防護的偶像望望。\\n當她立於舞台上時，能夠大大提升己方的物理攻擊力，\\n並且還可以一人擔下敵方的注意。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103001, '妮諾', '妮諾', 103001, 0, 0, 3, 5, 5, 450, 415, 1, 2.25, 103001, 0, 103001, 0, 12, 0, '【物理】位於中衛，能反覆操作強力的範圍攻擊，是擅用忍法的熟手。\\n當範圍攻擊的技能將敵人擊敗時，可大幅地累積 TP，\\n發動強力必殺技的頻率也隨之提高。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103101, '忍', '忍', 103101, 0, 0, 2, 5, 5, 450, 365, 1, 2.25, 103101, 0, 103101, 0, 14, 0, '【物理】位於中衛，能召喚父親的骷髏頭戰鬥的通靈少女。\\n召喚來的父親除了能和自己分開攻擊之外，\\n還能夠替自己抵擋傷害。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103201, '秋乃', '秋乃', 103201, 0, 0, 3, 5, 5, 450, 180, 1, 2.125, 103201, 0, 103201, 0, 8, 0, '【物理】位於前衛，能大鬧攻擊，是一名千金小姐劍士。\\n能夠在自身周遭創造出能使夥伴恢復的領域。而挺身佇立前線的無畏身影，更不負身為名家之名。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103301, '真陽', '真陽', 103301, 0, 0, 2, 6, 6, 450, 395, 1, 2.34, 103301, 0, 103301, 0, 5, 0, '【物理】位於中衛，可擾亂敵人動作的牧場主人。\\n能將敵方的前衛擊退，進而崩壞敵方陣形，逼迫其他敵人成為對象攻擊範圍。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103401, '優花梨', '優花梨', 103401, 0, 0, 1, 5, 5, 450, 405, 1, 2.4, 103401, 0, 103401, 0, 8, 0, '【物理】位於中衛，能以屏障和恢復守護己方的曾經的聖戰士。\\n能夠操作受傷的己方角色恢復 HP 與 TP 的技能，進而在你來我往的消耗戰中，給出相當關鍵的支援。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103601, '鏡華', '鏡華', 103601, 0, 0, 3, 7, 7, 450, 810, 2, 2.07, 103601, 0, 103601, 0, 0, 0, '【魔法】位於後衛，能施以強力魔法攻擊的小小優等生。\\n藉由技能，能瞬間提高魔法攻擊力後施放必殺技，\\n對敵方單體的威力卓越。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103701, '智', '智', 103701, 0, 0, 3, 4, 4, 450, 220, 1, 1.965, 103701, 0, 103701, 0, 2, 0, '【物理】在前線施展快速斬擊屠殺敵人的劍術少女。\\n必殺技則是使自己的攻擊力和行動速度上升，\\n將其他攻擊技能的殲滅能力提高到極限來輾壓敵人。', 0, '2019/01/09 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (103801, '栞', '栞', 103801, 0, 0, 2, 8, 8, 450, 710, 1, 1.97, 103801, 0, 103801, 0, 5, 0, '【物理】從最後方連續施展高火力技能的狙擊手。\\n擁有造成傷害後接著恢復ＴＰ的技能，\\n可以快速發動強力的必殺技。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104001, '碧', '碧', 104001, 0, 0, 1, 8, 8, 450, 785, 1, 1.97, 104001, 0, 104001, 0, 9, 0, '【物理】從最後方射出異常狀態之箭，孤獨的射手。\\n透過和傷害共同施加的毒或麻痺等追加效果，\\n就算是防御力高的敵人也能確實地削減其ＨＰ。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104201, '千歌', '千歌', 104201, 0, 0, 2, 7, 7, 450, 790, 2, 2.27, 104201, 0, 104201, 0, 10, 0, '【魔法】位於後衛，能支持前衛戰鬥的唱喚士。\\n能以必殺技，唱喚能幫助夥伴恢復，\\n以及攻擊敵人的精靈到最前線。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104301, '真琴', '真琴', 104301, 0, 0, 3, 5, 5, 450, 165, 1, 2.125, 104301, 0, 104301, 0, 4, 0, '【物理】位於前衛，能削弱對手的防禦力，是一名正義的獸人。\\n能夠降低敵方的防禦力，並且確確實實地給予傷害，\\n以便盡早突破敵方防守。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104401, '伊莉亞', '伊莉亞', 104401, 0, 0, 3, 3, 3, 450, 425, 2, 1.425, 104401, 0, 104401, 0, 12, 0, '【魔法】位於中衛，削弱生命來攻擊的「統御黑夜之人」。\\n以自己 HP 交換而發出的技能擁有高度殲滅能力，範圍攻擊的威力無人能及。', 0, '2018/10/22 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104501, '空花', '空花', 104501, 0, 0, 2, 2, 2, 450, 130, 1, 2.375, 104501, 0, 104501, 0, 12, 0, '【物理】在前方擔任誘餌，吸收攻擊後瘋狂爆發的超Ｍ少女。\\n透過必殺技對自己張開魔法屏障，\\n吸收敵人的魔法攻擊來恢復ＨＰ。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104601, '珠希', '珠希', 104601, 0, 0, 2, 2, 2, 450, 215, 1, 2.25, 104601, 0, 104601, 0, 8, 0, '【物理】位於前衛，擅長魔法隊伍的隨心所欲的貓娘。\\n專門攻擊「魔法攻擊力最高」的敵人，\\n以降低傷害及魔法攻擊力，並吸收 TP。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104701, '純', '純', 104701, 0, 0, 3, 5, 5, 450, 135, 1, 2.125, 104701, 0, 104701, 0, 2, 0, '【物理】在最前方施展完全防禦技能的騎士團長。\\n必殺技則可展開任何傷害都能吸收的屏障。\\n尤其以長期戰中的壓倒性防禦能力自豪。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104801, '美冬', '美冬', 104801, 0, 0, 2, 6, 6, 450, 420, 1, 2.19, 104801, 0, 104801, 0, 8, 0, '【物理】位於中衛，擁有暈眩與恢復的技能的傭兵戰士。\\n擁有高度防禦能力及自我恢復能力，\\n能使中衛的防守更為堅固，支撐整個隊伍。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (104901, '靜流', '靜流', 104901, 0, 0, 3, 5, 5, 450, 285, 1, 2.375, 104901, 0, 104901, 0, 6, 0, '【物理】位於前衛，可守護己方免於物理攻擊，\\n是一位姊姊。\\n一般技能可以展開物理無效的屏障，不僅能夠保護同伴全員\\n還能再於自身添加物理吸收屏障，進而挺身討伐敵方前衛。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105001, '美咲', '美咲', 105001, 0, 0, 1, 7, 7, 450, 760, 2, 2.07, 105001, 0, 105001, 0, 15, 0, '【魔法】位於後衛，能施放範圍攻擊的淑女小魔女。\\n擁有能多次範圍攻擊的技能，\\n以及能攻擊敵方全體的必殺技，擅長範圍殲滅。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105101, '深月', '深月', 105101, 0, 0, 2, 5, 5, 450, 565, 1, 2.25, 105101, 0, 105101, 0, 11, 0, '【物理】位於中衛，能弱化敵方領域，是一位獨眼惡魔。\\n領域的持續時間雖然不長，\\n但卻能大幅度減少範圍內的敵方之物理防禦力。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105201, '莉瑪', '莉瑪', 105201, 0, 0, 1, 4, 4, 450, 105, 1, 2.215, 105201, 0, 105201, 0, 5, 0, '【物理】在最前方守護夥伴的戀愛少女。\\n戰鬥開始後不久就會朝最前線突進，\\n挺身擋在敵人眼前。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105301, '莫妮卡', '莫妮卡', 105301, 0, 0, 3, 4, 4, 450, 410, 1, 2.24, 105301, 0, 105301, 0, 12, 0, '【物理】位於中衛，能鼓舞己方進攻，是一位女軍人。\\n不僅能大幅度增加全體的物理、魔法攻擊力，\\n就連攻擊速度都能提升，使戰鬥得以盡早結束。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105401, '紡希', '紡希', 105401, 0, 0, 2, 1, 1, 450, 195, 1, 2.42, 105401, 0, 105401, 0, 10, 0, '【物理】位於前衛，特化妨礙敵人技能的裁縫師女孩。\\n除了可強制移動敵人使敵方陣形瓦解，\\n還可降低對手的移動速度、行動速度，使其反擊變得遲緩。', 0, '2018/11/08 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105501, '步未', '步未', 105501, 0, 0, 1, 48, 48, 450, 510, 1, 2.34, 105501, 0, 105501, 0, 12, 0, '【物理】位於中衛，竄來竄去地對敵人來干擾的純情少女。\\n除了能造成敵人暈眩的技能以外，必殺技更是能夠使對方\\n造成黑暗與混亂狀態，進而來造成敵人的麻煩。', 0, '2019/07/26 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105601, '流夏', '流夏', 105601, 0, 0, 3, 4, 4, 450, 158, 1, 1.965, 105601, 0, 105601, 0, 2, 0, '【物理】在前方為夥伴揮劍的豪氣大姊。\\n擁有能夠反擊對手的挑釁招數，\\n降低敵人防禦力的必殺技，在攻防兩面都能發揮實力。', 0, '2019/03/13 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105701, '吉塔', '吉塔', 105701, 0, 0, 3, 4, 4, 450, 245, 1, 1.965, 105701, 0, 170101, 0, 4, 0, '【物理】位於前衛，可先發制人施展大招的騎空士。\\n藉由技能可回復ＴＰ，加快必殺技的發動。\\n從戰鬥初期就能對敵方前衛造成大傷害。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105801, '貪吃佩可', '貪吃佩可', 105801, 0, 0, 1, 5, 5, 450, 155, 1, 2.25, 105801, 0, 105801, 0, 1, 0, '【物理】位於前衛，全力保護同伴，是一位貪吃的劍士。\\n能夠增加 HP，也能恢復自己的 HP，\\n擔任己方的保護牆的角色。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (105901, '可可蘿', '可可蘿', 105901, 0, 0, 1, 6, 6, 450, 500, 1, 2.34, 105901, 0, 105901, 0, 1, 0, '【物理】位於中衛，無論是輔助還是攻擊皆能支援，是一位負責指引迷津的巫女。\\n必殺技能增加己方全體的攻擊力，\\n且有使自己恢復的功能，因此能持續支援。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (106001, '凱留', '凱留', 106001, 0, 0, 1, 7, 7, 450, 750, 2, 2.07, 106001, 0, 106001, 0, 1, 0, '【魔法】位於後衛，能施以強力的全體攻擊，是一隻頑強的貓娘。\\n技能可以使敵人的物理、魔法防禦力雙雙減弱，\\n對於綜合編成隊伍的戰鬥中有著奇效。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (106101, '矛依未', '矛依未', 106101, 0, 1, 3, 2, 2, 450, 162, 1, 1.8, 106101, 0, 106101, 0, 7, 0, '【物理】位於前衛，揮舞著傳說之劍「天樓霸斷劍」的少女。\\n使著那輕攻擊的通常戰鬥風格於轉眼之間，在必殺技發動\\n後所執起的那把「天樓霸斷劍」，能將敵人一掃而空。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
insert into unit_data values (106301, '亞里莎', '亞里莎', 106301, 0, 0, 3, 8, 8, 450, 625, 1, 1.97, 106301, 0, 106301, 0, 1, 0, '【物理】位於後衛，擅長長期戰的森林守護者見習生。\\n能藉由恢復 TP 迅速發動必殺技。\\n並且由於使用後技能效果會上升，具有高度的殲滅能力。', 0, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (106401, '雪菲', '雪菲', 106401, 0, 0, 3, 4, 4, 450, 368, 1, 1.84, 106401, 0, 106401, 0, 1, 0, '【物理】位於中衛，操縱冰雪冰凍消滅敵人的冰龍少女。\\n消耗發動必殺技所獲得的【冰龍之印】，\\n增幅技能的效果，以清廉的劍技斬殺敵人。', 0, '2021/06/19 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (106501, '嘉夜', '嘉夜', 106501, 0, 0, 3, 1, 1, 450, 168, 1, 2.17, 106501, 0, 106501, 0, 17, 0, '【物理】位於前衛，用拳頭痛毆敵人的龍人族打手。\\n先以技能提升自己的物理攻擊力，再全神貫注所釋放\\n而出的第一發必殺技，擁有著引以為傲的超大破壞力。', 0, '2020/03/19 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (106601, '祈梨', '祈梨', 106601, 0, 0, 3, 3, 3, 450, 197, 1, 1.55, 106601, 0, 106601, 0, 17, 0, '【物理】位於前衛，擅長範圍攻擊與嘴上功夫的龍人少女。\\n必殺技能夠將範圍內的敵人一同擊飛，\\n打亂敵人陣型後將敵人一口氣燒之殆盡。', 0, '2020/09/16 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (106701, '帆稀', '帆稀', 106701, 0, 1, 3, 21, 7, 450, 727, 2, 2.27, 106701, 0, 106701, 0, 17, 0, '【魔法】位於後衛，以無窮魔力支配一切的龍之首領。\\n能以龍眼掌握阻擋的敵人，使用多樣的技能來降低敵人\\n對魔法攻擊的耐力，並使出雷霆，一擊將敵人化為灰燼。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
insert into unit_data values (106801, '拉比林斯達', '拉比林斯達', 106801, 0, 1, 3, 4, 4, 450, 560, 1, 1.55, 106801, 0, 106801, 0, 6, 0, '【物理】位於中衛，能自在創造萬物的「迷宮女王」。\\n解放七冠權能的必殺技能夠極大幅度強化自身，\\n釋放無以比擬的攻擊力殲滅敵對之人。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
insert into unit_data values (106901, '霸瞳皇帝', '霸瞳皇帝', 106901, 0, 1, 3, 0, 45, 450, 660, 1, 2.0, 106901, 0, 106901, 0, 1, 0, '【物理】位於後衛，眼裡寄宿著天上明星，朝霸道前進的\\n霸皇。能解放權能來解除敵方全體的護盾與所有能力提升\\n效果，然後以降低耐力的技能、涵蓋複數敵人的\\n廣範圍攻擊來殲滅敵方。', 0, '2023/03/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (107001, '似似花', '似似花', 107001, 0, 1, 3, 0, 0, 450, 660, 2, 2.0, 107001, 0, 107001, 0, 1, 0, '【魔法】位於後衛，創造分身來凌駕敵人的變貌大妃。\\n分身將施展出與似似花本人相同的技能對敵人進行攻擊。\\n那攻擊次數的密度與壓制力的高度，使人望塵莫及。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
insert into unit_data values (107101, '克莉絲提娜', '克莉絲提娜', 107101, 0, 1, 3, 10, 4, 450, 290, 1, 2.0, 107101, 0, 107101, 0, 2, 0, '【物理】在前方擁有絕對壓制力的『誓約女君』。\\n同時擁有攻防一體超大破壞力的必殺技，\\n以及可將其加速發動的ＴＰ上升技能。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
insert into unit_data values (107501, '貪吃佩可（夏日）', '貪吃佩可（夏日）', 107501, 0, 1, 3, 5, 5, 450, 235, 1, 2.125, 107501, 0, 107501, 0, 1, 0, '【物理】位於前衛，可施展強烈的範圍攻擊，是海岸邊的公主。\\n吃了刨冰一口氣提升物理攻擊力後，\\n可施展必殺技消滅前方的敵人。', 0, '2023/11/26 16:00:00', '2023/12/01 15:59:59', 0);
insert into unit_data values (107601, '可可蘿（夏日）', '可可蘿（夏日）', 107601, 0, 1, 1, 6, 6, 450, 535, 1, 2.34, 107601, 0, 107601, 0, 1, 0, '【物理】位於中衛，是位可為物理角色進行輔助的治癒巫女。\\n擁有為ＨＰ最少的我方單體進行特大幅度恢復的技能，\\n可瞬間治癒我方瀕死的角色。', 0, '2018/12/03 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (107701, '鈴莓（夏日）', '鈴莓（夏日）', 107701, 0, 1, 3, 7, 7, 450, 775, 2, 2.07, 107701, 0, 107701, 0, 3, 0, '【魔法】位於後衛擁有優秀的防衛能力，夏天的冒失女僕。\\n除可提升我方全體的防禦力及施加持續恢復HP的狀態外，\\n更可呼叫如城牆般的魔像，替戰線帶來支援。', 0, '2023/12/23 16:00:00', '2023/12/27 15:59:59', 0);
insert into unit_data values (107801, '凱留（夏日）', '凱留（夏日）', 107801, 0, 1, 3, 7, 7, 450, 780, 2, 1.92, 107801, 0, 107801, 0, 1, 0, '【魔法】位於後衛，可施展強力單體攻擊的盛夏貓娘。\\n以技能降低敵人的魔法防禦力後，\\n不斷對敵方單體施放魔法攻擊，能夠帶來大量的傷害。', 0, '2023/11/26 16:00:00', '2023/12/01 15:59:59', 0);
insert into unit_data values (107901, '珠希（夏日）', '珠希（夏日）', 107901, 0, 1, 3, 2, 2, 450, 225, 1, 2.0, 107901, 0, 107901, 0, 8, 0, '【物理】位於前衛施放連續攻擊，是海邊的隨性貓娘。\\n可隨機對敵方造成總計４次單體攻擊的必殺技，\\n雖然目標是隨機的但威力卻相當出色。', 0, '2023/12/23 16:00:00', '2023/12/27 15:59:59', 0);
insert into unit_data values (108001, '美冬（夏日）', '美冬（夏日）', 108001, 0, 1, 1, 6, 6, 450, 495, 1, 2.19, 108001, 0, 108001, 0, 8, 0, '【物理】位於中衛，熟稔攻擊與輔助的海之傭兵。\\n必殺技的效果能夠提升我方全體的物理攻擊力，\\n在物理攻擊隊伍的能力提升上可出一份力。', 0, '2019/01/03 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (108101, '忍（萬聖節）', '忍（萬聖節）', 108101, 0, 1, 3, 3, 3, 450, 440, 1, 1.55, 108101, 0, 108101, 0, 0, 0, '【物理】在中線獵取虛弱敵人的幻夜靈力少女。\\n必殺技會對一名減弱的敵人造成大量傷害，\\n打倒對象時還會對敵人全體造成追加傷害。', 0, '2024/02/26 16:00:00', '2024/02/29 15:59:59', 0);
insert into unit_data values (108201, '宮子（萬聖節）', '宮子（萬聖節）', 108201, 0, 1, 1, 1, 1, 450, 590, 1, 2.42, 108201, 0, 108201, 0, 0, 0, '【物理】從中線狙擊敵陣後方的幽靈狼少女。\\n能無視前方對手，直接對敵陣後方施放必殺技或技能，\\n對防禦力低的角色特別有威脅性。', 0, '2019/03/25 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (108301, '美咲（萬聖節）', '美咲（萬聖節）', 108301, 0, 1, 3, 7, 7, 450, 815, 2, 2.07, 108301, 0, 108301, 0, 15, 0, '【魔法】在後方施展妨礙技能的魔法少女。\\n使對手陷入誘惑狀態的技能或同時施加複數\\n異常狀態的必殺技都能擾亂對手陣腳。', 0, '2024/02/26 16:00:00', '2024/02/29 15:59:59', 0);
insert into unit_data values (108401, '千歌（聖誕節）', '千歌（聖誕節）', 108401, 0, 1, 3, 7, 7, 450, 770, 2, 2.27, 108401, 0, 108401, 0, 10, 0, '【魔法】位於後衛，唱喚出精靈作戰的雪花歌姬。\\n使用必殺技唱喚出的精靈們具備恢復 HP 或 TP 的能力，\\n能夠逆轉不利的戰況。', 0, '2024/04/23 16:00:00', '2024/04/28 15:59:59', 0);
insert into unit_data values (108501, '胡桃（聖誕節）', '胡桃（聖誕節）', 108501, 0, 1, 1, 3, 3, 450, 295, 1, 1.55, 108501, 0, 108501, 0, 15, 0, '【物理】位於前衛，以鈴聲守護夥伴的嬌小女伶。\\n具備在受到攻擊前使周圍夥伴的防禦力大量提升的技能。\\n只要能夠活用就堪稱具備無與倫比的耐久力。', 0, '2019/05/16 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (108601, '綾音（聖誕節）', '綾音（聖誕節）', 108601, 0, 1, 3, 3, 3, 450, 190, 1, 1.425, 108601, 0, 108601, 0, 10, 0, '【物理】位於前衛，將敵人掃除的聖誕夜小熊女孩。\\n必殺技雖會消耗 HP，但威力絕大。\\n使用技能提升物理攻擊力，並且使傷害上升。', 0, '2024/04/23 16:00:00', '2024/04/28 15:59:59', 0);
insert into unit_data values (108701, '日和（新年）', '日和（新年）', 108701, 0, 1, 3, 1, 1, 450, 170, 1, 2.17, 108701, 0, 108701, 0, 7, 0, '【物理】位於前衛，負責物理的火力輸出，穿著振袖和服的活力女孩。\\n使用讓物理攻擊角色攻擊力提升的技能。\\n單體必殺技威力極高，其攻擊力名列前茅。', 0, '2024/07/12 16:00:00', '2024/07/19 15:59:59', 0);
insert into unit_data values (108801, '優衣（新年）', '優衣（新年）', 108801, 0, 1, 3, 21, 7, 450, 745, 2, 2.27, 108801, 0, 108801, 0, 7, 0, '【魔法】位於後衛，以輔助魔法強化，光鮮亮麗的治療者。\\n以自己的攻擊力換取強力的屏障與施加在全體夥伴\\n的持續恢復狀態。絕大的防禦能力傲視群倫。', 0, '2024/07/12 16:00:00', '2024/07/19 15:59:59', 0);
insert into unit_data values (108901, '怜（新年）', '怜（新年）', 108901, 0, 1, 1, 22, 4, 450, 153, 1, 2.215, 108901, 0, 108901, 0, 7, 0, '【物理】位於前衛，為夥伴擋下魔法攻擊的華麗劍士。\\n使用技能對範圍內的夥伴展開魔法無效的屏障，\\n而且以必殺技中斷敵人攻擊並使其暈眩。', 0, '2019/06/17 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109001, '惠理子（情人節）', '惠理子（情人節）', 109001, 0, 1, 1, 3, 3, 450, 187, 1, 1.9, 109001, 0, 109001, 0, 11, 0, '【物理】位於前衛，是能夠將危機轉變為契機的愛之破壞者。\\n消耗 HP 來使用技能，以提升自身的攻擊力。並且，\\n必殺技在 HP 越低時，更能夠發揮超絕的威力。', 0, '2019/07/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109101, '靜流（情人節）', '靜流（情人節）', 109101, 0, 1, 3, 5, 5, 450, 385, 1, 2.125, 109101, 0, 109101, 0, 7, 0, '【物理】位於中衛，出手幫助大家的甜點師姐姐。\\n能夠展開同時上升多種數值的領域，並且在效果發動時，\\n還能夠大幅度的強化技能的效果。', 0, '2024/05/24 16:00:00', '2024/05/28 15:59:59', 0);
insert into unit_data values (109201, '安', '安', 109201, 0, 0, 3, 23, 7, 450, 630, 2, 2.27, 109201, 0, 109201, 0, 6, 0, '【魔法】位於後衛以英靈召喚術為武器來戰鬥的公主。\\n同時能給予友方英靈之加護，也能夠施展強大威力的魔法，\\n因此可以同時勝任輔助與主力輸出的兩種定位。', 0, '2019/08/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109301, '露', '露', 109301, 0, 1, 1, 23, 7, 450, 640, 2, 2.27, 109301, 0, 109301, 0, 9, 0, '【魔法】位於後衛，和小眼球並肩作戰，天真無邪的少女。\\n以技能所叫出來的小眼球，除了能防禦敵人的攻擊以外，\\n當小眼球的數量越多時，必殺技的威力也會提升。', 0, '2019/08/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109401, '古蕾婭', '古蕾婭', 109401, 0, 0, 3, 1, 1, 450, 525, 2, 2.27, 109401, 0, 109401, 0, 9, 0, '【魔法】位於中衛，操使火炎將敵人剿滅，龍人之少女。\\n相當的擅長活用那強大魔法攻擊力來進行範圍攻擊。\\n並且，利用飛行能力所進行的突進攻擊也相當強力。', 0, '2019/08/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109501, '空花（大江戶）', '空花（大江戶）', 109501, 0, 0, 3, 2, 7, 450, 140, 2, 2.066, 109501, 0, 109501, 0, 12, 0, '【魔法】位於最前衛，以和服之姿鼓舞夥伴的受虐狂妹子。\\n透過提升夥伴的魔法攻擊力，降低敵方的魔法防禦力，\\n作為魔法隊伍的支援兼防禦角色而活躍。', 0, '2019/09/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109601, '妮諾（大江戶）', '妮諾（大江戶）', 109601, 0, 0, 3, 2, 2, 450, 175, 1, 2.0, 109601, 0, 109601, 0, 12, 0, '【物理】位於最前衛，運用忍法與敵人戰鬥的女忍者。\\n必殺技擁有在暴擊時將傷害變成 1.5 倍的特性，\\n時機拿捏好時威力十分驚人。', 0, '2019/09/20 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109701, '雷姆', '雷姆', 109701, 0, 1, 3, 24, 13, 450, 540, 1, 2.25, 109701, 0, 109701, 0, 9, 0, '【物理】位於中衛，以鬼之力粉碎敵人，雙胞胎的妹妹。\\n除了恢復技能，也擅長操控溫度的技能，\\n能將敵人凍結之後使用必殺技殲滅。', 0, '2021/04/16 16:00:00', '2021/04/27 15:59:59', 0);
insert into unit_data values (109801, '拉姆', '拉姆', 109801, 0, 1, 1, 24, 7, 450, 545, 2, 2.5, 109801, 0, 109801, 0, 9, 0, '【魔法】位於中衛，以特殊能力協助夥伴，雙胞胎的姊姊。\\n擁有看穿對手的物理攻擊並迴避的「千里眼」，\\n和束縛所有敵人的必殺技輔助，效果十分強大。', 1, '2019/10/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (109901, '愛蜜莉雅', '愛蜜莉雅', 109901, 0, 1, 3, 23, 7, 450, 725, 2, 2.27, 109901, 0, 109901, 0, 9, 0, '【魔法】位於後衛，對於攻擊和恢復都運用自如的半精靈族。\\n能以強大的魔力反覆施放攻擊魔法將敵人擊飛，\\n必殺技是連瀕死的夥伴都能在一瞬間治癒的能力。', 0, '2021/04/16 16:00:00', '2021/04/27 15:59:59', 0);
insert into unit_data values (110001, '鈴奈（夏日）', '鈴奈（夏日）', 110001, 0, 1, 3, 8, 8, 450, 705, 1, 1.82, 110001, 0, 110001, 0, 15, 0, '【物理】位於後衛，能使用高威力弓技的夏之超級名模。\\n在每場戰鬥中第一次施放「夏日伸展台♪」時，\\n能完全恢復ＴＰ，而之後每次的必殺技都將會發動暴擊。', 0, '2023/11/26 16:00:00', '2023/12/01 15:59:59', 0);
insert into unit_data values (110101, '伊緒（夏日）', '伊緒（夏日）', 110101, 0, 1, 1, 5, 5, 450, 715, 2, 2.4, 110101, 0, 110101, 0, 15, 0, '【魔法】位於後衛，能奪取敵人體力，是夏季夜晚的老師。\\n擁有多項能夠在造成傷害的同時恢復夥伴們的技能，\\n不管位於攻守方都能夠發揮優異的能力。', 0, '2019/11/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (110201, '美咲（夏日）', '美咲（夏日）', 110201, 0, 0, 1, 7, 7, 0, 0, 0, 0.0, 0, 0, 0, 0, 15, 0, '', 0, '2019/11/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (110301, '咲戀（夏日）', '咲戀（夏日）', 110301, 0, 1, 3, 4, 4, 450, 585, 1, 2.24, 110301, 0, 110301, 0, 3, 0, '【物理】位於中衛，能夠支援夥伴速攻，是海邊的咲戀媽媽。\\n在戰鬥開始時可以一口氣補充全體夥伴的TP，\\n也因此使得能夠更加迅速的發動必殺技。', 0, '2023/11/26 16:00:00', '2023/12/01 15:59:59', 0);
insert into unit_data values (110401, '真琴（夏日）', '真琴（夏日）', 110401, 0, 1, 3, 5, 5, 450, 180, 1, 2.125, 110401, 0, 110401, 0, 4, 0, '【物理】位於前衛，擅長攻擊單一目標的海之狼女。\\n當僅有一個攻擊目標時，才能夠發揮技能與必殺技的\\n真正價值，並以絕大的破壞力斬除敵人。', 0, '2023/12/23 16:00:00', '2023/12/27 15:59:59', 0);
insert into unit_data values (110501, '香織（夏日）', '香織（夏日）', 110501, 0, 1, 1, 1, 1, 450, 425, 1, 2.42, 110501, 0, 110501, 0, 4, 0, '【物理】位於中衛，能將敵人一網打盡，是盛夏的南國女孩。\\n能以帶有擊飛效果的技能將敵人聚集起來，並且以攻擊目標\\n越多時，越能夠增加威力的必殺技將敵人一掃而空。', 0, '2019/12/16 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (110601, '真步（夏日）', '真步（夏日）', 110601, 0, 1, 3, 7, 7, 450, 792, 2, 2.27, 110601, 0, 110601, 0, 4, 0, '【魔法】位於後衛，施展著龐大的魔力，是盛夏的童話公主。\\n以高火力對隨機的目標進行攻擊的技能與必殺技，\\n當攻擊目標僅有一人時，能夠造成絕大的傷害。', 0, '2023/12/23 16:00:00', '2023/12/27 15:59:59', 0);
insert into unit_data values (110701, '碧（插班生）', '碧（插班生）', 110701, 0, 0, 3, 8, 8, 450, 680, 1, 1.82, 110701, 0, 110701, 0, 4, 0, '【物理】位於後衛，擅於用毒，一名邊緣人精靈族的插班生。\\n具有當敵人處於中毒或猛毒狀態時獲得強化的攻擊技能，\\n同時還能以降低敵人物理防禦力的技能來幫助夥伴們。', 0, '2020/01/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (110801, '克蘿依', '克蘿依', 110801, 0, 0, 3, 2, 2, 450, 185, 1, 2.125, 110801, 0, 110801, 0, 2, 0, '【物理】位於前衛，靠著攻擊數壓制，一名陰暗系的精靈族。\\n每當進行攻擊時，便會向對手追加【畏懼】的狀態，\\n使技能的傷害和降低物理防禦力的能力更上一層樓。', 0, '2020/01/10 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (110901, '琪愛兒', '琪愛兒', 110901, 0, 0, 3, 1, 1, 450, 222, 1, 2.295, 110901, 0, 110901, 0, 2, 0, '【物理】位於前衛，用拳頭釋放切嚕嗨到Max的女孩子。\\n每當造成暴擊時將會累積【切嚕】，不但能持續強化自己，\\n當戰鬥越到後半時，就越能用切嚕把敵人全部滅光光。', 0, '2020/07/20 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (111001, '優妮', '優妮', 111001, 0, 0, 3, 26, 7, 450, 807, 2, 2.27, 111001, 0, 111001, 0, 2, 0, '【魔法】位於後衛，是特化於支援夥伴的象牙之塔才女。\\n儘管自身的攻擊力不高，但能強化物理攻擊力高的夥伴，\\n還能以魔法無效屏障守護全體夥伴，帶領眾人迎向勝利。', 0, '2020/07/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (111101, '鏡華（萬聖節）', '鏡華（萬聖節）', 111101, 0, 1, 3, 7, 7, 450, 820, 2, 2.07, 111101, 0, 111101, 0, 13, 0, '【魔法】位於後衛，為夥伴提升火力，變裝黑貓的資優生。\\n不僅可以依自己的魔法攻擊力多寡來增減強化的技能，\\n效果還能進行累積，能以多樣的技能來提升攻擊力。', 0, '2024/02/26 16:00:00', '2024/02/29 15:59:59', 0);
insert into unit_data values (111201, '禊（萬聖節）', '禊（萬聖節）', 111201, 0, 1, 1, 1, 1, 450, 212, 1, 2.17, 111201, 0, 111201, 0, 13, 0, '【物理】位於前衛，施展著範圍攻擊，南瓜裝扮的搗蛋鬼。\\n不管是對敵人設下倒數5次後就會爆炸的炸彈，又或丟丟\\n那南瓜手榴彈，靠著各式各樣的範圍攻擊來玩弄對手。', 0, '2020/02/04 16:00:00', '2020/02/18 15:59:59', 0);
insert into unit_data values (111301, '美美（萬聖節）', '美美（萬聖節）', 111301, 0, 0, 3, 5, 5, 450, 365, 1, 2.0, 111301, 0, 111301, 0, 13, 0, '【物理】位於中衛，對攻擊進行了特化的繃帶兔兔娘。\\n儘管攻擊的頻率較低，但能夠施展高威力的範圍攻擊，\\n大幅提升夥伴們物理攻擊力的技能，效果也相當優異。', 0, '2020/02/19 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (111401, '露娜', '露娜', 111401, 0, 0, 3, 25, 7, 450, 765, 2, 1.87, 111401, 0, 111401, 0, 0, 0, '【魔法】位於後衛，以死靈術來製造朋友的可愛少女。\\n每當露娜造成傷害時便能聚集朋友，而將它們做為各個\\n技能的消耗時，更能依消耗數來發揮出更強大的力量。', 0, '2020/03/04 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (111501, '克莉絲提娜（聖誕節）', '克莉絲提娜（聖誕節）', 111501, 0, 1, 3, 10, 4, 450, 265, 1, 2.0, 111501, 0, 111501, 0, 13, 0, '【物理】位於前衛，享受著戰鬥、聖夜的『誓約女君』。\\n使用必殺技將會獲得硬幣，依照其有無則能在輔助與攻擊\\n兩種定位進行切換，同時華麗地斬殺敵人。', 0, '2024/04/23 16:00:00', '2024/04/28 15:59:59', 0);
insert into unit_data values (111601, '望（聖誕節）', '望（聖誕節）', 111601, 0, 1, 1, 4, 4, 450, 418, 1, 2.24, 111601, 0, 111601, 0, 13, 0, '【物理】位於中衛，挺身而出守護著整個隊伍的聖誕偶像。\\n能以技能將物理攻擊無效，並且其必殺技能在當後方夥伴\\n越多時，提升越多的物理防禦力來增加生存率。', 0, '2020/04/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (111701, '伊莉亞（聖誕節）', '伊莉亞（聖誕節）', 111701, 0, 0, 3, 3, 3, 450, 255, 2, 1.3, 111701, 0, 111701, 0, 13, 0, '【魔法】位於前衛，以血做為代價進行攻擊的聖夜吸血鬼。\\n當HP越低時傷害便會提升的技能與能夠消耗HP的技能\\n相互配合時，將能發揮出絕大的破壞力。', 0, '2020/04/22 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (111801, '貪吃佩可（新年）', '貪吃佩可（新年）', 111801, 0, 1, 3, 22, 4, 450, 248, 1, 1.965, 111801, 0, 111801, 0, 13, 0, '【物理】位於前衛，精神飽滿地祝賀新年的貪吃公主。\\n可利用提高物理攻擊力的技能強化我方，\\n以傷害力隨HP提升的必殺技討伐敵人。', 0, '2024/05/28 16:00:00', '2024/06/02 15:59:59', 0);
insert into unit_data values (111901, '可可蘿（新年）', '可可蘿（新年）', 111901, 0, 1, 3, 23, 7, 450, 159, 1, 1.87, 111901, 0, 111901, 0, 1, 0, '【物理】位於前衛，優秀的全體輔助，新風之巫女。\\n擁有在物理暴擊時增加傷害的技能，不僅能提高夥伴們的\\n殲滅能力，還能用挑釁與持續恢復技能來支援生存面。', 0, '2024/07/12 16:00:00', '2024/07/19 15:59:59', 0);
insert into unit_data values (112001, '凱留（新年）', '凱留（新年）', 112001, 0, 1, 3, 21, 7, 450, 690, 2, 2.07, 112001, 0, 112001, 0, 3, 0, '【魔法】位於後衛，是攻守雙方面皆活躍的和服貓娘。\\n必殺技可以依照造成的傷害來提升魔法攻擊力，能夠使\\n原本就高性能的攻擊力再更上一層樓。', 0, '2024/07/12 16:00:00', '2024/07/19 15:59:59', 0);
insert into unit_data values (112101, '鈴莓（新年）', '鈴莓（新年）', 112101, 0, 1, 1, 21, 7, 450, 722, 2, 2.27, 112101, 0, 112101, 0, 1, 0, '【魔法】位於後衛，以祈禱來輔助的冒失娘巫女。\\n能利用魔法無效的屏障來提高生存面，更能以必殺技\\n恢復HP與提升行動速度來掌控戰局。', 0, '2020/05/05 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (112201, '霞（魔法少女）', '霞（魔法少女）', 112201, 0, 0, 3, 7, 7, 450, 730, 2, 2.27, 112201, 0, 112201, 0, 9, 0, '【魔法】位於後衛，擁有專精於妨礙能力的魔法偵探。\\n具有能大幅度減少對手TP的技能，還能以束縛的技能來\\n操弄遠距離的敵人，進而達到混亂敵人連攜的效果。', 0, '2020/06/03 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (112301, '栞（魔法少女）', '栞（魔法少女）', 112301, 0, 1, 1, 8, 8, 450, 712, 1, 1.97, 112301, 0, 112301, 0, 5, 0, '【物理】位於後衛，能讓夥伴發揮最大火力的魔法獵人。\\n必殺技具有大幅度提高全體夥伴物理攻擊力的能力，使用\\n技能射出的光輝箭矢則能提高範圍內的物理攻擊力。', 0, '2020/06/03 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (112401, '卯月（NGs）', '卯月（NGs）', 112401, 0, 1, 3, 4, 4, 450, 370, 1, 2.24, 112401, 0, 112401, 0, 18, 0, '【物理】位於中衛，鼓舞著夥伴並同時戰鬥的可愛少女。\\n不僅能以提升夥伴TP與攻擊力的技能來進行輔助，\\n自己還擁有著能夠將敵人一掃而空的強力範圍攻擊。', 0, '2021/08/09 16:00:00', '2021/08/25 15:59:59', 0);
insert into unit_data values (112501, '凜（NGs）', '凜（NGs）', 112501, 0, 1, 3, 4, 4, 450, 153, 1, 2.215, 112501, 0, 112501, 0, 18, 0, '【物理】位於前衛，是一位創造冰來守護夥伴的冷酷少女。\\n能以束縛或挑釁來妨礙眼前敵人的行動，同時能對自己\\n展開屏障，發揮如銅牆鐵壁般的防禦能力。', 0, '2021/08/09 16:00:00', '2021/08/25 15:59:59', 0);
insert into unit_data values (112601, '未央（NGs）', '未央（NGs）', 112601, 0, 1, 1, 7, 7, 450, 695, 2, 2.27, 112601, 0, 112601, 0, 18, 0, '【魔法】位於後衛，攻守兩面皆活躍，熱情滿溢的少女。\\n能以受到傷害的夥伴為中心施展恢復魔法來進行輔助，\\n同時還能施展魔法攻擊來將敵人一網打盡。', 1, '2015/04/01 15:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (112701, '鈴（遊俠）', '鈴（遊俠）', 112701, 0, 0, 3, 6, 6, 450, 422, 1, 2.19, 112701, 0, 112701, 0, 3, 0, '【物理】位於中衛，能夠貫穿敵人防禦的橡子遊俠。\\n在戰鬥開始時便能大幅度降低範圍內敵人的物理防禦力，\\n以至於在序盤便可以帶出能造成大規模傷害的戰況。', 0, '2020/08/06 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (112801, '真陽（遊俠）', '真陽（遊俠）', 112801, 0, 0, 3, 6, 6, 450, 390, 1, 2.19, 112801, 0, 112801, 0, 5, 0, '【物理】位於中衛，擅長對付多數敵人的牧場戰士。\\n能夠使用擊飛敵人的技能來強行壓退敵人的陣型。\\n再使用範圍攻擊跟附加混亂的技能一口氣殲滅敵人。', 0, '2020/08/21 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (112901, '璃乃（奇幻）', '璃乃（奇幻）', 112901, 0, 0, 3, 8, 8, 450, 730, 1, 1.97, 112901, 0, 112901, 0, 6, 0, '【物理】位於後衛，以希望為戰力的不可思議之國救世主。\\n擁有只要暴擊就會提升效果的技能，並且以同伴數量越多、\\n威力就跟著越強的必殺技來殲滅敵人。', 0, '2020/09/04 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113001, '步未（奇幻）', '步未（奇幻）', 113001, 0, 1, 1, 6, 6, 450, 508, 1, 2.34, 113001, 0, 113001, 0, 12, 0, '【物理】位於中衛，能夠自由操縱戰場動向的白兔少女。\\n能使用讓敵人行動速度下降並且提升同伴的速度的技能，\\n以及停止敵人時間的技能，總是在支援著同伴們。', 0, '2020/09/04 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113101, '流夏（夏日）', '流夏（夏日）', 113101, 0, 1, 3, 27, 27, 450, 192, 1, 1.3, 113101, 0, 113101, 0, 6, 0, '【物理】位於前衛，揮舞優雅太刀使人魅惑的若夏大姐頭。\\n以隨著造成傷害的次數提升攻擊力的技能，跟所攜帶的雙刀\\n的數刃斬擊，將眼前的敵人切碎。', 0, '2023/12/27 16:00:00', '2024/01/01 15:59:59', 0);
insert into unit_data values (113201, '杏奈（夏日）', '杏奈（夏日）', 113201, 0, 1, 1, 28, 5, 450, 256, 2, 1.625, 113201, 0, 113201, 0, 12, 0, '【魔法】位於前衛，借取眾人手中之力的盛夏的冥姬。\\n以全體同伴的魔法攻擊力做為代價來劇烈提升自己的\\n魔法攻擊力，不斷使用熾烈的攻擊魔法製造大量傷害。', 0, '2020/11/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113301, '七七香（夏日）', '七七香（夏日）', 113301, 0, 0, 3, 7, 7, 450, 468, 2, 2.07, 113301, 0, 113301, 0, 12, 0, '【魔法】位於中衛，擅長對付多數敵人的盛夏魔法少女。\\n使用依照敵人數量而提升防禦力降低效果的技能，以及各種\\n多樣化的範圍攻擊，以最可愛的姿態稱霸盛夏的戰鬥。', 0, '2020/11/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113401, '初音（夏日）', '初音（夏日）', 113401, 0, 1, 3, 29, 7, 450, 567, 2, 1.92, 113401, 0, 113401, 0, 12, 0, '【魔法】位於中衛，使用出魔法攻擊的超能力啦啦隊女孩。\\n必殺技能對全體敵人釋放威力極大的一擊，\\n在那之後便會因為發動超能力的反彈抱起枕頭陷入夢境中。', 0, '2023/12/27 16:00:00', '2024/01/01 15:59:59', 0);
insert into unit_data values (113501, '美里（夏日）', '美里（夏日）', 113501, 0, 1, 1, 7, 7, 450, 697, 2, 2.07, 113501, 0, 113501, 0, 12, 0, '【魔法】位於後衛，從敵人威脅保護學生的夏日女神老師。\\n使用提高同伴魔法防禦力，持續回復HP的技能，\\n還有展開屏障的必殺技，堅固地守護同伴。', 0, '2022/09/23 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113601, '純（夏日）', '純（夏日）', 113601, 0, 0, 3, 5, 5, 450, 182, 1, 2.0, 113601, 0, 113601, 0, 12, 0, '【物理】位於前衛，燃燒業火斬向邪惡的盛夏騎士團長。\\n有著對敵方單體造成傷害的同時提升攻擊力的必殺技，\\n以及施展能夠造成火傷的技能，將敵人燒滅殆盡。', 0, '2020/12/26 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113701, '茜里（天使）', '茜里（天使）', 113701, 0, 0, 3, 7, 7, 450, 530, 2, 1.92, 113701, 0, 113701, 0, 12, 0, '【魔法】位於中衛，使用魔法痛擊敵人的雙胞胎妹妹天使。\\n雖然持續時間短暫，但能使用效果極大的強化魔法來\\n提升自己的攻擊力，用威力拔群的單體魔法掃蕩敵人。', 0, '2020/12/28 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113801, '依里（天使）', '依里（天使）', 113801, 0, 0, 3, 6, 6, 450, 531, 2, 2.34, 113801, 0, 113801, 0, 12, 0, '【魔法】位於中衛，使用魔法輔助夥伴的雙胞胎姊姊天使。\\n能夠大幅度提升防禦力，或將敵人的注意力引到目標夥伴\\n身上，並使用特殊又強力的技能愚弄敵人。', 0, '2020/01/20 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (113901, '紡希（萬聖節）', '紡希（萬聖節）', 113901, 0, 1, 3, 2, 2, 450, 152, 1, 2.375, 113901, 0, 113901, 0, 12, 0, '【物理】位於前衛，妨礙敵人物理攻擊的吸血鬼裁縫師。\\n能挑釁敵方和展開物理無效的護盾，並對物理攻擊力最高的敵人施展妨礙技能，織起如鐵壁般的防禦網。', 0, '2024/02/26 16:00:00', '2024/02/29 15:59:59', 0);
insert into unit_data values (114001, '怜（萬聖節）', '怜（萬聖節）', 114001, 0, 0, 3, 4, 4, 450, 375, 1, 1.965, 114001, 0, 114001, 0, 12, 0, '【物理】位於中衛，將身負重傷的敵人導向死亡的幽雅劍士。\\n當敵方的HP在50%以下時，攻擊威力會大幅度提升，\\n施展範圍攻擊一口氣重創負傷敵人的戰意。', 0, '2021/02/19 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (114101, '茉莉（萬聖節）', '茉莉（萬聖節）', 114101, 0, 1, 1, 1, 1, 450, 186, 1, 2.295, 114101, 0, 114101, 0, 12, 0, '【物理】位於前衛，以銳爪進行突刺的萬聖節見習騎士。\\n技能會根據必殺技的施展次數而強化，\\n使造成的傷害和行動速度提升，撕裂眼前的敵人。', 0, '2021/02/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (114201, '莫妮卡（魔法少女）', '莫妮卡（魔法少女）', 114201, 0, 0, 3, 4, 4, 450, 528, 1, 2.24, 114201, 0, 114201, 0, 12, 0, '【物理】位於中衛，懷抱著愛與希望之力來執掌一切的\\n魔法提督。\\n必殺技能降低敵方單體的物理防禦力，並擁有提升範圍內\\n所有夥伴物理攻擊力的技能，一口氣左右戰局。', 0, '2021/03/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (114301, '智（魔法少女）', '智（魔法少女）', 114301, 0, 0, 3, 4, 4, 450, 402, 1, 2.0, 114301, 0, 114301, 0, 2, 0, '【物理】位於中衛，將正義之力乘載於魔法中戰鬥的魔法劍士。\\n以增加自身物理攻擊力與魔法攻擊力的魔法少女之力\\n及不懈努力磨練出的劍技，施放華麗的魔法攻擊。', 0, '2021/03/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (114401, '秋乃（聖誕節）', '秋乃（聖誕節）', 114401, 0, 1, 3, 5, 5, 450, 189, 1, 2.0, 114401, 0, 114401, 0, 2, 0, '【物理】位於前衛，靠己方支援力量戰鬥的聖夜大小姐。\\n每受到能力提升的效果時便會增加【聖夜的光輝】\\n，以此大幅度強化技能，於聖夜中施放絢爛的一擊。', 0, '2024/04/23 16:00:00', '2024/04/28 15:59:59', 0);
insert into unit_data values (114501, '咲戀（聖誕節）', '咲戀（聖誕節）', 114501, 0, 0, 3, 4, 4, 450, 150, 1, 2.09, 114501, 0, 114501, 0, 3, 0, '【物理】位於前衛，驅使光明守護我方的聖夜咲戀媽媽。\\n擁有能防範敵人攻擊並造成傷害的必殺技，\\n以及在造成傷害的同時恢復的技能，攻擊和防守都能勝任。', 0, '2021/04/27 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (114601, '優花梨（聖誕節）', '優花梨（聖誕節）', 114601, 0, 1, 1, 5, 5, 450, 408, 1, 2.09, 114601, 0, 114601, 0, 2, 0, '【物理】位於中衛，支援物理角色的聖夜原聖騎士。\\n技能會使魔法攻擊力最高的敵方單體TP減少，並提升周圍所有夥伴的魔法防禦力，\\n在魔法戰中綻放無上的光輝。', 0, '2023/01/24 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (114701, '矛依未（新年）', '矛依未（新年）', 114701, 0, 1, 1, 31, 2, 450, 138, 1, 2.0, 114701, 0, 114701, 0, 2, 0, '【物理】位於前衛，在新春揮舞「天樓霸斷劍」的少女。\\n她會比眾人慢一步突擊，將敵方前衛擊飛後，\\n以強化後的範圍物理攻擊一舉掃蕩聚集的敵人。', 0, '2023/02/23 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (115001, '似似花（新年）', '似似花（新年）', 115001, 0, 1, 3, 21, 7, 450, 562, 2, 2.07, 115001, 0, 115001, 0, 2, 0, '【魔法】位於中衛，身著華麗新年和服的「變貌大妃」。\\n以提升我方魔法攻擊力與恢復HP、TP的技能，\\n進行攻守兼具的支援，即使是激烈的戰況，也能帶領夥伴通往勝利。', 0, '2024/05/28 16:00:00', '2024/06/02 15:59:59', 0);
insert into unit_data values (115501, '可可蘿（祭服）', '可可蘿（祭服）', 115501, 0, 1, 3, 2, 2, 450, 533, 1, 2.0, 115501, 0, 115501, 0, 1, 0, '【物理】位於中衛，身著尊貴儀裝的精靈族之村巫女。\\n會強化自身的攻擊力，並恢復TP增加攻擊頻率。\\n以暴擊時的猛攻，化為守護夥伴的劍。', 0, '2024/05/24 16:00:00', '2024/05/28 15:59:59', 0);
insert into unit_data values (115601, '優衣（祭服）', '優衣（祭服）', 115601, 0, 1, 1, 7, 7, 450, 578, 2, 1.97, 115601, 0, 115601, 0, 7, 0, '【魔法】位於中衛，施展精靈術支援我方的儀裝魔法師。\\n使用可強化數次物理攻擊力的技能支援我方的攻擊，\\n並以強大的恢復技能，重整前線掌握戰況。', 0, '2023/03/24 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (115701, '霞（夏日）', '霞（夏日）', 115701, 0, 0, 3, 7, 7, 450, 738, 2, 2.27, 115701, 0, 115701, 0, 9, 0, '【魔法】位於後衛，削減敵人防禦力的海岸名偵探。\\n鎖定攻擊力高的敵人降低其防禦力，\\n並以強化我方的技能，引導解決任何困難事件。', 0, '2021/06/27 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (115801, '莉瑪（灰姑娘）', '莉瑪（灰姑娘）', 115801, 116101, 1, 1, 4, 4, 450, 173, 1, 2.27, 115801, 0, 115801, 0, 5, 0, '【物理】位於前衛，用魔法果實變成公主的戀愛少女。\\n用必殺技從動物變身成人類一段時間，\\n藉由技能增加我方全體的攻擊力，發揮出最大火力。', 0, '2023/04/25 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (115901, '真琴（灰姑娘）', '真琴（灰姑娘）', 115901, 0, 0, 3, 5, 5, 450, 166, 1, 2.27, 115901, 0, 115901, 0, 4, 0, '【物理】位於前衛，身穿華麗服裝施展流麗劍舞的狼女。\\n技能的威力與提升物理攻擊力的技能效果會\\n依照周圍的我方數量增加，揮動鼓舞激勵的斬擊。', 0, '2021/07/05 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116001, '真步（灰姑娘）', '真步（灰姑娘）', 116001, 0, 0, 3, 7, 7, 450, 645, 2, 2.27, 116001, 0, 116001, 0, 9, 0, '【魔法】位於後衛，優雅地支援我方的真步老師。\\n藉由技能提升正在進行物理攻擊的我方的攻擊力，恢復最遠的我方的TP，\\n可以優雅優雅地改變戰況。', 0, '2021/07/21 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116201, '克蘿依（聖學祭）', '克蘿依（聖學祭）', 116201, 0, 0, 3, 2, 2, 450, 184, 1, 2.125, 116201, 0, 116201, 0, 2, 0, '【物理】位於前衛，如疾風般奔馳的陰暗系女孩。\\n藉由每當造成特定次數的傷害時完全恢復ＴＰ的技能\\n以及每次發動會提升攻擊力的技能來掌握最終的勝利。', 0, '2021/03/31 12:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116301, '琪愛兒（聖學祭）', '琪愛兒（聖學祭）', 116301, 0, 0, 3, 32, 1, 450, 223, 1, 2.295, 116301, 0, 116301, 0, 2, 0, '【物理】位於前衛，炒熱聖學祭氣氛的快樂切嚕系女孩。\\n每當攻擊命中時都會累積【切嚕】，藉由增加威力的技能\\n與提升我方全體的物理攻擊力效果的UB來引導我方獲得勝利。', 0, '2021/03/31 12:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116401, '優妮（聖學祭）', '優妮（聖學祭）', 116401, 0, 0, 3, 39, 7, 450, 808, 2, 2.27, 116401, 0, 116401, 0, 2, 0, '【魔法】位於後衛，能夠啟蒙夥伴的優妮們才女。\\n為自身以外的全體我方提升物理、魔法攻擊力，\\n並提升暴擊時的傷害，引領大家邁向勝利。', 0, '2022/06/12 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116501, '祈梨（時空旅行）', '祈梨（時空旅行）', 116501, 0, 0, 3, 3, 3, 450, 252, 1, 2.295, 116501, 0, 116501, 0, 17, 0, '【物理】位於前衛，充滿朝氣又滔滔不絕的嘴砲系龍。\\n提升全體物理攻擊力的UB雖會降低我方的防禦力，\\n相對地HP越多效果也會越高，能將我方導向勝利。', 0, '2021/09/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116601, '嘉夜（時空旅行）', '嘉夜（時空旅行）', 116601, 0, 0, 3, 1, 1, 450, 169, 1, 2.295, 116601, 0, 116601, 0, 17, 0, '【物理】位於前衛，能引導我方邁向勝利的龍人族打手。\\n必殺技能極大幅度提升自身攻擊速度，\\n和提升物理攻擊力的技能相輔相成，揮出如雨點般密集的拳頭。', 0, '2021/09/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116701, '碧（工作服）', '碧（工作服）', 116701, 0, 0, 3, 33, 33, 450, 527, 1, 1.82, 116701, 0, 116701, 0, 9, 0, '【物理】位於中衛，專門妨礙敵人，身穿工作服的邊緣精靈族。\\n與締結了深厚情意的好友「好朋友號」一同施放的UB，\\n能阻擋敵人的行動，使其物理防禦力急劇下降。', 0, '2021/10/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116801, '珠希（工作服）', '珠希（工作服）', 116801, 0, 1, 1, 2, 2, 450, 216, 1, 2.295, 116801, 0, 116801, 0, 8, 0, '【物理】位於前衛，用手中的鯛魚燒機懲奸除惡的貓娘店長。\\n能提升我方全體的物理攻擊力及恢復 TP，\\nUB更是能特大幅度強化自身攻擊力，發揮出驚人的威力。', 0, '2023/07/26 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (116901, '美冬（工作服）', '美冬（工作服）', 116901, 0, 0, 3, 6, 6, 450, 275, 1, 2.065, 116901, 0, 116901, 0, 8, 0, '【物理】位於前衛，作為盾帶領大家進攻的工廠領班。\\n在戰鬥剛開始時會發動強化物理攻擊力及防禦力的技能。\\n當UB發動時，能提升自身物理爆擊率，使出猛烈的連擊。', 0, '2021/10/18 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117001, '惠理子（夏日）', '惠理子（夏日）', 117001, 0, 1, 3, 3, 3, 450, 407, 1, 1.55, 117001, 0, 117001, 0, 11, 0, '【物理】位於中衛，纏繞火花點綴暮色的盛夏破壞者。\\n以兩個技能降低物理防禦力，並運用依照傷害量\\n延長暴擊上升的UB粉碎眼前的敵方。', 0, '2023/12/27 16:00:00', '2024/01/01 15:59:59', 0);
insert into unit_data values (117101, '靜流（夏日）', '靜流（夏日）', 117101, 0, 0, 3, 5, 5, 450, 163, 1, 2.0, 117101, 0, 117101, 0, 6, 0, '【物理】位於前衛，穩固地守護著夥伴的海邊姐姐。\\n擔任防禦及輔助角色時，能巧妙運用物理防禦力越高\\n效果越佳的多項技能，也能運用範圍攻擊擔任攻擊者。', 0, '2021/07/15 12:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117201, '望（夏日）', '望（夏日）', 117201, 0, 1, 3, 4, 4, 450, 417, 1, 2.09, 117201, 0, 117201, 0, 10, 0, '【物理】位於中衛，幫大家聲援的盛夏頂尖偶像。\\n會用所有技能支援我方全體的物理攻擊，\\n並從戰鬥中期起恢復我方全體的TP，引導大家獲勝。', 0, '2023/12/27 16:00:00', '2024/01/01 15:59:59', 0);
insert into unit_data values (117301, '千歌（夏日）', '千歌（夏日）', 117301, 0, 0, 3, 7, 7, 450, 520, 2, 2.07, 117301, 0, 117301, 0, 10, 0, '【魔法】位於中衛，獻上守護讚美曲的夏日海邊歌姬。\\n在戰鬥一開始時會大幅度強化我方的魔法防禦，面對任何猛攻都能用吸收護盾\\n和恢復HP技能守護我方。', 0, '2021/12/17 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117401, '紡希（夏日）', '紡希（夏日）', 117401, 0, 1, 1, 1, 1, 450, 355, 1, 2.42, 117401, 0, 117401, 0, 10, 0, '【物理】位於中衛，拉近敵方瓦解陣形，風平浪靜的裁縫師。\\n將攻擊力最高的敵方從敵陣拉近到我方陣地，\\n並用束縛和降低行動速度一類的技能束縛敵方的自由。', 0, '2023/09/25 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117501, '深月（大江戶）', '深月（大江戶）', 117501, 0, 0, 3, 5, 5, 450, 552, 1, 2.4, 117501, 0, 117501, 0, 11, 0, '【物理】位於中衛，緊盯著戰況全盤的獨眼密醫。\\n能夠束縛住HP少的敵人，對其使出降低防禦力的技能，\\n並以咒術造成每秒會增加的持續傷害，逐刻侵蝕敵人的生命。', 0, '2021/01/05 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117601, '雪（大江戶）', '雪（大江戶）', 117601, 0, 0, 3, 7, 7, 450, 798, 2, 2.0, 117601, 0, 117601, 0, 12, 0, '【魔法】位於後衛，拯救迷茫眾生、身著白衣的美之象徵。\\n能恢復一位我方TP最低角色的TP，並以HP恢復技能\\n守護該對象，再使用魔法攻擊力提升技能，一口氣攻陷對方。', 0, '2022/01/15 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117701, '香織（萬聖節）', '香織（萬聖節）', 117701, 0, 1, 3, 1, 1, 450, 156, 1, 2.17, 117701, 0, 117701, 0, 4, 0, '【物理】越是逼迫前衛的敵人，力量越會增強的幽玄南國少女。\\n能使用當敵人體力越少，效果便會增強的攻擊力提升技能\\n與提高傷害量的攻擊技能，不放過敵人，當場將其解決。', 0, '2024/02/26 16:00:00', '2024/02/29 15:59:59', 0);
insert into unit_data values (117801, '妮諾（萬聖節）', '妮諾（萬聖節）', 117801, 0, 1, 1, 36, 36, 450, 518, 1, 2.25, 117801, 0, 117801, 0, 12, 0, '【物理】位於中衛，強化物理攻擊型我方的忍者。\\n能夠使用提升我方物理攻擊力和行動速度的技能效果，\\n增強我方全體的攻擊力，並吹響勝利之笛。', 0, '2023/11/24 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (117901, '鈴奈（萬聖節）', '鈴奈（萬聖節）', 117901, 0, 0, 3, 8, 8, 450, 761, 1, 1.97, 117901, 0, 117901, 0, 15, 0, '【物理】位於後衛，瞄準主力輸出者的小惡魔超級名模。\\n會施展對誘惑狀態可提升威力的攻擊技能，以及奪取TP的\\n妨礙技能，針對物理攻擊力最高的敵人進行攻擊。', 0, '2022/02/01 16:00:00', '2030/12/31 15:00:00', 0);
