insert into unit_data values (118101, '蘭法', '蘭法', 118101, 0, 1, 3, 21, 7, 450, 782, 2, 2.07, 118101, 0, 118101, 0, 19, 0, '【魔法】位於後衛，編織美麗旋律進行支援的深沉平靜\\n歌姬。以強化我方與弱化敵人雙方面進行輔助，\\n同時與召喚的飛龍進行波狀攻擊鎮壓敵人。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
