insert into unit_data values (120901, '伊莉亞（新年）', '伊莉亞（新年）', 120901, 0, 1, 3, 38, 3, 450, 424, 2, 1.425, 120901, 0, 120901, 0, 12, 0, '【魔法】中衛，以自身血液為代價展現力量的\\n新春吸血鬼。自在操縱消耗自身ＨＰ施放的強力技能，\\n以及從敵人吸收ＴＰ的技能，藉以消滅面前的敵人。', 0, '2024/05/28 16:00:00', '2024/06/02 15:59:59', 0);
