insert into unit_data values (121201, '拉比林斯達（超載）', '拉比林斯達（超載）', 121201, 0, 1, 1, 40, 4, 450, 260, 1, 1.55, 121201, 0, 121201, 0, 6, 0, '【物理】前衛，眺望著世界所有真理的「迷宮女王」。\\n以技能強化我方攻擊面並為全體附加ＨＰ持續恢復。\\n重整戰線的同時自己也能以壓倒性力量殲滅敵人。', 0, '2024/03/26 16:00:00', '2030/12/31 15:00:00', 0);
