insert into unit_data values (121601, '祈梨（怪盜）', '祈梨（怪盜）', 121601, 0, 0, 3, 3, 3, 450, 292, 1, 1.425, 121601, 0, 121601, 0, 17, 0, '【物理】位於前衛，藏身於黑暗中釋放會心一擊的\\n怪盜龍。使用當處於隱密效果時，傷害會大幅提升\\n且必定發動暴擊的ＵＢ與技能輸出的穩定傷害打倒敵人。', 0, '2022/08/17 16:00:00', '2030/12/31 15:00:00', 0);
