insert into unit_data values (121901, '杏奈（海盜）', '杏奈（海盜）', 121901, 0, 0, 3, 5, 5, 450, 293, 2, 2.125, 121901, 0, 121901, 0, 12, 0, '【魔法】位於前衛，鼓舞自身並釋放高漲魔力的\\n蒼海之冥姬。\\n能以每次發動都能提升攻擊力和暴擊時傷害的ＵＢ，\\n和以自我犧牲為代價的強力範圍攻擊技消滅敵人。', 0, '2022/09/16 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122001, '忍（海盜）', '忍（海盜）', 122001, 0, 0, 3, 3, 3, 450, 380, 1, 1.425, 122001, 0, 122001, 0, 14, 0, '【物理】位於中衛，解放徘徊靈魂的海星靈能少女。\\n攻擊時運用賦予的【靈錨之烙印】，以提升傷害量的\\n技能和防禦力下降ＵＢ帶領敵人走向滅亡。', 0, '2022/09/04 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122101, '碧（露營）', '碧（露營）', 122101, 0, 1, 1, 35, 35, 450, 731, 1, 1.97, 122101, 0, 122101, 0, 9, 0, '【物理】位於後衛射出千百箭矢，孤獨的山居精靈族。\\n以物理魔法無效護盾擋下攻擊，朝敵方全體射擊\\n並對進入自身攻擊範圍的敵人以強烈的箭雨追擊掃蕩。', 0, '2024/08/26 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122201, '優花梨（露營）', '優花梨（露營）', 122201, 0, 0, 3, 4, 4, 450, 419, 1, 1.84, 122201, 0, 122201, 0, 8, 0, '【物理】位於中衛，與大自然一起享受最棒的那一杯的\\n前聖騎士。\\n【最幸福的一刻】增加得越多，我方物理攻擊力及\\n暴擊時的傷害也會得到強力的強化，並掌握勝利。', 0, '2022/10/02 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122301, '班比', '班比', 122301, 0, 0, 3, 43, 43, 450, 564, 2, 1.425, 122301, 0, 122301, 0, 0, 0, '【魔法】位於中衛，與幼龍同行的最～強吸血鬼公主。\\n能夠強化我方全體，並透過吸收敵人ＴＰ的技能蓄力，\\n發動ＵＢ後會與貝斯共鬥，並降低防禦力和以強擊發動攻勢。', 0, '2022/10/17 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122401, '日和（夏日）', '日和（夏日）', 122401, 0, 1, 1, 1, 1, 450, 264, 1, 2.42, 122401, 0, 122401, 0, 8, 0, '【物理】位於前衛，為夥伴們聲援打氣的盛夏活力女孩。\\n除了持續恢復我方ＨＰ並提升行動速度之外，\\n還能根據我方的數量來強化暴擊和攻擊力，進而致勝。', 1, '2022/11/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122501, '怜（夏日）', '怜（夏日）', 122501, 0, 1, 3, 4, 4, 450, 250, 1, 1.965, 122501, 0, 122501, 0, 0, 0, '【物理】位於前衛，身負水刃加持，馳騁於水面上的海岸\\n劍士。每次提升暴擊時的傷害並發動ＵＢ時，身旁的\\n【水刃的加持】都會提升所有技能傷害，同時貫穿敵陣。', 0, '2023/11/13 16:00:00', '2023/11/16 15:59:59', 0);
insert into unit_data values (122601, '優衣（夏日）', '優衣（夏日）', 122601, 0, 0, 3, 7, 7, 450, 554, 2, 2.27, 122601, 0, 122601, 0, 0, 0, '【魔法】位於中衛，負責輔助魔法角色的靜夜魔法師。\\n可累積的攻擊強化技能、ＴＰ恢復、物理與魔法無效護盾\\n以及持續恢復ＨＰ等招式攻守皆宜，藉此引導我方致勝。', 0, '2022/11/16 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122701, '鏡華（夏日）', '鏡華（夏日）', 122701, 0, 0, 3, 7, 7, 450, 812, 2, 1.92, 122701, 0, 122701, 0, 0, 0, '【魔法】位於後衛，勤奮練習困難魔法的海邊優等生。\\n可擊中隨機敵人的技能，其威力會因攻擊時留下的\\n【蝴蝶貼紙】而增加，並降低其防禦力。', 0, '2022/12/17 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122801, '禊（夏日）', '禊（夏日）', 122801, 0, 1, 1, 1, 1, 450, 237, 1, 2.295, 122801, 0, 122801, 0, 0, 0, '【物理】位於前衛，在敵後設置煙火的納涼搗蛋女孩。\\n會用暈眩來牽制敵方最前衛，同時施展以最後方為中心的\\nＵＢ來拉近窄範圍的敵人，並降低其ＴＰ，藉此阻斷攻勢。', 1, '2022/12/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (122901, '美美（夏日）', '美美（夏日）', 122901, 0, 1, 3, 5, 5, 450, 361, 1, 2.25, 122901, 0, 122901, 0, 0, 0, '【物理】位於中衛，帶著巨大氣球蹦蹦跳跳的夏日\\n小兔兔女孩。可使用ＵＢ和技能對所有複數目標造成追加傷害，\\n並運用自我強化和降低防禦力的效果來突破強敵。', 0, '2023/12/10 16:00:00', '2023/12/13 15:59:59', 0);
insert into unit_data values (123001, '愛梅斯', '愛梅斯', 123001, 0, 1, 3, 44, 44, 450, 746, 2, 2.27, 123001, 0, 123001, 0, 0, 0, '【魔法】位於後衛，以嶄新姿態支援騎士冒險的妖精。\\n會以傷害降低、提升防禦力、無效護盾等保護我方，\\n以護盾為起點支援攻擊力與ＴＰ來轉變攻勢。', 0, '2024/07/01 16:00:00', '2024/07/04 15:59:59', 0);
insert into unit_data values (123101, '真步（探險家）', '真步（探險家）', 123101, 0, 0, 3, 7, 7, 450, 793, 2, 1.795, 123101, 0, 123101, 0, 0, 0, '【魔法】位於後衛與玩偶們一起挑戰大冒險的真步隊員。\\n施加在我方身上的【軟綿綿羊毛】會在受到傷害時被消耗\\n以恢復ＴＰ，以一氣呵成的反擊開闢生路。', 0, '2023/01/05 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (123201, '綾音（探險家）', '綾音（探險家）', 123201, 0, 0, 3, 3, 3, 450, 256, 1, 1.425, 123201, 0, 123201, 0, 0, 0, '【物理】位於前衛，與搭檔一同克服困難\\n奮勇前進的綾音隊員。\\n以兩個範圍攻擊一口氣逮住敵群，提升周圍我方的\\n物理攻擊力與暴擊以擊垮阻礙冒險之牆。', 0, '2023/01/17 16:00:00', '2030/12/31 15:00:00', 0);
