insert into unit_data values (123701, '七七香（萬聖節）', '七七香（萬聖節）', 123701, 0, 0, 3, 21, 7, 450, 741, 2, 2.07, 123701, 0, 123701, 0, 0, 0, '【魔法】位於後衛，卯足全力\\n進行御宅活動的萬聖節魔法少女。\\n降低ＨＰ恢復量以打亂敵方戰略，依照傷害領域\\n與目標多寡增加火傷的威力，以狂熱的火焰包覆敵人。', 0, '2023/02/16 16:00:00', '2030/12/31 15:00:00', 0);
