insert into unit_data values (123901, '茉莉（狂野）', '茉莉（狂野）', 123901, 0, 0, 3, 2, 2, 450, 183, 1, 2.125, 123901, 0, 123901, 0, 0, 0, '【物理】位於前衛，身穿著母親製作的鎧甲，意氣昂揚的\\n獸人族騎士。自身的ＨＰ越低，造成的傷害與無效護盾的\\n耐久度就越高，除此之外還會運用降低防禦力及\\n永續攻擊力提升來開闢生路。', 0, '2023/03/19 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (124001, '茜里（聖誕節）', '茜里（聖誕節）', 124001, 0, 1, 3, 7, 7, 450, 438, 2, 2.07, 124001, 0, 124001, 0, 0, 0, '【魔法】位於中衛，為迎來聖夜的城鎮帶來幸福的雙胞胎\\n妹妹聖誕老人。除了能為我方全體附加效果會隨自身魔法\\n攻擊力提升的攻擊力強化，還會用單體特大幅度傷害的\\n魔法攻擊來演奏勝利的旋律。', 0, '2024/04/12 16:00:00', '2024/04/16 15:59:59', 0);
insert into unit_data values (124101, '依里（聖誕節）', '依里（聖誕節）', 124101, 0, 1, 1, 6, 6, 450, 433, 2, 1.965, 124101, 0, 124101, 0, 0, 0, '【魔法】位於中衛，點亮燈光、傳遞幸福的雙胞胎姊姊\\n聖誕老人。會為距離最近的一名角色提升魔法攻擊力及\\n行動速度，並恢復其ＴＰ，除此之外還會用支援所有人的\\n技能來點綴聖夜的勝利。', 1, '2023/04/01 16:00:00', '2030/12/31 15:00:00', 0);
insert into unit_data values (124201, '純（聖誕節）', '純（聖誕節）', 124201, 0, 0, 3, 5, 5, 450, 144, 1, 2.07, 124201, 0, 124201, 0, 0, 0, '【物理】在前方庇護夥伴並給予祝福的聖白騎士團長。\\n使用技能強化後方夥伴的攻擊能力並恢復其ＴＰ，\\n透過降低敵人防禦力及恢復自身ＨＰ的ＵＢ以掌握勝算。', 0, '2023/04/26 16:00:00', '2030/12/31 15:00:00', 0);
