insert into unit_data values (124501, '帆稀（新年）', '帆稀（新年）', 124501, 0, 1, 3, 21, 7, 450, 707, 2, 2.07, 124501, 0, 124501, 0, 0, 0, '【魔法】位於後衛，用笑容隱藏魔力並盛裝打扮的龍之首\\n領。強化自身攻擊並恢復ＴＰ，而ＵＢ使出的曙光之淚\\n將根據敵方數量增加威力及防禦力減少值，驅除所有災厄。', 0, '2024/05/14 16:00:00', '2024/05/17 15:59:59', 0);
insert into unit_data values (124601, '美里（新年）', '美里（新年）', 124601, 0, 1, 3, 21, 7, 450, 698, 2, 2.44, 124601, 0, 124601, 0, 0, 0, '【魔法】位於後衛以層層真心對待夥伴、女接待員之姿的\\n老師。以所有技能提升夥伴的物理攻擊力及暴擊能力，\\n透過支援魔防和ＴＰ以及恢復ＨＰ和ＴＰ來祈求萬福。', 0, '2024/05/14 16:00:00', '2024/05/17 15:59:59', 0);
