insert into unit_data values (128301, '祈梨（新年）', '祈梨（新年）', 128301, 0, 1, 1, 38, 3, 450, 191, 1, 2.09, 128301, 0, 128301, 0, 0, 0, '【物理】位於前衛，會捉弄敵人且得意忘形的年神龍人。\\n能挑釁敵方全體，反擊敵人的妨礙技能，\\n並使出讓前方全體敵人暈眩的技能來擾亂戰局。', 1, '2024/05/01 16:00:00', '2030/12/31 15:00:00', 0);
