insert into unit_enemy_data values (206000, '海獅怪', 206000, 0, 1, 270, 100, 1, 4.0, 0, 0, 0, '【物理】近距離攻擊\\n不論體型還態度，皆為條漢子的北\\n海之怪。以厚重的脂肪層來抵禦且\\n壓制敵人的物理攻擊。');
insert into unit_enemy_data values (206100, '睿智權杖', 206100, 0, 7, 270, 1080, 2, 4.5, 0, 0, 0, '【魔法】遠距離攻擊\\n以被噴到就會凍結的寒氣魔法，\\n剝奪敵人戰意的鸚鵡螺型魔物。');
insert into unit_enemy_data values (206200, '流氓水獺', 206200, 0, 1, 270, 650, 1, 3.5, 0, 0, 0, '【物理】中距離攻擊\\n人一不留意時就拿貝殼亂丟亂砸，\\n一種性格惡劣的北海小混混。');
insert into unit_enemy_data values (206201, '頑皮河狸', 206201, 0, 1, 270, 650, 1, 0.5, 0, 0, 0, '【物理】中距離攻擊\\n會從一端削木並使其倒下的河狸。\\n削下的木片並不是要用來造房子，\\n而是為了保護家園而拿來投擲。');
insert into unit_enemy_data values (206300, '露娜聚光燈', 206300, 0, 0, 270, 1110, 0, 10.0, 0, 0, 0, '【恢復】單體ＴＰ恢復\\n與露娜之塔所建時一同打造出來的一種能量增幅器。\\n將散布各處的魔力濃縮，並做為強化供給於守護者。');
insert into unit_enemy_data values (206301, '露娜聚光燈', 206300, 0, 0, 270, 1800, 0, 10.0, 0, 0, 0, '【恢復】單體ＴＰ恢復\\n與露娜之塔所建時一同打造出來的一種能量增幅器。\\n將散布各處的魔力濃縮，並做為強化供給於守護者。');
insert into unit_enemy_data values (206400, '小魔眼', 206400, 0, 0, 270, 100, 1, 2.5, 0, 0, 0, '【物理】近距離攻擊\\n侍奉獨眼族之王的小小侍從。\\n對於大王所給予的金光閃閃裝備特別的鍾愛。');
insert into unit_enemy_data values (206500, '擲岩猴老大', 206500, 0, 0, 300, 100, 1, 2.5, 0, 0, 0, '【物理】近距離攻擊\\n擲岩猴更加成長的姿態。從小就持續投擲大岩石\\n的一雙粗臂能持續造成強烈的打擊。');
insert into unit_enemy_data values (206700, '日和的闇影', 206700, 0, 1, 270, 200, 1, 2.295, 0, 0, 0, '有著日和樣貌的特殊魔物\\n\\n‧對面前的 1 名敵人造成大幅度的物理傷害，\\n\u3000並對附近小範圍內所有敵人造成\\n\u3000中幅度的物理傷害。\\n‧對面前的 1 名敵人造成中幅度的物理傷害。\\n‧對面前的 1 名敵人造成小幅度的物理傷害，\\n\u3000並中幅度提升自己的物理攻擊力。');
insert into unit_enemy_data values (206701, '怜（新年）的闇影', 206701, 0, 4, 270, 400, 1, 1.965, 0, 0, 0, '有著怜（新年）樣貌的特殊魔物\\n\\n‧對全體敵人造成中幅度的物理傷害，\\n\u3000並且使其暈眩。\\n‧【挑釁】對全體敵人挑釁。\\n\u3000若在挑釁後的動作中受到攻擊，\\n\u3000則對範圍內的所有敵人造成中幅度的物理傷害，\\n\u3000再小幅度降低其物理防禦力。\\n‧對自己周圍的全體夥伴\\n\u3000展開魔法無效的屏障，\\n\u3000並中幅度提升自己的魔法防禦力。');
insert into unit_enemy_data values (206702, '優衣（新年）的闇影', 206702, 0, 7, 270, 600, 2, 2.27, 0, 0, 0, '有著優衣（新年）樣貌的特殊魔物\\n\\n‧對全體夥伴展開物理/魔法無效的屏障，\\n\u3000並施加持續恢復 HP 的狀態，\\n\u3000但會大幅度降低自己的魔法攻擊力。\\n‧大幅度降低從前方算起第 2 位的 1 名敵人\\n\u3000的物理攻擊力和魔法攻擊力。\\n‧大幅度提升全體夥伴中魔法攻擊角色\\n\u3000的魔法攻擊力。');
