insert into unit_enemy_data values (216501, '莉莉（墮落）的闇影', 216501, 0, 3, 450, 464, 1, 2.09, 0, 0, 0, '有著莉莉（墮落）樣貌的特殊魔物\\n\\n‧【召喚】對面前的1名敵人造成大幅度的物理\\n\u3000傷害。此技能的傷害將會隨著自身的闇影數量\\n\u3000增加。並且不使用必殺技，製造出2隻自身的\\n\u3000闇影。製造出的闇影將使自身變成飛行狀態。\\n‧以自身為中心，特大幅度提升範圍內所有我方的\\n\u3000物理攻擊力。\\n‧對面前的1名敵人造成大幅度的物理傷害，\\n\u3000並小幅度降低物理防禦力。');
insert into unit_enemy_data values (216601, '可璃亞（墮落）的闇影', 216601, 0, 0, 450, 406, 1, 2.09, 0, 0, 0, '有著可璃亞（墮落）樣貌的特殊魔物\\n\\n‧【召喚】對面前的1名敵人\\n\u3000造成中幅度的物理傷害，\\n\u3000並中幅度恢復自身的HP。\\n\u3000依照自身以外，我方的剩餘HP，\\n\u3000此技能的傷害會隨之提升。\\n‧對面前的1名敵人造成中幅度的物理傷害，\\n\u3000並且中幅度降低物理防禦力。\\n\u3000依照自身以外，我方的剩餘HP，\\n\u3000此技能的傷害會隨之提升。\\n‧對自身以外，\\n\u3000HP最低的1名我方角色施加持續\\n\u3000恢復HP的狀態及【墮天使的加護】。\\n\u3000被施加【墮天使的加護】期間，\\n\u3000對該角色的攻擊會全部代為承受，\\n\u3000並降低對自身的物理傷害及魔法傷害，\\n\u3000使敵人對自身造成的無法行動狀態無效。');
insert into unit_enemy_data values (216700, '守護‧遠古遺物', 216700, 0, 0, 270, 400, 1, 1.9, 0, 0, 0, '【物理】傷害降低\\n與闇影一同守護著聖域，是記憶的看守者。\\n對真正的力量有所求之徒，將會給予相應的試煉。\\n\\n‧對我方全體施加物理魔法傷害降低效果。\\n\u3000同時，對自身以外，受到傷害的我方施加\\n\u3000無敵狀態，並提升其物理攻擊力與魔法攻擊力。');
insert into unit_enemy_data values (216800, '戰鬥‧遠古遺物', 216800, 0, 0, 270, 1200, 2, 0.9, 0, 0, 0, '【魔法】遠距離攻擊\\n與闇影一同守護著聖域，是記憶的看守者。\\n對真正的力量有所求之徒，將會給予相應的試煉。\\n\\n‧無視敵人的挑釁，對距離最遠的1名敵人造成\\n\u30005萬固定的魔法傷害。每當使用此技能時，要\\n\u3000使用下一項技能的等待時間將會縮短。');
insert into unit_enemy_data values (216801, '戰鬥‧遠古遺物', 216800, 0, 0, 270, 1200, 2, 0.9, 0, 0, 0, '【魔法】遠距離攻擊\\n與闇影一同守護著聖域，是記憶的看守者。\\n對真正的力量有所求之徒，將會給予相應的試煉。\\n\\n‧無視敵人的挑釁，對距離最遠的1名敵人造成\\n\u3000100萬固定的魔法傷害。每當使用此技能時，要\\n\u3000使用下一項技能的等待時間將會縮短。');
