insert into unit_enemy_data values (250100, '棘刺蟹', 250100, 0, 0, 270, 115, 1, 2.0, 0, 0, 0, '【物理】【防禦】【妨礙】\\n以巨大的螯鉗與堅硬的殼自豪的螃蟹魔物。\\n潛入沙中悠哉地等待著獵物的模樣，相當討喜。\\n\\n\\n‧近距離攻擊、防禦力降低\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250200, '石蜥', 250200, 0, 14, 270, 335, 1, 3.0, 0, 0, 0, '【物理】\\n有著堅固礦石表皮的岩石巨蜥。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250300, '小小哥布林', 250300, 0, 0, 270, 95, 1, 0.5, 0, 0, 0, '【物理】【物理防禦】\\n儘管幼小，仍舊勇猛的見習哥布林。\\n以靈巧彌補無力，在前線上活躍。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250400, '海獅怪', 250400, 0, 1, 270, 55, 1, 4.0, 0, 0, 0, '【物理】【防禦】\\n不論體型還態度，皆為條漢子的北海之怪。\\n以厚重的脂肪層來抵禦且壓制敵人的物理攻擊。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250500, '沙蟲', 250500, 0, 11, 270, 410, 1, 1.3, 0, 0, 0, '【物理】【遠距離】\\n在沙地底下等待獵物，\\n以大顎一口氣將其斃命的蜈蚣魔物\\n\\n‧會移動至從後方算起的第1名角色處進行近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250600, '小狸貓', 250600, 0, 0, 100, 120, 2, 0.5, 0, 0, 0, '【魔法】【中距離】【防禦】\\n雖然幼小，仍會以巧妙的變化\\n來拼命進行攻防的豆狸魔物。\\n\\n‧對從後方算起的第3名角色進行攻擊\\n‧一定時間內無敵\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250700, '魔像', 250700, 0, 16, 270, 20, 1, 5.0, 0, 0, 0, '【物理】【物理防禦】\\n有著堅硬的身軀，是攻守具佳的岩石魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250800, '青藍魔像', 250800, 0, 16, 270, 25, 1, 5.0, 0, 0, 0, '【物理】【魔法防禦】\\n從蘊含著魔力的岩石中誕生而出，\\n擁有魔法耐力的魔像。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (250900, '生者戰士像', 250900, 0, 16, 270, 100, 1, 4.0, 0, 0, 0, '【物理】【防禦】\\n無論是什麼攻擊都能以盾防禦，是座擁有生命的石像。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
