insert into unit_enemy_data values (251100, '岩龜暴君', 251100, 0, 16, 270, 10, 1, 5.5, 0, 0, 0, '【物理】【防禦】\\n無論是什麼攻擊都能用硬殼給彈開，\\n巨大的龜型魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251200, '哥布林護衛', 251200, 0, 15, 270, 85, 1, 4.0, 0, 0, 0, '【物理】【物理防禦】\\n以逼近身高高度的巨大盾牌守護著夥伴的，\\n見習哥布林士兵。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251300, '叢林護衛', 251300, 0, 15, 270, 90, 1, 4.0, 0, 0, 0, '【物理】【魔法防禦】\\n以看似臉譜的大型盾牌嚇阻入侵者，\\n是叢林的守護者。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251400, '狂野野豬', 251400, 0, 0, 270, 50, 1, 4.0, 0, 0, 0, '【物理】【防禦】【妨礙】\\n生活於熱地，原始的大野豬。\\n在不停衝撞岩漿岩後，\\n習得了連鎧甲都可粉碎的牙之一擊。\\n\\n‧近距離攻擊、物防下降\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251500, '守門者', 251500, 0, 0, 270, 15, 2, 3.5, 0, 0, 0, '【魔法】【範圍】【妨礙】\\n敞開地獄之門，\\n並吐出會使人火傷的熱炎，是魔界的守衛。\\n\\n‧對近距離2名角色進行攻擊，並施加火傷狀態\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到火傷傷害的影響不恢復TP');
insert into unit_enemy_data values (251600, '狂野角鹿', 251600, 0, 16, 270, 40, 1, 3.0, 0, 0, 0, '【物理】【防禦】\\n巨大的雙角除了攻擊也能防禦，是鹿型的魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251700, '冰雪角鹿', 251700, 0, 16, 270, 45, 1, 3.0, 0, 0, 0, '【物理】【防禦】\\n住在高山深處，厚實的毛皮下，\\n擁有強壯的身體，是一種馴鹿的魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251800, '樹精', 251800, 0, 15, 270, 80, 1, 2.5, 0, 0, 0, '【物理】【防禦】\\n把柔韌長枝\\n當作鞭子一樣攻擊的樹木魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (251900, '擰扭相思樹', 251900, 0, 15, 270, 70, 1, 2.5, 0, 0, 0, '【物理】【防禦】\\n能用扭捲而成的長枝，\\n將冒險家拖行到森林深處的樹木魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％\\n');
insert into unit_enemy_data values (252000, '多倫多獸', 252000, 0, 15, 270, 75, 1, 0.5, 0, 0, 0, '【物理】【防禦】\\n能自由伸展分枝，以老練的技巧\\n探中痛點的朽木魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252100, '紅蕈菇', 252100, 0, 14, 270, 315, 1, 2.0, 0, 0, 0, '【物理】\\n傳聞拿來燉煮後意外地好吃，一種會走路的魔菇魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252200, '毒牙蜥蜴', 252200, 0, 14, 270, 345, 1, 2.5, 0, 0, 0, '【物理】\\n被那尖銳之牙給咬上一口，\\n便會留下足以致命的負傷，是一種毒蜥蜴魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252300, '岩石蜥蜴', 252300, 0, 14, 270, 330, 1, 3.0, 0, 0, 0, '【物理】\\n那頭部的外層就如同鎧甲般堅硬，\\n是一種岩石巨蜥魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252400, '翠綠蜥蜴', 252400, 0, 14, 270, 325, 1, 3.0, 0, 0, 0, '【物理】\\n襟領上有著鮮豔的綠葉，\\n是蜥蜴魔物界的時尚大佬。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252500, '火蜥蜴', 252500, 0, 14, 270, 340, 1, 0.5, 0, 0, 0, '【物理】\\n靠著尾巴上永遠燃燒著的火焰，\\n常保身體溫熱的蜥蜴魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252600, '軟泥怪', 252600, 0, 15, 270, 300, 2, 2.0, 0, 0, 0, '【魔法】\\n非常濕黏的果凍狀魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252700, '閃焰軟泥怪', 252700, 0, 15, 270, 305, 2, 2.0, 0, 0, 0, '【魔法】\\n被森林大火給捲入而著火，\\n因此變得更加凶暴的軟泥怪。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252800, '獸人', 252800, 0, 13, 270, 30, 1, 2.5, 0, 0, 0, '【物理】【物理防禦】\\n擊身著從被擊倒的冒險家身上搶來的鎧甲，\\n且揮舞著巨斧的一種凶暴的森林獸人。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (252900, '高等獸人', 252900, 0, 13, 270, 35, 1, 2.5, 0, 0, 0, '【物理】【魔法防禦】\\n因反覆且嚴厲的鍛鍊，而獲得強韌肉體，\\n獸人裡頭的上位種。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253000, '重鎚巨蟹', 253000, 0, 0, 270, 310, 1, 2.5, 0, 0, 0, '【物理】\\n以大型的爪子以及堅硬的殼，\\n面對各種巨大的對手的螃蟹魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253100, '綠葉野豬', 253100, 0, 0, 270, 60, 1, 4.0, 0, 0, 0, '【物理】【防禦】\\n將堅硬的葉子如鎧甲般纏繞身體，\\n碰到即會帶來劇烈疼痛的大型野豬。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253200, '沙塵野豬', 253200, 0, 0, 270, 65, 1, 4.0, 0, 0, 0, '【物理】【防禦】\\n硬化沙子，將其作為鎧甲纏繞於身上，\\n即為不畏捨身的戰士的鐵證。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253300, '憤怒烏賊', 253300, 0, 0, 270, 405, 2, 2.5, 0, 0, 0, '【魔法】【妨礙】\\n怒髮衝冠的烏賊魔物。那金屬質的錨，儘管擁有高硬度卻相當沉重，\\n時常導致失衡而摔個十腳朝天。\\n\\n‧近距離攻擊、行動速度降低\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253400, '戳戳鳥', 253400, 0, 0, 270, 400, 1, 0.9, 0, 0, 0, '【物理】【妨礙】\\n對魔力的流動很敏感的啄木鳥。\\n以尖銳的喙做出的攻擊能消減精力。\\n\\n‧對魔攻高的角色進行攻擊，並使其TP下降\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253500, '女低音鷹鳥', 253500, 0, 11, 270, 360, 1, 2.0, 0, 0, 0, '【物理】【妨礙】\\n發出美麗歌聲的妖鳥。\\n牠會在獵物疏於防守時，伸出利爪將其捕獲。\\n\\n‧近距離攻擊\\n‧全體防禦力降低（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253600, '天使鏢魚', 253600, 0, 0, 270, 365, 1, 10.0, 0, 0, 0, '【物理】【輔助】\\n擁有熱帶魚模樣的魚之魔物。在牠那優雅的姿態背後，\\n則是會以那尖銳的下顎，將鎖定的獵物刺穿。\\n\\n‧近距離攻擊、自身行動速度提升\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253700, '鼠妖', 253700, 0, 11, 270, 320, 1, 1.5, 0, 0, 0, '【物理】\\n威力雖然不高，但能迅捷地進行多次攻擊，\\n是鼠型的魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253800, '草莓蛇', 253800, 0, 0, 270, 350, 1, 0.9, 0, 0, 0, '【物理】【中距離】【妨礙】\\n擬態為草莓且放出甜甜香氣的蛇怪。\\n襲擊無防備接近的敵人進行捕食。\\n\\n‧對從前方算起的第2名角色進行攻擊，並使其物防下降\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (253900, '盜人蛤蟆', 253900, 0, 0, 270, 415, 1, 4.8, 0, 0, 0, '【物理】【妨礙】\\n用那刺刺的肚子以驚人之勢撞向敵人將其彈飛，\\n是種外表看起來相當不可思議的青蛙。\\n\\n‧近距離攻擊、浮空、擊退\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254000, '狼人', 254000, 0, 0, 270, 355, 1, 2.5, 0, 0, 0, '【物理】【中距離】\\n以尖銳的利爪敵人撕裂，一種野狼的魔物。\\n\\n‧對從前方算起的第2名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254100, '寄居蟹騎士', 254100, 0, 6, 270, 110, 1, 3.0, 0, 0, 0, '【物理】【物理防禦】\\n將被擊倒的騎士所覆的頭盔做為自己的貝殼，\\n一種寄居蟹戰士。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254200, '堅果射手', 254200, 0, 15, 270, 370, 1, 2.5, 0, 0, 0, '【物理】【遠距離】【範圍】\\n將吃完後堅果殼給砸向敵人，\\n一種充滿惡意的松鼠魔物。\\n\\n‧以從後方算起的第2名角色為中心進行範圍攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254300, '臭鼬', 254300, 0, 15, 270, 375, 1, 2.5, 0, 0, 0, '【物理】【遠距離】【範圍】【妨礙】\\n能任意使用刺激眼部的惡臭氣體炸彈的臭鼬魔物。\\n\\n‧以從後方算起的第2名角色為中心進行範圍攻擊，並施加黑暗狀態\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254400, '歡呼半獸人', 254400, 0, 0, 270, 390, 1, 2.5, 0, 0, 0, '【物理】【妨礙】\\n模仿大人，敲擊樂器，\\n喜歡惡作劇的半獸人小孩。\\n\\n‧近距離攻擊、擊退\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254500, '木靈', 254500, 0, 17, 270, 380, 2, 3.0, 0, 0, 0, '【魔法】【中距離】\\n扔擲魔力的砲彈，\\n能攻擊到較後方的朽木魔物。\\n\\n‧對從前方算起的第2名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (254600, '石靈', 254600, 0, 17, 270, 385, 2, 3.0, 0, 0, 0, '【魔法】【中距離】【妨礙】\\n能投射出使人燃燒的火焰，使敵人受到火傷，\\n來追加傷害的詛咒石像。\\n\\n‧對從前方算起的第2名角色進行攻擊，並施加火傷狀態\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到火傷傷害的影響不恢復TP');
insert into unit_enemy_data values (254700, '女中音之鳥', 254700, 0, 0, 270, 395, 1, 4.0, 0, 0, 0, '【恢復】\\n那治癒人的歌聲甚至能傳送至遠方，\\n在鳥魔物界中是一位知名的歌手。\\n\\n‧我方全體恢復');
insert into unit_enemy_data values (254800, '毒蟾蜍', 254800, 0, 0, 270, 420, 1, 2.5, 0, 0, 0, '【物理】【妨礙】\\n以擬態的寶石勾引上鉤者並捕食，\\n擁有毒舌的癩蛤蟆魔物。\\n\\n‧近距離攻擊、毒\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到毒傷害的影響不恢復TP');
insert into unit_enemy_data values (254900, '洞窟蝙蝠', 254900, 0, 15, 270, 560, 2, 3.5, 0, 0, 0, '【魔法】\\n棲息於洞窟之中，聽取著獵物的腳步聲進行襲擊，\\n一種蝙蝠型魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255000, '土蛇', 255000, 0, 13, 270, 570, 1, 2.5, 0, 0, 0, '【物理】【妨礙】\\n躲在暗處吸取自各個角落飛來的魔力，\\n以填飽肚子的魔物蛇的變種。\\n魔力越高越容易被它盯上。\\n\\n‧對魔攻高的角色進行攻擊，並使其TP下降\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255100, '寒冰雛鳥', 255100, 0, 0, 270, 575, 2, 3.0, 0, 0, 0, '【魔法】【中距離】\\n以尾部羽毛上發射冰霜攻擊的鳥型魔物。\\n\\n‧對從前方算起的第2名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255200, '冰魚', 255200, 0, 12, 270, 580, 1, 2.6, 0, 0, 0, '【物理】\\n鑿開冰河作為巢穴，\\n對所有接近的人無差別進行襲擊的魚之魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255300, '熔岩魚', 255300, 0, 12, 270, 585, 1, 2.4, 0, 0, 0, '【物理】\\n以連岩石都能咬碎的強韌下顎，\\n將周遭事物給食之殆盡的魚之魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255400, '沙陸毒蛇', 255400, 0, 14, 270, 550, 1, 3.0, 0, 0, 0, '【物理】【妨礙】\\n挖掘洞穴，靜待獵物掉入洞穴，\\n再以毒牙將其斃命的蛇的魔物。\\n\\n‧近距離攻擊、行動速度下降、毒\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到毒傷害的影響不恢復TP');
insert into unit_enemy_data values (255500, '殺人蜂', 255500, 0, 0, 270, 565, 1, 0.9, 0, 0, 0, '【物理】【妨礙】\\n能夠迅速察覺到擁有威脅的敵人，\\n並確實地鎖定射穿對方的蜂魔物。\\n\\n‧對TP高的角色進行攻擊，HP低於50％時施加麻痺\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255600, '岩浣熊', 255600, 0, 0, 270, 555, 1, 2.5, 0, 0, 0, '【物理】【中距離】【妨礙】\\n一種愛乾淨到潔癖過了頭的浣熊怪。\\n如果被那蟾蜍油做的肥皂水給淋到，\\n可會連武器都無法握好。\\n\\n‧對從前方算起的第2名角色進行攻擊、擊退，並使其移動速度下降\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255700, '沉睡之梟', 255700, 0, 0, 270, 755, 2, 3.5, 0, 0, 0, '【魔法】【妨礙】\\n因為睡眠不足而倦怠的貓頭鷹魔物。\\n能死盯敵人施放法術來剝奪自由。\\n\\n‧對HP高的角色進行攻擊，並施加束縛狀態\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255800, '狗頭人', 255800, 0, 15, 270, 710, 1, 3.5, 0, 0, 0, '【物理】【中距離】\\n投擲著用骨頭做成的迴力鏢做為攻擊手段，\\n一種山犬的魔物。\\n\\n‧對從前方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (255900, '狗頭刺客', 255900, 0, 15, 270, 715, 1, 0.5, 0, 0, 0, '【物理】【中距離】\\n以快速步伐耍弄，\\n趁些微的空隙扔出飛刀的山犬魔物。\\n\\n‧對從前方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256000, '翼耳兔', 256000, 0, 15, 270, 730, 2, 3.5, 0, 0, 0, '【魔法】\\n能夠煽動巨大的長耳，\\n以著驚人之勢突擊而上，是一種兔子魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256100, '流浪之鴉', 256100, 0, 4, 270, 735, 1, 3.0, 0, 0, 0, '【物理】\\n到處打倒阿斯特朗中的\\n強力魔物的流浪烏鴉。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256200, '遷徙之鳶', 256200, 0, 4, 270, 740, 1, 2.6, 0, 0, 0, '【物理】【範圍】\\n崇拜著生於荒野，也將死於荒野的生活方式\\n是一隻孤鳥。\\n\\n‧對近距離2名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256300, '搗蛋惡魔', 256300, 0, 12, 270, 750, 2, 3.5, 0, 0, 0, '【魔法】\\n使用與身長比例不符、異常巨大的爪子做為攻擊，\\n是仍在見習中的惡魔。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256400, '小小格里芬', 256400, 0, 15, 270, 725, 1, 3.5, 0, 0, 0, '【物理】\\n羽毛還沒長齊，還飛不起來的雛獅鷲。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256500, '海馬騎士', 256500, 0, 0, 270, 720, 1, 2.0, 0, 0, 0, '【物理】【中距離】\\n奪取在海中溺水的騎士的矛，\\n劃界警備的海馬戰士。\\n\\n‧對從前方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256600, '頑皮河狸', 256600, 0, 1, 270, 700, 1, 0.5, 0, 0, 0, '【物理】【妨礙】\\n會從一端削木並使其倒下的河狸。\\n削下的木片並不是要用來造房子，\\n而是為了保護家園而拿來投擲。\\n\\n‧對魔攻高的角色進行攻擊，並使其魔攻降低\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256700, '流氓水獺', 256700, 0, 1, 270, 705, 1, 3.5, 0, 0, 0, '【物理】【遠距離】【妨礙】\\n趁人一不留意時就拿貝殼亂丟亂砸，\\n一種性格惡劣的北海小混混。\\n\\n‧對從後方算起的第2名角色進行攻擊，並施加暈眩狀態\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (256800, '洞窟猿猴', 256800, 0, 12, 270, 745, 1, 3.0, 0, 0, 0, '【物理】\\n手臂上強韌的肌肉，使牠甚至能夠\\n擺盪鐘乳石間穿梭，是一種大型猴怪魔物。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
