insert into unit_enemy_data values (257000, '鑽頭鼴鼠', 257000, 0, 16, 270, 780, 1, 3.5, 0, 0, 0, '【物理】【中距離】\\n能從地底伸手，從腳下開始\\n攻擊的鼴型魔物。\\n\\n‧對從後方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (257100, '寒冰鼴鼠', 257100, 0, 16, 270, 785, 1, 3.5, 0, 0, 0, '【物理】【中距離】\\n以靈活的利爪刨碎冰塊，並作為攻擊的雪鼴型魔物。\\n\\n‧對從後方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (257200, '毒莢花', 257200, 0, 15, 270, 770, 1, 2.5, 0, 0, 0, '【物理】【妨礙】\\n用那血盆大口之中所蘊含的腐蝕液體，將捕獲的獵物吃掉。\\n\\n‧近距離攻擊、毒\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到毒傷害的影響不恢復TP');
insert into unit_enemy_data values (257300, '惡魔寶寶', 257300, 0, 0, 270, 760, 2, 3.5, 0, 0, 0, '【魔法】【中距離】\\n用才剛學會的魔法，不管三七二十一\\n就到處攻擊的幼年惡魔。\\n\\n‧對從後方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (257400, '潛陸水母', 257400, 0, 0, 270, 775, 2, 3.0, 0, 0, 0, '【恢復】【輔助】\\n將撿來潛水服裡注滿了海水，\\n就算是在陸地上也能戰鬥的水母型魔物。\\n\\n‧全體恢復\\n‧全體魔攻上升');
insert into unit_enemy_data values (257500, '擲岩猴', 257500, 0, 0, 270, 900, 1, 3.5, 0, 0, 0, '【物理】【遠距離】\\n能夠投擲大石頭，從遠處\\n攻擊的猴子魔物。\\n\\n‧對最後方的角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
