insert into unit_enemy_data values (257700, '岩花', 257700, 0, 15, 270, 910, 1, 3.5, 0, 0, 0, '【物理】【中距離】【範圍】\\n能將石子像子彈一樣彈射而出來做為攻擊，\\n是以石頭製成且能夠移動的花。\\n\\n‧以從後方算起的第3名角色為中心進行範圍攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (257800, '癲狂之花', 257800, 0, 15, 270, 915, 1, 3.5, 0, 0, 0, '【物理】【遠距離】【範圍】\\n在尾端的花會散發著嚴重的惡臭，\\n也有若習慣那味道後， 會使人不禁上癮的說法。\\n\\n‧以從後方算起的第2名角色為中心進行範圍攻擊，並施加中毒狀態\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到毒傷害的影響不恢復TP');
insert into unit_enemy_data values (257900, '仙人掌花', 257900, 0, 15, 270, 920, 1, 3.5, 0, 0, 0, '【物理】【遠距離】【範圍】【妨礙】\\n無限發射會使人麻痺的針，\\n是一株會移動的仙人掌。\\n\\n‧以從後方算起的第2名角色為中心進行範圍攻擊，並施加麻痺狀態\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258000, '森之寧芙', 258000, 0, 15, 270, 930, 2, 2.5, 0, 0, 0, '【魔法】【召喚】\\n能自在利用大地的魔力來生長出魔菇型魔物，\\n分布於森林裡的精靈。\\n\\n‧近距離攻擊\\n‧召喚發光蕈菇\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258100, '寧芙', 258100, 0, 15, 270, 935, 2, 2.5, 0, 0, 0, '【魔法】【中距離】【妨礙】\\n只要找到了可以玩的對象，\\n就會無止盡的捉弄對方，一種森之精靈。\\n\\n‧對從前方算起的第2名角色進行攻擊，並使其行動速度下降\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258200, '幼年巨蜥', 258200, 0, 17, 270, 925, 2, 3.5, 0, 0, 0, '【魔法】\\n儘管還年幼仍然會吐著強力的火焰，是巨蜥的幼獸。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258300, '鬼燈螢', 258300, 0, 0, 270, 940, 1, 4.0, 0, 0, 0, '【恢復】\\n儲存於尾部的蜜，能夠成為\\n魔物的營養源，是種不可思議的螢火蟲。\\n\\n‧單體恢復');
insert into unit_enemy_data values (258400, '鈴蘭蟲', 258400, 0, 0, 270, 945, 2, 4.0, 0, 0, 0, '【恢復】\\n會從尾部的花朵散布鱗粉，\\n治療魔物，是種不可思議的螢火蟲。\\n\\n‧單體恢復');
insert into unit_enemy_data values (258500, '波奇卡', 258500, 0, 17, 270, 950, 2, 3.0, 0, 0, 0, '【魔法】【範圍】【妨礙】\\n棲息於花田，以特殊光線削弱\\n獵物的花蕾魔物,。\\n\\n‧對近距離2名角色進行攻擊，並使其攻擊力降低\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258600, '妖精', 258600, 0, 15, 270, 955, 2, 2.5, 0, 0, 0, '【魔法】【中距離】【妨礙】\\n能夠自由的操控風，將冒險者玩弄於股掌中的花之精靈。\\n\\n‧對從前方算起的第2名角色進行攻擊，並施加麻痺狀態\\n\u3000（即使傷害無效也會施加）\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258700, '噬岩怪', 258700, 0, 15, 270, 1035, 1, 3.5, 0, 0, 0, '【物理】【遠距離】\\n擁有就連岩石都能輕易嚼碎的堅硬牙齒，出沒在山中的精靈。\\n\\n‧對最後方的角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258800, '噬冰怪', 258800, 0, 15, 270, 1040, 1, 3.5, 0, 0, 0, '【物理】【遠距離】\\n以冰塊作為武器甚至食材的雪山的精靈。\\n\\n‧對最後方的角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (258900, '怨火之顱', 258900, 0, 17, 270, 1045, 2, 3.0, 0, 0, 0, '【魔法】【中距離】\\n身纏著地獄之火，\\n且朝著生者襲擊而來的怨念的集合體。\\n\\n‧對從後方算起的第3名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (259000, '雷雲長老', 259000, 0, 0, 270, 1050, 2, 3.0, 0, 0, 0, '【魔法】【中距離】【範圍】【輔助】\\n能夠自在地操縱氣象，能雷擊的雲型魔物。\\n\\n‧對從前方算起的第2、3名角色進行攻擊\\n‧全體魔攻上升\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (259100, '烏帕魯帕', 259100, 0, 0, 270, 1060, 1, 3.0, 0, 0, 0, '【物理】【中距離】【妨礙】\\n棲息於沉積泥沼地之中，\\n能從口中吐出強力的毒，一種山椒魚型魔物。\\n\\n\\n‧對從後方算起的第2名角色進行攻擊，\\n\u3000並施加中毒狀態\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到毒傷害的影響不恢復TP');
insert into unit_enemy_data values (259200, '岩漿烏帕魯帕', 259200, 0, 0, 270, 1065, 1, 3.0, 0, 0, 0, '【物理】【中距離】【妨礙】\\n潛藏在岩漿之海中，會對接近之物吐出灼熱體液，\\n一種山椒魚魔物。\\n\\n‧對從前方算起的第2名角色進行攻擊，並施加火傷狀態\\n‧依據造成的傷害使TP恢復量降低80％\\n‧受到火傷傷害的影響不恢復TP');
insert into unit_enemy_data values (259300, '火焰咒士', 259300, 0, 17, 270, 1075, 2, 3.5, 0, 0, 0, '【魔法】【遠距離】\\n能以地獄喚來火炎，使於魔法上進行攻擊，是一種上級的惡魔。\\n\\n‧對從後方算起的第2名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (259400, '惡魔加農', 259400, 0, 16, 270, 1055, 1, 4.0, 0, 0, 0, '【物理】\\n因為長年的使用而寄生著惡魔之魂，\\n擁有生命的砲台。\\n\\n‧近距離攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (259500, '魔法射線', 259500, 0, 0, 270, 1070, 2, 3.5, 0, 0, 0, '【魔法】【範圍】\\n以頭部石塊發射超音波，\\n輕鬆狩獵的魟魚魔物。\\n\\n‧對近距離2名角色進行攻擊\\n‧依據造成的傷害使TP恢復量降低80％');
insert into unit_enemy_data values (259600, '睿智權杖', 259600, 0, 7, 270, 1080, 2, 4.5, 0, 0, 0, '【魔法】【妨礙】\\n以被噴到就會凍結的寒氣魔法，\\n剝奪敵人戰意的鸚鵡螺型魔物。\\n\\n‧對HP高的角色進行攻擊，並使其攻擊力下降\\n‧依據造成的傷害使TP恢復量降低80％');
