insert into unit_enemy_data values (300100, '牛頭怪', 300100, 0, 3, 270, 3000, 1, 1.9, 0, 0, 0, '【物理】獲得了更多力量的金牛座之魔星獸。\\n超越了力量極限的戰斧一擊，能破壞所有的一切。\\n\\n‧容易受到物理攻擊力降低的效果影響。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自牛頭怪所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照牛頭怪的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並施加火傷狀態。同時小幅度提升自身的\\n\u3000物理攻擊力，直到戰鬥結束。\\n\u3000此技能造成的傷害，將不會恢復TP。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人造成特大幅度的\\n\u3000物理傷害，並施加暈眩狀態，同時造成擊退。\\n\u3000之後，該名敵人的HP低於60％時，\\n\u3000中幅度提升自身的物理攻擊力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧包含飛行狀態的敵人在內，\\n\u3000對敵方全體造成大幅度的魔法傷害，\\n\u3000並小幅度降低物理暴擊傷害與魔法暴擊傷害。');
insert into unit_enemy_data values (300101, '牛頭怪', 300101, 0, 3, 270, 3000, 1, 1.9, 0, 0, 0, '【物理】獲得了更多力量的金牛座之魔星獸。\\n超越了力量極限的戰斧一擊，能破壞所有的一切。\\n\\n‧容易受到物理攻擊力降低的效果影響。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自牛頭怪所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照牛頭怪的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並施加火傷狀態。同時小幅度提升自身的\\n\u3000物理攻擊力，直到戰鬥結束。\\n\u3000此技能造成的傷害，將不會恢復TP。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人造成特大幅度的\\n\u3000物理傷害，施加暈眩狀態的同時造成擊退。\\n\u3000之後，該名敵人的HP低於60％時，\\n\u3000中幅度提升自身的物理攻擊力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧包含飛行狀態的敵人在內，\\n\u3000對敵方全體造成大幅度的魔法傷害，\\n\u3000並小幅度降低物理暴擊傷害與魔法暴擊傷害。\\n‧對距離最近的2名敵人造成特大幅度的物理傷害，\\n\u3000並施加暈眩狀態。\\n\u3000之後，該2名敵人的HP皆低於60％時，\\n\u3000中幅度提升自身的物理攻擊力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (300102, '牛頭怪', 300102, 0, 3, 270, 3000, 1, 1.9, 0, 0, 0, '【物理】獲得了更多力量的金牛座之魔星獸。\\n超越了力量極限的戰斧一擊，能破壞所有的一切。\\n\\n‧容易受到物理攻擊力降低的效果影響。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。\\n‧戰鬥開始時，\\n\u3000展開中幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％的效果。\\n‧中幅度降低來自牛頭怪所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照牛頭怪的物理攻擊力\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並施加火傷狀態。同時小幅度提升自身的\\n\u3000物理攻擊力，直到戰鬥結束。\\n\u3000此技能造成的傷害，將不會恢復TP。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人造成特大幅度的\\n\u3000物理傷害，施加暈眩狀態，同時造成擊退。\\n\u3000之後，該名敵人的HP低於70％時，\\n\u3000中幅度提升自身的物理攻擊力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧包含飛行狀態的敵人在內，\\n\u3000對敵方全體造成大幅度的魔法傷害，\\n\u3000特大幅度降低其物理暴擊傷害與魔法暴擊傷害，\\n\u3000並施加麻痺狀態。\\n\u3000此技能造成的傷害，將不會恢復TP。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的2名敵人造成特大幅度的\\n\u3000物理傷害，施加暈眩狀態。\\n\u3000之後，該2名敵人的HP皆低於70％時，\\n\u3000中幅度提升自身的物理攻擊力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身與敵方全體的\\n\u3000物理攻擊力、魔法攻擊力與行動速度，\\n\u3000並中幅度降低物理防禦力與魔法防禦力。\\n\u3000同時對自身施加將持續恢復TP的狀態。');
insert into unit_enemy_data values (300200, '天蠍毒者', 300200, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '揮舞猛毒之尾棘來降下災厄，天蠍座的魔星獸。\\n守護沙漠之蟲神，因信仰的流失而化為了邪神。\\n\\n・對敵方全體造成大量的物理傷害。\\n\u3000每當Boss使用了一次必殺技，\\n\u3000便會提升給予敵人的傷害。\\n\u3000此效果，最多累積上限為3次。\\n・對前方範圍內的敵人給予中量物理傷害，\\n\u3000並使其中毒。\\n・將自身所被施放的降低素質的效果解除，\\n\u3000並且再額外增加自身的物理防禦力。');
insert into unit_enemy_data values (300201, '天蠍毒者', 300201, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】被徒留在失去信仰的神殿，\\n天蠍座魔星獸。過去那些虔誠的崇敬早已被遺忘，\\n如今只剩下毀滅。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000不會受到戰鬥中被施加的降低防禦力\\n\u3000技能的影響。\\n‧中幅度降低來自天蠍毒者的所有傷害\\n\u3000產生的隊伍角色TP恢復量。\\n‧必殺技能夠對敵方全體\\n\u3000造成大幅度的物理傷害。\\n\u3000在使用過此技能之後，每當進行了攻擊，\\n\u3000天蠍毒者在接下來每次對該敵人所造成的傷害，\\n\u3000都將會有所提升。\\n\u3000此效果最多累積次數為3次。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的中幅度物理傷害，\\n\u3000並且使其陷入中毒狀態。\\n\u3000此技能施加的中毒狀態所造成的傷害\\n\u3000將不會恢復TP。\\n‧中幅度提升自身的\\n\u3000物理攻擊力、物理防禦力、魔法防禦力。');
