insert into unit_enemy_data values (300300, '梅莎路提姆', 300300, 0, 7, 270, 3000, 2, 2.5, 0, 0, 0, '【魔法】獲得了更多力量的牡羊座之魔星獸。\\n在鬱悶難耐的睡夢牢獄中，享盡冒險者們的魂魄。\\n\\n‧能夠將飛行狀態的敵人設為攻擊目標。\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自梅莎路提姆所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並對敵方全體依照其角色最大HP造成\\n\u3000魔法比例傷害。對無法行動狀態的敵人\\n\u3000追加造成大幅度的魔法傷害，\\n\u3000並中幅度降低其TP。\\n\u3000此技能造成的追加傷害將不會恢復TP。\\n‧對前方範圍內的敵人依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n‧無視敵人的挑釁，\\n\u3000對剩餘HP比例最低的1名敵人\\n\u3000施加束縛狀態。當敵方角色的\\n\u3000HP低於60％時，以其角色為中心\\n\u3000對小範圍施加束縛狀態。');
insert into unit_enemy_data values (300301, '梅莎路提姆', 300301, 0, 7, 270, 3000, 2, 2.5, 0, 0, 0, '【魔法】獲得了更多力量的牡羊座之魔星獸。\\n在鬱悶難耐的睡夢牢獄中，享盡冒險者們的魂魄。\\n\\n‧能夠將飛行狀態的敵人設為攻擊目標。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低梅莎路提姆所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照敵人的最大HP，\\n\u3000對敵方全體造成魔法比例傷害。\\n\u3000對陷入無法行動狀態的敵人追加\\n\u3000大幅度的魔法傷害，同時大幅度降低其TP。\\n\u3000此技能造成的追加傷害，將不會恢復TP。\\n‧依照敵人的最大HP，\\n\u3000對前方範圍內的敵人造成魔法比例傷害。\\n‧無視敵人的挑釁，對剩餘HP比例最低的\\n\u30001名敵人施加束縛狀態。\\n\u3000敵人的HP低於60％時，以該名敵人為中心，\\n\u3000對小範圍施加束縛狀態。\\n‧解除敵方全體的護盾，並依照敵人的最大HP，\\n\u3000對敵方全體造成魔法比例傷害，同時施加\\n\u3000短時間的束縛狀態。敵人的HP\\n\u3000低於60％時，施加長時間的束縛狀態。');
insert into unit_enemy_data values (300302, '梅莎路提姆', 300302, 0, 7, 270, 3000, 2, 2.5, 0, 0, 0, '【魔法】獲得了更多力量的牡羊座之魔星獸。\\n在鬱悶難耐的睡夢牢獄中，享盡冒險者們的魂魄。\\n\\n‧能夠將飛行狀態的敵人設為攻擊目標。\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。\\n‧戰鬥開始時，\\n\u3000展開中幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧中幅度降低來自梅莎路提姆所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並對敵方全體依照其角色最大HP造成\\n\u3000魔法比例傷害。對無法行動狀態的敵人\\n\u3000追加造成大幅度的魔法傷害，\\n\u3000並中幅度降低其TP。此技能造成的傷害\\n\u3000將不會恢復TP。\\n‧對前方範圍內的敵人依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n‧無視敵人的挑釁，\\n\u3000對剩餘HP比例最低的1名敵人\\n\u3000施加束縛狀態。當敵方角色的\\n\u3000HP低於70％時，以其角色為中心\\n\u3000對小範圍施加束縛狀態。\\n‧解除敵方全體的護盾，\\n\u3000並對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害\\n\u3000並施加短時間的束縛狀態。\\n\u3000當敵方角色的HP低於70％時，\\n\u3000對其施加長時間的束縛狀態。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧解除敵方全體的護盾，\\n\u3000並對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。同時，\\n\u3000中幅度降低其物理攻擊力、\\n\u3000大幅度降低其魔法攻擊力、\\n\u3000特大幅度降低其HP吸收及HP恢復量。\\n\u3000此技能造成的傷害將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (300400, '阿爾珈提', 300400, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '以妖異的邪教圖謀支配世界，魔羯座的魔星獸。\\n對在沙漠受誘惑而迷途的人，以惡魔的儀式進行俘虜。\\n\\n‧必殺技對可全體敵人造成大幅度的魔法傷害，\\n\u3000並且降低物理攻擊角色的物理攻擊力，\\n\u3000與降低魔法攻擊角色的魔法防禦力。\\n‧對物理攻擊力最高的 1 名角色造成\\n\u3000特大幅度的魔法傷害，並降低其 TP。\\n‧對前方的 2 名角色造成小幅度的魔法傷害，\\n\u3000並且施加沉默狀態。\\n\\n‧再對全體敵人造成中幅度的魔法傷害。');
insert into unit_enemy_data values (300401, '阿爾珈提', 300401, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】窮極了邪教的秘術，摩羯座的魔星獸。\\n能隨心所欲地操弄龐大魔力，\\n進而剝奪萬物的自由。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自阿爾珈提\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對進行物理攻擊的敵方全體\\n\u3000大幅度降低其物理攻擊力，\\n\u3000同時對進行魔法攻擊的敵方全體\\n\u3000大幅度降低其魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000在進行物理攻擊或是魔法攻擊的角色\\n\u3000只有飛行狀態的角色時也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且使其陷入沉默狀態。\\n\u3000之後，對敵方全體\\n\u3000造成大幅度的魔法傷害。\\n\u3000此技能造成的全體傷害\\n\u3000將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且中幅度降低TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (300402, '阿爾珈提', 300402, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】窮極了邪教的秘術，摩羯座的魔星獸。\\n能隨心所欲地操弄龐大魔力，\\n進而剝奪萬物的自由。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自阿爾珈提\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對進行物理攻擊的敵方全體\\n\u3000大幅度降低其物理攻擊力，\\n\u3000同時對進行魔法攻擊的敵方全體\\n\u3000大幅度降低其魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000在進行物理攻擊或是魔法攻擊的角色\\n\u3000只有飛行狀態的角色時也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且使其陷入沉默狀態。\\n\u3000之後，對敵方全體造成大幅度的魔法傷害。\\n\u3000此技能造成的全體傷害將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且中幅度降低TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成大幅度的魔法傷害，\\n\u3000並且使其陷入沉默狀態。\\n\u3000此技能造成的傷害將不會恢復TP。');
insert into unit_enemy_data values (300403, '阿爾珈提', 300403, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】窮極了邪教的秘術，摩羯座的魔星獸。\\n能隨心所欲地操弄龐大魔力，\\n進而剝奪萬物的自由。\\n\\n‧必殺技可對全體敵人造成大幅度的魔法傷害，\\n\u3000並且降低物理攻擊角色的物理攻擊力，\\n\u3000同時降低魔法攻擊角色的魔法攻擊力。\\n‧對前方的1名角色造成特大幅度的魔法傷害，\\n\u3000並且使其沉默。\\n\u3000同時對全體敵人造成中幅度的魔法傷害。\\n‧對物理攻擊力最高的 1 名角色造成\\n\u3000大幅度的魔法傷害，並降低其TP。\\n‧當阿爾珈提受到中毒、猛毒、詛咒、火傷\\n\u3000等等效果時，將會對自己施加沉默狀態，\\n\u3000同時造成大幅度的魔法傷害。\\n\u3000當沒有受到以上效果時，\\n\u3000將會對全體敵人造成沉默狀態，\\n\u3000並且同時造成大幅度的魔法傷害。');
insert into unit_enemy_data values (300404, '阿爾珈提', 300404, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】窮極了邪教的秘術，摩羯座的魔星獸。\\n能隨心所欲地操弄龐大魔力，\\n進而剝奪萬物的自由。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開中幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自阿爾珈提\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧大幅度降低來自敵方\\n\u3000所有傷害產生的阿爾珈提TP恢復量。\\n‧必殺技會對進行物理攻擊的敵方全體\\n\u3000特大幅度降低其物理攻擊力，\\n\u3000同時對進行魔法攻擊的敵方全體\\n\u3000特大幅度降低其魔法攻擊力。\\n\u3000並且特大幅度降低敵方全體的TP上昇。\\n\u3000此技能所造成的攻擊力降低效果，\\n\u3000在進行物理攻擊或是魔法攻擊的角色\\n\u3000只有飛行狀態的角色時也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且使其陷入UB封印狀態。\\n\u3000之後，對敵方全體造成\\n\u3000中幅度的魔法傷害。\\n\u3000當距離最近的1名敵人的HP在70％以下時，\\n\u3000對敵方全體造成特大幅度的魔法傷害，\\n\u3000並且使其陷入UB封印狀態。\\n\u3000此技能造成的全體傷害將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且中幅度降低TP。\\n\u3000之後，當距離最近的1名敵人\\n\u3000的HP在70％以下時，\\n\u3000中幅度降低敵方全體的TP，\\n\u3000並且大幅度恢復自身的TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並且使其陷入沉默狀態。\\n\u3000此技能造成的傷害將不會恢復TP。');
insert into unit_enemy_data values (300500, '蜥蜴人', 300500, 0, 4, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】能自在地揮舞\\n手中的兩把劍刃，\\n還可以使用煙霧彈來模糊\\n對手視野的巨蜥戰士。\\n\\n‧對隨機 1 名敵人造成中幅度的物理傷害。\\n\\n‧對以隨機敵人為中心的小範圍內，\\n\u3000造成小幅度的物理傷害，\\n\u3000並且施加黑暗狀態。');
insert into unit_enemy_data values (300600, '爬行者', 300600, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】能從頭部施放劇毒\\n與藤蔓所組成的範圍物理攻擊，\\n能將貿然接近的冒險家的 HP \\n給一口氣奪走的魔性之花。\\n\\n‧對前方的 2 名角色造成中幅度的物理傷害。\\n\\n‧對前方的 3 名角色降低其物理防禦力，\\n\u3000並且施加中毒狀態。');
insert into unit_enemy_data values (300601, '芒刺爬行者', 300601, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因奇異的力量而變質的爬行者。\\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\\n\\n・對前方範圍內的敵人造成中幅度的物理傷害，\\n並使其呈現麻痺狀態。\\n・對前方範圍內的敵人造成物理防禦力下降，\\n並使其呈現中毒狀態。');
insert into unit_enemy_data values (300602, '芒刺爬行者', 300602, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因特殊力量而變質的爬行者。\\n用擁有無數根尖刺的藤蔓包裹住對方，剝奪其行動。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧承受來自芒刺爬行者所有的傷害，\\n\u3000導致中幅度降低\\n\u3000隊伍角色的TP恢復量。\\n‧必殺技會對距離最近的2名角色造成中幅度的\\n\u3000物理傷害，並使其陷入拘束狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度降低距離最近的3名角色的物理防禦力，\\n\u3000並使其陷入中毒狀態。\\n\u3000此技能施加的中毒狀態所造成的傷害\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (300603, '芒刺爬行者', 300603, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因特殊力量而變質的爬行者。\\n用擁有無數根尖刺的藤蔓包裹住對方，剝奪其行動。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧承受來自芒刺爬行者所有的傷害\\n\u3000導致中幅度降低\\n\u3000隊伍角色的TP恢復量。\\n‧必殺技會對距離最近的2名角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並使其陷入拘束狀態，同時大幅度降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度降低距離最近的3名角色的物理防禦力，\\n\u3000並使其陷入中毒狀態。\\n\u3000此技能施加的中毒狀態所造成的傷害\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (300604, '沙之爬行者', 300604, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】棲息於廣闊砂海的\\n巨大食人植物魔物。以帶有凶惡針刺的藤蔓\\n封鎖冒險者的行動並捕食。\\n\\n‧必殺技會對前方的2名角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並且使其陷入拘束狀態。\\n\\n‧降低前方3名角色的物理防禦力，\\n\u3000並且使其陷入中毒狀態。');
insert into unit_enemy_data values (300605, '芒刺爬行者', 300605, 0, 2, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】因特殊力量而變質的爬行者。\\n用擁有無數根尖刺的藤蔓包裹住對方，剝奪其行動。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧承受來自芒刺爬行者所有的傷害\\n\u3000導致中幅度降低\\n\u3000隊伍角色的TP恢復量。\\n‧必殺技會對距離最近的2名角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並使其陷入拘束狀態，同時大幅度降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度降低距離最近的3名角色的物理防禦力，\\n\u3000並使其陷入中毒狀態。\\n\u3000此技能施加的中毒狀態所造成的傷害\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (300606, '芒刺爬行者', 300606, 0, 2, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】被特異之力影響而產生變質的爬行者。\\n以擁有著無數針刺的藤蔓纏繞敵人，遏止行動。\\n\\n‧中幅度降低來自芒刺爬行者所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對距離最近的2名敵人\\n\u3000造成大幅度的物理傷害。\\n\u3000同時施加拘束狀態\\n\u3000並特大幅度降低其TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧大幅度降低距離最近的3名敵人的\\n\u3000物理防禦力，同時施加中毒狀態。\\n\u3000此技能施加的中毒狀態所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (300700, '獸人領導者', 300700, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】以族長的姿態鼓舞著群體，\\n以能震裂大地般的強力連擊\\n造成敵人大幅度的物理傷害，\\n是半獸人中的半獸人。\\n\\n‧對隨機3名敵人造成\\n\u3000中幅度的物理傷害。\\n\\n‧提升全體夥伴的物理攻擊力。');
insert into unit_enemy_data values (300701, '獸人酋長', 300701, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】被特異之力影響而產生變質的獸人領導者\\n手持巨大盾牌，能揮出攻防一體的連擊。\\n\\n‧容易受到物理攻擊力降低的效果影響。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自獸人酋長\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照獸人酋長的物理攻擊力，\\n\u3000對前方3名角色造成固定的物理傷害，\\n\u3000並施加暈眩狀態。此技能\\n\u3000所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧小幅度降低前方2名角色的TP，\\n\u3000並特大幅度降低HP吸收。同時\\n\u3000小幅度恢復自身的TP，大幅度提升行動速度。\\n‧普通攻擊會依照獸人酋長的\\n\u3000物理攻擊力，對最靠近的1名敵人\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (300702, '獸人酋長', 300702, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】被特異之力影響而\\n產生變質的獸人領導者。手持巨大盾牌，\\n能揮出攻防一體的連擊。\\n\\n‧容易受到物理攻擊力降低的效果影響。\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自獸人酋長\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的\\n\u3000獸人酋長TP恢復量。\\n‧必殺技會依照獸人酋長的物理攻擊力，\\n\u3000對前方3名角色造成固定的物理傷害，\\n\u3000並施加暈眩狀態。並且若使用\\n\u3000此技能將敵人擊倒，\\n\u3000則能特大幅度恢復自身的TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧小幅度降低前方2名角色的TP，\\n\u3000並特大幅度降低HP吸收。同時\\n\u3000小幅度恢復自身的TP，\\n\u3000小幅度提升物理防禦力，\\n\u3000以及大幅度提升行動速度。\\n‧普通攻擊會依照獸人酋長的\\n\u3000物理攻擊力，對最靠近的1名敵人\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (300703, '獸人酋長', 300703, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】因奇異的力量而變質的\\n獸人領導者。手持巨大盾牌，\\n能揮出攻防一體的連擊。\\n\\n‧必殺技能夠對前方3名角色造成\\n\u3000大幅度的物理傷害，並使其暈眩。\\n\\n‧大幅度降低前方2名角色的\\n\u3000物理防禦力。');
