insert into unit_enemy_data values (301003, '希德雷克', 301003, 0, 14, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】吐出強力冰之吐息將前衛瞬間冰凍，\\n接著將身軀一口咬碎的凶猛水龍。\\n\\n‧中幅度降低來自希德雷克所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、分身以外\\n\u3000距離最近的3名敵人\\n\u3000造成特大幅度的物理傷害，\\n\u3000並特大幅度降低其物理防禦力與魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對除了召喚物、分身以外距離最近的2名敵人\\n\u3000造成大幅度的魔法傷害，\\n\u3000並且施加短時間的凍結狀態。\\n\u3000當敵方角色的HP低於70％時，\\n\u3000對其施加長時間的凍結狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (301100, '殘暴狂熊', 301100, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】以不符合體格的靈敏度\\n以及撕咬攻擊奪取體力，\\n並以巨大的爪子撕裂敵人，\\n是慘無人道的森林支配者。\\n\\n‧對前方的 4 名角色造成大幅度的\\n\u3000物理傷害。\\n\\n‧對前方的 1 名角色造成小幅度的\\n\u3000物理傷害，並降低其物理攻擊力。');
insert into unit_enemy_data values (301101, '狂焰戰熊', 301101, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】被特異之力影響而產生變質\\n的殘暴狂熊。用蘊含怒氣的鐵臂\\n將敵人連著地面一同撕裂。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果\\n\u3000而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自狂焰戰熊\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會以距離\\n\u3000最近的1名敵人為中心，\\n\u3000造成小範圍的特大幅度物理傷害，\\n\u3000並使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度物理傷害，\\n\u3000並特大幅度降低物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (301102, '狂焰戰熊', 301102, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】被特異之力影響而產生變質\\n的殘暴狂熊。用蘊含怒氣的鐵臂\\n將敵人連著地面一同撕裂。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果\\n\u3000而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降\\n\u3000技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低\\n\u3000敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低\\n\u3000減少100％效果。\\n‧中幅度降低來自狂焰戰熊\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方\\n\u3000所有傷害產生的\\n\u3000狂焰戰熊TP恢復量。\\n‧必殺技會以距離最近的\\n\u30001名敵人為中心，\\n\u3000造成小範圍的特大幅度物理傷害，\\n\u3000同時造成擊退，\\n\u3000並使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度物理傷害，\\n\u3000並特大幅度降低物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (301103, '狂焰戰熊', 301103, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】被特異之力影響而產生變質\\n的殘暴狂熊。用蘊含怒氣的鐵臂\\n將敵人連著地面一同撕裂。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果\\n\u3000而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧大幅度降低來自狂焰戰熊\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會以最靠近的1名敵人\\n\u3000為中心，造成小範圍的\\n\u3000大幅度物理傷害，\\n\u3000並使其暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以最靠近的1名敵人為中心，\\n\u3000造成小範圍的大幅度物理傷害，\\n\u3000並大幅度降低物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (301104, '瘋狂巨熊', 301104, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】因塔之魔力而變狂暴的森林支配者。\\n因陷入瘋狂而使出的斬擊，將周遭一切粉碎殆盡。\\n\\n‧戰鬥開始時，對自己使用傷害上限技能，\\n\u3000在一次行動中受到的傷害超過一定值時，\\n\u3000減少所受到的傷害量。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧小幅度降低來自瘋狂巨熊\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會以最靠近的1名敵人為中心，\\n\u3000造成小範圍的特大幅度物理傷害，\\n\u3000並使其暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以最靠近的1名敵人為中心，\\n\u3000造成小範圍的大幅度物理傷害，\\n\u3000並特大幅度降低物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (301105, '狂焰戰熊', 301105, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】被特異之力影響而產生變質\\n的殘暴狂熊。用蘊含怒氣的鐵臂\\n將敵人連著地面一同撕裂。\\n\\n‧必殺技會以距離最近的\\n\u30001名敵人為中心，\\n\u3000造成小範圍的特大幅度物理傷害，\\n\u3000並對其施加暈眩。\\n\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的大幅度物理傷害，\\n\u3000並大幅度降低物理防禦力。');
insert into unit_enemy_data values (301200, '碎裂魔像', 301200, 0, 13, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】能無止盡地召喚出魔像，\\n並以尖銳岩石削弱其體力與精神。\\n用魔力將無數的魔像殘骸\\n重塑而成的巨大魔像。\\n\\n‧對前方的4名角色造成中幅度物理傷害，\\n\u3000並減少其 TP。\\n\\n‧召喚魔像。');
insert into unit_enemy_data values (301201, '水晶魔像', 301201, 0, 13, 270, 1000, 1, 1.3, 0, 0, 0, '【物理】以露娜的魔力結晶覆蓋全身的\\n巨大魔像。身上圍繞的結晶既是堅固的盾，\\n也是貫穿一切的矛。\\n\\n‧當水晶魔像受到中毒、猛毒、詛咒、\\n\u3000火傷等效果時，將會大幅度降低行動速度。\\n‧必殺技會解除所有自身的減益效果，\\n\u3000大幅度提升物理防禦力與魔法防禦力。\\n\u3000同時為自身以外的我方\\n\u3000中幅度提升物理防禦力與魔法防禦力。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並使其陷入暈眩狀態。\\n‧對前方範圍內的敵人造成大幅度的物理傷害，\\n\u3000並小幅度提升我方全體的物理攻擊力與\\n\u3000魔法攻擊力。並且中幅度恢復自己的TP。\\n‧當自身的HP低於40％時，\\n\u3000攻擊變得更加兇暴。');
insert into unit_enemy_data values (301202, '寒冰魔像', 301202, 0, 13, 270, 1000, 1, 1.3, 0, 0, 0, '【物理】潛伏在冰凍大地的洞窟中的\\n巨大魔像。身上圍繞的\\n結晶既是堅固的盾也是矛。\\n\\n‧當寒冰魔像受到中毒、猛毒、詛咒\\n\u3000火傷等效果時，將會\\n\u3000大幅度降低行動速度。\\n\\n‧解除所有自身的減益效果，\\n\u3000中幅度提升物理防禦力與\\n\u3000魔法防禦力。\\n\\n‧對敵方全體造成中幅度的\\n\u3000物理傷害。\\n\\n‧對前方範圍內的敵人造成大幅度的\\n\u3000物理傷害。');
insert into unit_enemy_data values (301203, '寒冰魔像', 301202, 0, 13, 270, 1000, 1, 1.3, 0, 0, 0, '【物理】潛伏在冰凍大地的洞窟中的\\n巨大魔像。身上圍繞的\\n結晶既是堅固的盾也是矛。\\n\\n‧當寒冰魔像受到中毒、猛毒、詛咒\\n\u3000火傷等效果時，將會\\n\u3000大幅度降低行動速度。\\n\\n‧解除所有自身的減益效果，\\n\u3000中幅度提升物理防禦力與\\n\u3000魔法防禦力。\\n\\n‧對敵方全體造成中幅度的\\n\u3000物理傷害。\\n\\n‧對前方範圍內的敵人造成大幅度的\\n\u3000物理傷害。');
insert into unit_enemy_data values (301300, '大胃王', 301300, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '惡名昭彰的暴食之兇獸，是為天秤座的魔星獸。\\n秤量被剝奪之魂的善惡，以品嘗之間差異為樂。\\n\\n・不具有對敵人的攻擊所造成的麻痺、暈眩、\\n\u3000束縛，以及行動速度低下的抗性。\\n・對敵方全體造成大量的魔法傷害，並且對自\\n\u3000身的物理與魔法攻擊力給予特大量上升。\\n・對後方範圍內的敵人造成中量的魔法傷害。\\n・對前方範圍內的敵人造成中量的魔法傷害，並\\n\u3000小幅度降低TP。\u3000並且使自身的TP，中幅度\\n\u3000上升。');
insert into unit_enemy_data values (301301, '大胃王', 301301, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】貪圖著萬物的三魂七魄，\\n天秤座的魔星獸。那天秤，與公正背道而馳地\\n傾向了那暴食獸的食慾。\\n\\n‧容易受到物理攻擊力降低與\\n\u3000魔法攻擊力降低的效果影響。\\n‧戰鬥開始時，小幅度提升\\n\u3000自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自大胃王\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照自身的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000同時依照自身的魔法攻擊力，\\n\u3000降低其物理攻擊力與魔法攻擊力。\\n\u3000同時特大幅度提升自身的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能所造成的傷害\\n\u3000會使敵人無法恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最遠的2名敵人\\n\u3000造成特大幅度的物理傷害。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並依照自身的魔法攻擊力，降低其TP。\\n\u3000同時中幅度提升自身的TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧當左側的目標被破壞時，\\n\u3000將特大幅度降低自身的物理攻擊力。\\n‧當中央的目標被破壞時，\\n\u3000將大幅度降低自身的物理防禦力與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將特大幅度降低自身的魔法攻擊力。');
insert into unit_enemy_data values (301302, '大胃王', 301302, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】貪圖著萬物的三魂七魄，\\n天秤座的魔星獸。那天秤，與公正背道而馳地\\n傾向了那暴食獸的食慾。\\n\\n‧容易受到物理攻擊力降低與\\n\u3000魔法攻擊力降低的效果影響。\\n‧戰鬥開始時，小幅度提升\\n\u3000自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自大胃王\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照自身的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000同時依照自身的魔法攻擊力，\\n\u3000降低其物理攻擊力與魔法攻擊力。\\n\u3000同時特大幅度提升自身的\\n\u3000物理攻擊力與魔法攻擊力。此技能所造成的傷害\\n\u3000會使敵人無法恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最遠的2名敵人\\n\u3000造成特大幅度的物理傷害。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並依照自身的魔法攻擊力，降低其TP。\\n\u3000同時中幅度提升自身的TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧對TP最高的3名敵人施加束縛狀態，\\n\u3000並依照自身的魔法攻擊力，\\n\u3000降低其行動速度。\\n\u3000TP達到最大值的角色不在此內。\\n‧當左側的目標被破壞時，\\n\u3000將特大幅度降低自身的物理攻擊力。\\n‧當中央的目標被破壞時，\\n\u3000將大幅度降低自身的物理防禦力與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將特大幅度降低自身的魔法攻擊力。');
insert into unit_enemy_data values (301303, '大胃王', 301303, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】貪圖著萬物的三魂七魄，天秤座的魔星獸。\\n那天秤，與公正背道而馳地傾向了那暴食獸的食慾。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力和魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧承受來自大胃王所有的傷害，\\n\u3000導致小幅度\\n\u3000降低隊伍角色的TP恢復量。\\n‧必殺技能夠對敵方全體造成\\n\u3000大幅度的魔法傷害，\\n\u3000同時大幅度提升自身的物理攻擊力與\\n\u3000魔法攻擊力。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧對距離最遠的2名角色造成中幅度的魔法傷害。\\n‧對距離最近的3名角色造成小幅度的魔法傷害，\\n\u3000並且小幅度降低其TP。\\n\u3000同時中幅度提升自身的TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧當左側的目標被破壞時，將會大幅度\\n\u3000降低自身的物理防禦力與魔法防禦力。\\n‧當中央的目標被破壞時，將會特大幅度\\n\u3000降低自身的魔法攻擊力。\\n‧當右側的目標被破壞時，將會大幅度\\n\u3000降低自身的物理防禦力與魔法防禦力。');
