insert into unit_enemy_data values (301600, '席茲', 301600, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】帶來強烈暴風的大怪鳥。\\n逃出魔物圖鑑，做盡破壞之能事。\\n\\n・根據角色的最高 HP，對全體敵人\\n造成大幅度的傷害。\\n・對前方範圍內的敵人造成中幅度的物理傷害，\\n並且使其暈眩。');
insert into unit_enemy_data values (301601, '席茲', 301600, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】帶來強烈暴風的大怪鳥。\\n逃出魔物圖鑑，做盡破壞之能事。\\n\\n・根據角色的最高 HP，對全體敵人\\n造成大幅度的傷害。\\n・對前方範圍內的敵人造成中幅度的物理傷害，\\n並且使其暈眩。\\n・根據角色的最高 HP，對前方範圍內的敵人\\n造成中幅度的傷害，並降低其物理防禦力。\\n・當席茲本身的 HP 降至 50％ 以下，\\n其攻擊力將會越發殘暴。');
insert into unit_enemy_data values (301602, '暴風‧席茲', 301602, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】盤據在沙島的凶猛大怪鳥。\\n牠捲起紫電龍捲風的模樣，\\n被視為天災而遭人畏懼。\\n\\n‧必殺技能夠對敵方全體依照其\\n\u3000角色最大HP造成物理比例傷害。\\n\\n‧以前方的第1名敵人為中心，\\n\u3000對小範圍且最多2名的角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並且使其暈眩。');
insert into unit_enemy_data values (301700, '賈巴沃克', 301700, 0, 3, 270, 1000, 1, 2.6, 0, 0, 0, '【物理】棲息在毒瘴闇稜巔峰的醜惡之狂獸。\\n以猛毒的瘴氣奪走生命，透過食腐做為力量來源。\\n\\n‧必殺技可以對全體敵人造成大幅度的魔法傷害，\\n\u3000並且使其陷入中毒狀態，同時擊退。\\n‧對面前2名角色造成物理傷害，同時降低物理\\n\u3000攻擊力與TP。\\n‧當賈巴沃克自身的血量降至40%以下時，\\n\u3000其攻擊力將會更加殘暴，\\n\u3000同時再召喚出3隻怪物。\\n‧當剩餘時間不到20秒時，將會對全體敵人造成\\n\u3000強力的中毒狀態，同時特大幅度減少自己的TP。');
insert into unit_enemy_data values (301800, '人馬座', 301800, 0, 3, 270, 1000, 1, 2.0, 0, 0, 0, '將衝向天際的許多標的射倒，射手座的魔星獸。\\n令纏繞著電芒的箭，在剎那間貫穿一切的至高絕技。\\n\\n‧必殺技可對所有敵人依最大 HP\\n\u3000造成魔法傷害。\\n\u3000可對黑暗狀態的敵人造成 3 倍傷害。\\n‧對全體敵人造成中幅度的魔法傷害，\\n\u3000並對剩餘 HP 比例最低的 1 名敵人\\n\u3000施加強力黑暗狀態。\\n‧對前方的 3 名敵人造成物理傷害。');
insert into unit_enemy_data values (301801, '人馬座', 301801, 0, 3, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】守護著星穹神殿的射手座魔星獸。\\n貫穿萬物的一閃，是以紫電輝煌此神域的絕技。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自人馬座所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的魔法傷害，\\n\u3000並依照【黑星】的數量，造成追加的魔法傷害。\\n‧對敵方全體造成小幅度的魔法傷害，並在自身\\n\u3000的箭矢上追加1個【黑星】。同時對前方大範圍\\n\u3000內的敵人造成大幅度的魔法傷害，範圍內有敵人\\n\u3000在場上時，在自身的箭矢上追加1個【黑星】。\\n‧對距離最近的2名敵人造成中幅度的物理傷害，\\n\u3000中幅度降低物理防禦力。此技能所造成的狀態\\n\u3000異常，即使傷害無效也會施加。');
insert into unit_enemy_data values (301802, '人馬座', 301802, 0, 3, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】守護著星穹神殿的射手座魔星獸。\\n貫穿萬物的一閃，是以紫電輝煌此神域的絕技。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自人馬座所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的魔法傷害，\\n\u3000並依照【黑星】的數量，造成追加的魔法傷害。\\n\u3000此技能所造成的傷害，會大幅度降低TP恢復\\n\u3000量。\\n‧對敵方全體造成小幅度的魔法傷害，並在自身\\n\u3000的箭矢上追加1個【黑星】。同時對前方大範圍\\n\u3000內的敵人造成大幅度的魔法傷害，範圍內有敵人\\n\u3000在場上時，在自身的箭矢上追加2個【黑星】。\\n\u3000此技能所造成的傷害，會大幅度降低TP恢復\\n\u3000量。\\n‧對距離最近的2名敵人造成中幅度的物理傷害，\\n\u3000並中幅度降低其物理防禦力。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。\\n‧解除敵方全體的護盾，並造成小幅度的魔法\\n\u3000傷害，同時對前方大範圍內的敵人造成大幅度\\n\u3000的魔法傷害。此技能所造成的傷害，會大幅度\\n\u3000降低TP恢復量。');
insert into unit_enemy_data values (301803, '人馬座', 301803, 0, 3, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】守護著星穹神殿的射手座魔星獸。\\n貫穿萬物的一閃，是以紫電輝煌此神域的絕技。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧戰鬥開始時，展開中幅度降低敵方全體TP上升\\n\u3000的領域。\\n‧戰鬥開始時，對自身施加TP降低減少100％\\n\u3000效果。\\n‧中幅度降低來自人馬座所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的人馬座\\n\u3000TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的魔法傷害，\\n\u3000並依照【黑星】的數量，造成追加的魔法比例\\n\u3000傷害。此技能造成的傷害將不會恢復TP。\\n‧對敵方全體造成小幅度的魔法傷害，並在自身\\n\u3000的箭矢上追加1個【黑星】。同時對前方大範圍\\n\u3000內的敵人造成大幅度的魔法傷害，範圍內有敵人\\n\u3000在場上時，在自身的箭矢上追加2個【黑星】。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧對距離最近的2名敵人造成中幅度的物理傷害，\\n\u3000並大幅度降低其物理防禦力。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。\\n‧解除敵方全體的護盾，並造成小幅度的魔法\\n\u3000傷害，同時對前方大範圍內的敵人造成大幅度\\n\u3000的魔法傷害。同時依照【黑星】的數量，恢復\\n\u3000自身的TP。此技能造成的傷害將不會恢復\\n\u3000TP。');
insert into unit_enemy_data values (301900, '嘯浪虎鯨', 301900, 0, 11, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】以銳利的眼神追尋獵物\\n及浪濤的海中獵人。\\n利用魔力讓潮水不斷湧出\\n進而削弱敵方的鋒芒和氣力。\\n\\n‧對前方的 4 名角色造成中幅度的物理傷害，\\n\u3000並降低其物理攻擊力。\\n\\n‧對前方的 2 名角色造成小幅度的魔法傷害，\\n\u3000並降低其 TP。');
insert into unit_enemy_data values (301901, '殺人虎鯨', 301901, 0, 11, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】藉由月之魔力更趨兇暴化的大海之獵人。\\n以經過嚴峻鍛鍊的身驅，施展化為利刃的水流之擊。\\n\\n‧對前方4名角色造成大幅度的物理傷害，\\n\u3000並且中幅度降低物理攻擊力。同時小幅度\\n\u3000提升全體夥伴的TP上升。\\n‧對前方小範圍造成中幅度的魔法傷害，\\n\u3000同時大幅度降低TP。\\n‧以從後方數來第2名的角色為中心，\\n\u3000造成小範圍的中幅度物理傷害，同時\\n\u3000大幅度降低魔法攻擊力。');
insert into unit_enemy_data values (302000, '狂暴格里芬', 302000, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】在嚴酷的環境中進化的小小格里芬。\\n用龍捲風將獵物掃於天際，在空中狩之。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自狂暴格里芬所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、\\n\u3000分身以外距離最近的3名敵人\\n\u3000造成中幅度魔法傷害。');
insert into unit_enemy_data values (302001, '狂暴格里芬', 302001, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】在嚴酷的環境中進化的小小格里芬。以龍捲風將獵物捲起，並在空中獵捕。\\n\\n・對前方範圍內的敵人造成中幅度的魔法傷害。');
insert into unit_enemy_data values (302002, '狂暴格里芬', 302002, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】在嚴酷的環境中進化的小小格里芬。\\n用龍捲風將獵物掃於天際，在空中狩之。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。 \\n‧戰鬥開始時，展開小幅度降低敵方全體TP\\n\u3000上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧中幅度降低來自狂暴格里芬所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、\\n\u3000分身以外距離最近的3名敵人\\n\u3000造成中幅度的魔法傷害並擊退。\\n\u3000同時，當角色的HP低於70％時，\\n\u3000施加暈眩狀態。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對除了召喚物、分身以外\\n\u3000距離最近的3名敵人\\n\u3000造成中幅度的魔法傷害，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (302003, '暴風獅鷲', 302003, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】在嚴峻的環境中生存下來的兇猛獅鷲。\\n強健的翅膀所拍打出的強風，\\n能輕易地吹走冒險者。\\n\\n‧必殺技會對前方的3名角色\\n\u3000造成中幅度的魔法傷害，\\n\u3000同時造成擊退。\\n\u3000當該角色的HP低於70％時，\\n\u3000施加暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\\n‧對前方的3名角色\\n\u3000造成中幅度的魔法傷害，\\n\u3000且小幅度降低魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
