insert into unit_enemy_data values (302200, '逝者・卡拉迪翁', 302200, 0, 16, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】因露娜的魔力而甦醒的古代機械兵器。\\n施放無情的魔力以阻擋入侵者\\n\\n‧對敵方全體造成特大幅度的魔法傷害，\\n\u3000並大幅度降低魔法防禦力。\\n‧對前方的3名角色造成大幅度的物理傷害。\\n‧對前方的2名角色造成大幅度的魔法傷害，\\n\u3000並使其陷入麻痺狀態。');
insert into unit_enemy_data values (302300, '睿智之鴞', 302300, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】棲息於大樹之頂，絕頂聰明的大梟。\\n融會了森林之生者的英智，能巧妙地施展魔術。\\n\\n‧必殺技可以對全體敵人造成魔法傷害。\\n\\n‧隨機對1名角色造成物理傷害，同時使其\\n\u3000陷入麻痺狀態。');
insert into unit_enemy_data values (302400, '電弧守護者', 302400, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】自遠古便守護此遺跡至今的人工生命體。\\n藉由製造者的秘術，獲得了能永遠行動的無限動力。\\n\\n‧必殺技可以對全體敵人造成大幅度的魔法傷害。\\n\\n‧對前方的範圍內造成物理傷害。\\n‧對全體敵人造成魔法傷害，同時使其\\n\u3000陷入沉默狀態。');
insert into unit_enemy_data values (302500, '奇美拉', 302500, 0, 16, 270, 1000, 1, 1.4, 0, 0, 0, '【物理】居於孤塔之頂，一隻暴虐的混合魔物。\\n以那千變萬化的猛攻，屠殺了無數的挑戰者。\\n\\n‧必殺技可以對前方範圍內造成大幅度的\\n\u3000物理傷害，同時造成擊退。\\n‧對前方3名角色造成物理傷害，同時使其\\n\u3000陷入火傷狀態。\\n‧對全體敵人造成魔法傷害，同時降低行動速度、\\n\u3000物理防禦力、魔法防禦力。\\n‧當剩餘時間不到10秒時，將會對全體敵人造成\\n\u3000極大的魔法傷害，再特大幅度減少自己的TP。');
insert into unit_enemy_data values (302600, '野豬雙胞胎', 302600, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】獲得更多力量的雙子座的魔星獸。\\n在前線一邊防禦，一邊用熟練的技能進行攻擊。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自野豬雙胞胎\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000依照其最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力與魔法攻擊力。\\n\u3000此技能所造成的傷害，不會使角色恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對後方範圍內的最多3名角色造成\\n\u3000中幅度的物理傷害，並施加暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對後方範圍內的最多3名角色造成\\n\u3000中幅度的物理傷害，並小幅度降低TP。\\n\u3000之後前方的野豬雙胞胎會移動到前方。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能所造成的傷害會使角色無法恢復TP。');
