insert into unit_enemy_data values (302803, '奧爾獅', 302803, 0, 16, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】君臨於荒涼原野的獅子座之魔星獸。\\n那銳爪撕裂著大氣，\\n以業火之咆嘯使大地化為焦黑。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開中幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧小幅度降低來自奧爾獅\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的\\n\u3000奧爾獅TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的物理傷害，\\n\u3000同時使其短時間內陷入火傷狀態。\\n\u3000當角色的HP低於70％時，\\n\u3000使其長時間陷入火傷狀態。\\n\u3000此技能所造成的傷害，會使角色無法恢復TP。\\n\u3000此技能施加的火傷狀態\\n\u3000所造成的傷害，將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方1名角色造成特大幅度的物理傷害。\\n\u3000該角色陷入火傷狀態時\\n\u3000追加固定的物理傷害。此技能所造成的傷害，\\n\u3000會使角色無法恢復TP。\\n‧對前方範圍內的敵人造成大幅度的物理傷害，\\n\u3000其陷入火傷狀態時\\n\u3000追加固定的物理傷害。此技能所造成的傷害，\\n\u3000會使角色無法恢復TP。\\n‧對敵方全體依照其最大HP造成魔法比例傷害，\\n\u3000並使其短時間內陷入暈眩狀態。\\n\u3000當角色的HP低於50％時，\\n\u3000使其長時間陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧無視敵方的挑釁，\\n\u3000以從前方數來第2名的角色為中心，\\n\u3000對範圍內造成持續的魔法傷害，\\n\u3000並展開小幅度降低行動速度的領域。\\n\u3000此技能所造成的傷害會使敵人無法恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (302900, '水之里歐斯', 302900, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '淪陷於寄宿在神瓶之中那魔力的水瓶座之魔星獸。\\n沉溺於強大的力量中，卻因非得支配大海而暴走。\\n\\n‧必殺技，可對全體敵人\\n\u3000造成共計六次的小幅度魔法傷害。\\n\u3000當角色的 HP 低於 80% 時，\\n\u3000能對其造成擊退\\n\u3000而當低於 40% 時，\\n\u3000除了擊退之外還能夠其陷入暈眩狀態。\\n・對面前的 3 名敵人造成中幅度的物理傷害。\\n・能將自身的素質降低效果，\\n\u3000與敵方全體的素質提升的效果打消解除，\\n\u3000並且還能夠增加自己的TP。');
insert into unit_enemy_data values (302901, '水之里歐斯', 302901, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】操縱寄宿於瓶中之魔力的\\n水瓶座之魔星獸。\\n以激流將萬物吞噬殆盡的蒼海支配者為名而君臨。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自水之里歐斯所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000造成大幅度的魔法傷害，同時造成擊退。\\n\u3000而且除了召喚物、分身以外，\\n\u3000當距離最近的2名敵人的HP\\n\u3000低於80％時將其大幅度擊退。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧大幅度降低敵方全體的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000同時小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力，且小幅度恢復TP。\\n‧對距離最近的3名敵人造成大幅度的物理傷害。');
