insert into unit_enemy_data values (303001, '托爾披頓', 303001, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】背負著大海而聳立，爆走的\\n雙魚座魔星獸。被他吸引的小弟居於背上。\\n與牠一同邁向霸道之極。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自托爾披頓所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000同時造成大幅度的物理傷害，\\n\u3000並特大幅度提升自身的行動速度\\n\u3000與物理攻擊力。此技能造成的傷害，\\n\u3000將不會使敵人恢復TP。\\n‧對敵方全體造成小幅度的物理傷害。\\n\u3000此技能造成的傷害，\\n\u3000將不會使敵人恢復TP。\\n‧對距離最近的2名敵人造成中幅度的物理傷害，\\n\u3000並大幅度降低其物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (303002, '托爾披頓', 303002, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】背負著大海而聳立，\\n爆走的雙魚座魔星獸。被他吸引的小弟居於背上。\\n與牠一同邁向霸道之極。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自托爾披頓所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000同時造成大幅度的物理傷害，\\n\u3000並特大幅度提升自身的行動速度\\n\u3000與物理攻擊力。此技能造成的傷害，\\n\u3000將不會使敵人恢復TP。\\n‧對敵方全體造成小幅度的物理傷害。\\n\u3000當場上有拿著旗子的小弟時，\\n\u3000再造成追加物理傷害。\\n\u3000此技能所造成的傷害量，\\n\u3000會隨旗子的小弟數量大幅提升。\\n\u3000此技能造成的傷害，\\n\u3000將不會使敵人恢復TP。\\n‧對距離最近的2名敵人\\n\u3000造成中幅度的物理傷害，\\n\u3000並大幅度降低其物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧召喚2隻拿著旗子的小弟，\\n\u3000並中幅度降低自身的物理防禦力與魔法防禦力。\\n‧拿著旗子的小弟將會小幅度\\n\u3000恢復托爾披頓的TP。\\n‧當拿著旗子的小弟的HP低於一定數值時，\\n\u3000中幅度提升敵方全體的\\n\u3000物理攻擊力與魔法攻擊力，\\n\u3000同時當場消失。');
