insert into unit_enemy_data values (303100, '冬眠之熊', 303100, 0, 13, 270, 1000, 1, 3.5, 0, 0, 0, '【物理】躲藏於冰穴最深處，\\n決意要終身冬眠的巨大野熊。\\n長年累月在睡夢中度過，\\n僅於狩獵的一瞬間甦醒過來。\\n\\n‧對全體敵人造成大幅度的魔法傷害，\\n\u3000並降低其物理攻擊力。\\n\\n‧對前方的 3 名角色造成小幅度的\\n\u3000物理傷害。並且將前方的 1 名角色\\n\u3000大幅度擊飛，再施加凍結狀態\\n\u3000降低其移動速度。');
