insert into unit_enemy_data values (303200, '寒冰孔雀夫人', 303200, 0, 6, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】伴隨著暴風雪現身，\\n會呼朋引伴的襲擊\\n侵犯地盤的人，\\n是雪國之中最華麗的女士。\\n\\n‧對前方的 4 名角色造成中幅度的\\n\u3000魔法傷害。\\n\\n‧對隨機的 2 名角色造成\\n\u3000小幅度的魔法傷害，\\n\u3000並且施加凍結狀態。');
insert into unit_enemy_data values (303300, '石像鬼', 303300, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】遺留在腐朽的遺跡裡，守護著\\n主人死去的地方，謎樣的鳥型雕像。\\n隨著城鎮、命令的逝去，現在的他們\\n無論來者是誰，全部予以火焰攻擊。\\n\\n‧對前方5名角色造成中幅度魔法\\n\u3000傷害，並且施加火傷狀態。同時\\n\u3000降低魔法防禦力。\\n\\n‧隨機對3名角色造成\\n\u3000小幅度的魔法傷害，\\n\u3000同時使其陷入黑暗狀態。');
insert into unit_enemy_data values (303301, '闇黑石像鬼', 303301, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】侍奉於暴食的君主，近衛軍的黑鐵像。\\n以巧妙伎倆拐騙於冥府徬徨之魂，再獻之於主。\\n\\n‧容易受到物理防禦力降低與\\n\u3000魔法防禦力降低的效果影響。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自闇黑石像鬼\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照自身的物理防禦力，\\n\u3000對進行物理攻擊的敵方全體造成傷害，\\n\u3000並施加火傷狀態；同時依照自身的魔法防禦力，\\n\u3000對進行魔法攻擊的敵方全體造成傷害，\\n\u3000並施加火傷狀態。再者，還大幅度降低\\n\u3000敵方全體的HP吸收。\\n\u3000此技能施加的火傷狀態所造成的傷害，\\n\u3000不會恢復TP。\\n‧對敵方全體造成小幅度的物理傷害，\\n\u3000並將其擊退。此技能所造成的傷害\\n\u3000會使敵人無法恢復TP。');
insert into unit_enemy_data values (303302, '闇黑石像鬼', 303302, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】侍奉於暴食的君主，近衛軍的黑鐵像。\\n以巧妙伎倆拐騙於冥府徬徨之魂，再獻之於主。\\n\\n‧容易受到物理防禦力降低與\\n\u3000魔法防禦力降低的效果影響。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自闇黑石像鬼\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的\\n\u3000闇黑石像鬼TP恢復量。\\n‧必殺技會依照自身的物理防禦力，\\n\u3000對進行物理攻擊的敵方全體造成傷害，\\n\u3000並施加火傷狀態，同時依照自身的魔法防禦力，\\n\u3000對進行魔法攻擊的敵方全體造成傷害，\\n\u3000並施加火傷狀態。再者，還特大幅度降低\\n\u3000敵方全體的HP吸收。\\n\u3000此技能施加的火傷狀態所造成的傷害，\\n\u3000不會恢復TP。\\n‧對敵方全體造成小幅度的物理傷害，\\n\u3000並將其擊退。同時對最靠近的1名敵人，\\n\u3000造成固定的魔法追加傷害。此技能所造成的傷害\\n\u3000會使敵人無法恢復TP。');
insert into unit_enemy_data values (303303, '沙塵石像鬼', 303303, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】蘊含塔之魔力而寄宿著生命的\\n砂岩雕像魔物。在沙塵飛揚之地等待敵人，\\n再吐出灼熱火焰襲擊。\\n\\n‧對前方的範圍內造成大幅度的魔法傷害，\\n\u3000使其陷入火傷狀態。\\n\u3000並且中幅度降低魔法防禦力。\\n‧對前方的3名角色造成中幅度的魔法傷害，\\n\u3000並使敵方全體陷入黑暗狀態。\\n‧對面前的1名敵方角色造成大幅度的物理傷害。\\n\u3000對敵方全體中火傷狀態的敵人造成\\n\u3000大幅度的魔法傷害。');
insert into unit_enemy_data values (303304, '滴水嘴獸', 303304, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】守護黃沙大地的漆黑惡魔像\\n對著遼闊的沙漠一端，\\n噴吐焚盡一切氣息，\\n阻擋冒險者在灼熱大地前進。\\n\\n‧必殺技能夠對敵方全體\\n\u3000造成中幅度的魔法傷害，\\n\u3000施加火傷狀態，\\n\u3000同時小幅度降低魔法防禦力。\\n\u3000在此技能造成的傷害狀態下，\\n\u3000敵人不會恢復TP。\\n\\n‧對前方1名敵人造成\\n\u3000中幅度的魔法傷害，\\n\u3000再對敵方全體\\n\u3000造成中幅度的魔法傷害，\\n\u3000並中幅度降低物理攻擊力\\n\u3000與魔法攻擊力。');
insert into unit_enemy_data values (303305, '闇黑石像鬼', 303305, 0, 13, 270, 3000, 1, 2.1, 0, 0, 0, '【物理】侍奉於暴食的君主，近衛軍的黑鐵像。以巧妙伎倆拐騙於冥府徬徨之魂，再獻之於主。\\n\\n‧中幅度降低來自闇黑石像鬼\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會依照自身的物理防禦力，\\n\u3000對進行物理攻擊的敵方全體造成傷害，\\n\u3000並施加火傷狀態，\\n\u3000同時依照自身的魔法防禦力，\\n\u3000對進行魔法攻擊的敵方全體造成傷害，\\n\u3000並施加火傷狀態。\\n\u3000同時，大幅度降低敵方全體的HP吸收。\\n\u3000此技能施加的火傷所造成的傷害，\\n\u3000將不會恢復TP。\\n‧對敵方全體造成小幅度的物理傷害，\\n\u3000同時造成擊退。\\n\u3000同時對最靠近的1名敵人，\\n\u3000造成固定的魔法追加傷害。\\n\u3000此技能造成的傷害，將不會使敵人恢復TP。');
insert into unit_enemy_data values (303400, '沙蜥蜴', 303400, 0, 13, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】在生存競爭激烈的\\n沙漠中的王者，是隻巨大蜥蜴。\\n使用劇毒，活用沙子的戰略，\\n就是嚴酷環境中習得的生存之道。\\n\\n‧對全體敵人造成中幅度的物理傷害\\n，並降低其行動速度。\\n\u3000再提升全體夥伴的物理攻擊力。\\n‧對以前方 1 名角色為中心的\\n\u3000小範圍內造成小幅度的物理傷害，\\n\u3000並施加中毒狀態。再降低其物理\\n\u3000防禦力。');
insert into unit_enemy_data values (303401, '岩之錘', 303401, 0, 13, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】身負月之魔力而產生變異的巨大蜥蜴。\\n棘狀且堅硬的鱗片，能輕易削去敵人的體力與鎧甲。\\n\\n‧對全體敵人造成大幅度的物理傷害，\\n\u3000並且減少其TP。\\n‧對前方的 2 名角色造成中幅度的物理傷害，\\n\u3000並且使其中毒。\\n‧對前方的 3 名角色造成物理傷害。\\n\u3000並對前方的 1 名敵人\\n\u3000造成大幅度的物理傷害，\\n\u3000並中幅度降低其物理防禦力和魔法防禦力。');
insert into unit_enemy_data values (303402, '岩之錘', 303402, 0, 13, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】身負月之魔力而產生變異的巨大蜥蜴。\\n棘狀且堅硬的鱗片，能輕易削去敵人的體力與鎧甲。\\n\\n‧對全體敵人造成大幅度的物理傷害，\\n\u3000並且減少其TP。\\n‧對前方的 2 名角色造成中幅度的物理傷害，\\n\u3000並且使其中毒。\\n‧對前方的 3 名角色造成物理傷害。\\n\u3000並對前方的 1 名敵人\\n\u3000造成大幅度的物理傷害，\\n\u3000並中幅度降低其物理防禦力和魔法防禦力。');
insert into unit_enemy_data values (303500, '怒蛇', 303500, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】從吸取他人的魔力之中，\\n體悟出了妖術的魔物。\\n能從敵人的傷口上奪取魔力，\\n進而獲得更大的力量。\\n\\n‧對全體敵人造成中幅度的魔法傷害，\\n\u3000並降低全體敵人中\\n\u3000魔法攻擊角色的 TP。\\n\\n‧對以前方 1 名角色為中心的\\n\u3000小範圍內造成小幅度的魔法傷害，\\n\u3000並降低其魔法防禦力。');
insert into unit_enemy_data values (303501, '怒蛇', 303501, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】藉由持續吸取他人魔力獲得妖術的魔物。\\n從敵人的傷口中奪取魔力，追求更強大的力量。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降\\n\u3000技能的影響。\\n‧承受來自怒蛇所有的傷害，\\n\u3000導致小幅度降低隊伍角色的TP恢復量。\\n‧必殺技會以對距離最近的1名敵方角色為中心，\\n\u3000造成小範圍的中毒狀態，\\n\u3000同時中幅度降低敵方全體的物理攻擊力，\\n\u3000並大幅度降低敵方全體的魔法攻擊力。\\n\u3000被此技能施加的中毒傷害將不會恢復TP。\\n‧以距離最近的1名敵方角色為中心，\\n\u3000造成小範圍的大幅度物理傷害。');
insert into unit_enemy_data values (303502, '怒蛇', 303502, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】藉由持續吸取他人魔力獲得妖術的魔物。\\n從敵人的傷口中奪取魔力，追求更強大的力量。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降\\n\u3000技能的影響。\\n‧戰鬥開始時，對自己施加TP降低減少100％\\n\u3000效果。\\n‧承受來自怒蛇所有的傷害，\\n\u3000導致小幅度降低隊伍角色的TP恢復量。\\n‧必殺技會以對距離最近的1名敵方角色為中心，\\n\u3000造成小範圍的中毒狀態，\\n\u3000同時中幅度降低敵方全體的物理攻擊力、\\n\u3000大幅度降低敵方全體的魔法攻擊力，\\n\u3000並使敵方全體的HP吸收特大幅度下降。\\n\u3000被此技能施加的中毒傷害將不會恢復TP。\\n‧以距離最近的1名敵方角色為中心，\\n\u3000造成小範圍的大幅度物理傷害。');
insert into unit_enemy_data values (303503, '怒蛇', 303503, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】以持續吸取他人魔力，習得妖術的魔物。\\n從敵人的傷口中奪取魔力，追求更強大的力量。\\n\\n‧小幅度降低來自怒蛇\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會以最靠近的1名敵人為中心，\\n\u3000造成小範圍的中毒狀態。同時，\\n\u3000中幅度降低敵方全體的物理攻擊力，\\n\u3000以及大幅度降低其魔法攻擊力。此技能施加的中毒狀態所造成的傷害，\\n\u3000不會恢復TP。\\n‧以最靠近的1名敵人為中心，\\n\u3000造成小範圍的大幅度物理傷害。');
insert into unit_enemy_data values (303504, '怒蛇', 303504, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【魔法】以持續吸取他人魔力，習得妖術的魔物。\\n傷害敵人並奪取體力，進而奪取性命。\\n\\n‧中幅度降低來自怒蛇所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的中毒狀態，同時特大幅度\\n\u3000降低敵方全體的物理攻擊力、\\n\u3000魔法攻擊力與HP吸收。\\n\u3000此技能施加的中毒狀態所造成的傷害，\\n\u3000將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的大幅度物理傷害。');
insert into unit_enemy_data values (303600, '熾焰巨獸龍', 303600, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】靠著強韌的雙腳與銳利的爪子，\\n可在一瞬間捕獲獵物的兇猛恐龍。\\n在滅亡的大地上吞食熔岩，\\n找出生存之道的孤高存在。\\n\\n‧對全體敵人造成中幅度的物理傷害，\\n\u3000並提升自己的行動速度。\\n\\n‧對前方的 3 名敵人造成中幅度的物理傷害，\\n\u3000並降低其行動速度。');
insert into unit_enemy_data values (303601, '熾焰巨獸龍', 303601, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】擁有強韌雙腳與銳利爪子的兇猛恐龍。\\n在滅亡的大地上吞食熔岩求存的孤高存在。\\n\\n‧戰鬥開始時，對自己使用傷害上限技能，\\n\u3000在一次行動中受到的傷害超過一定值時，\\n\u3000減少所受到傷害量。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降\\n\u3000技能的影響。\\n‧必殺技能夠對敵方全體造成中幅度的物理傷害，\\n\u3000同時大幅度提升自身行動速度。\\n\u3000此技能所造成的傷害，\\n\u3000不會使敵人恢復TP。\\n‧對前方的3名角色造成中幅度的物理傷害，\\n\u3000並中幅度降低其行動速度。\\n‧對前方的1名角色造成特大幅度的物理傷害。\\n\u3000此技能造成的傷害會減少TP恢復量。');
insert into unit_enemy_data values (303602, '熾焰巨獸龍', 303602, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】擁有強韌雙腳與銳利爪子的兇猛恐龍。\\n在滅亡的大地上吞食熔岩求存的孤高存在。\\n\\n‧戰鬥開始時，對自己使用傷害上限技能，\\n\u3000在一次行動中受到的傷害超過一定值時，\\n\u3000減少所受到傷害量。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降\\n\u3000技能的影響。\\n‧戰鬥開始時，\\n\u3000大幅度提升自身的物理攻擊力。\\n‧必殺技能夠對敵方全體造成中幅度的物理傷害，\\n\u3000同時大幅度提升自身行動速度。\\n\u3000此技能所造成的傷害，\\n\u3000不會使敵人恢復TP。\\n‧對前方的3名角色造成中幅度的物理傷害，\\n\u3000並中幅度降低其行動速度。\\n‧對前方的1名角色造成特大幅度的物理傷害。\\n\u3000此技能造成的傷害會減少TP恢復量。');
