insert into unit_enemy_data values (303700, '地獄獵犬', 303700, 0, 14, 270, 1000, 1, 0.7, 0, 0, 0, '【物理】儘管墜入了那火山之中，\\n依舊身負地獄之炎復甦的雙頭魔犬。\\n與愛犬波尼一同在地盤上奔馳著，\\n並讓入侵者親身體驗那灼熱的洗禮。\\n\\n‧對全體敵人造成中幅度的物理傷害，\\n\u3000並施加火傷狀態。\\n\u3000再恢復召喚怪物的 HP。\\n\\n‧召喚波尼。');
