insert into unit_enemy_data values (303903, '獨眼巨人', 303903, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】在沙塵之中佇立如山的獨眼巨人。\\n以獨眼發射超強力的光線，\\n將冒險者掃蕩一空。\\n\\n‧必殺技會對敵方全體\\n\u3000造成大幅度的魔法傷害。\\n\u3000並使其中幅度降低行動速度。\\n\u3000對剩餘HP為100％的角色\\n\u3000提升造成的傷害。\\n‧對物理攻擊力最高的1名角色，\\n\u3000依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000並且大幅度降低其TP。');
insert into unit_enemy_data values (303904, '獨眼巨人', 303904, 0, 13, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】以怪力引以為傲，兇暴的獨眼巨人。\\n將魔力凝聚於瞳中，進而釋放強力的怪異光線。\\n\\n‧中幅度降低來自獨眼巨人所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000依照其最大HP造成魔法比例傷害，\\n\u3000並大幅度降低物理攻擊力、中幅度降低\\n\u3000魔法攻擊力、大幅度降低TP，\\n\u3000解除能力提升效果。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧無視挑釁，以物理攻擊力或魔法攻擊力最高的\\n\u3000敵方1名角色為中心，\\n\u3000對小範圍內造成大幅度的物理傷害，\\n\u3000同時施加暈眩狀態，\\n\u3000並中幅度降低其行動速度。\\n\u3000此技能造成的傷害，\\n\u3000將不會使角色恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (304000, '夢魘', 304000, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】誕生於幻想，徬徨於黑暗中的魔獸。\\n將獵物囚於惡夢之牢中，並大快朵頤那些精氣。\\n\\n‧容易受到挑釁的效果影響。\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自夢魘所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對物理攻擊力或魔法攻擊力\\n\u3000最高的1名敵方角色\\n\u3000造成特大幅度的魔法傷害，\\n\u3000同時特大幅度減少其TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧無視敵人的挑釁，\\n\u3000以距離最遠的1名敵人為中心，\\n\u3000使小範圍內所有敵人陷入詛咒狀態。\\n\u3000此技能施加的詛咒狀態所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (304001, '夢魘', 304001, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】誕生於幻想，徬徨於黑暗中的魔獸。\\n將獵物囚於惡夢之牢中，並大快朵頤那些精氣。\\n\\n‧容易受到挑釁效果的影響。\\n‧戰鬥開始時，大幅度提升自身的物理防禦力，\\n\u3000中幅度提升魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低100％減少的效果。\\n‧中幅度降低來自夢魘所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除物理攻擊力或魔法攻擊力\\n\u3000最高的1名敵人的能力提升效果後，\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並特大幅度降低其TP。\\n\u3000此技能造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧無視敵人的挑釁，以距離最遠的1名敵人\\n\u3000為中心，小幅度降低範圍內所有敵人的TP，\\n\u3000並施加詛咒狀態。被此技能施加的詛咒傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (304002, '夢魘', 304002, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】誕生於幻想，徬徨於黑暗中的魔獸。\\n將獵物囚於惡夢之牢中，並大快朵頤那些精氣。\\n\\n‧中幅度降低來自夢魘所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除物理攻擊力或魔法攻擊力\\n\u3000最高的1名敵方角色的能力提升效果後，\\n\u3000造成特大幅度的魔法傷害，\\n\u3000同時特大幅度減少其TP。\\n‧無視敵人的挑釁，以距離最遠的1名敵人為中心，\\n\u3000小幅度降低小範圍內所有敵人的TP，\\n\u3000並且使其陷入詛咒狀態。此技能施加的\\n\u3000詛咒狀態所造成的傷害，\\n\u3000不會恢復TP。\\n‧普通攻擊會對物理攻擊力或魔法攻擊力最高的\\n\u3000敵方1名角色造成大幅度的物理傷害。\\n');
insert into unit_enemy_data values (304100, '泰坦龍龜', 304100, 0, 13, 270, 1000, 1, 2.5, 0, 0, 0, '就連甲殼上都能扎起大樹之根的遠古陸龜。\\n持續忍受著攻擊，伺機用憤怒之擊還以顏色。\\n\\n・對敵方全體造成大量的物理傷害。\\n・將躲在甲殼裡時所受到的傷害量，給予敵人相\\n\u3000應的物理傷害。');
insert into unit_enemy_data values (304101, '泰坦龍龜', 304101, 0, 13, 270, 1000, 1, 0.1, 0, 0, 0, '【物理】甲殼甚至能讓大樹生根的巨型老陸龜。\\n持續忍受著攻擊，伺機用憤怒之擊還以顏色。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧小幅度降低來自泰坦龍龜\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成\\n\u3000大幅度的物理傷害。此技能所造成的傷害，\\n\u3000會使角色無法恢復TP。\\n‧縮進甲殼內時，\\n\u3000特大幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000同時依照縮進甲殼內時所受到的傷害量，\\n\u3000對包含飛行狀態在內的敵方全體\\n\u3000造成物理傷害。此技能所造成的傷害，\\n\u3000會使角色無法恢復TP。\\n‧當全部的目標部位都被破壞時，\\n\u3000一定時間內將無法行動。無法行動的期間，\\n\u3000大幅度降低自身的物理防禦力與魔法防禦力，\\n\u3000部位不會被破壞。');
insert into unit_enemy_data values (304102, '泰坦龍龜', 304102, 0, 13, 270, 1000, 1, 0.1, 0, 0, 0, '【物理】甲殼甚至能讓大樹生根的巨型老陸龜。\\n持續忍受著攻擊，伺機用憤怒之擊還以顏色。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧泰坦龍龜造成的所有傷害，\\n\u3000會使隊伍角色無法恢復TP。\\n‧大幅度降低來自敵方所有傷害產生的\\n\u3000泰坦龍龜TP恢復量。\\n‧必殺技會對敵方全體造成大幅度的物理傷害，\\n\u3000並大幅度降低其行動速度。此技能\\n\u3000所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧縮進甲殼內時，\\n\u3000特大幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000同時依照縮進甲殼內時\\n\u3000所受到的傷害量，對包含\\n\u3000飛行狀態在內的敵方全體\\n\u3000造成物理傷害。\\n‧當全部的目標部位都被破壞時，\\n\u3000一定時間內將無法行動。無法行動的期間，\\n\u3000大幅度降低自身的物理防禦力與魔法防禦力，\\n\u3000部位不會被破壞。');
insert into unit_enemy_data values (304103, '泰坦龍龜', 304103, 0, 13, 270, 1000, 1, 0.1, 0, 0, 0, '【物理】甲殼甚至能讓大樹生根的巨型老陸龜。\\n持續忍受著攻擊，伺機用憤怒之擊還以顏色。\\n\\n‧來自泰坦龍龜的所有傷害，\\n\u3000將不會恢復隊伍角色的TP。\\n‧必殺技會對敵方全體造成中幅度的物理傷害，\\n\u3000同時大幅度降低其行動速度。此技能\\n\u3000所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧縮進甲殼內時，特大幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。之後，對包含飛行狀態\\n\u3000的敵方全體造成特大幅度的\\n\u3000物理傷害。\\n‧當全部的目標部位都被破壞時，\\n\u3000一定時間內將無法行動。無法行動的期間，\\n\u3000大幅度降低自身的物理防禦力與魔法防禦力，\\n\u3000部位不會被破壞。');
insert into unit_enemy_data values (304200, '加爾格', 304200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】啃食屍體的殘暴的大型魔獸。\\n強韌的刺佈滿全身，追捕獵物的執念甚深。\\n・根據角色的最高 HP，對全體敵人\\n造成中幅度的物理傷害。\\n・對後方範圍的敵人造成中幅度的物理傷害，\\n並使其暈眩。');
insert into unit_enemy_data values (304201, '加爾格', 304200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】啃食屍體的殘暴的大型魔獸。\\n強韌的刺佈滿全身，追捕獵物的執念甚深。\\n・根據角色的最高 HP，對全體敵人\\n造成中幅度的物理傷害。\\n・對後方範圍的敵人造成中幅度的物理傷害，\\n並使其暈眩。\\n・對前方數來第 2 名的敵人造成大幅度的物理傷害。\\n・當加爾格本身的 HP 降至 50％ 以下，\\n其攻擊力將會越發殘暴。');
insert into unit_enemy_data values (304300, '天塔帕斯', 304300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】君臨於海邊的巨大軟體魔獸。\\n強韌的觸手與鋼鐵的大矛將把一切破壞殆盡。\\n・對敵方全體角色依照最大HP造成\\n\u3000大幅度的物理傷害。\\n・對後方範圍內造成中幅度的魔法傷害、\\n\u3000賦予麻痺狀態，並特大幅度降低物理攻\\n\u3000力及魔法攻擊力。\\n・經過一定時間後對敵方單體\\n\u3000造成超特大幅度的物理傷害。\\n・當天塔帕斯自身的HP低於40％後，\\n\u3000其攻擊將會變得越發殘暴。');
insert into unit_enemy_data values (304301, '天塔帕斯', 304300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】君臨於海邊的巨大軟體魔獸。\\n強韌的觸手與鋼鐵的大矛將把一切破壞殆盡。\\n・對敵方全體角色依照最大HP造成\\n\u3000大幅度的物理傷害。\\n・對後方範圍內造成中幅度的魔法傷害、\\n\u3000賦予麻痺狀態，並特大幅度降低物理攻\\n\u3000力及魔法攻擊力。');
