insert into unit_enemy_data values (304304, '奧克塔斯基德', 304304, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】計畫利用塔的力量向人類復仇的軟體魔獸。\\n年輕時被魚叉刺中的仇恨，比大海還要深。\\n\\n‧對全體敵人造成大幅度的物理傷害。\\n‧對前方數來第3名之後的敵人造成中幅度的\\n\u3000魔法傷害，中幅度降低物理防禦力與行動速度。\\n\u3000並且解除素質提升效果。\\n‧對剩餘HP最多的敵人依照其剩餘HP\\n\u3000造成大幅度的物理傷害，中幅度降低TP。');
insert into unit_enemy_data values (304400, '島鯨', 304400, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】將為數眾多的探險家給吞噬的幻島之巨鯨。無慈悲地葬送了那些被慾望、幻想給憑依的愚者。\\n\\n‧對全體敵人依照其最大HP造成大幅度的\\n\u3000物理傷害。\\n‧對全體敵人造成中幅度的魔法傷害，\\n\u3000並且對島鯨自身的物理防禦力、\\n\u3000魔法防禦力給予極大幅度的上升。\\n‧對前方數來第3名角色造成大幅度的魔法傷害，\\n\u3000並且大幅度將其擊飛。\\n‧當島鯨自身的HP低於40%時，\\n\u3000牠的攻擊將會變得更加兇暴。');
