insert into unit_enemy_data values (304800, '亡魂支配者', 304800, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】將強者之魂作為幽鬼使喚的冥府高官。\\n以亡者意念糾纏刀刃，使敵方行動更為遲鈍。\\n\\n‧容易受到魔法攻擊力降低的效果影響。\\n‧能夠將飛行狀態的敵人設為攻擊目標。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自亡魂支配者\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000依照亡魂支配者的魔法攻擊力\\n\u3000造成中範圍的魔法比例傷害。\\n\u3000之後，依照給予的總傷害\\n\u3000降低其最大HP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，\\n\u3000對中範圍賦予會依照亡魂支配者的魔法攻擊力\\n\u3000造成傷害的詛咒狀態，\\n\u3000同時特大幅度降低其HP恢復量與HP吸收。\\n\u3000此技能施加的詛咒狀態所造成的傷害，\\n\u3000不會恢復TP。\\n‧普通攻擊會對距離最近的1名敵人，\\n\u3000依照亡魂支配者的魔法攻擊力\\n\u3000造成魔法比例傷害。');
insert into unit_enemy_data values (304801, '亡魂支配者', 304801, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】將強者之魂作為幽鬼使喚的冥府高官。\\n以亡者意念糾纏刀刃，使敵方行動更為遲鈍。\\n\\n‧容易受到魔法攻擊力降低的效果影響。\\n‧能夠將飛行狀態的敵人設為攻擊目標。\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧中幅度降低來自亡魂支配者\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧大幅度降低來自敵方\\n\u3000所有傷害產生的亡魂支配者TP恢復量。\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000依照亡魂支配者的魔法攻擊力\\n\u3000造成中範圍的魔法比例傷害。\\n\u3000之後，依照給予的總傷害\\n\u3000降低其最大HP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，\\n\u3000對中範圍賦予會依照亡魂支配者的魔法攻擊力\\n\u3000造成傷害的詛咒狀態，同時特大幅度降低\\n\u3000其HP恢復量與HP吸收。\\n\u3000此技能施加的詛咒狀態\\n\u3000所造成的傷害，不會恢復TP。\\n‧普通攻擊會對距離最近的1名敵人，\\n\u3000依照亡魂支配者的魔法攻擊力\\n\u3000造成魔法比例傷害。');
