insert into unit_enemy_data values (305006, '傑克南瓜布丁', 305004, 0, 16, 270, 1000, 1, 0.8, 0, 0, 0, '【物理】企圖對人類進行復仇，靈界的住民。\\n「我乃傑克！傑克南瓜布丁！\\n\u3000若不和我玩的話，我可就要搗蛋囉～」\\n\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成中幅度的物理傷害，\\n\u3000同時若角色的HP低於40％時，\\n\u3000使其陷入誘惑狀態。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並使該角色的能力提升效果解除。\\n‧對距離最近的敵人造成大幅度的\\n\u3000物理傷害，同時以距離最近的敵人為中心，\\n\u3000對3名敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n‧當傑克南瓜布丁自身的HP\\n\u3000低於40％時，他的攻擊\\n\u3000將會變得更加兇暴。\\n');
insert into unit_enemy_data values (305007, '傑克南瓜布丁', 305004, 0, 16, 270, 1000, 1, 0.8, 0, 0, 0, '【物理】企圖對人類進行復仇，靈界的住民。\\n「我乃傑克！傑克南瓜布丁！\\n\u3000若不和我玩的話，我可就要搗蛋囉～」\\n\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成中幅度的物理傷害，\\n\u3000同時若角色的HP低於40％時，\\n\u3000使其陷入誘惑狀態。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並使該角色的能力提升效果解除。\\n‧對距離最近的敵人造成大幅度的\\n\u3000物理傷害，同時以距離最近的敵人為中心，\\n\u3000對3名敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n‧當傑克南瓜布丁自身的HP\\n\u3000低於40％時，他的攻擊\\n\u3000將會變得更加兇暴。\\n');
insert into unit_enemy_data values (305100, '獨角兔', 305100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】利用那巨大身體為非作歹的巨兔魔物。\\n重複打出的一擊消耗劇烈，似乎是精力過剩。\\n\\n‧必殺技能夠對全體敵人造成大幅度的\\n\u3000物理傷害，並且造成擊退。\\n\u3000之後，在頭暈目眩期間，將大幅度降低\\n\u3000自己的物理防禦力與魔法防禦力。\\n‧對全體敵人造成中幅度的物理傷害，同時\\n\u3000對前方範圍內的敵人造成暈眩。\\n\u3000此技能所造成的狀態異常，同時也會使傷害\\n\u3000無效化。');
insert into unit_enemy_data values (305101, '獨角兔', 305100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】驅使巨大的身體為非作歹，\\n一種巨兔魔物。接二連三的每一擊\\n消耗甚大，使人難以招架。\\n\\n‧對全體敵人造成大幅度的\\n\u3000物理傷害，同時擊退。\\n\u3000發動技能後，在頭暈目眩期間內，\\n\u3000大幅度降低自身的物理防禦力\\n\u3000與魔法防禦力。\\n\\n‧對全體敵人造成中幅度的物理傷害，\\n\u3000同時對前方範圍內的敵人造成\\n\u3000暈眩。');
insert into unit_enemy_data values (305102, '獨角兔', 305102, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】驅動著巨大身軀肆虐的巨兔魔物。\\n重複打出的一擊消耗劇烈，似乎是精力過剩。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果上升的防禦\\n\u3000力，不會受到戰鬥中被施加的降低防禦力技能\\n\u3000的影響。\\n‧中幅度降低來自獨角兔的所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技能夠對敵方全體造成中幅度的物理\\n\u3000傷害，並且擊退。同時當角色的HP低於80％\\n\u3000時，施加暈眩狀態。此技能所造成的狀態\\n\u3000異常，即使傷害無效也會施加。此技能造成\\n\u3000的傷害將不會恢復TP。\\n‧對敵方全體造成小幅度的物理傷害，並且擊飛。\\n\u3000之後小幅度恢復自身的TP。\\n‧普通攻擊會對距離最近的1名敵人造成大幅度\\n\u3000的物理傷害並擊退。');
insert into unit_enemy_data values (305103, '獨角兔', 305103, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】驅動著巨大身軀肆虐的巨兔魔物。\\n重複打出的一擊消耗劇烈，似乎是精力過剩。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自獨角兔所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的\\n\u3000獨角兔TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的物理傷害，\\n\u3000並且擊退。同時當角色的HP低於95％時，\\n\u3000施加暈眩狀態。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧對敵方全體造成小幅度的物理傷害，\\n\u3000並且擊飛。之後中幅度恢復自身的TP。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧普通攻擊會對距離最近的1名敵人\\n\u3000造成大幅度的物理傷害並擊退。');
insert into unit_enemy_data values (305200, '奧拉姆', 305200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】在遠古之前便被生產出的防衛兵器。\\n因惡黨的陰謀，迎來了那悠久之沉睡的終焉。\\n\\n‧對全體敵人造成對應最大 HP 的\\n\u3000大幅度物理傷害，\\n\u3000同時大幅度降低自己的物理攻擊力。\\n‧對前方中範圍內的敵人造成中幅度的物理傷害，\\n\u3000並大幅度降低其物理攻擊力。\\n‧對前方廣範圍內的敵人造成中幅度的物理傷害，\\n\u3000並施加麻痺狀態。\\n\u3000當該角色的 HP 低於 70% 以下時，\\n\u3000麻痺的效果時間將會延長。\\n‧當奧拉姆自身的 HP 低於 40% 之時，\\n\u3000它的攻擊將會變得更加兇暴。');
