insert into unit_enemy_data values (305300, '高等加爾格', 305300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】帶有露娜魔力的變異兇猛魔獸。\\n堅韌的利牙甚至能輕易撕裂身經百戰的冒險者。\\n\\n‧必殺技會對敵方全體造成大幅度的物理傷害，\\n\u3000同時恢復自身的HP。\\n‧對後方範圍內的敵人造成物理傷害，\\n\u3000並使其暈眩。\\n\u3000同時中幅度降低敵方全體的物理防禦力。\\n‧對前方3名角色造成物理傷害，\\n\u3000並使其陷入中毒狀態。');
insert into unit_enemy_data values (305301, '寒冰加爾格', 305301, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】棲息於雪山中的凶猛魔狼。\\n堅韌的利牙甚至能輕易將\\n身經百戰的冒險者變成冰塊的墓碑。\\n\\n‧必殺技會對敵方全體造成\\n\u3000大幅度的物理傷害\\n\\n‧以最後方的敵方角色為中心，\\n\u3000對範圍內的所有敵人造成小幅度的\\n\u3000物理傷害，並使其暈眩。\\n\u3000同時中幅度降低敵方全體的\\n\u3000物理防禦力。\\n\\n‧對前方範圍內的所有敵人\\n\u3000造成小幅度的物理傷害，\\n\u3000並使其陷入中毒狀態。');
insert into unit_enemy_data values (305302, '寒冰加爾格', 305301, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】棲息於雪山中的凶猛魔狼。\\n堅韌的利牙甚至能輕易將\\n身經百戰的冒險者變成冰塊的墓碑。\\n\\n‧必殺技會對敵方全體造成\\n\u3000大幅度的物理傷害\\n\\n‧以最後方的敵方角色為中心，\\n\u3000對範圍內的所有敵人造成小幅度的\\n\u3000物理傷害，並使其暈眩。\\n\u3000同時中幅度降低敵方全體的\\n\u3000物理防禦力。\\n\\n‧對前方範圍內的所有敵人\\n\u3000造成小幅度的物理傷害，\\n\u3000並使其陷入中毒狀態。');
insert into unit_enemy_data values (305400, '冰角', 305400, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】此獨角獸那強力的尖刺，\\n就算再牢固的盾也能貫穿。\\n牠會將冒險者逼到北方的冰丘上，\\n再一舉推下至那冰獄。\\n\\n‧對全體敵人造成大幅度的魔法傷害，\\n\u3000並降低其魔法防禦力。\\n\u3000同時提升所有夥伴的\\n\u3000TP 上升率。\\n\\n‧對前方小範圍內造成小幅度魔法傷害，\\n\u3000並且使其陷入凍結狀態。\\n\u3000同時對全體造成小幅度的魔法傷害，\\n\u3000再將前方 3 名角色\\n\u3000稍微擊飛。');
insert into unit_enemy_data values (305500, '霧奇', 305500, 0, 16, 270, 2160, 2, 3.0, 0, 0, 0, '【魔法】在許多的傳說之中留下名號，記憶的大精靈。\\n如霧狀般的不具實體，可將一切覆蓋隱藏。\\n\\n‧必殺技可對所有敵人\\n\u3000造成大幅度的魔法傷害，\\n\u3000當敵人處於詛咒狀態時，\\n\u3000傷害會上升。\\n‧對全體敵人造成中幅度的魔法傷害。\\n\u3000並且施加詛咒狀態。\\n‧從分身體（紫）（紅）（藍）之中\\n\u3000任意召喚 1 匹。\\n\u3000召喚時將全體敵人擊退。\\n‧【物理】分身體（紫）可對自己展開\\n\u3000吸收物理/魔法的屏障。\\n‧【物理】分身體（紅）可對全體敵人\\n\u3000造成中幅度的魔法傷害。\\n‧【物理】分身體（藍）可對全體敵人\\n\u3000造成中幅度的魔法傷害。\\n\u3000再施加詛咒狀態。\\n\u3000分身體（藍） 的 HP 在 30% 以下時，\\n\u3000恢復霧奇的 300 TP。\\n‧當霧奇自身的 HP 低於 \\n\u300050% 時，牠的攻擊將會\\n\u3000變得更加兇暴。');
