insert into unit_enemy_data values (305508, '迷霧追蹤者', 305508, 0, 16, 270, 1000, 2, 3.0, 0, 0, 0, '【魔法】自縈繞高塔魔力的迷霧具現化的魔物。\\n散布怨念，和分身一起伺機加害冒險者。\\n\\n‧對敵方全體造成大幅度的魔法傷害，\\n\u3000當敵人處於詛咒狀態時傷害上升，\\n\u3000處於咒術狀態時傷害又再提升。\\n‧對敵方全體造成中幅度的魔法傷害。\\n\u3000當敵人處於詛咒狀態時施加咒術狀態，\\n\u3000且迷霧追蹤者自身的HP低於80%時，\\n\u3000短時間內大幅度降低物理攻擊力及魔法攻擊力。\\n‧召喚分身體。\\n\u3000召喚時將敵方全體擊退。\\n‧分身體對敵方全體造成中幅度的物理傷害，\\n\u3000並施加詛咒狀態。\\n‧分身體的HP低於30％時，\\n\u3000我方魔法攻擊力最高的1名角色的\\n\u3000TP恢復1000。\\n‧迷霧追蹤者自身的HP低於80%時，\\n\u3000攻擊會變得更加兇暴。');
insert into unit_enemy_data values (305600, '邪惡領主', 305600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】覬覦月之魔力而現身的異界魔物。\\n以魔眼與率領的兵力，放眼於征服露娜之塔。\\n\\n‧對全體敵人造成大幅度的魔法傷害，\\n\u3000並中幅度降低全體敵人的魔法防禦力。\\n‧隨機分 6 次對 1 名角色造成小幅度的\\n\u3000魔法傷害。\\n‧對從後方算起第 3 名角色造成中幅度的\\n\u3000魔法傷害，並施加長時間的拘束狀態。\\n\u3000再對全體造成小幅度的的魔法傷害。');
insert into unit_enemy_data values (305700, '哥布林王者', 305700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】豪邁地揮舞著戰斧，老練的哥布林戰士。\\n單手抱著年幼的戰士，展現畢生的絕技。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧必殺技會對距離最近的1名敵人\\n\u3000造成特大幅度的物理傷害，\\n\u3000同時小幅度提升自身的\\n\u3000物理攻擊力與物理防禦力直到戰鬥結束。\\n\u3000此技能所造成的傷害，\\n\u3000會中幅度降低TP恢復量。');
insert into unit_enemy_data values (305701, '哥布林王者', 305701, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '豪邁地揮舞著戰斧，是老練的哥布林戰士。\\n單手抱著年幼的戰士，展現畢生的絕技。\\n\\n‧必殺技可中幅度提升自己的物理攻擊力\\n\u3000與小幅度提升物理防禦力，並對面前的\\n\u30001 名角色造成特大幅度的物理傷害。');
insert into unit_enemy_data values (305702, '哥布林王者', 305702, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】豪邁地揮舞著戰斧，老練的哥布林戰士。\\n單手抱著年幼的戰士，展現畢生的絕技。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。 \\n‧戰鬥開始時，展開小幅度降低敵方全體TP\\n\u3000上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自哥布林王者\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對距離最近的1名敵人\\n\u3000造成特大幅度的\\n\u3000物理傷害，同時施加暈眩狀態，\\n\u3000並小幅度提升自身的\\n\u3000物理攻擊力與物理防禦力直到戰鬥結束。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成大幅度的物理傷害，\\n\u3000同時施加暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (305703, '蠻族的隊長', 305703, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】靠著頑強的身心在沙漠\\n生存的蠻族戰士。為了守護年幼的\\n親生孩子，不斷揮舞著戰斧。\\n\\n‧必殺技會中幅度提升自身的\\n\u3000物理攻擊力，小幅度提升\\n\u3000物理防禦力。\\n\u3000並且對面前的1名角色造成\\n\u3000特大幅度物理傷害。');
insert into unit_enemy_data values (305704, '哥布林王者', 305704, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】豪邁地揮舞著戰斧，\\n是老練的哥布林戰士。單手抱著年幼的戰士，\\n展現畢生的絕技。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果\\n\u3000中幅度降低來自哥布林王者所有傷害\\n\u3000產生的隊伍角色TP恢復量。\\n‧必殺技可小幅度提升自身的\\n\u3000物理攻擊力與物理防禦力，\\n\u3000對距離最近的1名角色造成\\n\u3000特大幅度的物理傷害，\\n\u3000並且賦予暈眩的狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名角色\\n\u3000造成大幅度的物理傷害，\\n\u3000同時賦予暈眩的狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (305705, '哥布林王者', 305705, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】豪邁地揮舞著戰斧，老練的哥布林戰士。\\n單手抱著年幼的戰士，展現畢生的絕技。\\n\\n‧中幅度降低來自哥布林王者所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會對距離最近的1名敵人\\n\u3000造成特大幅度的物理傷害，\\n\u3000並使其陷入暈眩狀態。之後小幅度提升自身的\\n\u3000物理攻擊力與物理防禦力直到戰鬥結束。此技能\\n\u3000所造成的狀態異常，即使傷害無效也會施\\n\u3000加。\\n‧對距離最近的1名敵人造成大幅度的物理傷害，\\n\u3000同時施加暈眩狀態。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (305707, '哥布林王者', 305707, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】被領主視為左膀的哥布林大戰士。\\n豪邁地揮舞著巨大的戰斧，擊碎眼前的敵人。\\n‧戰鬥開始時，\\n小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，使用傷害上限技能，\\n\u3000在一次行動中造成的傷害值超過一定值時，\\n\u3000會根據造成的傷害值減少傷害量。\\n‧承受來自哥布林王者所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對前方的1名角色造成特大幅度\\n\u3000的物理傷害，並小幅度提升自身的\\n\u3000物理攻擊力與物理防禦力。\\n‧對前方的1名角色依照其角色最大HP\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (305800, '涅普堤利昂', 305800, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】海神的使者，不可思議的軟體魔物。\\n據說身體組成幾乎都是蘊含魔力的海水。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧小幅度降低來自涅普堤利昂\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000造成大幅度的魔法傷害，\\n\u3000並大幅度降低其物理攻擊力\\n\u3000與魔法攻擊力。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n‧無視敵方的挑釁，\\n\u3000以從前方數來第4名的角色為中心，\\n\u3000對小範圍內造成持續的小幅度魔法傷害。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n‧普通攻擊會無視敵方的挑釁，\\n\u3000對從前方數來第4名的角色造成\\n\u3000特大幅度的魔法傷害，並以該角色為中心，\\n\u3000大幅度降低小範圍內的行動速度。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (305801, '海神使徒', 305801, 0, 16, 270, 1000, 2, 2.6, 0, 0, 0, '【魔法】大鬧於露娜之塔的海獸。曾仕於太古之\\n海神，然而失去大義的如今已成了一介破壞者。\\n\\n・對全體敵人造成魔法傷害。\\n・在一定的範圍內，展開降低物理攻擊力與\\n\u3000魔法防禦力，同時造成持續魔法傷害的領域。\\n\u3000\\n・對從前方數來第 2 與 3 名的角色，\\n\u3000造成大幅度的魔法傷害。');
insert into unit_enemy_data values (305802, '涅普堤利昂', 305802, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】侍奉海神的奇妙魔物。\\n\u3000看見威脅水域平靜的侵入者時，\\n便會使用神使的力量，將其清理乾淨。\\n\\n‧對全體敵人造成大幅度的魔法傷害。\\n\\n‧對前方的第2名角色造成特大幅度的\\n\u3000魔法傷害，對前方的第3名角色造成\\n\u3000中幅度的魔法傷害。\\n\\n‧在一定範圍內，展開降低魔法防禦力\\n\u3000並持續給予魔法傷害的\\n\u3000領域。');
