insert into unit_enemy_data values (306010, '邪餅神鏡餅', 306000, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【物理】由邪念的殘餘物聚集所化成的邪神。\\n將其具現的煩惱給全數淨化，以迎接新年吧！\\n\\n‧必殺技可以夠對全體敵人依照\\n\u3000其最大HP造成大幅度物理的\\n\u3000比例傷害。\\n‧對前方3名角色造成中幅度的魔法傷害，\\n\u3000同時降低物理攻擊力。\\n‧在前方中距離召喚定時炸彈年糕。\\n‧當炸彈年糕被召喚出來的8秒後\\n\u3000造成大爆炸，對範圍內的全體敵人\\n\u3000造成中幅度的物理傷害。\\n\u3000但是若炸彈年糕在爆炸前的HP低於\\n\u300050%時，將小幅度恢復全體敵人的HP，\\n\u3000同時當場消失。\\n‧當邪餅神鏡餅自身的HP低於\\n\u300060% 時，祂的攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (306100, '貪婪豺狼', 306100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】藏匿於黑暗，以竊盜村落維生的\\n魔物頭目\\n會將偷來的武器毫不留情的丟出，\\n是開拓的過程中必須除掉的麻煩。\\n\\n‧對前方的3名角色造成中幅度的物理傷害，\\n\u3000並且使其麻痺。\\n\\n‧對全體敵人造成小幅度的物理傷害，\\n\u3000降低行動速度。\\n\u3000並且使前方小範圍內造成中毒狀態。\\n');
insert into unit_enemy_data values (306101, '貪婪豺狼', 306101, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】藏匿於黑暗，\\n以竊盜村落維生的魔物頭目。\\n會使用偷來的武器戰鬥的無賴。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自貪婪豺狼\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成大幅度的\\n\u3000物理傷害，並使其陷入短時間的麻痺狀態。\\n\u3000當敵方角色處於中毒狀態時，\\n\u3000使其陷入長時間的麻痺狀態。\\n\u3000此技能造成的傷害\\n\u3000將不會恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並且使其陷入中毒狀態。\\n\u3000此技能施加的中毒狀態所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (306102, '貪婪豺狼', 306102, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】藏匿於黑暗，\\n以竊盜村落維生的魔物頭目。\\n會使用偷來的武器戰鬥的無賴。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。\\n‧戰鬥開始時，展開小幅度降低敵方全體TP\\n\u3000上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧中幅度降低來自貪婪豺狼所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會對敵方全體造成大幅度的\\n\u3000物理傷害，並使其陷入短時間的麻痺狀態。\\n\u3000當敵方角色處於中毒狀態時，\\n\u3000使其陷入長時間的麻痺狀態。\\n\u3000此技能造成的傷害\\n\u3000將不會恢復TP。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000同時特大幅度降低其HP\\n\u3000吸收，並且使其陷入中毒狀態。\\n\u3000此技能造成的傷害\\n\u3000將不會恢復TP。\\n‧普通攻擊會對距離最近的1名敵人\\n\u3000造成中幅度的物理傷害，\\n\u3000同時施加中毒狀態，\\n\u3000並小幅度降低其行動速度。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000戰鬥開始後，第一次\\n\u3000使用普通攻擊的傷害量將會大幅度提升。\\n‧貪婪豺狼施加的中毒所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (306103, '貪婪豺狼', 306103, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】藏匿於黑暗，以竊盜村落維生的魔物頭目。\\n會使用偷來的武器戰鬥的無賴。\\n\\n‧戰鬥開始時，小幅度提升自身物理防禦力與魔法\\n\u3000防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧中幅度降低來自貪婪豺狼所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會對敵方全體造成大幅度物理傷害，並使\\n\u3000其陷入短時間的麻痺狀態。\\n\u3000目標處於中毒狀態時則造成長時間的麻痺狀態。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n‧對敵方全體造成中幅度的物理傷害，同時中幅度\\n\u3000降低行動速度。\\n\u3000且特大幅度降低敵方全體的HP吸收。\\n\u3000角色HP低於90％的時候，施加中毒狀態。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n\u3000被此技能施加的中毒傷害將不會恢復TP。\\n‧普通攻擊會對距離最近的1名敵方角色造成\\n\u3000大幅度的物理傷害，並施加中毒狀態。\\n\u3000被此技能施加的中毒傷害將不會恢復TP。');
insert into unit_enemy_data values (306200, '大王‧山狸', 306200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】盤踞森林深處，\\n與手下支配著周遭一帶，老奸巨猾的大狸。\\n使人陷入不存在的虛幻，巧妙地操縱\\n侵蝕精神的幻影攻擊。\\n\\n‧對全體敵人造成中幅度魔法傷害，\\n\u3000且召喚出 2 隻小狸貓。\\n\\n・對全體敵人造成小幅度魔法傷害，\\n\u3000一併消滅不處於無敵狀態之中的召\\n\u3000喚魔物。\\n\u3000且當敵人 HP 在 40% 以下時，\\n\u3000使其陷入麻痺狀態。');
insert into unit_enemy_data values (306201, '大王‧山狸', 306201, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】盤踞森林深處，與手下支配著周遭一帶，老奸巨猾的大狸。\\n牠可以召喚小狸貓，與其相互配合以擾亂對手。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技能對敵方全體造成中幅度的魔法傷害，\\n\u3000且召喚出2隻小狸貓。\\n\u3000在此技能造成的傷害狀態下，\\n\u3000敵人不會恢復TP。\\n‧對敵方全體造成小幅度的魔法傷害並擊退，\\n\u3000且一併消滅不處於無敵狀態的小狸貓。\\n\u3000當敵人HP低於50％時，\\n\u3000使其陷入麻痺狀態。');
