insert into unit_enemy_data values (306203, '大王‧山狸', 306203, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】盤踞森林深處，與手下支配著周遭一帶，老奸巨猾的大狸。\\n牠可以召喚小狸貓，與其相互配合以擾亂對手。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技能對敵方全體造成中幅度的魔法傷害，\\n\u3000且召喚出2隻小狸貓。\\n\u3000在此技能造成的傷害狀態下，\\n\u3000敵人不會恢復TP。\\n‧對敵方全體造成小幅度的魔法傷害並擊退，\\n\u3000且一併消滅不處於無敵狀態的小狸貓。\\n\u3000當敵人HP低於50％時，\\n\u3000使其陷入麻痺狀態。');
insert into unit_enemy_data values (306204, '大王‧山狸', 306204, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】盤踞森林深處，與手下支配著周遭一帶，老奸巨猾的大狸。\\n牠可以召喚小狸貓，與其相互配合以擾亂對手。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技能對敵方全體造成中幅度的魔法傷害，\\n\u3000且召喚出2隻小狸貓。\\n\u3000在此技能造成的傷害狀態下，\\n\u3000敵人不會恢復TP。\\n‧對敵方全體造成小幅度的魔法傷害並擊退，\\n\u3000且一併消滅不處於無敵狀態的小狸貓。\\n\u3000當敵人HP低於50％時，\\n\u3000使其陷入麻痺狀態。');
insert into unit_enemy_data values (306300, '擲岩猴仙', 306300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】統領著使用月之魔力的擲岩猴們的仙猴。\\n能自由使用寶武具之棍，將敵人一掃而空。\\n\\n‧對全體敵人造成大幅度的物理傷害。\\n\u3000此攻擊對於對火傷狀態的敵人\\n\u3000傷害為 1.8 倍。\\n‧對前方中範圍內的敵人造成中幅度的物理傷害。\\n‧對前方小範圍內的敵人造成中幅度的魔法傷害，\\n\u3000並施加火傷狀態。');
insert into unit_enemy_data values (306301, '擲岩猴仙', 306301, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】統領著使用月之魔力的擲岩猴們的仙猴。\\n能自由使用寶武具之棍，將敵人一掃而空。\\n\\n‧對全體敵人造成大幅度的物理傷害，\\n\u3000並降低物理攻擊角色的物理攻擊力。\\n\u3000此攻擊對於對火傷狀態的敵人\\n\u3000傷害為 1.8 倍。\\n‧對前方中範圍內的敵人造成中幅度的物理傷害。\\n‧對前方小範圍內的敵人造成中幅度的魔法傷害，\\n\u3000並施加火傷狀態。');
insert into unit_enemy_data values (306302, '擲岩猴仙', 306302, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】統領著使用月之魔力的擲岩猴們的仙猴。\\n能自由使用寶武具之棍，將敵人一掃而空。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力不會受到戰鬥\\n\u3000中被施加的降低防禦力技能的影響。\\n‧中幅度降低來自擲岩猴仙的所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成大幅度的物理傷害，同時使其陷入暈眩狀態。\\n\u3000此攻擊對於火傷狀態的敵人傷害為1.5倍。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧對前方3名角色造成中幅度的物理傷害。\\n‧對前方2名角色造成中幅度的魔法傷害，\\n\u3000同時使其陷入火傷狀態。\\n\u3000此技能施加的火傷狀態所造成的傷害將不會恢復\\n\u3000TP。\\n‧對前方1名角色造成大幅度的物理傷害。');
insert into unit_enemy_data values (306303, '擲岩猴仙', 306303, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】統領著使用月之魔力的擲岩猴們的仙猴。\\n能自由使用寶武具之棍，將敵人一掃而空。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力不會受到戰鬥\\n\u3000中被施加的降低防禦力技能的影響。\\n‧戰鬥開始時，特大幅度提升我方全體的\\n\u3000物理防禦力與魔法防禦力。\\n‧中幅度降低來自擲岩猴仙的所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成大幅度的物理傷害，同時使其陷入暈眩狀態。\\n\u3000此攻擊對於火傷狀態的敵人傷害為1.5倍。\\n\u3000此技能造成的傷害將不會恢復TP。\\n‧對前方3名角色造成中幅度的物理傷害。\\n‧對前方2名角色造成中幅度的魔法傷害，\\n\u3000同時使其陷入火傷狀態。\\n\u3000此技能施加的火傷狀態所造成的傷害將不會恢復\\n\u3000TP。\\n‧對前方1名角色造成大幅度的物理傷害。');
insert into unit_enemy_data values (306304, '擲岩猴仙', 306304, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】使用魔力統領著擲岩猴們的仙猴。\\n能自由使用寶武具之棍，將敵人一掃而空。\\n\\n‧中幅度降低來自擲岩猴仙所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技能對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000同時使其陷入暈眩狀態。\\n\u3000此技能對火傷狀態的敵人傷害為1.5倍。\\n\u3000此技能造成的傷害，\\n\u3000將不會使敵人恢復TP。\\n‧對距離最近的3名敵人造成中幅度的物理傷害。\\n‧對距離最近的2名敵人造成中幅度的魔法傷害，\\n\u3000並且使其陷入火傷狀態。\\n\u3000此技能施加的火傷所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (306400, '野蠻之爪', 306400, 0, 16, 270, 1000, 1, 3.5, 0, 0, 0, '【物理】以捕食者之姿君臨於荒山中\\n，一隻兇惡的怪鳥獸。\\n用著那日以繼夜的狩獵鍛鍊而出的\\n臂力與利爪，將入侵者擒獲。\\n\\n‧對全體敵人造成大幅度物理傷害\\n，並且降低其 TP 上升。\u3000\\n每次發動技能的同時，還能夠\\n提升傷害。這個效果最多累積次\\n數為 3 次。\\n\\n‧對從後方算起第 2 名角色為中心\\n的小範圍，造成小幅度的物理傷害\\n，並且使其麻痺。');
insert into unit_enemy_data values (306401, '野蠻之爪', 306401, 0, 16, 270, 1000, 1, 3.5, 0, 0, 0, '【物理】以捕食者之姿君臨於荒山中的\\n兇惡怪鳥獸。用著那日以繼夜的狩獵鍛鍊而出的\\n臂力與利爪，將入侵者擒獲。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自野蠻之爪\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的物理傷害，\\n\u3000並大幅度降低TP上升。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。  \\n‧以從後方數來第2名的角色為中心，\\n\u3000對小範圍內造成大幅度的物理傷害，\\n\u3000並施加麻痺狀態。');
insert into unit_enemy_data values (306500, '部落半獸人', 306500, 0, 16, 270, 1000, 1, 0.9, 0, 0, 0, '【物理】邊境之森半獸人們的首領。\\n傳播著自己獨有的民族性奇異旋律。\\n那個奇怪的聲音，\\n會使敵人掉入混亂的漩渦。\\n\\n‧對全體敵人\\n造成大幅度的魔法傷害\\n降低物理防禦力與魔法防禦力\\n並且提高自己的物理攻擊力\\n與魔法攻擊力。\\n\\n‧對前方的 4 名角色\\n造成小幅度的魔法傷害\\n敵人的ＨＰ低於70％的狀況\\n會使對方陷入混亂狀態。');
insert into unit_enemy_data values (306501, '噪音半獸人', 306501, 0, 16, 270, 1000, 1, 0.9, 0, 0, 0, '【物理】被露娜之塔的力量吸引的獸人首領。\\n用太鼓敲打著怪異的旋律，在戰場中引發騷亂。\\n\\n‧對全體敵人造成大幅度的物理傷害，並降低其\\n\u3000物理防禦力與魔法防禦力，\\n\u3000並提升自身的物理攻擊力與魔法攻擊力。\\n‧對前方的 3 名角色造成小幅度的魔法傷害，\\n\u3000並小幅度的擊飛。當敵人的 HP 低於\\n\u300070% 以下時，使其陷入混亂狀態。\\n‧對全體敵人造成對應該角色剩餘 HP 的\\n\u3000大幅度魔法傷害，同時降低其行動速度。\\n\u3000最後，解除全體敵人身上的\\n\u3000狀態提升效果。');
insert into unit_enemy_data values (306600, '猛毒火蠑螈', 306600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】為了尋求獵物，在水邊\\n注滿猛毒的山椒魚魔物。\\n累積到近乎溢出的猛毒十分危險，\\n只要接近就會使人失去意識。\\n\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並使其陷入中毒狀態。\\n\u3000再進一步使其陷入猛毒狀態。\\n\\n‧以距離最近的敵人為中心，\\n\u3000對範圍內所有敵人造成\\n\u3000小幅度的魔法傷害，\\n\u3000並使其陷入猛毒狀態。');
insert into unit_enemy_data values (306700, '醜陋可愛', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因辱罵而暴走，一隻巨大的巧克力魔物。\\n傾訴的愛意若有所誤，便將會幻化為傷人之刃。\\n\\n‧必殺技，能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的物理傷害，並且若對方為\\n\u3000火傷的狀態時，將有更多的追加傷害。\\n‧對前方的 3 名角色造成中幅度的物理傷害，\\n\u3000並且使其暈眩。\\n\u3000而當其角色的 HP 在 50% 以下時，\\n\u3000使其陷入火傷狀態。\\n・當醜陋可愛自身的 HP 低於 50%之時，\\n\u3000他的攻擊將變得更加的兇暴。');
insert into unit_enemy_data values (306701, '醜陋可愛', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因辱罵而暴走，一隻巨大的巧克力魔物。\\n傾訴的愛意若有所誤，便將會幻化為傷人之刃。\\n\\n‧必殺技，能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的物理傷害，並且若對方為\\n\u3000火傷的狀態時，將有更多的追加傷害。\\n‧對前方的 3 名角色造成中幅度的物理傷害，\\n\u3000並且使其暈眩。\\n\u3000而當其角色的 HP 在 50% 以下時，\\n\u3000使其陷入火傷狀態。');
insert into unit_enemy_data values (306702, '醜陋可愛', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因辱罵而暴走，一隻巨大的巧克力魔物。\\n傾訴的愛意若有所誤，便將會幻化為傷人之刃。\\n\\n‧必殺技，能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的物理傷害，並且若對方為\\n\u3000火傷的狀態時，將有更多的追加傷害。\\n‧對前方的 3 名角色造成中幅度的物理傷害，\\n\u3000並且使其暈眩。\\n\u3000而當其角色的 HP 在 50% 以下時，\\n\u3000使其陷入火傷狀態。\\n・當醜陋可愛自身的 HP 低於 50%之時，\\n\u3000他的攻擊將變得更加的兇暴。');
insert into unit_enemy_data values (306703, '醜陋可愛', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因辱罵而暴走，一隻巨大的巧克力魔物。\\n傾訴的愛意若有所誤，便將會幻化為傷人之刃。\\n\\n‧必殺技，能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的物理傷害，並且若對方為\\n\u3000火傷的狀態時，將有更多的追加傷害。\\n‧對前方的 3 名角色造成中幅度的物理傷害，\\n\u3000並且使其暈眩。\\n\u3000而當其角色的 HP 在 50% 以下時，\\n\u3000使其陷入火傷狀態。\\n・當醜陋可愛自身的 HP 低於 50%之時，\\n\u3000他的攻擊將變得更加的兇暴。');
insert into unit_enemy_data values (306704, '醜陋可愛', 306700, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE1】將巧克力大噴射，並且隱藏身軀。\\n若持續受到敵人的攻擊，將會炸裂憤怒的一擊。\\n\\n‧對全體敵人造成小幅度的物理傷害，並且在戰鬥\\n\u3000期間造成火傷狀態。\\n・在躲於巧克力保壘之中的期間，醜陋可愛將會\\n\u3000中幅度提升自身的物理防禦力。\\n\u3000在巧克力保壘的狀態解除之際，\\n\u3000將會依照在狀態中所受到傷害量，\\n\u3000對敵人來造成物理傷害。\\n\u3000並且醜陋可愛自身的物理防禦力，將會大幅度\\n\u3000的降低。');
insert into unit_enemy_data values (306705, '醜陋可愛', 306700, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE2】可可亞烏拉挺身而出，\\n 一頭栽進了死地。\\n 皆是為了從被激化的魔物手\\n 中，保護最珍惜的人們。\\n\\n‧必殺技，能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的物理傷害。\\n・可可亞烏拉將會以夥伴的身份出現。\\n\u3000當可可亞烏拉出現時，將會中幅度提升\\n\u3000全體角色的物理攻擊力、\\n\u3000魔法攻擊力和行動速度。\\n・對全體敵人造成小幅度魔法傷害，\\n\u3000並且大幅度的降低行動速度。\\n');
insert into unit_enemy_data values (306706, '醜陋可愛', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【MODE3】震耳欲聾的咆哮，壟罩著整個會場\\n交織愛恨情仇的情人節事變終究迎向了大結局。\\n\\n‧必殺技，能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的物理傷害，並且若對方為\\n\u3000火傷的狀態時，將有更多的追加傷害。\\n‧對前方的 3 名角色造成中幅度的物理傷害，\\n\u3000並且使其暈眩。\\n\u3000且當其角色的 HP 在 50%以下時，\\n\u3000使其陷入火傷狀態。');
insert into unit_enemy_data values (306708, '醜陋可愛', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因辱罵而暴走，一隻巨大的巧克力魔物。\\n傾訴的愛意若有所誤，便將會幻化為傷人之刃。\\n\\n‧必殺技可以對敵方全體依照其角色最大HP\\n\u3000造成大幅度的物理傷害，並且若對方為\\n\u3000火傷的狀態時，將有更多的追加傷害。\\n‧對前方3名角色依照其角色最大HP造成\\n\u3000中幅度的物理傷害，\\n\u3000並使其陷入火傷狀態。');
