insert into unit_enemy_data values (306900, '三重搖滾', 306900, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】被過去的人們祭祀著的三戰士的石像。\\n互相增強各自的能力，發出必殺的一擊。\\n\\n・容易被物理攻擊力下降與魔法攻擊力下降的效果\\n所影響。\\n・極大幅度提高自己的物理攻擊力與魔法攻擊力\\n\u3000對全體敵人造成中幅度的物理傷害與\\n\u3000中幅度的魔法傷害。\\n・對前方範圍內的敵人造成小幅度的魔法傷害。\\n・對全體敵人造成中幅度的物理傷害。');
insert into unit_enemy_data values (306901, '三重搖滾', 306901, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】被過去的人們祭祀著的三戰士的石像。\\n互相增強各自的能力，發出必殺的一擊。\\n\\n‧更加地容易受到物理攻擊力降低與魔法攻擊力\\n\u3000降低的效果影響。\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自三重岩所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會依照自身的物理攻擊力，對敵方全體\\n\u3000造成物理比例傷害，並依照自身的魔法攻擊力，\\n\u3000對敵方全體造成魔法比例傷害。同時大幅度\\n\u3000提升自身的物理攻擊力與魔法攻擊力。此技能\\n\u3000造成的傷害將不會恢復TP。\\n‧對敵方全體造成大幅度的物理傷害，同時依照\\n\u3000自身的物理攻擊力，對進行物理攻擊的敵方全體\\n\u3000降低其物理攻擊力，並依照自身的魔法攻擊力，\\n\u3000對進行魔法攻擊的敵方全體降低其魔法攻擊力。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧普通攻擊會對距離最近的1名敵人，依照自身的\\n\u3000物理攻擊力，造成固定的物理傷害，並依照自身\\n\u3000的魔法攻擊力，造成固定的魔法傷害。');
insert into unit_enemy_data values (306902, '三重搖滾', 306902, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】被過去的人們祭祀著的三戰士的石像。\\n互相增強各自的能力，發出必殺的一擊。\\n\\n‧更加地容易受到物理攻擊力降低與魔法攻擊力\\n\u3000降低的效果影響。\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧戰鬥開始時，展開小幅度降低敵方全體TP上升\\n\u3000的領域。\\n‧戰鬥開始時，對自身施加TP降低減少50％\\n\u3000效果。\\n‧中幅度降低來自三重岩所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的三重岩\\n\u3000TP恢復量。\\n‧必殺技會依照自身的物理攻擊力，對敵方全體\\n\u3000造成物理比例傷害，並依照自身的魔法攻擊力，\\n\u3000對敵方全體造成魔法比例傷害。同時，大幅度\\n\u3000提升自身的物理攻擊力與魔法攻擊力。此技能\\n\u3000造成的傷害將不會恢復TP。\\n‧對敵方全體造成大幅度的物理傷害，同時依照\\n\u3000自身的物理攻擊力，對進行物理攻擊的敵方全體\\n\u3000降低其物理攻擊力，並依照自身的魔法攻擊力，\\n\u3000對進行魔法攻擊的敵方全體降低其魔法攻擊力。\\n\u3000此技能造成的傷害將不會恢復TP。此技能所\\n\u3000造成的狀態異常，即使傷害無效也會施加。\\n‧普通攻擊會對距離最近的1名敵人，依照自身的\\n\u3000物理攻擊力，造成固定的物理傷害，並依照自身\\n\u3000的魔法攻擊力，造成固定的魔法傷害。');
