insert into unit_enemy_data values (307100, '機械莉瑪（模式1）', 307100, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '堅固且擁有多樣招式的謎之巨大兵器的第一型態。\\n以蘋果型補給機能夠充填未知的能量，並且還能\\n使出將廣範圍焦土化的連續攻擊，請小心！\\n\\n‧必殺技能夠對全體敵人造成大幅度的物理傷害。\\n‧對前方的 1 名角色造成小幅度的物理傷害。\\n‧於一定範圍內，展開降低物理攻擊力與迴避，\\n\u3000同時造成物理傷害的領域。\\n\u3000並且解除前方範圍內角色的\\n\u3000素質提升效果。\\n‧對前方範圍內造成中幅度的物理傷害，\\n\u3000且提升自己的 TP 上升。');
insert into unit_enemy_data values (307101, '機械莉瑪（模式2）', 307101, 0, 16, 270, 3000, 1, 1.2, 0, 0, 0, '完全地切換到進攻模式的謎之巨大兵器的第二型態。\\n核心莉瑪反應爐所釋放的光線，能將大地燃燒殆盡。\\n趁機用大量傷害將無防備的巨大兵器狠狠揍一頓吧！\\n\\n‧必殺技能夠對前方小範圍內造成特大幅度的\\n\u3000物理傷害，中範圍處造成大幅度的物理傷害，\\n\u3000廣範圍處則是造成中幅度的物理傷害，\\n\u3000並且大幅度向後擊飛。\\n‧對前方的 1 名角色造成小幅度的物理傷害。\\n‧對隨機的 1 名角色造成小幅度的物理傷害。\\n‧於一定範圍內，展開大幅度降低物理攻擊力\\n\u3000與魔法攻擊力，同時造成魔法傷害的領域。\\n\u3000同時使魔法攻擊力最高的 2 名角色沉默。');
insert into unit_enemy_data values (307102, '機械莉瑪（模式3）', 307102, 0, 16, 270, 3000, 1, 5.5, 0, 0, 0, '能夠自我修復耗損的謎之巨大兵器的第三型態。\\n對破綻百出的本體攻擊時，將會有防衛機器人阻撓。\\n用範圍攻擊滅掉那些機器人，並且把握這個好機會！\\n\\n‧將全體敵人大幅度向後擊飛，\\n\u3000並且召喚 3 隻迷你機械莉瑪。\\n‧迷你機械莉瑪能夠對前方的 1 名\\n\u3000角色造成小幅度的物理傷害。\\n‧迷你機械莉瑪能夠對隨機的 1 名\\n\u3000角色造成中幅度的魔法傷害。\\n‧對全體敵人依照其剩餘 HP 造成\\n\u3000特大幅度的物理比例傷害。');
insert into unit_enemy_data values (307200, '巴西利斯克', 307200, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】追求月之魔力因而飛來的大怪鳥。\\n統馭著雛鳥，且揮舞著石化的詛咒之劍。\\n\\n・對未處於石化狀態的全體角色，造成大幅度的\\n\u3000魔法傷害，且短時間內石化。\\n\u3000對石化狀態的全體角色，\\n\u3000造成最大HP80%的傷害。\\n‧對前方 3 名角色造成小幅度的魔法傷害。\\n\u3000並且此技能傷害所影響造成的中毒狀態，\\n\u3000將會使角色陷入長時間的石化。\\n・對前方範圍內的敵人給予中幅度的物魔法傷害，\\n\u3000並且使其中毒。\\n\u3000當自身的HP在50%以下時，額外將全體敵人\\n\u3000向後擊飛，並且召喚咯咯。\\n・咯咯能夠對前方範圍內物理攻擊力最高的\\n\u30001 名角色，造成小幅度的物理傷害，並且使其\\n\u3000陷入中毒狀態。\\n\u3000另外當自己的HP低於最大HP35%時，\\n\u3000將會對物理攻擊力最高的同伴\\n\u3000中幅度的提高TP，\\n\u3000而自己將會無法繼續戰鬥。');
insert into unit_enemy_data values (307201, '巴西利斯克', 307201, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】帶著石化詛咒之劍的怪鳥。\\n用尾部毒蛇的毒牙侵蝕獵物，\\n並用詛咒之劍的石化阻止冒險者的腳步。\\n\\n‧能夠將飛行狀態的敵人設為攻擊目標。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自巴西利斯克所有攻擊產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對沒有陷入無法行動狀態的敵方全體\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並施加石化狀態。\\n\u3000對陷入無法行動狀態的敵方全體\\n\u3000造成最大HP100％的魔法比例傷害。\\n\u3000此技能造成的傷害，將不會恢復TP。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧造成前方廣範圍的特大幅度魔法傷害，\\n\u3000敵人陷入中毒狀態時，再施加石化狀態。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成特大幅度的魔法傷害，\\n\u3000並施加中毒狀態。\\n\u3000被此技能施加的中毒傷害，將不會恢復TP。');
insert into unit_enemy_data values (307202, '巴西琉斯', 307202, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】阻撓穿越森林的冒險者\\n的石化怪鳥。會以有毒瘴氣侵蝕，\\n並用石化確實給予致命一擊。\\n\\n‧必殺技會對敵方全體\\n\u3000造成中幅度的魔法傷害，\\n\u3000施加石化狀態。\\n\u3000且解除敵方全體的能力\\n\u3000提升效果。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\\n‧對敵方全體\\n\u3000造成中幅度的魔法傷害，\\n\u3000施加中毒狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
