insert into unit_enemy_data values (307401, '貝特魯吉烏斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在露格尼卡臭名昭彰的魔女教幹部。\\n即使來到了異世界仍貫徹著自己的信念，\\n以強大權能「不可視之手」的力量在暗中活動著。\\n\\n‧必殺技能對全體敵人依照其最大HP造成\\n\u3000魔法比例傷害。\\n‧對前方範圍內的所有角色造成中幅度的魔法傷害\\n\u3000並降低其 TP 。\\n\u3000且對全體敵人造成中幅度的魔法傷害。\\n‧對後方 2 名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n‧當左側的目標被破壞時，將會\\n\u3000小幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000中幅度降低魔法攻擊力。\\n‧當中央的目標被破壞時，將會\\n\u3000大幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000特大幅度降低魔法攻擊力。\\n‧當右側的目標被破壞時，將會\\n\u3000大幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000大幅度降低魔法攻擊力。');
insert into unit_enemy_data values (307402, '貝特魯吉烏斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在露格尼卡臭名昭彰的魔女教幹部。\\n即使來到了異世界仍貫徹著自己的信念，\\n以強大權能「不可視之手」的力量在暗中活動著。\\n\\n‧必殺技能對全體敵人依照其最大HP造成\\n\u3000魔法比例傷害。\\n‧對前方範圍內的所有角色造成中幅度的魔法傷害\\n\u3000並降低其 TP 。\\n\u3000且對全體敵人造成中幅度的魔法傷害。\\n‧對後方 2 名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n‧當左側的目標被破壞時，將會\\n\u3000小幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000中幅度降低魔法攻擊力。\\n‧當中央的目標被破壞時，將會\\n\u3000大幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000特大幅度降低魔法攻擊力。\\n‧當右側的目標被破壞時，將會\\n\u3000大幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000大幅度降低魔法攻擊力。\\n‧當剩餘時間低於40秒時，\\n\u3000其攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (307403, '貝特魯吉烏斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在露格尼卡臭名昭彰的魔女教幹部。\\n即使來到了異世界仍貫徹著自己的信念，\\n以強大權能「不可視之手」的力量在暗中活動著。\\n\\n‧必殺技能對全體敵人依照其最大HP造成\\n\u3000魔法比例傷害。\\n‧對前方範圍內的所有角色造成中幅度的魔法傷害\\n\u3000並降低其 TP 。\\n\u3000且對全體敵人造成中幅度的魔法傷害。\\n‧對後方 2 名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n‧當左側的目標被破壞時，將會\\n\u3000小幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000中幅度降低魔法攻擊力。\\n‧當中央的目標被破壞時，將會\\n\u3000大幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000特大幅度降低魔法攻擊力。\\n‧當右側的目標被破壞時，將會\\n\u3000大幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000大幅度降低魔法攻擊力。\\n‧當剩餘時間低於40秒時，\\n\u3000其攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (307404, '貝特魯吉烏斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】召喚出雷姆和拉姆的闇影。\\n被大罪司教的狂氣所吞噬的雙子之影，\\n成了魔女教的爪牙，擋住夥伴們的去路。\\n\\n‧對前方的 1 名敵人造成物理傷害。');
insert into unit_enemy_data values (307405, '貝特魯吉烏斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】威力增幅的強大權能\\n毫無慈悲的為怠惰的怨敵帶來終焉。\\n\\n‧必殺技能對全體敵人依照其最大HP造成\\n\u3000魔法比例傷害。\\n‧對後方 2 名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n‧對剩餘 HP 比例最少的 1 名敵人\\n\u3000造成大幅度的魔法傷害，並\\n\u3000賦予倒計時效果。\\n\u3000當倒數到 0 時，\\n\u3000會對該角色依照其最大HP造成\\n\u3000魔法比例傷害。\\n‧當左側的目標被破壞時，將會\\n\u3000小幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000中幅度降低魔法攻擊力。\\n‧當中央的目標被破壞時，將會\\n\u3000中幅度降低物理防禦力、\\n\u3000大幅度降低魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000特大幅度降低魔法攻擊力。\\n‧當右側的目標被破壞時，將會\\n\u3000中幅度降低物理防禦力、\\n\u3000大幅度降低魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000大幅度降低魔法攻擊力。');
insert into unit_enemy_data values (307406, '貝特魯吉烏斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】理性和人類樣貌盡失的狂熱信徒，\\n只依靠執念而行動，為忠愛奮戰致死。\\n\\n‧必殺技能對全體敵人依照其最大HP造成\\n\u3000魔法比例傷害。\\n‧對前方範圍內的所有角色造成中幅度的魔法傷害\\n\u3000並降低其 TP 。\\n\u3000且對全體敵人造成中幅度的魔法傷害。\\n‧對後方 2 名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n‧當左側的目標被破壞時，將會\\n\u3000小幅度降低物理防禦力與魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000中幅度降低魔法攻擊力。\\n‧當中央的目標被破壞時，將會\\n\u3000中幅度降低物理防禦力、\\n\u3000大幅度降低魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000特大幅度降低魔法攻擊力。\\n‧當右側的目標被破壞時，將會\\n\u3000中幅度降低物理防禦力、\\n\u3000大幅度降低魔法防禦力，\\n\u3000且小幅度降低物理攻擊力、\\n\u3000大幅度降低魔法攻擊力。\\n‧當剩餘時間低於40秒時，\\n\u3000其攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (307500, '席茲', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】纏繞著猛然兇狂暴風的大怪鳥。\\n從魔物圖鑑中脫逃，並將所見之物破壞殆盡。\\n\\n‧對敵方全體依照其最大 HP \\n\u3000造成大幅度的物理傷害。\\n‧以距離最近的敵人為中心，對範圍內的\\n\u30002名敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。');
insert into unit_enemy_data values (307501, '席茲', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】纏繞著猛然兇狂暴風的大怪鳥。\\n從魔物圖鑑中脫逃，並將所見之物破壞殆盡。\\n\\n‧對敵方全體依照其最大 HP \\n\u3000造成大幅度的物理傷害。\\n‧以距離最近的敵人為中心，對範圍內的\\n\u30002名敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。');
insert into unit_enemy_data values (307502, '席茲', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】纏繞著猛然兇狂暴風的大怪鳥。\\n從魔物圖鑑中脫逃，並將所見之物破壞殆盡。\\n\\n‧對敵方全體依照其最大 HP 造成大幅度的\\n\u3000物理傷害。\\n‧以距離最近的敵人為中心，對範圍內的\\n\u30002名敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n‧對前方範圍內的敵人依照其最大 HP 造成\\n\u3000中幅度的物理傷害。\\n‧當席茲自身的 HP 低於 50％ 時，牠的攻擊\\n\u3000將會變得更加兇暴。');
insert into unit_enemy_data values (307503, '席茲', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】纏繞著猛然兇狂暴風的大怪鳥。\\n從魔物圖鑑中脫逃，並將所見之物破壞殆盡。\\n\\n‧對敵方全體依照其最大 HP 造成大幅度的\\n\u3000物理傷害。\\n‧以距離最近的敵人為中心，對範圍內的\\n\u30002名敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n‧對前方範圍內的敵人依照其最大 HP 造成\\n\u3000中幅度的物理傷害。\\n‧當席茲自身的 HP 低於 50％ 時，牠的攻擊\\n\u3000將會變得更加兇暴。');
insert into unit_enemy_data values (307504, '紅極鳥', 307504, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】貪食塔之魔力，到處為非作歹的巨大魔鳥。\\n若因大意過於接近，將會被那暴風與豪雷撕成兩半。\\n\\n‧必殺技會對敵方全體造成\\n\u3000大幅度的物理傷害。\\n‧以從前方數來第2名的角色為中心，造成\\n\u3000小範圍的小幅度的物理傷害，並造成暈眩。\\n‧對前方範圍內的敵人造成中幅度的物理傷害。');
insert into unit_enemy_data values (307505, '席茲', 307505, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】纏繞著猛然兇狂暴風的大怪鳥。\\n從魔物圖鑑中脫逃，並將所見之物破壞殆盡。\\n\\n‧對全體敵人依照其最大 HP 造成大幅度的\\n\u3000物理傷害。\\n‧對前方範圍內的敵人造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n‧對前方範圍內的敵人依照其最大 HP 造成\\n\u3000中幅度的物理傷害。\\n‧當席茲自身的 HP 低於 50% 時，牠的攻擊\\n\u3000將會變得更加兇暴。');
insert into unit_enemy_data values (307600, '狂暴王冠', 307600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】謀劃把露娜之塔當作自己的舞台，\\n魔界的小丑。以開朗的笑容和瘋狂，玩弄觀眾的命。\\n\\n・對全體敵人造成大幅度的魔法傷害，\\n\u3000並使其陷入黑暗狀態。\\n‧隨機分 6 次對 1 名角色造成小幅度的物理傷害\\n\u3000對全體敵人造成大的物理傷害。\\n\u3000愈是在前排的角色\\n\u3000會變得愈容易被這個攻擊擊中。\\n‧在前方中距離召喚定時炸彈。\\n\u3000炸彈被召喚出來後不僅會在8 秒後大爆炸，\\n\u3000也會對範圍內的全體敵人造成中幅度的物理傷害。\\n\u3000但是，到爆炸為止前炸彈的ＨＰ\\n\u3000低於1％後，會造成狂暴王冠\\n\u3000最大ＨＰ15％的物理傷害。');
insert into unit_enemy_data values (307700, '阿爾瑪', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】自遠古起守護著迷宮的防衛兵器。在克服所有陷阱的最深處，靜靜地等待入侵者。\\n\\n‧對敵方全體依照其最大HP \\n\u3000造成特大幅度的魔法傷害。\\n‧以距離最近的敵人為中心，對最靠近的\\n\u30003名敵人造成中幅度的物理傷害。');
insert into unit_enemy_data values (307701, '阿爾瑪', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】自遠古起守護著迷宮的防衛兵器。在克服所有陷阱的最深處，靜靜地等待入侵者。\\n\\n‧對敵方全體依照其最大HP \\n\u3000造成特大幅度的魔法傷害。\\n‧以距離最近的敵人為中心，\\n\u3000對3名敵人造成中幅度的物理傷害。');
insert into unit_enemy_data values (307702, '阿爾瑪', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】自遠古起守護著迷宮的防衛兵器。在克服所有陷阱的最深處，靜靜地等待入侵者。\\n\\n‧對敵方全體依照其最大HP\\n\u3000造成特大幅度的魔法傷害。\\n‧以距離最近的敵人為中心，\\n\u3000對3名敵人造成中幅度的物理傷害。\\n‧對剩餘HP最高的敵人依照其最大HP \\n\u3000造成中幅度的魔法傷害，並使其麻痺。\\n‧當阿爾瑪自身的HP低於50％時，\\n\u3000其攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (307703, '阿爾瑪', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】自遠古起守護著迷宮的防衛兵器。在克服所有陷阱的最深處，靜靜地等待入侵者。\\n\\n‧對敵方全體依照其最大HP\\n\u3000造成特大幅度的魔法傷害。\\n‧以距離最近的敵人為中心，\\n\u3000對3名敵人造成中幅度的物理傷害。\\n‧對剩餘HP最高的敵人依照其最大HP \\n\u3000造成中幅度的魔法傷害，並使其麻痺。\\n‧當阿爾瑪自身的HP低於50％時，\\n\u3000其攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (307704, '詛咒‧阿爾瑪', 307704, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因為露娜之塔的魔力暴走的，古代兵器。\\n因魔力而凶暴化的守護之力，成了粉碎一切的災厄。\\n\\n‧對全體敵人造成特大幅度的物理傷害。\\n‧對前方小範圍內的隨機敵人角色，造成\\n\u3000共計3次的大幅度物理傷害。\\n‧對魔法攻擊力最高的1名角色，\\n\u3000造成中幅度的物理傷害，同時使其麻痺。\\n\u3000並且對該角色周圍小範圍的敵人角色，\\n\u3000造成中幅度的物理傷害。');
insert into unit_enemy_data values (307800, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】自古以來便司掌島嶼天候至今的守護神。\\n因苦求友人之心而鬱，導致演變成如此不祥的樣貌。\\n大地萬物皆將被那哀哭之雨淹沒殆盡。\\n\\n・必殺技可對全體敵人造成大幅度\\n\u3000的魔法傷害，同時擊飛。\\n\u3000並且，對前方範圍內全體敵人造成\\n\u3000中幅度的魔法追加傷害。\\n・對離最遠的 1 名敵人造成中幅度的魔法傷害，\\n\u3000並且小幅度的將其拉近。');
insert into unit_enemy_data values (307801, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】自古以來便司掌島嶼天候至今的守護神。\\n因苦求友人之心而鬱，導致演變成如此不祥的樣貌。\\n大地萬物皆將被那哀哭之雨淹沒殆盡。\\n\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，並將其向後擊飛。\\n\u3000同時以距離最近的敵人為中心，\\n\u3000對範圍內的敵方全體依照其最大HP\\n\u3000造成魔法比例傷害。\\n‧對離最遠的1名敵人\\n\u3000造成中幅度的魔法傷害，\\n\u3000並且小幅度的將其拉近。');
insert into unit_enemy_data values (307802, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】自古以來便司掌島嶼天候至今的守護神。\\n因苦求友人之心而鬱，導致演變成如此不祥的樣貌。\\n大地萬物皆將被那哀哭之雨淹沒殆盡。\\n\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，並將其向後擊飛。\\n\u3000同時以距離最近的敵人為中心，\\n\u3000對範圍內的敵方全體\\n\u3000依照其最大HP造成魔法比例傷害。\\n‧對前方範圍內的敵方全體造成中幅度的魔法傷害，\\n\u3000同時小幅度擊飛。並且對離最近的1名敵人，\\n\u3000造成中幅度的魔法追加傷害。\\n‧對離最遠的1名敵人造成中幅度的魔法傷害，\\n\u3000並且小幅度的將其拉近。\\n‧當雨神大人自身HP低於40％時，\\n\u3000其攻擊將會便得更加狂暴。');
