insert into unit_enemy_data values (307804, '雨神大人', 307801, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】因悲嘆而失去理智的雨吉，\\n將眾人的思念拒之千里，而那焦慮的內心，\\n甚至就連晴吉那所苦求殷盼許久的聲音，\\n也觸及不了。\\n\\n・展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000此護盾與傷害量無關，而是每當受到攻擊時，\\n\u3000便會漸漸的降低其耐久度。\\n\u3000當受到 200 次的攻擊後，護盾將會被破壞。\\n\u3000\\n・必殺技能夠對全體敵人造成大幅度的魔法傷害，\\n\u3000同時造成擊飛。並且對前方範圍內全體敵人造成\\n\u3000中幅度的追加魔法傷害。\\n・對前方範圍內的全體敵人造成中幅度的魔法傷害，\\n\u3000同時小幅度擊飛。並且對離最近的 1 名敵人，\\n\u3000造成中幅度的魔法追加傷害。');
insert into unit_enemy_data values (307805, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】哀哭之雨，伴隨著哀戚一同降下。\\n那漆黑的雨滴，夾帶對悲哀的渴望，\\n將人們的身心俱蝕。\\n\\n・在戰鬥開始時，祂將召喚出帶來詛咒的水晶。\\n\u3000而當詛咒的水晶仍然存在於在戰鬥場上的時候，\\n\u3000將會對全體敵人持續造成傷害。同時，\\n\u3000不論是打算以任何效果進行 HP 的恢復，\\n\u3000該恢復都將會無效，並將原本恢復量的 10%\\n\u3000轉變為傷害。\\n・必殺技能夠對全體敵人造成大幅度的魔法傷害，\\n\u3000同時造成擊飛。並且對前方範圍內全體敵人造成\\n\u3000中幅度的追加魔法傷害。\\n・對前方範圍內的全體敵人造成中幅度的魔法傷害，\\n\u3000同時小幅度擊飛。並且對離最近的一名敵人，\\n\u3000造成中幅度的魔法追加傷害。\\n・對離最遠的 1 名敵人造成中幅度的魔法傷害，\\n\u3000並且小幅度的將其拉近。');
