insert into unit_enemy_data values (308010, '奄美魔物', 308000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】從薄暮中現形的怨靈集合體，\\n將數千人魂撕成碎片。\\n\\n‧每當打倒了1隻的分身體，所剩的奄美魔物數量\\n\u3000將會減少1個。而之後，分身體將會再次被召喚。\\n‧奄美魔物將會依照所保持著的【吸魂】素質數量，\\n\u3000進而使普通攻擊和各種技能等等的效果受到強化。\\n\u3000\\n‧每次的普通攻擊，都會追加6個【吸魂】。\\n‧攻擊時奄美魔物會追加1個【吸魂】，\\n\u3000召喚3隻分身，並且將全體敵人\\n\u3000小幅度擊飛。\\n‧每當奄美魔物受到暴擊傷害時，\\n\u3000將會減少奄美魔物的2個【吸魂】。\\n‧每當分身體受到暴擊傷害時，\\n\u3000則會減少奄美魔物的1個【吸魂】。');
insert into unit_enemy_data values (308011, '奄美魔物', 308000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】在黎明前黑暗中肆虐的魑魅，\\n終於為了吞噬三千世界靈魂而露出獠牙。\\n\\n‧奄美魔物將會依照所保持著的【吸魂】素質數量，\\n\u3000進而使普通攻擊和各種技能等等的效果受到強化。\\n\u3000\\n‧每次的普通攻擊，都會追加6個【吸魂】。\\n‧必殺技會對全體敵人依照\\n\u3000所保持著的【吸魂】數量\\n\u3000造成魔法比例傷害，\\n\u3000並且追加5個【吸魂】。\\n‧對全體敵人造成小幅度的魔法傷害再減少TP，\\n\u3000並且展開降低TP上升的\\n\u3000領域。此領域的TP減少量效果值，\\n\u3000將會依照奄美魔物所保持著的【吸魂】\\n\u3000數量多寡而有所強化。\\n‧攻擊時奄美魔物會追加1個【吸魂】，\\n\u3000召喚3隻分身，並且將全體敵人\\n\u3000小幅度擊飛。\\n‧每當奄美魔物受到暴擊傷害時，\\n\u3000將會減少奄美魔物的2個【吸魂】。\\n‧每當分身體受到暴擊傷害時，\\n\u3000則會減少奄美魔物的1個【吸魂】。\\n‧當剩餘時間不到30秒時，其攻擊將會\\n\u3000變得更加狂暴。\\n\u3000並且追加20個【吸魂】。');
insert into unit_enemy_data values (308012, '奄美魔物', 308000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】以善良之魂為食，是兇殘至極的惡靈。\\n為貪求更好的祭品，那眾多的分身體正蠢蠢欲動。\\n\\n‧奄美魔物將會依照所保持著的【吸魂】素質數量，\\n\u3000進而使普通攻擊和各種技能等等的效果受到強化。\\n\u3000\\n‧每次的普通攻擊，都會追加6個【吸魂】。\\n‧必殺技會對全體敵人依照\\n\u3000所保持著的【吸魂】數量\\n\u3000造成魔法比例傷害，\\n\u3000並且追加5個【吸魂】。\\n‧對全體敵人造成小幅度的魔法傷害再減少TP，\\n\u3000並且展開降低TP上升的\\n\u3000領域。此領域的TP減少量效果值，\\n\u3000將會依照奄美魔物所保持著的【吸魂】\\n\u3000數量多寡而有所強化。\\n‧攻擊時奄美魔物會追加1個【吸魂】，\\n\u3000召喚3隻分身，並且將全體敵人\\n\u3000小幅度擊飛。\\n‧每當奄美魔物受到暴擊傷害時，\\n\u3000將會減少奄美魔物的2個【吸魂】。\\n‧每當分身體受到暴擊傷害時，\\n\u3000則會減少奄美魔物的1個【吸魂】。');
insert into unit_enemy_data values (308100, '天塔帕斯', 308100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】君臨於海邊的巨大軟體魔物。\\n以強韌的觸手與鋼鐵的大鉗，將一切都摧之殆盡。\\n\\n‧對敵方全體依照其最大HP造成大幅度的\\n\u3000物理傷害。\\n‧以第3近的敵人為中心，\\n\u3000對遠方3名敵人造成中幅度的魔法傷害，\\n\u3000使其陷入麻痺狀態，並且使其物理攻擊力、\\n\u3000魔法攻擊力特大幅度下降。\\n‧每當經過一定的時間，將會造成\\n\u3000敵方單體超特大幅度的物理傷害。\\n‧當天塔帕斯自身的HP低於40％時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (308101, '天塔帕斯', 308100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】君臨於海邊的巨大軟體魔物。\\n以強韌的觸手與鋼鐵的大鉗，將一切都摧之殆盡。\\n\\n‧對敵方全體依照其最大HP造成大幅度的\\n\u3000物理傷害。\\n‧對距離最遠的3名敵人造成中幅度的魔法傷害，\\n\u3000使其陷入麻痺狀態，並且使其物理攻擊力、\\n\u3000魔法攻擊力特大幅度下降。');
insert into unit_enemy_data values (308102, '天塔帕斯', 308100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】君臨於海邊的巨大軟體魔物。\\n以強韌的觸手與鋼鐵的大鉗，將一切都摧之殆盡。\\n\\n‧對敵方全體依照其最大HP造成大幅度的\\n\u3000物理傷害。\\n‧對距離最遠的3名敵人造成中幅度的魔法傷害，\\n\u3000使其陷入麻痺狀態，並且使其物理攻擊力、\\n\u3000魔法攻擊力特大幅度下降。\\n‧當天塔帕斯自身的HP低於40％時，\\n\u3000牠的攻擊將會變得更加兇暴。');
