insert into unit_enemy_data values (308201, '劇毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】碧栽培地異常良好的曼德拉草。\\n以堅韌的根和種子的子彈，壓制眼前的敵人。\\n\\n‧對前方2名角色依照其最大HP造成物理的\\n\u3000比例傷害。\\n‧必殺技能夠對全體敵人造成大幅度的\\n\u3000物理傷害，並且依據總計所造成的傷害，\\n\u3000來恢復自己的HP。\\n‧對全體敵人連續造成小幅度的物理傷害，\\n\u3000同時降低3次的物理防禦力。\\n‧在增加自己的物理攻擊力的同時，\\n\u3000也提升自己的TP。');
insert into unit_enemy_data values (308202, '劇毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】碧栽培地異常良好的曼德拉草。\\n以堅韌的根和種子的子彈，壓制眼前的敵人。\\n\\n‧對前方2名角色依照其最大HP造成物理的\\n\u3000比例傷害。\\n‧必殺技能夠對全體敵人造成大幅度的\\n\u3000物理傷害，並且依據總計所造成的傷害，\\n\u3000來恢復自己的HP。\\n‧對全體敵人連續造成小幅度的物理傷害，\\n\u3000同時降低3次的物理防禦力。\\n‧在增加自己的物理攻擊力的同時，\\n\u3000也提升自己的TP。');
insert into unit_enemy_data values (308203, '劇毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】碧栽培地異常良好的曼德拉草。\\n以堅韌的根和種子的子彈，壓制眼前的敵人。\\n\\n‧對前方2名角色依照其最大HP造成物理的\\n\u3000比例傷害。\\n‧必殺技能夠對全體敵人造成大幅度的\\n\u3000物理傷害，並且依據總計所造成的傷害，\\n\u3000來恢復自己的HP。\\n‧對全體敵人連續造成小幅度的物理傷害，\\n\u3000同時降低3次的物理防禦力。\\n‧在增加自己的物理攻擊力的同時，\\n\u3000也提升自己的TP。\\n‧當曼德拉草自身的HP低於40%時，\\n\u3000其攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (308204, '劇毒曼德拉草', 308204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】降下猛毒的胞子，使大地萬物\\n陷入一片荒蕪。\\n以無數的殘骸做為苗床，使那猛毒的一輪，\\n華麗綻放。\\n\\n按照以下的順序重複進行行動。\\n‧當曼德拉草把自己埋進地面下時，將會變成\\n\u3000無法受到傷害的狀態。\\n‧使全體敵人陷入強力的中毒狀態。\\n‧在解除自己身上被施放的所有降低素質的狀態\\n\u3000與效果後，極大幅度降低自己的物理防禦力與\\n\u3000魔法防禦力。');
insert into unit_enemy_data values (308205, '劇毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】從觸手上滴落的水珠含有猛毒。\\n僅需一滴便能使泉水枯竭、森林腐敗，生命殞滅。\\n\\n‧對前方2名角色造成物理傷害，並且使其\\n\u3000陷入中毒狀態。\\n‧必殺技能夠對全體敵人造成大幅度的\\n\u3000物理傷害，並且依據總計所造成的傷害，\\n\u3000來恢復自己的HP。\\n\u3000對陷入中毒狀態的角色，大幅度提高傷害。\\n‧對全體敵人連續造成小幅度的物理傷害，\\n\u3000同時降低3次的物理防禦力。\\n‧在增加自己的物理攻擊力的同時，\\n\u3000也提升自己的TP。');
insert into unit_enemy_data values (308206, '劇毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】絕命的咆哮，巨腕的蹂躪。\\n如同花朵般凋零的生命。以自身臨死的一聲\\n做為搖籃曲，使亡骸陷入永眠。\\n\\n‧對前方2名角色依照其最大HP造成物理的\\n\u3000比例傷害。\\n‧必殺技能夠對全體敵人造成大幅度的\\n\u3000物理傷害，並且依據總計所造成的傷害，\\n\u3000來恢復自己的HP。\\n‧對全體敵人連續造成小幅度的物理傷害，\\n\u3000同時降低3次的物理防禦力。\\n‧在增加自己的物理攻擊力的同時，\\n\u3000也提升自己的TP。\\n‧當剩餘時間低於40秒時，其攻擊將會變得\\n\u3000更加狂暴。');
insert into unit_enemy_data values (308207, '大王花曼德拉草', 308207, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】藉由塔之魔力開花並釋放劇毒的死亡大花\\n其毒會擾亂所有的知覺，腐蝕所有生物。\\n\\n‧對前方的2名角色造成物理傷害。\\n\u3000自身的HP低於80％之時，\\n\u3000同時使其陷入中毒狀態。\\n‧必殺技會對敵方全體造成大幅度的物理傷害，\\n\u3000對陷入中毒狀態的角色造成比例傷害。\\n\u3000之後大幅度降低敵方全體的物理攻擊力，\\n\u3000並大幅度提升自己的物理攻擊力。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000降低物理防禦力。\\n‧提升自身的物理攻擊力與TP。\\n‧當剩餘HP低於80％時，\\n\u3000攻擊將會變得更加激烈且凶暴。\\n‧當它把自己埋進地面下時，將會變成\\n\u3000無法受到傷害的狀態，\\n\u3000造成敵方全體中幅度物理傷害，\\n\u3000使敵方全體長時間陷入中毒狀態。\\n\u3000同時使中毒狀態的角色陷入猛毒狀態。\\n\u3000之後，當它鑽出地面上時，\\n\u3000將解除自己身上被施放的\\n\u3000所有降低素質的狀態與效果，\\n\u3000極大幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (308300, '島鯨', 308300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】將為數眾多的探險家給吞噬的幻島之巨鯨。無慈悲地葬送了那些被慾望、幻想給憑依的愚者。\\n\\n‧對全體敵人依照其最大HP造成大幅度的\\n\u3000物理傷害。\\n‧對全體敵人造成中幅度的魔法傷害，\\n\u3000並且對島鯨自身的物理防禦力、\\n\u3000魔法防禦力給予極大幅度的上升。\\n‧對前方數來第3名角色造成大幅度的魔法傷害，\\n\u3000並且大幅度將其擊飛。\\n‧當島鯨自身的HP低於40%時，\\n\u3000牠的攻擊將會變得更加兇暴。');
