insert into unit_enemy_data values (308400, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】走不出對孩子的執念，一名亡親之靈。\\n為了和女兒享受虛偽的天倫之樂，\\n對礙事者將毫不留情痛下毒手。\\n\\n‧必殺技能夠對全體敵人依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000同時降低魔法防禦力。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。\\n\u3000同時對前方3名角色依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000並且大距離向後擊飛。');
