insert into unit_enemy_data values (308402, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】走不出對孩子的執念，一名亡親之靈。\\n為了和女兒享受虛偽的天倫之樂，\\n對礙事者將毫不留情痛下毒手。\\n\\n‧必殺技能夠對全體敵人依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000同時降低魔法防禦力。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。\\n\u3000同時對前方3名角色依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000並且大距離向後擊飛。\\n‧對距離最遠的1名敵人造成中幅度的\\n\u3000物理傷害，並且將其拉至最前面一名角色\\n\u3000的位置，同時造成暈眩。\\n‧當幻影男爵自身的HP低於40%時，其攻擊\\n\u3000將會變得更加狂暴。');
insert into unit_enemy_data values (308403, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】走不出對孩子的執念，一名亡親之靈。\\n為了和女兒享受虛偽的天倫之樂，\\n對礙事者將毫不留情痛下毒手。\\n\\n‧必殺技能夠對全體敵人依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000同時降低魔法防禦力。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。\\n\u3000同時對前方3名角色依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000並且大距離向後擊飛。\\n‧對距離最遠的1名敵人造成中幅度的\\n\u3000物理傷害，並且將其拉至最前面一名角色\\n\u3000的位置，同時造成暈眩。\\n‧當幻影男爵自身的HP低於40%時，其攻擊\\n\u3000將會變得更加狂暴。');
insert into unit_enemy_data values (308404, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】召喚【小小甜心】\\n一行人的闇影。\\n向天真的少女們招手之處，是亡者蠢動的永夜之國。\\n\\n‧對前方1名角色造成魔法傷害。');
insert into unit_enemy_data values (308405, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】當冥府之門敞開之際，\\n生者之魂將殞落於深淵。早就已經回天也乏術，\\n便以寄情之物為標的散播黑暗。\\n\\n‧必殺技能夠對全體敵人依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000同時降低魔法防禦力。\\n‧對距離最遠的1名敵人造成中幅度的\\n\u3000物理傷害，並且將其拉至最前面一名角色\\n\u3000的位置，同時造成暈眩。\\n‧對前方3名角色造成中幅度的魔法傷害，\\n\u3000中幅度降低其TP，並且中幅度恢復\\n\u3000幻影男爵的TP。\\n\u3000同時解除前方3名進行物理攻擊\\n\u3000的角色身上的增益效果，\\n\u3000對前方3名進行魔法攻擊\\n\u3000的角色追加造成大幅度的\\n\u3000魔法傷害。');
insert into unit_enemy_data values (308406, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】與深愛之人別離正逐刻逼近。\\n為了反抗這悲傷的宿命，\\n怨靈尖聲發出了陣陣寂滅的慟哭。\\n\\n‧必殺技能夠對全體敵人依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000同時降低魔法防禦力。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。\\n\u3000同時對前方3名角色依照其最大HP\\n\u3000造成魔法的比例傷害，\\n\u3000並且大距離向後擊飛。\\n‧對距離最遠的1名敵人造成中幅度的\\n\u3000物理傷害，並且將其拉至最前面一名角色\\n\u3000的位置，同時造成暈眩。\\n‧在戰鬥剛開始時，提升自己的魔法攻擊力\\n\u3000與行動速度。');
insert into unit_enemy_data values (308408, '瘋狂幻影', 308408, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】怨魂為了塔的魔力群聚而成的亡靈。\\n會以邪惡之手呼喚生者將其拖入冥府。\\n\\n‧對敵方全體造成大幅度的魔法傷害，\\n\u3000並使其魔法防禦力下降。\\n‧對最靠近的1名敵人造成大幅度的魔法傷害，\\n\u3000同時對前方3名角色造成中幅度的魔法傷害。\\n\u3000並將其擊飛。\\n‧對最遠的1名敵人造成中幅度的物理傷害，\\n\u3000並且將其拉至最前面1名角色的位置，\\n\u3000同時造成暈眩。');
insert into unit_enemy_data values (308500, '水瑚麟', 308500, 0, 16, 270, 1000, 1, 2.8, 0, 0, 0, '【物理】沉溺於月之魔力，大鬧四方的水之神獸。\\n忘卻了職責，成為了試圖將萬物徹底淹滅的狂獸。\\n\\n‧對全體敵人造成大幅度的魔法傷害，且使其畏懼。\\n\u3000陷入畏懼狀態的角色，在一定的時間之內進行\\n\u3000攻擊與HP恢復的必殺技，其效果都將減半。\\n‧對前方範圍內造成中幅度的魔法傷害，並且\\n\u3000依照命中次數的多寡，小幅度提升自身的\\n\u3000物理防禦力。同時展開能構造成\\n\u3000魔法傷害的領域。\\n‧對前方1名角色造成大幅度的魔法傷害，\\n\u3000並且消費自身的HP，對全體敵人造成\\n\u3000中幅度的魔法追加傷害。\\n\u3000同時，小幅度提升自己的物理攻擊力與\\n\u3000魔法攻擊力。');
